INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent (e) de blanchisserie', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser tout ou partie des activit&#xE9;s de r&#xE9;ception, de tri, de lavage, de s&#xE9;chage, de pliage et de r&#xE9;exp&#xE9;dition du linge hospitalier, selon les standards de qualit&#xE9; requis<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Blanchisseur(euse)<br>Agent(e) de production linge<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Exp&#xE9;rience pr&#xE9;alable dans une unit&#xE9; de production<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Lavage du linge </li> <li>Pliage et conditionnement du linge </li> <li>R&#xE9;ception et triage du linge </li> <li>Traitement de finition du linge (s&#xE9;chage- repassage, pliage-triage des tenues) </li> <li>V&#xE9;rification / contr&#xF4;le du fonctionnement et essais de mat&#xE9;riels, &#xE9;quipements/ des installations sp&#xE9;cifiques &#xE0; son domaine </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Conduire des installations, des &#xE9;quipements relatifs au domaine de comp&#xE9;tence, en optimiser le fonctionnement </li> <li>&#xC9;valuer la qualit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riau </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Reconnaitre la mati&#xE8;re textile </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Installations- &#xE9;quipements de blanchisserie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; aux processus de nettoiement du linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Maintenance industrielle / &#xE9;quipements </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Produits de nettoiement du linge </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Protocole d&apos;hygi&#xE8;ne du linge </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologie des textiles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrement de blanchisserie pour la mise en place des procedures<br>Technicien(s) de maintenance des &#xE9;quipements pour le d&#xE9;pannage des mat&#xE9;riels<br>Personnels des unit&#xE9;s de soins pour les approvisionnements en linge<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAP EATEI<br>CAP BP<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Coprs des personnelles ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de logistique<br>Ling&#xE8;re-couturi&#xE8;re<br>Encadrant de production blanchisserie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent (e) d''accueil', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Recevoir, renseigner et orienter les usagers vers l&apos;interlocuteur ou le service recherch&#xE9;.<br>R&#xE9;pondre aux diff&#xE9;rents appels t&#xE9;l&#xE9;phoniques, renseigner le public et / ou orienter les appels t&#xE9;l&#xE9;phoniques en fonction de leur nature<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>H&#xF4;te(sse) d&apos;accueil<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Standardiste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement ou recherche de personnes dans les locaux, lieux internes (services, lieux de prise en charge.) ou externes </li> <li>Accueil des personnes (patients, usagers, familles, etc.) </li> <li>Contr&#xF4;le d&apos;identit&#xE9; des visiteurs </li> <li>Information et conseil &#xE0; des tiers (agents, patients, familles, etc.) dans son domaine d&apos;activit&#xE9; </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Remise de badges, laissez- passer, dossier d&apos;accueil, plans d&apos;acc&#xE8;s, etc. </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et orienter des personnes, des groupes, des publics </li> <li>G&#xE9;rer ses &#xE9;motions, son stress </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>Reformuler une demande (d&apos;information, de renseignement, etc.), en r&#xE9;sumer les points-cl&#xE9;s et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; professionnelle </li> <li>Renseigner des personnes au regard de son m&#xE9;tier </li> <li>S&apos;exprimer en face -&#xE0;- face aupr&#xE8;s d&apos;une ou plusieurs personnes </li> <li>S&apos;exprimer en public </li> <li>Se r&#xE9;p&#xE9;rer et s&apos;orienter dans l&apos;espace </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Accueil t&#xE9;l&#xE9;phonique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion du stress </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;accueil et &#xE0; l&apos;orientation des personnes </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Usagers pour toute r&#xE9;ponse &#xE0; des demandes d&apos;information, de renseignements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint administratif hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de gestion administrative<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Acheteur(euse)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Analyser et prospecter les march&#xE9;s, choisir les fournisseurs en fonction des besoins et des imp&#xE9;ratifs de co&#xFB;ts, de d&#xE9;lais, de qualit&#xE9;, de volume.<br>N&#xE9;gocier les conditions d&apos;achat et d&apos;approvisionnement des produits, services et travaux.<br>Evaluer et suivre les performances de ses achats.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Acheteur(euse) public<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gestionnaire de groupement de commandes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Analyse des offres et aide au choix des fournisseurs sous la supervision du responsable achat </li> <li>Conception du Plan d&apos;Action Achat (PAA) annuel (actions d&apos;achat quantifi&#xE9;es, actions de progr&#xE8;s sur les march&#xE9;s en cours, outils de communication) pour proposition au responsable achat </li> <li>Contr&#xF4;le et suivi des actions achat d&#xE9;finies au PAA dans son domaine de comp&#xE9;tence </li> <li>D&#xE9;finition des &#xE9;l&#xE9;ments cl&#xE9;s des documents de consultation et notamment proposition des crit&#xE8;res de choix </li> <li>Elaboration et suivi des tableaux de bord de suivi des gains achat en lien avec le PAA ainsi que les indicateurs du PAA dan son domaine de comp&#xE9;tence </li> <li>Mise en &#x153;uvre du Plan d&apos;Action Achat (PAA) sous la supervision du responsable achat dans son domaine de comp&#xE9;tence </li> <li>Mise en &#x153;uvre et animation du dialogue avec les prescripteurs dans son domaine de comp&#xE9;tence </li> <li>N&#xE9;gociation des contrats avec les fournisseurs dans son domaine de comp&#xE9;tence </li> <li>Prospection fournisseurs / produits -march&#xE9;s </li> <li>Revue des cahiers des charges des produits, services et travaux achet&#xE9;s en collaboration avec les services utilisateurs et les gestionnaires de march&#xE9; </li> <li>Suivi de l&apos;ex&#xE9;cution des march&#xE9;s en lien avec les gestionnaires de march&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des offres fournisseurs </li> <li>Analyser le besoin en collaboration avec les utilisateurs m&#xE9;tier </li> <li>Analyser un march&#xE9;, fournisseurs/clients, relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>Animer des r&#xE9;unions et des discussions avec des prescripteurs-acheteurs </li> <li>Identifier et mettre en place des actions achat quantifi&#xE9;es </li> <li>N&#xE9;gocier les contrats avec les prestataires/fournisseurs </li> <li>Utiliser des outils r&#xE9;glementaires sp&#xE9;cifiques aux achats hospitaliers </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Achat </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des march&#xE9;s publics </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;achat public </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits et fournisseurs </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Techniques quantitatives </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur des achats et responsable achat, tous responsables de directions acheteuses pour valider les objectifs annuels et les actions achat prioritaires<br>Cellule des march&#xE9;s (gestionnaires de march&#xE9;) pour le suivi des proc&#xE9;dures en lien avec les actions achats de son domaine<br>Services de soins, administratifs et techniques pour analyse du besoin et compr&#xE9;hension des sp&#xE9;cifications techniques<br>Fournisseurs pour la veille produit et la structuration du march&#xE9; fournisseurs<br>Fournisseurs pour analyse des offres et n&#xE9;gociation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +2/5, Formation sup&#xE9;rieure &#xE0; l&apos;achat public, &#xE0; la comptabilit&#xE9;, &#xE0; l&apos;approche co&#xFB;t complet<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Ing&#xE9;nieur hospitalier<br>Technicien Sup&#xE9;rieur Hospitalier<br>Contractuel de cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable des achats<br>Gestionnaire des march&#xE9;s publics<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent(e) de prévention et de sécurité des personnes et des biens', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Effectuer la surveillance dans un p&#xE9;rim&#xE8;tre donn&#xE9; en g&#xE9;rant les entr&#xE9;es et sorties en fonction de la politique s&#xE9;curitaire de l&apos;&#xE9;tablissement, ou dans une zone plus large n&#xE9;cessitant des rondes r&#xE9;guli&#xE8;res, afin d&apos;assurer la s&#xE9;curit&#xE9; des personnes et des biens et lutter conte les d&#xE9;gradations, sabotages, vols et agressions.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Agent(e) de s&#xE9;curit&#xE9; anti-malveillance<br>Agent(e) de s&#xFB;ret&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gardien(ne) d&apos;immeuble(s)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Agr&#xE9;ment de la pr&#xE9;fecture<br>Habilitation SSIAP 1 (arr&#xEA;t&#xE9; du 2 mai 2005 modifi&#xE9;)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement ou recherche de personnes dans divers locaux internes ou externes </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de l&apos;&#xE9;tat et du fonctionnement des &#xE9;quipements / des installations, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Contr&#xF4;le et suivi du bon d&#xE9;roulement du (des) process sp&#xE9;cifique(s) au domaine d&apos;activit&#xE9; </li> <li>Intervention et traitement d&apos;urgence suite &#xE0; alerte ou / et situation &#xE0; risques dans le domaine hygi&#xE8;ne-s&#xE9;curit&#xE9;-sant&#xE9;-environnement (HSE) </li> <li>Recensement / enregistrement des donn&#xE9;es / des informations li&#xE9;es &#xE0; la nature des activit&#xE9;s </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et orienter des personnes, des groupes, des publics </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Traiter et r&#xE9;soudre des situations agressives ou conflictuelles </li> <li>Utiliser des mat&#xE9;riels, des outils de diagnostic, de travail ou/et de contr&#xF4;le sp&#xE9;cifiques &#xE0; son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;accueil et &#xE0; l&apos;orientation des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la s&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des b&#xE2;timents </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Personnel de l&apos;&#xE9;tablissement et usagers pour information sur les consignes de s&#xE9;curit&#xE9; des biens et des personnes<br>Police, gendarmerie et pompiers pour l&apos;intervention sur incident<br>Entreprises ext&#xE9;rieures intervenant dans l&apos;&#xE9;tablissement pour intervention sur les syst&#xE9;mes de s&#xFB;ret&#xE9;<br>Service de s&#xE9;curit&#xE9; incendie pour intervention de la commune<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de niveau V attestant d&apos;une qualification professionnelle dans le domaine de la pr&#xE9;vention et de la s&#xE9;curit&#xE9;<br>Habilitation SSIAP 1<br>Dipl&#xF4;me d&apos;agent de pr&#xE9;vention et de s&#xFB;ret&#xE9;<br>CAP, BEP, Bac Pro<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ouvrier professionnel qualifi&#xE9;,<br>Ma&#xEE;tre ouvrier,<br>Ma&#xEE;tre ouvrier principal.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de s&#xE9;curit&#xE9; incendie<br>Encadrant de la s&#xE9;curit&#xE9; des personnes et des biens<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent de service mortuaire', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Prendre en charge les personnes d&#xE9;c&#xE9;d&#xE9;es et les corps des enfants pouvant &#xEA;tre d&#xE9;clar&#xE9;s sans vie &#xE0; l&apos;&#xE9;tat civil et assister le personnel m&#xE9;dical habilit&#xE9; dans la r&#xE9;alisation des soins et des activit&#xE9;s r&#xE9;alis&#xE9;s en chambre mortuaire.<br>Accueillir et r&#xE9;pondre aux besoins d&apos;information de la famille et des proches.<br>Entretenir les locaux et les mat&#xE9;riels de la chambre mortuaire.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Agent(e) de chambre mortuaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Formation d&apos;adaptation &#xE0; l&apos;emploi (FAE) dans les conditions d&#xE9;finies par l&apos;arr&#xEA;t&#xE9; du 16 juillet 2009<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, accompagnement et information des familles et des proches du patient d&#xE9;c&#xE9;d&#xE9; </li> <li>Gestion des pi&#xE8;ces anatomiques et dispositifs m&#xE9;dicaux implantables en vue de leur &#xE9;limination </li> <li>Nettoyage et entretien des locaux et des outils, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Pr&#xE9;paration du mat&#xE9;riel lors de la r&#xE9;alisation d&apos;autopsie et de pr&#xE9;l&#xE8;vements et restauration t&#xE9;gumentaire </li> <li>Pr&#xE9;paration du patient d&#xE9;c&#xE9;d&#xE9;, soins post mortem (hors soins de conservation) et habillage du corps </li> <li>Pr&#xE9;sentation du patient d&#xE9;c&#xE9;d&#xE9; dans un environnement propice au recueillement </li> <li>R&#xE9;ception des cercueils et accueil des personnels fun&#xE9;raires en vue de la lev&#xE9;e du corps </li> <li>Recueil, saisie, mise &#xE0; jour et/ou sauvegarde de donn&#xE9;es administratives et m&#xE9;dicales </li> <li>Signalement de pr&#xE9;sence d&apos;appareillage interne ou externe, d&apos;objets de valeur </li> <li>Transport de patients d&#xE9;c&#xE9;d&#xE9;s </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec l&apos;entourage du patient d&#xE9;c&#xE9;d&#xE9; </li> <li>G&#xE9;rer ses &#xE9;motions, son stress </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les techniques gestes et postures/ manutention </li> <li>Utiliser une proc&#xE9;dure, un langage, un protocole, une r&#xE9;glementation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit hospitalier et droit fun&#xE9;raire. R&#xE8;gles de bonnes pratiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pratiques sociales, rites et religions </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sociologie et psychologie de la mort et processus du deuil </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins d&apos;hygi&#xE8;ne et de pr&#xE9;sentation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques et protocoles de soins </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les services cliniques, les services de soins palliatifs et les maternit&#xE9;s pour la prise en charge des patients d&#xE9;c&#xE9;d&#xE9;s<br>Les &#xE9;quipes de coordination hospitali&#xE8;re pour les pr&#xE9;l&#xE8;vements<br>Les services sociaux pour instruire les dossiers sociaux des indigents<br>Les services d&apos;&#xE9;tat-civil de l&apos;h&#xF4;pital et des mairies pour les formalit&#xE9;s administratives<br>Les m&#xE9;decins anatomo-pathologistes et foeto-pathologistes pour les autopsies<br>Les tiers officiants cultuels pour les veill&#xE9;es et toilettes rituelles<br>Des stagiaires pour des actions de formation<br>Les op&#xE9;rateurs fun&#xE9;raires (maitre de c&#xE9;r&#xE9;monie et porteurs) pour l&apos;accueil du cercueil et les actes de thanatopraxie<br>Les fleuristes pour orner les cercueils et la chambre de pr&#xE9;sentation<br>Le service de police et le m&#xE9;decin l&#xE9;giste dans le cas d&apos;obstacle m&#xE9;dico-l&#xE9;gal et/ou hors communes du lieu de d&#xE9;c&#xE8;s et de cr&#xE9;mation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Aide-soignant<br>Agent de service hospitalier qualifi&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de st&#xE9;rilisation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent(e) de sécurité incendie', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Pr&#xE9;venir les risques incendie, intervenir dans l&apos;organisation des secours et la prise en charge des personnes en cas d&apos;incendie. Sensibiliser les agents au risque incendie.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Equipier(&#xE8;re) d&apos;intervention<br>Agent(e) de s&#xE9;curit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Habilitation SSIAP 1 (arr&#xEA;t&#xE9; du 2 mai 2005 modifi&#xE9;)Aptitude physique conform&#xE9;ment &#xE0; l&apos;article 6 de l&apos;arr&#xEA;t&#xE9; du 18 mai 1998 (abrog&#xE9; par arr&#xEA;t&#xE9; du 2 mai 2005)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>D&#xE9;clenchement et transmission des alertes / alarmes </li> <li>Information professionnelles de personnes (agents, stagiaires, etc.) </li> <li>Intervention et traitement d&apos;urgence suite &#xE0; alerte ou / et situation &#xE0; risques dans le domaine hygi&#xE8;ne-s&#xE9;curit&#xE9;-sant&#xE9;-environnement (HSE) </li> <li>R&#xE9;ception et traitement des appels t&#xE9;l&#xE9;phoniques et/ou des messages &#xE9;lectroniques </li> <li>Veille s&#xE9;curitaire </li> <li>V&#xE9;rification / contr&#xF4;le du fonctionnement et essais de mat&#xE9;riels, &#xE9;quipements/ des installations sp&#xE9;cifiques &#xE0; son domaine </li> <li>V&#xE9;rification de l&apos;accessibilit&#xE9; des voies de circulation sur le site et en p&#xE9;riph&#xE9;rie des b&#xE2;timents </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Observer et rendre compte des situations rencontr&#xE9;es </li> <li>Sensibiliser et informer les personnels et les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Utiliser des mat&#xE9;riels, des outils de diagnostic, de travail ou/et de contr&#xF4;le sp&#xE9;cifiques &#xE0; son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la s&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des b&#xE2;timents </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques et mat&#xE9;riels de lutte contre l&apos;incendie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable et encadrant s&#xE9;curit&#xE9; incendie pour la mise en place des proc&#xE9;dures.<br>Ensemble des personnels de l&apos;&#xE9;tablissement et tous les usagers pour information sur la s&#xE9;curit&#xE9; incendie<br>Pompiers pour la v&#xE9;rification p&#xE9;riodique des installations et des &#xE9;quipements de lutte contre l&apos;incendie.<br>Prestataires externes intervenant sur le site pour, notamment, le contr&#xF4;le du permis feu.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de niveau V attestant d&apos;une qualification professionnelle dans le domaine de la s&#xE9;curit&#xE9;,<br>CAP d&apos;agent de pr&#xE9;vention et de s&#xE9;curit&#xE9;<br>Habilitation SSIAP 1<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Personnels relevant essentiellement de la fili&#xE8;re ouvri&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de pr&#xE9;vention et de s&#xE9;curit&#xE9; des personnes et biens.<br>Encadrant s&#xE9;curit&#xE9; incendie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent(e) de bio-nettoyage', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des op&#xE9;rations de bio-nettoyage des surfaces et des locaux de l&apos;&#xE9;tablissement et v&#xE9;rifier, au moyen d&apos;autocontr&#xF4;les, la qualit&#xE9; des travaux effectu&#xE9;s en appliquant les instructions relatives au bio-nettoyage, afin de participer &#xE0; la lutte contre les infections nosocomiales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Agent(e) d&apos;entretien<br>Agent(e) hospitalier<br>Agent(e) de nettoyage<br>Agent(e) d&apos;entretien polyvalent<br>Agent(e) de service hospitalier (ASH)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Dans certains &#xE9;tablissements, l&apos;agent de bio-nettoyage prend &#xE9;galement en charge des activit&#xE9;s h&#xF4;teli&#xE8;res (mise en temp&#xE9;rature, distribution des repas, gestion du linge et/ou de d&#xE9;chets...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Approvisionnement en produits, en mat&#xE9;riels, dans son domaine </li> <li>Contr&#xF4;le et suivi de la propret&#xE9; des locaux, dans son domaine d&apos;activit&#xE9; </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;vacuation des d&#xE9;chets de diverses natures </li> <li>Nettoyage et entretien des locaux et des outils, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Renseignement de documents, de fichiers (fiches d&apos;activit&#xE9;, de tra&#xE7;abilit&#xE9;, etc.) </li> <li>Suivi des actions mises en oeuvre </li> <li>V&#xE9;rification / contr&#xF4;le du fonctionnement et essais de mat&#xE9;riels, &#xE9;quipements (autolaveuses, monobrosse, centrale vapeur) &#xE0; son domaine </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Appliquer le dosage et la dilution des produits relatifs &#xE0; son domaine de comp&#xE9;tence et telles qu&apos;indiqu&#xE9;es dans les protocoles </li> <li>&#xC9;valuer, choisir, utiliser les produits et/ou mat&#xE9;riaux n&#xE9;cessaire &#xE0; la pratique de son m&#xE9;tier </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Organiser et optimiser le stockage physique des produits, des mat&#xE9;riels dans son p&#xE9;rim&#xE8;tre d&apos;activit&#xE9; </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re et Pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Mat&#xE9;riel d&apos;hygi&#xE8;ne </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Nettoyage-d&#xE9;sinfection </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Produits de nettoyage et de d&#xE9;sinfection </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Tous services hospitaliers (secteurs soins, administratif, circulation) dans lesquels les agents de bio-nettoyage exercent leurs fonctions<br>service d&apos;hygi&#xE8;ne hospitali&#xE8;re pour les conseils sur les pratiques professionnelles<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me niveau V (exp: bac pro) ou exp&#xE9;rience professionnelle significative dans le domaine<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Agent des services hospitaliers qualifi&#xE9;<br>Agent d&apos;entretien qualifi&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de restauration et d&apos;h&#xF4;tellerie<br>Agent de blanchisserie<br>Aide-soignant,<br>Auxiliaire de pu&#xE9;riculture,<br>Aide-m&#xE9;dico psychologique<br>Agent de logistique<br>Encadrant Bio-nettoyage<br>Ambulancier<br>Brancardier<br>Agent de st&#xE9;rilisation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent(e) d''entretien des espaces verts', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Entretenir et am&#xE9;liorer les espaces verts, les terrasses et les ext&#xE9;rieurs<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Jardinier(&#xE8;re)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Exp&#xE9;rience conseill&#xE9;e en conduite d&apos;engins (CACES) en floriculture et p&#xE9;pini&#xE8;re.<br>Certiphyto obligatoire &#xE0; partir d&apos;octobre 2014<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Entretien des pelouses et plantes d&apos;int&#xE9;rieur </li> <li>Nettoyage et maintenance des &#xE9;quipements, machines, outillages, v&#xE9;hicules sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Plantation et entretien des arbres, des arbustes et des haies </li> <li>R&#xE9;alisation de divers travaux de voiries, r&#xE9;seaux et divers </li> <li>R&#xE9;alisation de d&#xE9;corations florales et v&#xE9;g&#xE9;tales relatives &#xE0; l&apos;organisation d&apos;&#xE9;v&#xE8;nements particuliers </li> <li>R&#xE9;alisation de massifs floraux </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Calculer des doses relatives &#xE0; des produits dans son domaine de comp&#xE9;tence </li> <li>Conduire un engin motoris&#xE9;, un v&#xE9;hicule de levage, de portage </li> <li>&#xC9;talonner des appareils de traitement </li> <li>Lire et interpr&#xE9;ter des plans techniques, des croquis relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Maintenir et d&#xE9;panner un mat&#xE9;riel, un &#xE9;quipement, une installation et/ou un syst&#xE8;me relatif &#xE0; son m&#xE9;tier </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Espaces verts </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Floriculture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Horticulture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">P&#xE9;pini&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Produits &#xE0; risques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation environnementale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsables de service pour la r&#xE9;alisation et l&apos;entretien d&apos;espaces verts.<br>Services techniques pour l&apos;entretien des &#xE9;quipements et de l&apos;outillage.<br>Prestataires ext&#xE9;rieurs pour la r&#xE9;alisation d&apos;espaces nouveaux ou importants.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Jardinier paysagiste<br>Technicien des syst&#xE8;mes naturels<br>CAP, BAC, BEP<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnel ouvriers.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant espaces verts<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Assistant(e) familial(e)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Accueillir &#xE0; son domicile des enfants ou adolescents jusqu&apos;&#xE0; 18 ans ou de jeunes majeurs jusqu&apos;&#xE0; 21 ans, ainsi que les m&#xE8;res avec enfants de moins de 3 ans.<br>Effectuer l&apos;ensemble des t&#xE2;ches n&#xE9;cessaires &#xE0; leur d&#xE9;veloppement physique, mental et social.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Agr&#xE9;ment d&apos;assistant(e) familial(e) d&#xE9;livr&#xE9; par les services du d&#xE9;partement<br>d&#xE9;cision de recrutement de l&apos;employeur<br>stage de 60 heures avant le premier accueil<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement ou recherche de personnes dans divers locaux internes ou externes </li> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc.), dans son domaine </li> <li>Assistance aux personnes : soutien scolaire de l&apos;enfant (aide aux devoirs, contact avec le milieu scolaire) </li> <li>Assistance aux personnes, aux patients pour l&apos;insertion ou la r&#xE9;insertion sociale et/ou professionnelle </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Analyser / &#xE9;valuer la situation clinique d&apos;une personne, d&apos;un groupe de personnes, relative &#xE0; son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>&#xC9;laborer des menus, composer des repas </li> <li>Organiser / animer des activit&#xE9;s sp&#xE9;cifiques &#xE0; son domaine de comp&#xE9;tence pour des patients, des personnels/des groupes </li> <li>Traiter et r&#xE9;soudre des situations agressives ou conflictuelles </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Comportement alimentaire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Education sant&#xE9; et &#xE0; la vie sociale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion de crise </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Parents-famille pour le partage d&apos;information et l&apos;accompagnement,<br>Autres travailleurs sociaux, m&#xE9;dicaux et psychologues pour le partage d&apos;information et le travail en &#xE9;quipe pluridisciplinaire,<br>Autres professionnels des secteurs sanitaire, social, m&#xE9;dico-social et enseignants pour la mise en oeuvre des projets personnalis&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Agr&#xE9;ment, 60 heures de stage, premier accueil, 240 heures de formation (dispense si titulaire des dip&#xF4;mes suivants: &#xE9;ducateur jeunes enfants, auxiliaire de puericulture, &#xE9;ducateur sp&#xE9;cialis&#xE9;)<br>Possibilit&#xE9; (pas obligation) du dipl&#xF4;me d&apos;etat d&apos;assistant(e) familial(e) dispensant ensuite du renouvellement de l&apos;agr&#xE9;ment<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>N&#xE9;ant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Aide-m&#xE9;dico-psychologique<br>Moniteur-&#xE9;ducateur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent de gestion administrative', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Recueillir et traiter les informations n&#xE9;cessaires au fonctionnement administratif du service. Suivre et g&#xE9;rer les processus administratifs.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Agent(e) d&apos;administration<br>Assistant(e) administratif(ve)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc...) </li> <li>Contr&#xF4;le des donn&#xE9;es / documents </li> <li>Gestion administrative relative au domaine d&apos;activit&#xE9; </li> <li>Inventaire de produits, de mat&#xE9;riels, &#xE9;quipements, dossiers </li> <li>R&#xE9;ception et traitement des appels t&#xE9;l&#xE9;phoniques et/ou des messages &#xE9;lectroniques </li> <li>Recueil, saisie, mise &#xE0; jour et / ou sauvegarde de donn&#xE9;es, d&apos;informations, de tableaux, de fichiers </li> <li>Reproduction et diffusion de documents, de dossiers </li> <li>Traitement des courriers, dossiers, documents (enregistrement, tri, traitement, diffusion, archivage) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et orienter des personnes, des groupes, des publics </li> <li>&#xC9;valuer la pertinence et la v&#xE9;racit&#xE9; des donn&#xE9;es et/ou informations </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Organiser et classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Travailler en &#xE9;quipe / en r&#xE9;seau </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Accueil t&#xE9;l&#xE9;phonique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Classement et archivistique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion administrative </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Personnels et services pour la gestion et le suivi des dossiers<br>Fournisseurs ext&#xE9;rieurs pour les prestations diverses<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Cat&#xE9;gorie C<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent d&apos;accueil<br>Archiviste<br>Secr&#xE9;taire<br>Gestionnaire de donn&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Architecte / Urbaniste du SI', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Conduire l&apos;&#xE9;volution et assurer la coh&#xE9;rence du SI dans le respect des contraintes externes et internes et des objectifs de l&apos;&#xE9;tablissement.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Architecte fonctionnel du syst&#xE8;me informatique,<br>Architecte applicatif,<br>Architecte du syst&#xE8;me d&apos;information,<br>Urbaniste du syst&#xE8;me d&apos;information<br>Architecte technique du syst&#xE8;me d&apos;information<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Capitalisation de l&apos;ensemble de la documentation sur le SI (cahier des charges, sp&#xE9;cifications, guide de proc&#xE9;dures, ...) </li> <li>Contr&#xF4;le de la coh&#xE9;rence globale des projets par rapport au sch&#xE9;ma directeur des syst&#xE8;mes d&apos;information (SDSI) avec les interlocuteurs internes et externes </li> <li>Cr&#xE9;ation, mise &#xE0; jour et test des r&#xE9;f&#xE9;rentiels, afin de g&#xE9;rer la coh&#xE9;rence des donn&#xE9;es SI </li> <li>Gestion (construction, mise &#xE0; jour et &#xE9;volution) de la cartographie m&#xE9;tier et fonctionnelle du SI et de la cartographie applicative et technique du SI (y compris les r&#xE9;f&#xE9;rentiels) </li> <li>Mise en &#x153;uvre du sch&#xE9;ma directeur des syst&#xE8;mes d&apos;information (SD-SI) </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes d&apos;impact </li> <li>Suivi des &#xE9;volutions r&#xE9;glementaires et technologiques du SI </li> <li>Traduction de la strat&#xE9;gie de l&apos;&#xE9;tablissement en sch&#xE9;ma directeur des syst&#xE8;mes d&apos;information (SD-SI) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Construire et utiliser des outils de pilotage (crit&#xE8;res, indicateurs / tableau de bord) </li> <li>G&#xE9;rer les &#xE9;volutions des diff&#xE9;rentes applications </li> <li>Identifier, analyser et valider des impacts (technologiques, organisationnels, fonctionnels et financiers) li&#xE9;s au projet </li> <li>Identifier, s&#xE9;lectionner, exploiter et capitaliser les informations dans le cadre de la veille professionnelle </li> <li>Mettre en &#x153;uvre la charte d&apos;utilisation du SI et la charte d&apos;identito-vigilance </li> <li>Mettre en &#x153;uvre les r&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es </li> <li>Mod&#xE9;liser et int&#xE9;grer les &#xE9;volutions du SI sur les couches m&#xE9;tier et fonctionnelle et sur les couches applicative et technique </li> <li>Piloter les diff&#xE9;rents projets et coordonner le SI afin d&apos;avoir une coh&#xE9;rence avec le SI existant </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Architecture SI m&#xE9;tier et fonctionnelle </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Charte d&apos;utilisation du SI et charte d&apos;identito-vigilance </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit des donn&#xE9;es informatiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Etude d&apos;impact des nouvelles solutions informatiques sur l&apos;organisation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thode de conduite de projet (planification, reporting, analyse de risques, ...) </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Produits et march&#xE9; informatique et son &#xE9;volution </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Projet d&apos;&#xE9;tablissement (orientations strat&#xE9;giques) </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Standards et normes applicables </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction pour l&apos;information sur l&apos;&#xE9;volution du SI; Utilisateurs r&#xE9;f&#xE9;rents pour information sur le SI (utilisation, pilotage, .); Architecte technique pour d&#xE9;finir la qualit&#xE9; de service attendue des applications; Service d&apos;information m&#xE9;dicale pour assurer la coh&#xE9;rence du SI m&#xE9;dical; Fournisseurs pour consultation et r&#xE9;ponse aux besoins d&apos;outils, de m&#xE9;thodologies et d&apos;applications<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac + 5<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Chef de projet informatique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent(e) de logistique', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser tout ou partie d&apos;op&#xE9;rations manuelles simples de diverses natures, parfois accompagn&#xE9;es d&apos;op&#xE9;rations de saisie de donn&#xE9;es (collecte, manutention de produits ou d&apos;objets, pr&#xE9;paration de livraison, transport de biens, distribution, livraison de biens, transport non sanitaire de personnes...), en appliquant les consignes donn&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Op&#xE9;rateur(trice) logistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Conducteur(trice) livreur(euse)<br>Conducteur(trice) transport de personnes<br>Chauffeur(euse)<br>Cariste<br>Manutentionnaire<br>Coursier(&#xE8;re)<br>Agent(e) de collecte des d&#xE9;chets<br>Agent(e) de voirie<br>Magasinier(&#xE8;re)<br>Vaguemestre<br>Transports produits dangereux<br>Reprographe<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Permis de conduire, permis cariste (CACES) si l&apos;agent est un chauffeur cariste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la conformit&#xE9; des produits relatifs &#xE0; son domaine </li> <li>D&#xE9;conditionnement, d&#xE9;boitage, d&#xE9;sensachage des mati&#xE8;res premi&#xE8;res </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Nettoyage et entretien des &#xE9;quipements, machines, outillages, v&#xE9;hicules sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Saisie, mise &#xE0; jour et / ou sauvegarde de donn&#xE9;es, d&apos;informations, de tableaux, dans son domaine d&apos;activit&#xE9; </li> <li>Transport et livraison de biens, de produits </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Calculer des valeurs relatives aux activit&#xE9;s de son domaine </li> <li>Choisir une m&#xE9;thode, un moyen correspondant &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la conformit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riel, d&apos;une prestation au regard des normes internes/externes </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Conduite d&apos;engin de manutention </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable de magasin et magasiniers pour l&apos;approvisionnement et le r&#xE9;approvisionnement<br>Secteurs utilisateurs pour le r&#xE9;approvisionnement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAP BEP logistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Coprs des personnelles ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Gestionnaire de stocks<br>Encadrant(e) logistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chargé(e) de communication', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre en oeuvre la politique de communication d&#xE9;finie par l&apos;&#xE9;tablissement en proposant, concevant et mettant en oeuvre des messages d&#xE9;clin&#xE9;s en actions de communication vers diff&#xE9;rents publics, en utilisant diff&#xE9;rents m&#xE9;dias et en visant &#xE0; faciliter les relations de l&apos;&#xE9;tablissement avec son environnement interne et externe.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable de la communication<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Attach&#xE9;(e) de presse<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conception et r&#xE9;daction de documents techniques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et mise en place du plan de communication, relatif &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement des &#xE9;l&#xE9;ments et des sp&#xE9;cifications techniques du cahier des charges </li> <li>Publication d&apos;informations en interne / externe </li> <li>R&#xE9;alisation de revues de presse </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Concevoir et organiser des &#xE9;v&#xE8;nements </li> <li>&#xC9;valuer la pertinence et la v&#xE9;racit&#xE9; des donn&#xE9;es et/ou informations </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>S&apos;exprimer dans une langue &#xE9;trang&#xE8;re </li> <li>S&apos;exprimer en public </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication de crise </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication &#xE9;crite </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication orale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conception graphique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;dition </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Histoire des m&#xE9;dias et de leurs sp&#xE9;cificit&#xE9;s </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur de la communication pour l&apos;&#xE9;laboration du plan de communication et des actions qu&apos;il contient<br>Chefs de service et cadres pour l&apos;&#xE9;valuation de leur besoin de communication et proposition d&apos;actions de communication<br>Agences de communication pour sous-traitance de certaines actions<br>Responsables d&apos;associations de malades et d&apos;usagers pour la proposition de la mise en place d&apos;actions de communication sp&#xE9;cifiques<br>Partenaires institutionnels pour la diffusion d&apos;information<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me ou formation sp&#xE9;cialis&#xE9;e dans le domaine (CELSA, Master en communication, etc.)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitaliers<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Contractuel de cat&#xE9;gorie B ou A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Webmestre &#xE9;ditorial<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Accueillant familial', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Accueillir &#xE0; son domicile, pour un temps donn&#xE9;, et prendre en charge au quotidien, des personnes &#xE2;g&#xE9;es ou handicap&#xE9;es (physiques et/ou psychiques) adultes dans le cadre d&apos;un contrat de gr&#xE9; &#xE0; gr&#xE9; ou g&#xE9;r&#xE9; par un &#xE9;tablissement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Famille d&apos;accueil<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Accueillant familial th&#xE9;rapeutique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Agr&#xE9;ment par le pr&#xE9;sident du conseil g&#xE9;n&#xE9;ral du d&#xE9;partement de r&#xE9;sidence de la personne ou du couple demandeur avec le plus souvent une formation initiale diligent&#xE9;e par le Conseil G&#xE9;n&#xE9;ral.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil des personnes (patients, usagers, familles, etc.) </li> <li>Accueil et installation du patient, am&#xE9;nagement de l&apos;environnement (confort et s&#xE9;curit&#xE9;) </li> <li>&#xC9;laboration et r&#xE9;daction de projets individuels </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Tenue d&apos;entretiens avec des personnes (patients, familles,...) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>G&#xE9;rer ses &#xE9;motions, son stress </li> <li>Reformuler une demande (d&apos;information, de renseignement, etc.), en r&#xE9;sumer les points-cl&#xE9;s et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; professionnelle </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie des &#xE2;ges de la vie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE9;seaux sanitaires, sociaux et m&#xE9;dico-sociaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>L&apos;accueillant familial b&#xE9;n&#xE9;ficie d&apos;une formation initiale et continue de la part du d&#xE9;partement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des agents de service hospitaliers qualifi&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Aide m&#xE9;dico-psychologique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Bio informaticien(ne)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre au point des outils et logiciels informatiques permettant d&apos;organiser, de comparer, d&apos;analyser, de diffuser des donn&#xE9;es biologiques, physiques, chimiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>D&#xE9;finition des prototypages et jeux d&apos;essai </li> <li>D&#xE9;finition et r&#xE9;alisation d&apos;interfaces homme-machine </li> <li>D&#xE9;veloppement des progiciels et programmation n&#xE9;cessaires &#xE0; l&apos;exploitation des donn&#xE9;es biologiques et m&#xE9;dicales </li> <li>Formation de personnes aux techniques et proc&#xE9;dures de son domaine, et &#xE0; leur application </li> <li>Param&#xE9;trage des outils, logiciels, syst&#xE8;mes relevant de son domaine d&apos;activit&#xE9; </li> <li>Publication scientifiques et m&#xE9;dicales en interne ou externe </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Traitement et analyse de l&apos;information/donn&#xE9;es m&#xE9;dicale et/ou bilogique : extraction, regroupement, repr&#xE9;sentation graphique </li> <li>Veille professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter et optimiser le fonctionnement des &#xE9;quipements, des installations, des syst&#xE8;mes, des m&#xE9;thodes de travail dans son domaine de comp&#xE9;tence </li> <li>Analyser les propri&#xE9;t&#xE9;s mol&#xE9;culaires et cellulaires des donn&#xE9;es biologiques </li> <li>Concevoir et mod&#xE9;liser une solution informatique, &#xE0; partir d&apos;un besoin m&#xE9;tier </li> <li>Concevoir, actualiser, optimiser une base de donn&#xE9;es, relatives &#xE0; la nature de ses activit&#xE9;s </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Programmer dans diff&#xE9;rents environnements informatiques </li> <li>Rechercher, s&#xE9;lectionner, exploiter et capitaliser les informations li&#xE9;es &#xE0; la veille dans son domaine d&apos;activit&#xE9; </li> <li>S&apos;exprimer dans une langue &#xE9;trang&#xE8;re </li> <li>Traiter, retoucher des images et/ou des vid&#xE9;os </li> <li>Travailler en &#xE9;quipe / en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Architecture du syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Biologie m&#xE9;dicale </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">D&#xE9;veloppement informatique / analyse et programmation </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Gestion de donn&#xE9;es, relatives &#xE0; son domaine </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Informatique / Syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Math&#xE9;matiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Physique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Statistiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Biologistes et g&#xE9;n&#xE9;ticiens pour la validation des besoins, les choix m&#xE9;thodologiques et l&apos;analyse des r&#xE9;sultats.<br>Equipe des laboratoires de biologie m&#xE9;dicale pour le recueil des besoins et la formation.<br>Equipe de recherche clinique pour une aide &#xE0; la conception des m&#xE9;thodes.<br>Direction informatique pour les projets d&apos;investissement et l&apos;architecture informatique. Gestionnaires des donn&#xE9;es biom&#xE9;dicales et biostatisticiens pour la gestion et le traitement des donn&#xE9;es.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau I (BAC+5 &#xE0; BAC+8)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Contractuel de cat&#xE9;gorie A (ing&#xE9;nieur)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Biostatisticien<br>Ing&#xE9;nieur en biologie m&#xE9;dicale<br>Charg&#xE9; des applications informatiques<br>Chef de projets informatique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Archiviste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Collecter, classer, conserver, rechercher et communiquer les documents archiv&#xE9;s aux demandeurs en appliquant des proc&#xE9;dures, protocoles, consignes sp&#xE9;cifiques &#xE0; l&apos;archivage m&#xE9;dical et administratif des &#xE9;tablissements de sant&#xE9;.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Technicien (ne) d&apos;archivage<br>Technicien (ne) d&apos;archivage m&#xE9;dical<br>Gestionnaire de dossiers m&#xE9;dicaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissance de l&apos;outil informatique/utilisation de logiciels m&#xE9;tiers<br>Connaissances en culture g&#xE9;n&#xE9;rale<br>Notions connaissances m&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le des donn&#xE9;es / documents relatifs &#xE0; son domaine </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Organisation de la collecte, de la conservation et de la communication des fonds d&apos;archives </li> <li>R&#xE9;alisation et contr&#xF4;le des d&#xE9;lais de conservation et des conditions de stockage </li> <li>Saisie, mise &#xE0; jour et / ou sauvegarde de donn&#xE9;es, d&apos;informations, de tableaux, dans son domaine d&apos;activit&#xE9; </li> <li>Tri, classement et inventaire des archives (dossiers, documents..) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et orienter des personnes, des groupes, des publics </li> <li>D&#xE9;finir et utiliser les techniques et pratiques adapt&#xE9;es &#xE0; son m&#xE9;tier </li> <li>D&#xE9;finir/adapter et utiliser un plan de classement et d&apos;archivage, un syst&#xE8;me de gestion documentaire </li> <li>Identifier les fonds d&apos;archives &#xE0; collecter </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Reformuler une demande (d&apos;information, de renseignement, etc.), en r&#xE9;sumer les points-cl&#xE9;s et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; professionnelle </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Classement et archivistique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion des archives </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation des archives </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Ensemble des services de l&apos;&#xE9;tablissement pour la recherche, la pr&#xE9;paration et la transmission des dossiers archiv&#xE9;s et prestataires charg&#xE9;s de la conservation ou de l&apos;&#xE9;limination<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BEP sanitaire et social<br>BAC g&#xE9;n&#xE9;ral ou sciences medico-sociales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps de cat&#xE9;gorie C<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant archives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent(e) de maintenance générale des bâtiments', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des travaux de maintenance g&#xE9;n&#xE9;rale des b&#xE2;timents<br>Il peut r&#xE9;aliser des travaux techniques de premier niveau<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Agent(e) technique b&#xE2;timent<br>Ouvrier(&#xE8;re) de maintenance des b&#xE2;timents<br>Technicien(ne) de maintenance b&#xE2;timents<br>Agent(e) de maintenance de proximit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Habilitation &#xE9;lectrique suivant le niveau d&apos; intervention dans le domaine<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Ajustement, pose et fixation d&apos;&#xE9;quipements sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Entretien, maintenance, pr&#xE9;vention et d&#xE9;pannage des mat&#xE9;riels, outillages, &#xE9;quipements, syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>Mise en jeu d&apos;&#xE9;l&#xE9;ments menuis&#xE9;s </li> <li>Pose de cloisons, de rev&#xEA;tements, de protections murales </li> <li>R&#xE9;alisation de travaux d&apos;appr&#xEA;t (nettoiement, &#xE9;grenage, pon&#xE7;age) </li> <li>R&#xE9;alisation de travaux de ma&#xE7;onnerie annexes &#xE0; la couverture ( solins, joints de chemin&#xE9;e...) </li> <li>R&#xE9;alisation des installations dans son domaine d&apos;activit&#xE9;, en relation avec les utilisateurs, les fournisseurs </li> <li>Remplacement de vitres simples ou isolantes sur tous les types de menuiserie </li> <li>Suivi de la r&#xE9;alisation des travaux, de la r&#xE9;solution des probl&#xE8;mes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements, outillages, dans son domaine de comp&#xE9;tence </li> <li>Dessiner un plan ou un croquis technique relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la r&#xE9;sistance m&#xE9;canique appropri&#xE9;e &#xE0; l&apos;ouvrage r&#xE9;alis&#xE9; </li> <li>Lire et interpr&#xE9;ter des plans techniques, des croquis relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Utiliser des mat&#xE9;riels, des outils de d&#xE9;pannage, de r&#xE9;paration ou de maintenance, relatifs &#xE0; son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Calcul, g&#xE9;om&#xE9;trie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Dessin en b&#xE2;timent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectricit&#xE9; g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Ma&#xE7;onnerie, pl&#xE2;trerie, c&#xE9;ramique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Plomberie zinguerie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Serrurerie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrant maintenance b&#xE2;timent VRD pour la programmation du travail et la prise de d&#xE9;cision en mati&#xE8;re de r&#xE9;paration<br>Encadrement des services (services m&#xE9;dicaux et autres) pour la programmation quotidienne et le d&#xE9;roulement des travaux<br>Magasinier pour l&apos;approvisionnement en mat&#xE9;riaux et outillage<br>Fournisseurs pour le choix et la r&#xE9;ception des produits et des mat&#xE9;riels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAP ou BEP, et une premi&#xE8;re exp&#xE9;rience dans un m&#xE9;tier de second oeuvre en b&#xE2;timent<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant maintenance corps d&apos;&#xE9;tat secondaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Ambulancier(ère)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Transporter et accompagner dans des v&#xE9;hicules affect&#xE9;s &#xE0; cet usage, des personnes, notamment des malades, des bless&#xE9;s ou parturientes.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En service mobile d&apos;urgence et de r&#xE9;animation (SMUR)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Permis B, C, D, titulaire du certificat d&apos;ambulancier<br>Formation d&apos;adaptation &#xE0; l&apos;emploi pour les personnes affect&#xE9;es au SMUR<br>Tests psychotechniques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et installation du patient, am&#xE9;nagement de l&apos;environnement (confort et s&#xE9;curit&#xE9;) </li> <li>Intervention et traitement d&apos;urgence suite &#xE0; alerte ou / et situation &#xE0; risques dans le domaine hygi&#xE8;ne-s&#xE9;curit&#xE9;-sant&#xE9;-environnement (HSE) </li> <li>Nettoyage et entretien des &#xE9;quipements, machines, outillages, v&#xE9;hicules sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction de documents techniques, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Surveillance de l&apos;&#xE9;tat de sant&#xE9; des personnes accueillies dans son domaine d&apos;intervention </li> <li>Transport de patients </li> <li>V&#xE9;rification / contr&#xF4;le du fonctionnement et essais de mat&#xE9;riels, &#xE9;quipements/ des installations sp&#xE9;cifiques &#xE0; son domaine </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Conduire un v&#xE9;hicule automobile </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>Maintenir et d&#xE9;panner un mat&#xE9;riel, un &#xE9;quipement, une installation et/ou un syst&#xE8;me relatif &#xE0; son m&#xE9;tier </li> <li>S&apos;exprimer dans un langage radio </li> <li>Utiliser et appliquer les protocoles de nettoyage et de d&#xE9;contamination pour la d&#xE9;sinfection des mat&#xE9;riels </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication et transmission radio </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Conduite automobile </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie du secteur sanitaire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation des transports </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; routi&#xE8;re </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins et infirmi&#xE8;res pour les transmissions de consignes concernant le confort, la surveillance et la s&#xE9;curit&#xE9; du patient<br>Services administratifs pour la gestion des modalit&#xE9;s du transfert des patients<br>R&#xE9;gulateurs ambulanciers et permanenciers SAMU pour l&apos;envoi sur certaines interventions<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Conducteur ambulancier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Brancardier<br>Aide-soignant<br>Encadrant transport sanitaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Audioprothésiste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser un bilan audioproth&#xE9;tique aupr&#xE8;s de personnes pr&#xE9;sentant un trouble de l&apos;audition et/ou de l&apos;intelligibilit&#xE9; n&#xE9;cessitant une correction auditive.<br>Choisir, adapter, d&#xE9;livrer la proth&#xE8;se auditive, en contr&#xF4;ler son efficacit&#xE9;, et r&#xE9;aliser l&apos;&#xE9;ducation proth&#xE9;tique du patient.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Adaptation, contr&#xF4;le d&apos;efficacit&#xE9; imm&#xE9;diate et d&#xE9;livrance de l&apos;appareillage </li> <li>Conseil et information g&#xE9;n&#xE9;rale du patient, choix d&apos;une d&#xE9;marche d&apos;appareillage </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Mise en place de d&#xE9;marches qualit&#xE9; / environnement et r&#xE9;daction de documents requis pour l&apos;activit&#xE9; de l&apos;entreprise </li> <li>Pr&#xE9;paration de l&apos;appareil </li> <li>R&#xE9;alisation de l&apos;&#xE9;ducation proth&#xE9;tique </li> <li>Recueil d&apos;informations, entretiens, &#xE9;valuations en vue de d&#xE9;terminer la situation audiologique du patient </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Suivi personnalis&#xE9; du patient, conseil sur les aides techniques et maintenance des proth&#xE8;ses auditives </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter l&apos;appareillage aux besoins et aux caract&#xE9;ristiques du patient </li> <li>Communiquer et conduire une relation dans un contexte d&apos;intervention </li> <li>Concevoir et conduire une d&#xE9;marche d&apos;information, de conseil et d&apos;&#xE9;ducation </li> <li>Concevoir une d&#xE9;marche d&apos;appareillage auditif individualis&#xE9;e </li> <li>Coop&#xE9;rer avec d&apos;autres professionnels </li> <li>Evaluer la situation audiologique d&apos;un patient </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>R&#xE9;aliser le suivi de l&apos;appareillage </li> <li>R&#xE9;aliser un bilan audiom&#xE9;trique d&apos;orientation proth&#xE9;tique </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Acoustique - Psychoacoustique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Audiologie et otologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Audioproth&#xE8;se </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectronique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sciences humaines </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;audioproth&#xE8;siste (3 ans en Universit&#xE9;)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>N&#xE9;ant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Cadre administratif(ve) de pôle (CAP)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, coordonner et encadrer les moyens d&apos;un p&#xF4;le<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Cadre gestionnaire de p&#xF4;le<br>Assistant(e) de p&#xF4;le<br>Cadre sup&#xE9;rieur(e) adminitratif(ve) de p&#xF4;le<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le, suivi et bilan des actions r&#xE9;alis&#xE9;es, dans son domaine </li> <li>Coordination et suivi de la prise en charge de prestations </li> <li>&#xC9;laboration et mise en place des programmes de formation et de DPC, en relation avec l&apos;&#xE9;quipe p&#xE9;dagogique </li> <li>&#xC9;laboration et r&#xE9;daction de rapports d&apos;activit&#xE9; </li> <li>&#xC9;laboration, mise en place et suivi du plan emploi </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion des moyens et des ressources : techniques, financi&#xE8;res, humaines, informationnelles </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Concevoir et b&#xE2;tir un plan emploi / ressources humaines </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir, allouer et optimiser les ressources au regard des priorit&#xE9;s, des contraintes et variations externes / internes </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Contr&#xF4;le de gestion </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit hospitalier </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes d&apos;&#xE9;valuation m&#xE9;dico-&#xE9;conomique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Chef de p&#xF4;le et cadre soignant de p&#xF4;le pour la coodination et la gestion du p&#xF4;le<br>Directions fonctionnelles pour les &#xE9;changes concernant les objectifs, moyens et r&#xE9;alisations (dans les domaines financiers et contr&#xF4;le de gestion, RH, SI, services g&#xE9;n&#xE9;raux, Qualit&#xE9;, etc.)<br>Encadrement du p&#xF4;le pour adapter les proc&#xE9;dures, les fonctionnements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC+ 3/5 (niveau II -license professionnelle- &#xE0; niveau 1 -master-) dans le domaine gestion-management<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Contr&#xF4;leur de gestion<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Aide médico-psychologique (AMP)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Aider le r&#xE9;sidant, la personne handicap&#xE9;e ou &#xE2;g&#xE9;e d&#xE9;pendante, dans les actes de la vie quotidienne afin que cette personne maintienne et d&#xE9;veloppe son autonomie et son bien-&#xEA;tre.<br>Mettre en &#x153;uvre des activit&#xE9;s d&apos;animation, individuelles ou collectives.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Auxiliaire &#xE9;ducatif (ve)<br>Aide-m&#xE9;dico-&#xE9;ducatif(ve)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc.), dans son domaine </li> <li>Assistance aux personnes, aux patients pour la r&#xE9;alisation des actes de la vie quotidienne (repas, toilette, etc.) </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>R&#xE9;ception et distribution de produits (m&#xE9;dicaments, repas, mat&#xE9;riels...) </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction de comptes-rendus relatifs aux observations / aux interventions, dans son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Choisir et mettre en oeuvre les techniques et pratiques adapt&#xE9;es au patient, au regard de son m&#xE9;tier / sa sp&#xE9;cialit&#xE9; </li> <li>&#xC9;valuer le degr&#xE9; d&apos;autonomie d&apos;une personne ou d&apos;un groupe de personnes </li> <li>Organiser / animer des activit&#xE9;s sp&#xE9;cifiques &#xE0; son domaine de comp&#xE9;tence pour des patients, des personnels/des groupes </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Renseigner des personnes au regard de son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Di&#xE9;t&#xE9;tique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion du stress </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement des structures sociales et m&#xE9;dico-sociales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>&#xC9;quipes m&#xE9;dicales, param&#xE9;dicales,&#xE9;ducatives et psychologiques pour l&apos;assistance dans les actes de la vie quotidienne et travail en &#xE9;quipe pluridisciplinaire,<br>Famille et environnement de la personne pour partage d&apos;informations,<br>Autres professionnels des secteurs sanitaire, social et m&#xE9;dico-social pour la mise en oeuvre des projets individualis&#xE9;s et des &#xE9;changes d&apos;exp&#xE9;rience et de pratiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>dipl&#xF4;me d&apos;etat d&apos;aide m&#xE9;dico-psychologique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Aide-soignant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Moniteur-&#xE9;ducateur<br>&#xC9;ducateur sp&#xE9;cialis&#xE9;<br>Aide-soignant<br>Infirmier en soins g&#xE9;n&#xE9;raux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent(e) de restauration et / ou d''hôtellerie', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser, en restauration collective, les op&#xE9;rations d&apos;assemblage, de composition et de distribution des repas.<br>Entretenir les locaux et les &#xE9;quipements, en cuisine<br>R&#xE9;aliser des prestations h&#xF4;teli&#xE8;res (accueil, repas, collations, lingerie, entretien des locaux) aupr&#xE8;s des patients et des r&#xE9;sidents<br>en lien avec les &#xE9;quipes soignante et &#xE9;ducative<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Employ&#xE9;(e) polyvalent de restauration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>D&#xE9;tention du dipl&#xF4;me,<br>Exp&#xE9;rience en milieu restauration collective ou h&#xE9;bergement social<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil des personnes (patients, usagers, familles, etc.) </li> <li>Approvisionnement en produits, en mat&#xE9;riels, dans son domaine </li> <li>Contr&#xF4;le de la conformit&#xE9; des produits relatifs &#xE0; son domaine </li> <li>Contr&#xF4;le et suivi de la propret&#xE9; des locaux, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des produits </li> <li>D&#xE9;conditionnement, d&#xE9;boitage, d&#xE9;sensachage des mati&#xE8;res premi&#xE8;res </li> <li>Pr&#xE9;paration et r&#xE9;alisation des prestations de restauration et d&apos;h&#xF4;tellerie </li> <li>R&#xE9;alisation du service en salle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Appr&#xE9;cier un niveau de cuisson </li> <li>Choisir et mettre en oeuvre les techniques et pratiques adapt&#xE9;es au patient, au regard de son m&#xE9;tier / sa sp&#xE9;cialit&#xE9; </li> <li>Choisir et utiliser des mat&#xE9;riels, des outils de travail ou / et de contr&#xF4;le, aff&#xE9;rents &#xE0; son m&#xE9;tier </li> <li>Discerner les saveurs, les ar&#xF4;mes </li> <li>&#xC9;valuer l&apos;&#xE9;tat de propret&#xE9; des surfaces et d&#xE9;finir les travaux &#xE0; r&#xE9;aliser </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Renseigner des personnes au regard de son m&#xE9;tier </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Di&#xE9;t&#xE9;tique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">H&#xF4;tellerie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;h&#xF4;tellerie/ restauration </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques culinaires </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrement de la production culinaire pour l&apos;information sur les commandes, les conditions de livraison et la satisfaction des patients<br>service di&#xE9;t&#xE9;tique pour l&apos;adaptation des menus en fonction de l&apos;attente des patients<br>cuisinier pour l&apos;&#xE9;laboration des pr&#xE9;parations culinaires<br>clients du restaurant du personnel pour le service du d&#xE9;jeuner<br>service lingerie pour la gestion et la distribution du linge<br>conseiller h&#xF4;telier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formation h&#xF4;tellerie/restauration<br>CAP ou BEP du domaine h&#xF4;tellerie restauration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnelles ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de bio-nettoyage<br>Cuisinier<br>Ma&#xEE;tre / Ma&#xEE;tresse de maison<br>Encadrant production culinaire / alimentaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Biostatisticien(ne)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir les m&#xE9;thodes des protocoles de recherche clinique, pr&#xE9;parer puis r&#xE9;aliser les analyses statistiques des donn&#xE9;es de ces protocoles.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Charg&#xE9;(e) de l&apos;exploitation et de l&apos;analyse des donn&#xE9;es cliniques<br>Ing&#xE9;nieur(e) en biostatistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>M&#xE9;thodologiste<br>Coordonnateur biostatistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me en statistique (BAC+3 &#xE0; BAC+8)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;daction de documents techniques, relatifs au domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>Publication d&apos;informations en interne / externe </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes et de travaux de recherche dans son domaine </li> <li>R&#xE9;daction de comptes-rendus relatifs aux observations / aux interventions, dans son domaine d&apos;activit&#xE9; </li> <li>Traitement et analyse de l&apos;information/donn&#xE9;es m&#xE9;dicale et/ou bilogique : extraction, regroupement, repr&#xE9;sentation graphique </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, traduire et formuler une question biom&#xE9;dicale en &#xE9;tude clinique, en solution, en programmes d&apos;analyse et justifier des r&#xE9;sultats </li> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Choisir et utiliser des outils statistiques </li> <li>Choisir une m&#xE9;thode, un moyen correspondant &#xE0; son domaine de comp&#xE9;tence </li> <li>Elaborer, r&#xE9;diger et exploiter des requ&#xEA;tes relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Param&#xE9;trer des outils, logiciels, syst&#xE8;mes relevant de son domaine d&apos;activit&#xE9; </li> <li>Programmer dans diff&#xE9;rents environnements informatiques </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Biologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;pid&#xE9;miologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation relative &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Statistiques </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins investigateurs pour la conception du projet et l&apos;analyse des donn&#xE9;es<br>Coordinateur d&apos;etudes cliniques pour la s&#xE9;lection et le recueil des donn&#xE9;es<br>Gestionnaires de donn&#xE9;es biom&#xE9;dicales pour la gestion et le traitement des donn&#xE9;es.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau I (BAC+5 &#xE0; BAC+8)<br>Master ou doctorat en statistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des Ing&#xE9;nieurs<br>Cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Gestionnaire de donn&#xE9;es biom&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chargé (e) des applications informatiques', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Maintenir en condition op&#xE9;rationnelle un portefeuille d&apos;applications<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Expert(e) applications,<br>Expert(e) produits Syst&#xE8;me d&apos;Information<br>Responsable des applications informatiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Analyse, veille et prospection du march&#xE9; des fournisseurs informatiques pour mettre en &#x153;uvre la strat&#xE9;gie d&apos;achat SI </li> <li>D&#xE9;finition, planification et mise en place des normes, standards, proc&#xE9;dures et outils li&#xE9;es &#xE0; la s&#xE9;curit&#xE9; du SI (MSSI) </li> <li>Maintenance correctrice, &#xE9;volutive et/ou pr&#xE9;ventive de la solution, du mat&#xE9;riel et des &#xE9;quipements et param&#xE9;trage du SI </li> <li>Pilotage des actions de maintenance correctrice, &#xE9;volutive et/ou pr&#xE9;ventive du SI, ainsi que des &#xE9;volutions impos&#xE9;es par les &#xE9;diteurs (arbitrage, priorisation, planification) </li> <li>Planification de l&apos;ensemble des moyens (humains, financiers, techniques) &#xE0; mettre en &#x153;uvre pour la continuit&#xE9; de service (action de suivi et de maintenance) de son portefeuille d&apos;applications </li> <li>Positionnement des droits sur les applications et habilitations </li> <li>R&#xE9;alisation de la recette fonctionnelle et traitement des anomalies </li> <li>Recueil et qualification des demandes et besoins de maintenance correctrice, &#xE9;volutive et/ou pr&#xE9;ventive du SI </li> <li>R&#xE9;daction, mise &#xE0; disposition et mise &#xE0; jour de la documentation utilisateur </li> <li>Suivi des &#xE9;volutions r&#xE9;glementaires et technologiques du SI </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter la documentation utilisateur (proc&#xE9;dure, manuel utilisateurs, ...), fournie par l&apos;&#xE9;diteur </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Diagnostiquer les dysfonctionnements, les pannes, les incidents r&#xE9;seau, le d&#xE9;faut des mat&#xE9;riaux, des &#xE9;quipements et des applications </li> <li>Elaborer des proc&#xE9;dures de secours en cas de d&#xE9;faillance de la solution </li> <li>Evaluer et documenter les r&#xE9;sultats obtenus par rapport aux sp&#xE9;cifications </li> <li>Former et conseiller les formateurs relais dans son domaine de comp&#xE9;tences </li> <li>Mettre en &#x153;uvre la charte d&apos;utilisation du SI et la charte d&apos;identito-vigilance </li> <li>Mettre en &#x153;uvre les r&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Recueillir, analyser, formaliser et valider les besoins fonctionnels et non fonctionnels utilisateurs (y compris les interfaces avec les applications existantes et les compl&#xE9;ments aux applications informatiques) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Architecture SI m&#xE9;tier et fonctionnelle </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Documentation technique fournie par l&apos;&#xE9;diteur ou l&apos;int&#xE9;grateur et archivage </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Informatique, SI et veille technologique </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thode de conduite de projet (planification, reporting, analyse de risques, ...) </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes et/ou outils de mod&#xE9;lisation (ex : UML, SADT, MEGA, ...), de d&#xE9;veloppement et d&apos;analyse informatique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Produits et march&#xE9; informatiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; informatique, s&#xE9;curit&#xE9; SI </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Standards et normes applicables (y compris les r&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es) </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>R&#xE9;f&#xE9;rents utilisateurs et utilisateurs pour la d&#xE9;finition des besoins, la pr&#xE9;paration des cahiers de charges et la consultation des &#xE9;diteurs, le choix d&apos;une solution et sa mise en oeuvre, ainsi que pour les demandes de maintenance corrective ou &#xE9;volutive / Utilisateurs ou centre d&apos;appel pour les &#xE9;changes d&apos;informations sur l&apos;application et l&apos;intervention de troisi&#xE8;me niveau en vue de la remise en condition op&#xE9;rationnelle de l&apos;application informatique / &#xC9;diteurs, diffuseurs et maintenaneurs des applicatifs informatiques pour les informations sur les produits, les r&#xE9;ponses aux consultations, la maintenance &#xE9;volutive et corrective.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac + 2/3 &#xE0; Bac+5 dans le domaine concern&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier<br>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Expert technique (r&#xE9;seau, BDD)<br>Chef de projet informatique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Analyste des données d''activités hospitalières', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, analyser et traiter les donn&#xE9;es<br>des activit&#xE9;s de soins, notamment m&#xE9;dicales<br>en vue de leurs descriptions, analyses et &#xE9;valuations.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la conformit&#xE9; et / ou de validit&#xE9; des documents, relatifs &#xE0; son domaine </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Restauration des bases de donn&#xE9;es </li> <li>Traitement et analyse de l&apos;information/donn&#xE9;es m&#xE9;dicale et/ou bilogique : extraction, regroupement, repr&#xE9;sentation graphique </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Choisir et utiliser des outils statistiques </li> <li>Concevoir, actualiser, optimiser une base de donn&#xE9;es, relatives &#xE0; la nature de ses activit&#xE9;s </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Construire / adapter des outils / m&#xE9;thodes de travail </li> <li>D&#xE9;finir et utiliser les techniques et pratiques adapt&#xE9;es &#xE0; son m&#xE9;tier </li> <li>Elaborer, r&#xE9;diger et exploiter des requ&#xEA;tes relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;valuer la pertinence et la v&#xE9;racit&#xE9; des donn&#xE9;es et/ou informations </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais informatique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Architecture du syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">D&#xE9;veloppement informatique / analyse et programmation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;pid&#xE9;miologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion de donn&#xE9;es, relatives &#xE0; son domaine </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Informatique / Syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; au traitement de l&apos;information m&#xE9;dicale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>DIM des autres &#xE9;tablissements pour l&apos;analyse comparative et statistiques des donn&#xE9;es m&#xE9;dicales<br>Chercheurs pour le traitement et l&apos;analyse de donn&#xE9;es statistiques<br>Assurance maladie et tutelles pour les r&#xE9;ponses aux questionnaires et la transmission des donn&#xE9;es<br>Cliniciens pour le traitement et l&apos;analyse des donn&#xE9;es m&#xE9;dicales<br>Direction des affaires financi&#xE8;res pour la facturation, l&apos;optimisation des recettes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Licence<br>Master 2<br>Eventuellement doctorat<br>Domaine : informatique, statistique, ing&#xE9;nierie de la sant&#xE9;, data management<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier<br>Ing&#xE9;nieur hospitalier<br>Contractuel de cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Statisticien<br>Gestionnaire de donn&#xE9;es biom&#xE9;dicales<br>Exploitant technique (sp&#xE9;cificit&#xE9; BDD)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Agent(e) de stérilisation', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Pr&#xE9;parer, conditionner et st&#xE9;riliser le mat&#xE9;riel et les produits des services de soins et m&#xE9;dico-techniques. Assurer la qualit&#xE9; du traitement et sa tra&#xE7;abilit&#xE9;.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Conducteur d&apos;autoclave<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Chargement/d&#xE9;chargement des produits et dispositifs m&#xE9;dicaux (laveur-d&#xE9;sinfecteur, autoclave,...) </li> <li>Nettoyage et entretien des locaux et des outils, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Pr&#xE9;paration, traitement et st&#xE9;rilisation des mat&#xE9;riels et dispositifs m&#xE9;dicaux (nettoyage, d&#xE9;sinfection, ...) </li> <li>Recensement / enregistrement des donn&#xE9;es / des informations li&#xE9;es &#xE0; la nature des activit&#xE9;s </li> <li>R&#xE9;ception, nettoyage, tri, contr&#xF4;le, reconstitution, recomposition des plateaux et bo&#xEE;tes d&apos;instrumentation, conditionnement et st&#xE9;rilisation du mat&#xE9;riel, des produits et des dispositifs m&#xE9;dicaux. </li> <li>Transport, manutention, entreposage / stockage / d&#xE9;stockage de produits et/ou de mat&#xE9;riels et de dispositifs m&#xE9;dicaux </li> <li>V&#xE9;rification, mise en route, pilotage et surveillance des &#xE9;quipements, mise &#xE0; l&apos;arr&#xEA;t </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements, outillages, dans son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer l&apos;&#xE9;tat d&apos;un mat&#xE9;riau, d&apos;un produit </li> <li>&#xC9;valuer l&apos;&#xE9;tat de propret&#xE9; des surfaces et d&#xE9;finir les travaux &#xE0; r&#xE9;aliser </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser et appliquer les protocoles de nettoyage et de d&#xE9;contamination pour la d&#xE9;sinfection des mat&#xE9;riels </li> <li>Utiliser les logiciels m&#xE9;tier </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bonnes pratiques de st&#xE9;rilisation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la pharmacie/ st&#xE9;rilisation </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Mat&#xE9;riel d&apos;hygi&#xE8;ne et de pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Principes de l&apos;assurance qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Agents de tous services de soins, de blocs op&#xE9;ratoires et m&#xE9;dico-techniques, pour le traitement des dispositifs m&#xE9;dicaux, du linge<br>Services logistiques dans le cadre de flux de mat&#xE9;riels et des produits<br>Services techniques et biom&#xE9;dicaux pour la maintenance, le d&#xE9;pannage des installations<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Certification Agent de st&#xE9;rilisation en milieu hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des aide-soignant et des agents des services hospitaliers qualifi&#xE9;s<br>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de bionettoyage<br>Agent de restauration et d&apos;h&#xF4;tellerie<br>Agent de blanchisserie<br>Aide-soignant,<br>Auxiliaire de pu&#xE9;riculture,<br>Aide m&#xE9;dico-psychologique<br>Agent de logistique<br>Ambulancier<br>Brancardier<br>Pr&#xE9;parateur en pharmacie<br>Encadrant de st&#xE9;rilisation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chargé(e) de valorisation de la recherche', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, mettre en oeuvre et piloter des op&#xE9;rations de promotion et de valorisation de la recherche dans un ou plusieurs domaines scientifiques, sous ses aspects administratifs, juridique (propri&#xE9;t&#xE9; intellectuelle), &#xE9;conomique et en &#xE9;valuer les r&#xE9;sultats<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Animateur(trice)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, d&#xE9;velopper, organiser et conduire des projets d&apos;animation visant &#xE0; l&apos;&#xE9;panouissement, la socialisation et l&apos;autonomie des personnes en prenant en compte leur environnement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Animateur(trice) socioculturel<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Brevet professionnel de la jeunesse, de l&apos;&#xE9;ducation populaire et du sport (BPJEPS)<br>Dipl&#xF4;me d&apos;Etat de la jeunesse, de l&apos;&#xE9;ducation populaire et du sport (DEJEPS)<br>Dipl&#xF4;me d&apos;Etat sup&#xE9;rieur de la jeunesse, de l&apos;&#xE9;ducation populaire et du sport (DESJEPS)<br>Brevet d&apos;&#xC9;tat fran&#xE7;ais d&apos;Animateur Technicien de l&apos;&#xC9;ducation Populaire et de la jeunesse (BEATEP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc.), dans son domaine </li> <li>Conception de projets d&apos;animations dans le domaine culturel ou &#xE9;ducatif </li> <li>&#xC9;laboration et mise en place du projet individuel concernant la personne et le groupe dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et suivi de budget(s), relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Evaluation quantitative et qualitative du programme d&apos;actions d&apos;animation </li> <li>Identification, recensement des besoins et des attentes des patients, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Information et promotion des r&#xE9;alisations et des projets dans son domaine d&apos;activit&#xE9; </li> <li>Mise en oeuvre d&apos;actions d&apos;animation </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Conduire un projet individuel </li> <li>D&#xE9;finir et utiliser les techniques et pratiques adapt&#xE9;es &#xE0; son m&#xE9;tier </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la satisfaction des patients / des clients / des personnels </li> <li>&#xC9;valuer le degr&#xE9; d&apos;autonomie d&apos;une personne ou d&apos;un groupe de personnes </li> <li>Organiser / animer des activit&#xE9;s sp&#xE9;cifiques &#xE0; son domaine de comp&#xE9;tence pour des patients, des personnels/des groupes </li> <li>Stimuler les capacit&#xE9;s affectives, intellectuelles, psychomotrices et sociales d&apos;une personne </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;riatrie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sport et techniques sportives </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Personnels soignants pour d&#xE9;finir le meilleur projet pour le patient ou le groupe de patients. Partenaires ext&#xE9;rieurs, personnels soignants et familles pour la mise en oeuvre des actions d&apos;animation. Directions fonctionnelles pour la construction et la r&#xE9;alisation des actions d&apos;animation. Conseiller en Economie Sociale et Familiale et Technicien d&apos;Intervention Sociale et Familiale pour les aspects financiers exceptionnels d&apos;une animation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Brevet professionnel de la jeunesse, de l&apos;&#xE9;ducation populaire et du sport (BPJEPS) du champ de l&apos;animation socioculturelle ;Dipl&#xF4;me d&apos;Etat de la jeunesse, de l&apos;&#xE9;ducation populaire et du sport (DEJEPS) animation sociale ou d&#xE9;veloppement de projet, territoires et r&#xE9;seaux ;Dipl&#xF4;me d&apos;Etat sup&#xE9;rieur de la jeunesse, de l&apos;&#xE9;ducation populaire et du sport (DESJEPS) direction de projet; Brevet d&apos;&#xC9;tat fran&#xE7;ais d&apos;Animateur Technicien de l&apos;&#xC9;ducation Populaire et de la jeunesse (BEATEP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Animateur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Educateur sportif<br>Educateur sp&#xE9;cialis&#xE9;<br>Moniteur-&#xE9;ducateur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Aide-soignant(e)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Dispenser,en collaboration avec l&apos;infirmier, des soins de pr&#xE9;vention, de maintien, de relation et d&apos;&#xE9;ducation &#xE0; la sant&#xE9; pour pr&#xE9;server et restaurer la continuit&#xE9; de la vie, le bien-&#xEA;tre et l&apos;autonomie de la personne.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Assistant(e) de soins en g&#xE9;rontologie (ASG)<br>Aide-soignant(e) polyhandicap<br>Agent(e) de service mortuaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;&#xE9;tat d&apos;aide-soignant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement du patient et/ou du r&#xE9;sident et/ou de son entourage (s&#xE9;jour, retour au domicile, placement, etc) </li> <li>Accueil des personnes (patients, usagers, familles, etc.) </li> <li>Accueil, information et formation des nouveaux personnels, des stagiaires et des professionnels de sant&#xE9; </li> <li>Aide l&apos;infirmier &#xE0; la r&#xE9;alisation de soins </li> <li>Entretien de l&apos;environnement imm&#xE9;diat de la personne et la r&#xE9;fection des lits </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Observation de l&apos;&#xE9;tat de sant&#xE9; et le comportement relationnel et social d&apos;une personne </li> <li>Observation du bon fonctionnement des appareillages et dispositifs m&#xE9;dicaux </li> <li>Observation et mesure des param&#xE8;tres vitaux </li> <li>R&#xE9;alisation de soins de confort et de bien-&#xEA;tre </li> <li>Transmission de ses observations par oral et par &#xE9;crit pour maintenir la continuit&#xE9; des soins </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Analyser / &#xE9;valuer la situation clinique d&apos;une personne, d&apos;un groupe de personnes, relative &#xE0; son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>Etablir une communication adapt&#xE9;e &#xE0; la personne et &#xE0; son entourage </li> <li>&#xC9;valuer le degr&#xE9; d&apos;autonomie d&apos;une personne ou d&apos;un groupe de personnes </li> <li>Organiser son travail au sein d&apos;une &#xE9;quipe pluriprofessionnelle </li> <li>R&#xE9;aliser des soins adapt&#xE9;s &#xE0; l&apos;&#xE9;tat clinique de la personne </li> <li>Rechercher, traiter et transmettre les informations pour assurer la continuit&#xE9; des soins </li> <li>Restaurer / maintenir l&apos;autonomie de la personne dans les actes de la vie quotidienne </li> <li>Utiliser les techniques d&apos;entretien des locaux et du mat&#xE9;riel sp&#xE9;cifiques aux &#xE9;tablissements sanitaires, sociaux et m&#xE9;dico-sociaux </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne, confort et r&#xE9;ponse aux besoins de la personne </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">St&#xE9;rilisation </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les infirmiers pour le travail en collaboration<br>Les cadres de sant&#xE9; pour l&apos;organisation des activit&#xE9;s<br>Les m&#xE9;decins pour transmission des informations concernant l&apos;&#xE9;tat de sant&#xE9; des patients<br>Les param&#xE9;dicaux des m&#xE9;tiers de r&#xE9;&#xE9;ducation pour une prise en charge globale de la personne soign&#xE9;e<br>Les services logistiques pour les prestations h&#xF4;teli&#xE8;res<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;&#xE9;tat d&apos;aide-soignant (Formation de 41 semaines, modulaire)<br>Validation des acquis de l&apos;exp&#xE9;rience<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Aide-soignant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Auxiliaire de pu&#xE9;riculture<br>Aide m&#xE9;dico-psychologique<br>Agent de st&#xE9;rilisation<br>Ambulancier<br>infirmier en soins g&#xE9;n&#xE9;raux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Coordinateur(trice) qualité / gestion des risques', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finir, piloter et contr&#xF4;ler la mise en &#x153;uvre de la politique et du plan d&apos;action assurance qualit&#xE9; de l&apos;&#xE9;tablissement. Garantir la conformit&#xE9; aux r&#xE9;f&#xE9;rentiels par rapport aux exigences et aux r&#xE9;glementations en vigueur, en relation avec les clients et les fournisseurs. D&#xE9;finir et assurer le d&#xE9;ploiement op&#xE9;rationnel de la gestion des risques et du programme de s&#xE9;curit&#xE9;.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Ing&#xE9;nieur(e) qualit&#xE9;<br>Responsable qualit&#xE9;/gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Responsable qualit&#xE9;<br>Responsable gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Audit de processus </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>&#xC9;laboration et mise en service d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Organisation du syst&#xE8;me de management de la qualit&#xE9; </li> <li>Planification, contr&#xF4;le et reporting des activit&#xE9;s et des moyens </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recensement, identification, analyse et traitement des risques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Choisir et utiliser des mat&#xE9;riels, des outils de travail ou / et de contr&#xF4;le, aff&#xE9;rents &#xE0; son m&#xE9;tier </li> <li>Concevoir la cartographie des risques </li> <li>D&#xE9;finir et utiliser les techniques et pratiques adapt&#xE9;es &#xE0; son m&#xE9;tier </li> <li>D&#xE9;finir, conduire et &#xE9;valuer la politique relative &#xE0; son domaine de comp&#xE9;tence </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Piloter et &#xE9;valuer un projet, relevant de son domaine de comp&#xE9;tence </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Rechercher, s&#xE9;lectionner, exploiter et capitaliser les informations li&#xE9;es &#xE0; la veille dans son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Audit interne </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion de crise </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des risques professionnels </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; au management de la qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques / assurance qualit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">R&#xE9;f&#xE9;rentiels qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions fonctionnelles, responsables des p&#xF4;les et des services de soins pour mener avec eux les projets d&apos;am&#xE9;lioration de la qualit&#xE9;/gestion des risques<br>Service des relations usagers dans le cadre de la satisfaction des usagers, service contentieux pour suivre les dysfonctionnements et aider l&apos;&#xE9;tablissement &#xE0; fournir des preuves (tra&#xE7;abilit&#xE9;)<br>Sous-commissions de la CME dans le cadre d&apos;un apport m&#xE9;thodologique, et pour soutenir la politique qualit&#xE9;/gestion des risques dans les domaines concern&#xE9;s par les sous-commissions (m&#xE9;dicaments, hygi&#xE8;ne...)<br>Membres du CHSCT<br>Assureurs, dans le cadre de partenariats en vue de la ma&#xEE;trise des risques<br>Organismes d&apos;accr&#xE9;ditation et de certification pour les d&#xE9;marches de reconnaissance externe<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+5 dans le domaine concern&#xE9;<br>Exp&#xE9;rience professionnelle suffisante compl&#xE9;t&#xE9;e par un DU ou un Master dans le domaine concern&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br>Technicien sup&#xE9;rieur hospitalier<br>Cadre de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Auxiliaire de vie sociale', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Assister les personnes fragiles, d&#xE9;pendantes ou en difficult&#xE9; sociale en leur apportant une aide dans l&apos;accomplissement des t&#xE2;ches et des activit&#xE9;s de la vie quotidienne, y compris les loisirs, en fournissant un soutien psychologique et social<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Auxiliaire de vie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Personnes &#xE2;g&#xE9;es<br>Handicap&#xE9;es<br>Enfants<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement du patient et des aidants tout au long de son parcours de sant&#xE9; </li> <li>Accueil et installation du patient, am&#xE9;nagement de l&apos;environnement (confort et s&#xE9;curit&#xE9;) </li> <li>Assistance aux personnes, aux patients pour la r&#xE9;alisation des actes de la vie quotidienne (repas, toilette, etc.) </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Tenue d&apos;entretiens avec des personnes (patients, familles,...) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>Restaurer / maintenir l&apos;autonomie de la personne dans les actes de la vie quotidienne </li> <li>Stimuler les capacit&#xE9;s affectives, intellectuelles, psychomotrices et sociales d&apos;une personne </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie des &#xE2;ges de la vie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>&#xC9;quipes m&#xE9;dicales, param&#xE9;dicales,&#xE9;ducatives et psychologiques pour l&apos;assistance dans les actes de la vie quotidienne et travail en &#xE9;quipe pluridisciplinaire,<br>Famille et environnement de la personne pour partage d&apos;informations,<br>Autres professionnels des secteurs sanitaire, social et m&#xE9;dico-social pour la mise en oeuvre des projets individualis&#xE9;s et des &#xE9;changes d&apos;exp&#xE9;rience et de pratiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;Auxiliaire de Vie Sociale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des Agents des services hospitaliers qualifi&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Aide M&#xE9;dico Psychologique<br>Aide-soignant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Assistant(e) de régulation médicale', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Accueillir, localiser et orienter les appels t&#xE9;l&#xE9;phoniques dans un contexte d&apos;urgence et de d&#xE9;tresse.<br>Prioriser l&apos;orientation vers le m&#xE9;decin r&#xE9;gulateur du SAMU centre 15, dans le cadre d&apos;un travail en bin&#xF4;me en fonction du degr&#xE9; d&apos;urgence &#xE9;valu&#xE9;.<br>G&#xE9;rer les flux et moyens d&#xE9;finis par le m&#xE9;decin r&#xE9;gulateur, en assurant la tra&#xE7;abilit&#xE9; jusqu&apos;&#xE0; la fin de la prise en charge du patient.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Permanencier(&#xE8;re) auxiliaire de r&#xE9;gulation m&#xE9;dicale (PARM)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Attestation de formation aux gestes de soins d&apos;urgence (AFGSU)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement du patient et des proches tout au long de la prise en charge t&#xE9;l&#xE9;phonique </li> <li>Classement et tenue de la documentation professionnelle </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion des moyens &#xE0; d&#xE9;ployer pour r&#xE9;pondre &#xE0; la prescription m&#xE9;dicale </li> <li>Pr&#xE9;paration et organisation du mat&#xE9;riel dans son domaine de comp&#xE9;tences et dans le cadre de plan de secours (sur le lieu de travail ou lieu de l&apos;&#xE9;v&#xE8;nement) </li> <li>R&#xE9;ception et traitement des appels t&#xE9;l&#xE9;phoniques dans un contexte d&apos;urgence et de d&#xE9;tresse </li> <li>Recueil et collecte de donn&#xE9;es ou informations sp&#xE9;cifiques suivant les recommandations de bonnes pratiques, les protocoles &#xE9;tablis </li> <li>Suivi des actions mises en oeuvre </li> <li>Transmission syst&#xE9;matique d&apos;informations synth&#xE9;tiques et factuelles au m&#xE9;decin r&#xE9;gulateur </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Conseiller les gestes de premier secours </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>Identifier, analyser, prioriser selon le degr&#xE9; d&apos;urgence </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Reformuler une demande (d&apos;information, de renseignement, etc.), en r&#xE9;sumer les points-cl&#xE9;s et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; professionnelle </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Accueil t&#xE9;l&#xE9;phonique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et transmission radio </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie du secteur sanitaire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion du stress </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation des syst&#xE8;mes d&apos;urgence </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement hospitalier </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE9;seaux sanitaires, sociaux et m&#xE9;dico-sociaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>L&apos;&#xE9;quipe de r&#xE9;gulation (m&#xE9;decins et PARM) pour la transmission et le suivi des appels<br>SMUR, m&#xE9;decins lib&#xE9;raux, secouristes, transports sanitaires, &#xE9;tablissements de sant&#xE9; (structures d&apos;urgences, sp&#xE9;cials&#xE9;es et plateaux techniques)<br>Les partenaires des services sociaux et autorit&#xE9;s administratives, sanitaires, maritimes, a&#xE9;ronautiques et ferroviaires pour la mise en oeuvre et suivi des interventions.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Certificat d&apos;assistant de r&#xE9;gulation m&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Assistant m&#xE9;dico-administratif (Cat&#xE9;gorie B)<br>Permanencier auxillaire de r&#xE9;gulation m&#xE9;dicale (Cat&#xE9;gorie C : corps plac&#xE9; en cadre d&apos;extinction - D&#xE9;cret n&#xB0;2011 - 660 du 14 juin 2011, Article 23)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Aide-soignant<br>Secr&#xE9;taire m&#xE9;dical<br>Ambulancier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Animateur(trice) qualité / gestion des risques', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Recueillir et exploiter les informations terrain (internes / externes) et mettre en oeuvre les actions ad&#xE9;quates dans le domaine de la qualit&#xE9; et la gestion des risques en coh&#xE9;rence avec la politique qualit&#xE9; et de gestion des risques de l&apos;&#xE9;tablissement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Assistant(e) qualit&#xE9;<br>Assistant(e) qualit&#xE9; / gestion des risques<br>Technicien(ne) qualit&#xE9; risques<br>Qualiticien(ne)<br>Technicien(ne) qualit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Charg&#xE9;(e) d&apos;enqu&#xEA;tes qualit&#xE9; risques et d&apos;&#xE9;valuation<br>Auditeur(trice) qualit&#xE9; interne<br>Gestionnaire documentaire sp&#xE9;cialis&#xE9;(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation de personnes aux techniques et proc&#xE9;dures de son domaine, et &#xE0; leur application </li> <li>Organisation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es </li> <li>Organisation et description des processus sp&#xE9;cifiques &#xE0; son domaine, traitement des dysfonctionnements </li> <li>Suivi de la r&#xE9;alisation des travaux, de la r&#xE9;solution des probl&#xE8;mes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser un / des processus </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Choisir une m&#xE9;thode, un moyen correspondant &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir/adapter et utiliser un plan de classement et d&apos;archivage, un syst&#xE8;me de gestion documentaire </li> <li>&#xC9;valuer la conformit&#xE9; de la mise en oeuvre d&apos;une proc&#xE9;dure/norme/r&#xE8;gle, consigne relevant de son domaine de comp&#xE9;tence </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Utiliser les logiciels m&#xE9;tier </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; au suivi de la qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes des sciences sociales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">R&#xE9;f&#xE9;rentiels qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Statistiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsables de service / responsables projets pour l&apos;assistance m&#xE9;thodologique &#xE0; l&apos;utilisation des outils de gestion de la qualit&#xE9; et des risques<br>Membres du CHSCT<br>Service des relations avec les usagers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+2 dans le domaine de la qualit&#xE9; DUT, BTS en QHSE<br>Exp&#xE9;rience professionnelle suffisante compl&#xE9;t&#xE9;e par un DU dans le domaine<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des techniciens et techniciens sup&#xE9;rieurs hospitaliers<br>Cadre de sant&#xE9;<br>Adjoint des cadres hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) maintenance biomédicale', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter les activit&#xE9;s de maintenance biom&#xE9;dicale soit en animant une &#xE9;quipe de professionnels, soit par le biais de prestataires ext&#xE9;rieurs<br>G&#xE9;rer la mat&#xE9;riovigilance<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) d&apos;atelier maintenance biom&#xE9;dicale<br>Adjoint(e) au responsable biom&#xE9;dical<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Achat de produits / de prestations, dans son domaine </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de l&apos;&#xE9;tat et du fonctionnement des &#xE9;quipements / des installations, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi du bon d&#xE9;roulement du (des) process sp&#xE9;cifique(s) au domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Organisation du syst&#xE8;me de management de la qualit&#xE9; </li> <li>Planification des activit&#xE9;s / interventions internes/externes, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Utilisation de la Gestion de maintenance Assist&#xE9;e par Ordinateur (GMAO) dont il est garant de la qualit&#xE9; des donn&#xE9;es qui y sont saisies </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter et optimiser le fonctionnement des &#xE9;quipements, des installations, des syst&#xE8;mes, des m&#xE9;thodes de travail dans son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer le niveau de comp&#xE9;tence n&#xE9;cessaire &#xE0; des interventions techniques, relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer une charge de travail </li> <li>Identifier, &#xE9;valuer, choisir des mat&#xE9;riels, des outils adapt&#xE9;s &#xE0; la r&#xE9;alisation des activit&#xE9;s de son domaine </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier et coordonner des travaux et / ou des interventions (maintenance, entretien et /ou installation de mat&#xE9;riels, outils, &#xE9;quipements, syst&#xE8;mes...) </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Achat </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Anglais technique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Automatisme </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Biom&#xE9;dical </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Economie de la maintenance </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectronique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Fluides m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de gestion de maintenance biom&#xE9;dicale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Maintenance industrielle / &#xE9;quipements </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les m&#xE9;decins, cadres de soins et m&#xE9;dico-techniques.<br>Les services achats, logistiques, financiers, du personnel et de la formation permanente<br>Les ing&#xE9;nieurs biom&#xE9;dicaux.<br>Les responsables de la gestion des risques.<br>Les techniciens biom&#xE9;dicaux,<br>Les responsables des services techniques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>&#xC9;lectronique, &#xE9;lectrotechnique, physiologie, informatique industrielle, g&#xE9;nie industriel<br>TSIBH / licence ou &#xE9;quivalent<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier<br>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable des services biom&#xE9;dicaux<br>Conseiller en ing&#xE9;nierie biom&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Assistant(e) maternel(le)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Accueillir quotidiennement et de fa&#xE7;on non permanente, &#xE0; son domicile, des enfants de moins de 6 ans<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>nourrice agr&#xE9;&#xE9;e<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Agr&#xE9;ment n&#xE9;cessaire pour exercer la profession d&apos;assistant maternel d&#xE9;livr&#xE9; par le pr&#xE9;sident du conseil g&#xE9;n&#xE9;ral du d&#xE9;partement o&#xF9; le demandeur r&#xE9;side.<br>Formation initiale d&apos;assistant(e) maternelle<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc.), dans son domaine </li> <li>Identification, recensement des besoins et des attentes des patients, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation de soins sp&#xE9;cifiques &#xE0; son domaine d&apos;intervention </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>D&#xE9;velopper une relation de confiance avec l&apos;enfant </li> <li>&#xC9;laborer des menus, composer des repas </li> <li>&#xC9;laborer un projet d&apos;accueil et de formation pour des personnes / publics divers </li> <li>&#xC9;valuer une prestation, un projet, une solution, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>G&#xE9;rer ses &#xE9;motions, son stress </li> <li>Organiser / animer des activit&#xE9;s sp&#xE9;cifiques &#xE0; son domaine de comp&#xE9;tence pour des patients, des personnels/des groupes </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Droit des enfants et de la protection de la petite enfance </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Pu&#xE9;riculture </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les parents des enfants accueillis pour rendre compte le service employeur.<br>Les services du conseil g&#xE9;n&#xE9;ral dans le cadre de la protection de l&apos;enfant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>1er degr&#xE9; du CAP petite enfance<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>N&#xE9;ant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Auxilliaire de pu&#xE9;riculture<br>Assistant familial<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Assistant(e) social(e)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Aider au d&#xE9;veloppement des capacit&#xE9;s du public concern&#xE9; pour maintenir ou restaurer son autonomie.<br>Mener des interventions susceptibles de pr&#xE9;venir ou de surmonter les difficult&#xE9;s.<br>Agir avec les personnes, familles, groupes afin d&apos;am&#xE9;liorer leurs conditions de vie sur le plan social, sanitaire, familial, &#xE9;conomique, culturel et professionnel.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Assistant(e) social(e) aupr&#xE8;s des personnels<br>Assistant(e) social(e) aupr&#xE8;s des usagers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;Assistant(e) de Service Social(e) (DEASS)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, encadrement et accompagnement p&#xE9;dagogique de personnes (agents, &#xE9;tudiants, stagiaires, etc.) </li> <li>Animation de r&#xE9;unions intra et extrahospitali&#xE8;res (service, synth&#xE8;se, partenaires ext&#xE9;rieurs, etc.) </li> <li>Assistance aux personnes, aux patients pour l&apos;insertion ou la r&#xE9;insertion sociale et/ou professionnelle </li> <li>D&#xE9;finition et mise en place de mesures correctives dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et mise en place du projet individuel concernant la personne et le groupe dans son domaine d&apos;activit&#xE9; </li> <li>Information et conseil &#xE0; des tiers (agents, patients, familles, etc.) dans son domaine d&apos;activit&#xE9; </li> <li>Information sur l&apos;offre de soins du territoire, conseil et orientation des patients </li> <li>M&#xE9;diation des relations avec la famille, le repr&#xE9;sentant l&#xE9;gal de la personne et les diff&#xE9;rents partenaires ext&#xE9;rieurs </li> <li>Montage et instruction de dossiers relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction de comptes-rendus relatifs aux observations / aux interventions, dans son domaine d&apos;activit&#xE9; </li> <li>Tenue d&apos;entretiens avec des personnes (patients, familles,...) </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Conduire un entretien d&apos;aide </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les outils bureautiques / TIC </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Audit social </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit civil </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Intervention sociale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;diation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement des structures sociales et des r&#xE9;seaux sanitaires et m&#xE9;dico-sociales </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Equipes pluridisciplinaires des &#xE9;tablissements ou des associations, autres travailleurs sociaux, justice, &#xE9;ducation nationale, organismes administratifs pour l&apos;accompagnement coordonn&#xE9; des prises en charge des patients et des familles<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;Assistant(e) de Service Social (DEASS)<br>Droit<br>Sciences &#xE9;conomiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Assistant socio-&#xE9;ducatif - cat&#xE9;gorie B<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Educateur sp&#xE9;cialis&#xE9;<br>Encadrant socio-educatif<br>Mandataire judiciaire &#xE0; la protection des majeurs<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Assistant(e) dentaire', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire:<br>&quot;Assister le dentiste ou le chirurgien-dentiste dans les t&#xE2;ches techniques et administratives n&#xE9;cessaires aux diff&#xE9;rents types de soins dentaires.&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et installation du patient, am&#xE9;nagement de l&apos;environnement (confort et s&#xE9;curit&#xE9;) </li> <li>Assistance technique pour la r&#xE9;alisation des soins, sp&#xE9;cifique au domaine d&apos;activit&#xE9; </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Prise de rendez-vous / gestion des agendas </li> <li>Reproduction et diffusion de documents, de dossiers </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>&#xC9;laborer, adapter et optimiser le planning de travail, de rendez-vous, de visites </li> <li>Organiser et classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>Utiliser et appliquer les protocoles de nettoyage et de d&#xE9;contamination pour la d&#xE9;sinfection des mat&#xE9;riels </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Accueil t&#xE9;l&#xE9;phonique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Classement et archivistique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes et techniques d&apos;hygi&#xE8;ne et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Odontologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">St&#xE9;rilisation </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Ce m&#xE9;tier s&apos;exerce le plus souvent seul.<br>Toutefois, certaines interventions conduisent &#xE0; travailler en &#xE9;quipe avec le professionnel<br>Le contact avec la client&#xE8;le est constant.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Ce m&#xE9;tier est g&#xE9;n&#xE9;ralement accessible sans formation sp&#xE9;cifique particuli&#xE8;re ni exp&#xE9;rience pr&#xE9;alable.<br>Toutefois, une formation pratique, mise en place par la profession en cours d&apos;emploi, permet aux salari&#xE9;s d&#xE9;butants de pr&#xE9;parer l&apos;examen leur reconnaissant la qualification d&apos;assistant en cabinet m&#xE9;dical.<br>Etre titulaire d&apos;un dipl&#xF4;me de niveau V bis (BEPC, brevet des coll&#xE8;ges...) ou d&apos;une formation &#xE9;quivalente et &#xEA;tre &#xE2;g&#xE9; de plus de 18 ans est indispensable pour pr&#xE9;tendre &#xE0; cet examen de qualification.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des aide-soignant<br>Corps des agents des services hospitaliers qualifi&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Secr&#xE9;taire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) en économie sociale familiale (CESF)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Accompagner dans une d&#xE9;marche &#xE9;ducative et sociale globale les personnes, les groupes ou les familles dans les domaines de la vie quotidienne, en vue de r&#xE9;soudre des probl&#xE8;mes divers (sociaux, &#xE9;conomiques, administratifs, familiaux...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de conseiller en &#xE9;conomie sociale familiale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, encadrement et accompagnement p&#xE9;dagogique de personnes (agents, &#xE9;tudiants, stagiaires, etc.) </li> <li>Assistance sp&#xE9;cifique aux personnes et aux groupes dans le domaine de la gestion de la vie quotidienne </li> <li>Conseil p&#xE9;dagogique aupr&#xE8;s des personnels / utilisateurs / usagers dans son domaine </li> <li>D&#xE9;veloppement d&apos;un r&#xE9;seau de partenaires dans des dynamiques de contribution sociale locale </li> <li>Entretien avec l&apos;entourage et les aidants pour l&apos;accompagnement du patient </li> <li>Entretiens d&apos;aide avec la personne et mise en place de mesures d&apos;insertion socioprofessionnelles </li> <li>Etablissement et suivi de budgets relatifs &#xE0; son domaine d&apos;activit&#xE9; en vue de la pr&#xE9;vention de l&apos;exclusion sociale </li> <li>Montage, mise en oeuvre, suivi et gestion de projets sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction et mise &#xE0; jour des notes, documents et rapports, toute documentation relative &#xE0; ses activit&#xE9;s dans son domaine de comp&#xE9;tences </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Conseiller et orienter les choix des personnes </li> <li>Etablir, &#xE9;valuer et optimiser un budget </li> <li>Evaluer le niveau d&apos;autonomie de la personne dans la vie quotidienne (budget, logement, habilit&#xE9;s sociales, d&#xE9;marches sociales, emploi...) </li> <li>N&#xE9;gocier des prestations, des contrats, des accords avec des interlocuteurs internes/externes </li> <li>R&#xE9;aliser un diagnostic ou bilan global des capacit&#xE9;s de la personne dans sa vie quotidienne en garantissant une approche holistique de la personne et de son entourage </li> <li>Reformuler une demande (d&apos;information, de renseignements, etc.) et en r&#xE9;sumer les points cl&#xE9;s </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;conomie sociale familiale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Intervention sociale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins, soignants et travailleurs sociaux avec un objectif de coordination et de synth&#xE8;se dans les champs sanitaires et sociaux<br>Autres conseillers techniques afin de compl&#xE9;ter les conseils<br>Elus et associations locaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BTS de conseiller(&#xE8;re) en &#xE9;conomie sociale familialern<br>Dipl&#xF4;me de conseiller(&#xE8;re) en &#xE9;conomie sociale familiale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Conseiller en &#xE9;conomie sociale familiale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable socio-&#xE9;ducatif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) en génétique', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Conseiller les patients ou leurs familles concern&#xE9;s par une affection g&#xE9;n&#xE9;tique, qu&apos;ils en soient atteints ou susceptibles de l&apos;&#xEA;tre ou de la transmettre.<br>Leur apporter les informations appropri&#xE9;es pour leur permettre de prendre des d&#xE9;cisions &#xE9;clair&#xE9;es et les accompagner tout au long de leur parcours en assurant leur suivi m&#xE9;dico-social et psychologique.<br>Exercer sous la responsabilit&#xE9; d&apos;un m&#xE9;decin g&#xE9;n&#xE9;ticien et sur prescription m&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de conseiller en g&#xE9;n&#xE9;tique ou autorisation d&apos;exercice de la profession de conseiller en g&#xE9;n&#xE9;tique selon l&apos;arr&#xEA;t&#xE9; du 10 avril 2008<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil des patients et des familles dans son domaine d&apos;activit&#xE9; </li> <li>Actions de formation en conseil g&#xE9;n&#xE9;tique des &#xE9;tudiants sous la responsabilit&#xE9; de l&apos;&#xE9;quipe p&#xE9;dagogique. </li> <li>Bilan clinique d&apos;un patient, sp&#xE9;cifique au domaine (donn&#xE9;es m&#xE9;dicales, examens compl&#xE9;mentaires, travail de synth&#xE8;se...) </li> <li>Conduite d&apos;entretien: &#xE9;coute, recueil et d&#xE9;livrance d&apos;informations en mati&#xE8;re de d&#xE9;pistage, de prise en charge et de pr&#xE9;vention, Informations sur le consentement aux analyses </li> <li>Conseil, aide &#xE0; la d&#xE9;cision et information &#xE0; la personne et &#xE0; son entourage </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>Organisation de la prise en charge des personnes, prescrite par le m&#xE9;decin g&#xE9;n&#xE9;ticien: planification des examens compl&#xE9;mentaires et consultations, coordination des intervenants internes et externes, secteur associatif... </li> <li>Recueil d&apos;informations sur les pathologies, les facteurs de risque (r&#xE9;alisation d&apos;enqu&#xEA;tes familiales et d&apos;arbre g&#xE9;n&#xE9;alogique) aupr&#xE8;s des individus eux-m&#xEA;mes ou dans les dossiers m&#xE9;dicaux </li> <li>R&#xE9;daction de comptes-rendus relatifs aux observations / aux interventions, dans son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir, accompagner et informer la personne et sa famille au long de son parcours de soins </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>Evaluer une situation &#xE0; risque dans le domaine de la g&#xE9;n&#xE9;tique et de la m&#xE9;decine pr&#xE9;dictive (y compris les risques durant la grossesse, le DPN/DPI : diagnostic pr&#xE9;natal et diagnostic pr&#xE9; implantatoire) </li> <li>G&#xE9;rer et transmettre les donn&#xE9;es et utiliser les syst&#xE8;mes d&apos;information </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>Informer et former des professionnels et des personnes en formation </li> <li>Rechercher et traiter des donn&#xE9;es professionnelles et scientifiques </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Biologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;n&#xE9;tique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pharmacologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sciences humaines </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins g&#xE9;n&#xE9;ticiens et d&apos;autres sp&#xE9;cialit&#xE9;s (p&#xE9;diatres, gyn&#xE9;co-obst&#xE9;triciens, neurologues, radiodiagnostic et imagerie, foetopathologistes), biologistes, sages-femmes, psychologues, professions param&#xE9;dicales, bio informaticiens, assistants sociaux pour la prise en charge du patient.<br>Secr&#xE9;taires m&#xE9;dicales pour la gestion des dossiers des patients<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Master 2 professionnel : sciences de la sant&#xE9;, mention pathologie humaine, sp&#xE9;cialit&#xE9; conseil g&#xE9;n&#xE9;tique et m&#xE9;decine pr&#xE9;dictive (Seul lieu actuel d&apos;enseignement : universite Aix - Marseille II)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Contractuel de cat&#xE9;gorie A (ing&#xE9;nieur)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Assistant(e) de recherche clinique (ARC)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre en place sur site ou &#xE0; distance puis assurer la surveillance et le contr&#xF4;le qualit&#xE9; aux plans scientifique, technique et r&#xE9;glementaire des protocoles de recherche clinique, pour le compte du promoteur ou du gestionnaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Attach&#xE9;(e) de Recherche Clinique<br>Assitant(e) de Recherche Clinique (ARC) promoteur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; et / ou de validit&#xE9; des documents, relatifs &#xE0; son domaine </li> <li>Contr&#xF4;le de la faisabilit&#xE9; des circuits logistiques de l&apos;&#xE9;tude </li> <li>Contr&#xF4;le et suivi du bon d&#xE9;roulement du (des) process sp&#xE9;cifique(s) au domaine d&apos;activit&#xE9; </li> <li>Organisation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es </li> <li>R&#xE9;daction de comptes-rendus relatifs aux observations / aux interventions, dans son domaine d&apos;activit&#xE9; </li> <li>Suivi des &#xE9;v&#xE9;nements ind&#xE9;sirables </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et utiliser des informations &#xE0; partir du dossier hospitalier du patient et du dossier m&#xE9;dical personnalis&#xE9; </li> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>&#xC9;laborer, adapter et optimiser le planning de travail, de rendez-vous, de visites </li> <li>&#xC9;valuer la pertinence et la v&#xE9;racit&#xE9; des donn&#xE9;es et/ou informations </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>S&apos;exprimer dans une langue &#xE9;trang&#xE8;re </li> <li>S&apos;exprimer en face -&#xE0;- face aupr&#xE8;s d&apos;une ou plusieurs personnes </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication &#xE9;crite </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication orale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion de donn&#xE9;es, relatives &#xE0; son domaine </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation relative &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins investigateurs, techniciens d&apos;etudes cliniques, coordinateurs d&apos;etudes cliniques et chefs de projets de recherche clinique, biostatisticiens, data-managers, sp&#xE9;cialistes en pharmacovigilance hospitali&#xE8;re pour la mise en oeuvre et la conduite des &#xE9;tudes cliniques.<br>Pharmaciens des pharmacies &#xE0; usage int&#xE9;rieur pour le contr&#xF4;le du circuit m&#xE9;dicamenteux des &#xE9;tudes cliniques<br>Instances administratives et r&#xE9;glementaires (comit&#xE9; de protection des personnes (CPP), Agence Nationale de S&#xE9;curit&#xE9; du M&#xE9;dicament (ANSM), pour autorisations, validation et suivi des &#xE9;tudes cliniques.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau II (BAC+3) ou niveau I (BAC+5)<br>Etudes param&#xE9;dicales ou Dipl&#xF4;me Universitaire dans le domaine des &#xE9;tudes cliniques ou d&apos;autres domaines scientifiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier,<br>Infirmier dipl&#xF4;m&#xE9; d&apos;etat,<br>Technicien de laboratoire,...ou ing&#xE9;nieur hospitalier<br>Cat&#xE9;gorie B ou A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Technicien d&apos;Etudes Cliniques (TEC)<br>Coordinateur d&apos;etudes cliniques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Brancardier(ère)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Transporter, accompagner et assurer la manutention des patients au sein d&apos;un &#xE9;tablissement de soins.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Aptitude physique au port de charges<br>Attestation AFGSU<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et installation du patient, am&#xE9;nagement de l&apos;environnement (confort et s&#xE9;curit&#xE9;) </li> <li>Nettoyage et entretien des &#xE9;quipements, machines, outillages, v&#xE9;hicules sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Saisie, mise &#xE0; jour et / ou sauvegarde de donn&#xE9;es, d&apos;informations, de tableaux, dans son domaine d&apos;activit&#xE9; </li> <li>Surveillance de l&apos;&#xE9;tat de sant&#xE9; des personnes accueillies dans son domaine d&apos;intervention </li> <li>Transport de patients </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser / &#xE9;valuer la situation clinique d&apos;une personne, d&apos;un groupe de personnes, relative &#xE0; son domaine de comp&#xE9;tence </li> <li>Choisir et utiliser des mat&#xE9;riels, des outils de travail ou / et de contr&#xF4;le, aff&#xE9;rents &#xE0; son m&#xE9;tier </li> <li>D&#xE9;finir et mettre en oeuvre les soins et activit&#xE9;s th&#xE9;rapeutiques adapt&#xE9;s au patient, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Soignants et &#xE9;quipes m&#xE9;dicales des unit&#xE9;s de soins pour la programmation et la pr&#xE9;paration des patientsrnSoignants des diff&#xE9;rents plateaux techniques pour g&#xE9;rer le flux des patientsrnPompiers et/ou ambulances priv&#xE9;es pour assurer la continuit&#xE9; de la prise en charge du patient en internernAmbulanciers internes &#xE0; l&apos;&#xE9;tablissement et SAMU pour la continuit&#xE9; de la prise en charge<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des aide-soignant<br>Corps des agents des services hospitaliers qualifi&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Aide-soignant<br>Auxiliaire de pu&#xE9;riculture<br>Ambulancier<br>Agent de service mortuaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant (e) Accueil standard', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Encadrer et animer une &#xE9;quipe de standardistes et d&apos;agents d&apos;accueil<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Encadrant (e) de plate-forme t&#xE9;l&#xE9;phonique<br>Encadrant (e) de centre d&apos;accueil t&#xE9;l&#xE9;phonique<br>Encadrant (e) h&#xF4;tes (esses) d&apos;accueil<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Adapter un / des postes de travail aux caract&#xE9;ristiques des personnels (charge physique ou/et mentale) </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>&#xC9;valuer la conformit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riel, d&apos;une prestation au regard des normes internes/externes </li> <li>&#xC9;valuer la satisfaction des patients / des clients / des personnels </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Accueil t&#xE9;l&#xE9;phonique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Animation d&apos;&#xE9;quipe </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;accueil et &#xE0; l&apos;orientation des personnes </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Techniciens du r&#xE9;seau t&#xE9;l&#xE9;phonique pour la maintenance des mat&#xE9;rielsrnServices utilisateurs et usagers pour l&apos;information sur l&apos;organisation et le fonctionnement du standard<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitaliers<br>Assitant m&#xE9;dico-administratif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chargé(e) de la trésorerie et de la dette', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser les flux de d&#xE9;penses et de recettes (encaissement-d&#xE9;caissement), optimiser les d&#xE9;lais de paiement et rechercher des sources de financement en relation avec les &#xE9;tablissements bancaires.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable de la tr&#xE9;sorerie / dette<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le du respect des d&#xE9;lais, des dates limites (produits, dossiers, interventions.) </li> <li>Contr&#xF4;le et suivi du bon d&#xE9;roulement du (des) process sp&#xE9;cifique(s) au domaine d&apos;activit&#xE9; </li> <li>D&#xE9;finition et mise en place de mesures correctives dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Gestion de la tr&#xE9;sorerie et de la dette </li> <li>Gestion des outils bancaires (lignes de tr&#xE9;sorerie et emprunts) et gestion dynamique de la dette (mise en concurrence, d&#xE9;pouillement des offres et mise &#xE0; l&apos;arbitrage) </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Suivi de la liquidation des titres de recettes et des mandats </li> <li>Tenue &#xE0; jour des donn&#xE9;es / des fichiers relatifs au domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Analyser un march&#xE9;, fournisseurs/clients, relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Calculer des valeurs relatives aux activit&#xE9;s de son domaine </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>N&#xE9;gocier des prestations, des contrats, des accords avec des interlocuteurs internes/externes </li> <li>Optimiser la tr&#xE9;sorerie </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Analyse financi&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion de la tr&#xE9;sorerie et de la dette </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions gestionnaires de comptes, tr&#xE9;sorier, pour la collecte d&apos;informations financi&#xE8;res<br>Banques pour la n&#xE9;gociation d&apos;emprunts<br>D&#xE9;partement d&apos;information m&#xE9;dicale et bureau des admissions pour la connaissance du niveau pr&#xE9;visionnel des recettes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me en comptabilit&#xE9;, en analyse financi&#xE8;re<br>Licence ou master 2<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitaliers,<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable budg&#xE9;taire et financier<br>Contr&#xF4;leur de gestion<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Bibliothécaire', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Constituer et enrichir un fonds documentaire. Promouvoir la lecture et les pratiques culturelles aupr&#xE8;s des patients et du personnel hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>M&#xE9;diath&#xE9;caire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil des enfants du personnel </li> <li>Accueil, conseil et orientation des lecteurs </li> <li>Cr&#xE9;ation d&apos;outils de pilotage et d&apos;&#xE9;valuation et exploitation des r&#xE9;sultats </li> <li>Cr&#xE9;ation de partenariats avec les institutions locales </li> <li>Elaboration d&apos;une politique culturelle autour du livre et de la lecture </li> <li>Elaboration de la politique d&apos;acquisition </li> <li>&#xC9;laboration et suivi de budget(s), relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Encadrement des aides-biblioth&#xE9;caires, stagiaires </li> <li>Gestion du circuit du document <br><em>Pr&#xE9;paration et suivi des commandes, traitement des documents, pr&#xEA;ts et retours, r&#xE9;colement</em> </li> <li>Pr&#xEA;t de documents dans les services hospitaliers aupr&#xE8;s des patients </li> <li>Veille et utilisation des outils du web </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Acqu&#xE9;rir les collections </li> <li>Communiquer sur les ressources, les mettre en valeur et les promouvoir </li> <li>D&#xE9;velopper des m&#xE9;diations culturelles en direction des publics </li> <li>&#xC9;valuer la qualit&#xE9; des prestations et s&apos;inscrire dans une d&#xE9;marche qualit&#xE9; </li> <li>Evaluer les besoins des patients et personnels hospitaliers en mati&#xE8;re d&apos;information et de culture </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Culture g&#xE9;n&#xE9;rale portant sur le champ et les &#xE9;volutions des sciences, des arts et de la litt&#xE9;rature </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion des documents num&#xE9;riques et audiovisuels </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des syst&#xE8;mes d&apos;information des biblioth&#xE9;ques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ma&#xEE;trise des thesaurii de lecture publique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Ma&#xEE;trise du circuit et des proc&#xE9;dures de traitement des documents </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodologie de l&apos;&#xE9;valuation sp&#xE9;cifique aux biblioth&#xE8;ques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Relations directes avec les usagers<br>Echanges r&#xE9;guliers avec les biblioth&#xE9;caires<br>Relations r&#xE9;guli&#xE8;res avec la direction de l&apos;&#xE9;tablissement<br>Contacts nombreux avec les services de communication, financier, informatique, technique.<br>Relations r&#xE9;guli&#xE8;res avec les acteurs locaux et les professionnels de l&apos;action artistique et culturelle.<br>Contacts avec les fournisseurs et le milieu &#xE9;ditorial.<br>Contacts avec les r&#xE9;seaux documentaires.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>dipl&#xF4;me de niveau III (DUT), II (licence professionnelle) &#xE0; niveau I (Master) dans les m&#xE9;tiers de la biblioth&#xE8;que, documentation, gestion du document<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitaliers<br>Technicien sup&#xE9;rieur hospitalier<br>Contractuel de cat&#xE9;gorie B<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Bio-hygiéniste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Assurer une veille sur la qualit&#xE9; de l&apos;environnement (surface, eau, air, dispositifs m&#xE9;dicaux) et contr&#xF4;ler l&apos;application des proc&#xE9;dures en hygi&#xE8;ne environnementale et hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Technicien(ne) bio-hygi&#xE9;niste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Si possible exercice en sant&#xE9; publique, st&#xE9;rilisation ou pharmacie &#xE0; usage int&#xE9;rieur ou pour ls techniciens de laboratoire m&#xE9;dical en microbiologie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement du d&#xE9;veloppement des innovations techniques dans le cadre de l&apos;am&#xE9;lioration de l&apos;hygi&#xE8;ne de l&apos;environnement </li> <li>Conseil, suivi et contr&#xF4;le en mati&#xE8;re de pr&#xE9;servation de l&apos;hygi&#xE8;ne environnementale (dans le cadre de travaux, d&apos;&#xE9;laboration de plans...) dans le respect des normes, proc&#xE9;dures, protocoles </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards relatifs &#xE0; l&apos;hygi&#xE8;ne de l&apos;environnement </li> <li>Coordination ou r&#xE9;alisation des pr&#xE9;l&#xE8;vements d&apos;environnement et suivi de l&apos;&#xE9;cologie microbienne </li> <li>&#xC9;laboration et mise en place de suivi des plans et actions de pr&#xE9;vention </li> <li>&#xC9;laboration et r&#xE9;daction de rapports d&apos;activit&#xE9; </li> <li>Etablissement / actualisation et mise en oeuvre de proc&#xE9;dures, protocoles, consignes relatives &#xE0; l&apos;hygi&#xE8;ne de l&apos;environnement </li> <li>Formation et sensibilisation des professionnels et usagers aux techniques et proc&#xE9;dures relatives &#xE0; l&apos;hygi&#xE8;ne de l&apos;environnement, et leur application </li> <li>Intervention et traitement d&apos;urgence suite &#xE0; alerte ou / et situation &#xE0; risques dans le domaine hygi&#xE8;ne-s&#xE9;curit&#xE9;-sant&#xE9;-environnement (HSE) </li> <li>Organisation et r&#xE9;alisation d&apos;audits en hygi&#xE8;ne relatifs &#xE0; l&apos;environnement (surface, eau, air, dispositifs m&#xE9;dicaux) </li> <li>Recueil des donn&#xE9;es et suivi des indicateurs nationaux et institutionnels en relation avec la pr&#xE9;vention des infections associ&#xE9;es aux soins </li> <li>Veille et suivi des &#xE9;v&#xE8;nements ind&#xE9;sirables et/ou des infections associ&#xE9;es aux soins </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Evaluer la conformit&#xE9; de la mise en oeuvre d&apos;une proc&#xE9;dure/norme/r&#xE8;gle, consigne </li> <li>Former et conseiller les professionnels et les usagers </li> <li>Identifier, analyser, &#xE9;valuer les risques li&#xE9;s &#xE0; l&apos;hygi&#xE8;ne environnemental et proposer des actions correctives </li> <li>S&apos;exprimer aupr&#xE8;s d&apos;une ou plusieurs personnes </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser une proc&#xE9;dure, un langage, un protocole, une r&#xE9;glementation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Bureautique (Excel et Word avanc&#xE9;s et bases de donn&#xE9;es) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;pid&#xE9;miologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne - S&#xE9;curit&#xE9; - Environnement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Mat&#xE9;riel d&apos;hygi&#xE8;ne et mat&#xE9;riels n&#xE9;cessitant une d&#xE9;sinfection de haut niveau ou niveau interm&#xE9;diaire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Microbiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Statistiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>L&apos;ensemble des professionnels m&#xE9;dicaux, param&#xE9;dicaux et en lien avec l&apos;hygi&#xE8;ne, pour am&#xE9;liorer les pratiques, adapter les proc&#xE9;dures, s&apos;assurer qu&apos;elles sont mises en &#x153;uvre et les &#xE9;valuer.<br>Le conseiller en pr&#xE9;vention des risques professionnel et le service de sant&#xE9; au travail dans le cadre de la pr&#xE9;vention des risques professionnels (risque biologique et chimique).<br>Le(s) hygi&#xE9;nistes()<br>Les instituts de formation pour sensibiliser et former les professionnels &#xE0; l&apos;hygi&#xE8;ne hospitali&#xE8;re.<br>Les laboratoires de microbiologie pour distinguer les types de pr&#xE9;l&#xE8;vements et savoir les pratiquer.<br>Les directions de soins et les directions fonctionnelles (services &#xE9;conomiques...) pour guider le choix des achats de produits d&apos;entretien, de mat&#xE9;riels.<br>La sous-commission de la CME charg&#xE9;e de la lutte contre les infections nosocomiales pour d&#xE9;finir les orientations relatives &#xE0; la pr&#xE9;vention des infections associ&#xE9;es aux soins.<br>Le CHSCT pour valider les orientations relatives &#xE0; la pr&#xE9;vention des infections associ&#xE9;es aux soins.<br>La commission de soins, de r&#xE9;&#xE9;ducation et m&#xE9;dico-technique pour d&#xE9;finir les orientations relatives &#xE0; la pr&#xE9;vention des infections associ&#xE9;es aux soins.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me param&#xE9;dical de niveau III et DU hygi&#xE8;ne hospitali&#xE8;re compl&#xE9;t&#xE9; par une formation sp&#xE9;cialis&#xE9;e dans les pr&#xE9;l&#xE8;vements environnementaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien de laboratoire m&#xE9;dical<br>Corps des infirmiers et infirmiers sp&#xE9;cialis&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Animateur qualit&#xE9;<br>Coordinateur qualit&#xE9; / Gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chargé(e) du développement des ressources humaines', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Traiter, en tout ou partie, des aspects individuels et collectifs de la gestion de l&apos;emploi : recrutement, insertion et orientation, mobilit&#xE9;, &#xE9;volution professionnelle, d&#xE9;parts et planification de l&apos;emploi / GPMC<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable du d&#xE9;veloppement des ressources humaines<br>Conseiller(&#xE8;re) en ressources humaines<br>Charg&#xE9;(e) de l&apos;emploi et des comp&#xE9;tences<br>Responsable GPMC / GPEC<br>Sp&#xE9;cialiste en d&#xE9;veloppement professionnel<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En GPMC (gestion pr&#xE9;visionnelle des m&#xE9;tiers et comp&#xE9;tences)<br>En recrutement, mobilit&#xE9; et parcours professionnel<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception, formalisation et mise en oeuvre d&apos;outils sp&#xE9;cifiques &#xE0; l&apos;&#xE9;valuation des comp&#xE9;tences, &#xE0; l&apos;orientation individuelle et au suivi d&apos;int&#xE9;gration des agents. </li> <li>Conseil sur la d&#xE9;finition de(s) projet(s) professionnel(s) / individuel(s) et bilan des r&#xE9;alisations </li> <li>Description des postes / fonctions / m&#xE9;tiers, et des profils correspondants </li> <li>&#xC9;laboration, mise en place et suivi du plan emploi </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Organisation et suivi de l&apos;accueil (agents, stagiaires, nouveaux recrut&#xE9;s.) </li> <li>Pr&#xE9;paration et animation de r&#xE9;unions et de divers groupes de travail ou de projets institutionnels </li> <li>R&#xE9;alisation et exploitation d&apos;enqu&#xEA;tes, de donn&#xE9;es et d&apos;entretiens, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Recrutement de personnes : agents, candidats, int&#xE9;rimaires </li> <li>Suivi des effectifs (r&#xE9;mun&#xE9;r&#xE9;s, cibles) et des mouvements de personnel au sein de l&apos;&#xE9;tablissement. </li> <li>Tenue &#xE0; jour des donn&#xE9;es / des fichiers relatifs au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Concevoir et b&#xE2;tir un plan emploi / ressources humaines </li> <li>Concevoir, conduire, adapter le programme de d&#xE9;veloppement professionnel d&apos;une cat&#xE9;gorie professionnelle ou d&apos;une personne </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Conduire des entretiens professionnels (recrutement, orientation...) </li> <li>G&#xE9;rer un budget ou une norme quantitative et optimiser la gestion financi&#xE8;re propre &#xE0; son domaine d&apos;activit&#xE9; et rapporter &#xE0; sa hi&#xE9;rarchie tout risque d&apos;&#xE9;cart. </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> <li>Travailler en partenariat avec les responsables de l&apos;encadrement des unit&#xE9;s concern&#xE9;es </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Audit social </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Descriptions des postes/m&#xE9;tiers </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion RH </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes et outils de recrutement, mobilit&#xE9;, orientation professionnelle </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Statut des personnels non m&#xE9;dicaux de la fonction publique hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions fonctionnelles et encadrement pour identification des &#xE9;volutions sur les emplois.<br>Organisations syndicales pour &#xE9;changes d&apos;informations et dialogues<br>Partenaires ext&#xE9;rieurs :P&#xF4;le emploi, APEC, agences d&apos;int&#xE9;rim et autres prestataires pour la recherche de candidatures, OPCA, organismes de formation, cabinet conseils,<br>&#xC9;ventuellement &#xE9;tablissements scolaires, pour la recherche de membres de jurys.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formations de niveau III (DUT gestion et administration d&apos;entreprise option GRH)<br>Formation de niveau II (license professionnelle) en droit, &#xE9;conomie, gestion, GRH, sciences humaines,<br>Formation de niveau I (master) &#xE9;cole sup&#xE9;rieure de commerce ou de management.<br>Dipl&#xF4;me de cadre de sant&#xE9; compl&#xE9;t&#xE9; par une sp&#xE9;cialisation universitaire dans les domaines cit&#xE9;s en supra.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re,<br>Adjoint des cadres hospitaliers,<br>Cadre de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Charg&#xE9; de la formation continue<br>Responsable des ressources humaines<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) juridique', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Effectuer et /ou mettre en oeuvre des analyses, des conseils et proc&#xE9;dures juridiques, afin de s&#xE9;curiser les activit&#xE9;s et d&#xE9;fendre les int&#xE9;r&#xEA;ts de l&apos;&#xE9;tablissement.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable contentieux<br>Juriste (droit hospitalier)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En assurances (contentieux patients)<br>En march&#xE9;s publics<br>En ressources humaines<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Analyse juridique des recours et traitement des r&#xE9;clamations (usagers et tiers) </li> <li>Assistance et / ou repr&#xE9;sentation des personnes, relative au domaine d&apos;activit&#xE9; </li> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; et / ou de validit&#xE9; des documents, relatifs &#xE0; son domaine </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>R&#xE9;daction d&apos;actes, de documents ou notes juridiques / r&#xE9;glementaires </li> <li>Traitement des courriers, dossiers, documents dans son domaine (enregistrement, tri, traitement, diffusion, archivage) </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;laborer et argumenter des sc&#xE9;narii/ des solutions acceptables &#xE0; des probl&#xE8;mes relevant de son domaine de comp&#xE9;tence </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Rechercher, s&#xE9;lectionner, exploiter et capitaliser les informations li&#xE9;es &#xE0; la veille dans son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;diger des actes et / ou documents juridiques et / ou contractuels </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Contentieux administratif </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit administratif </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la sant&#xE9; (relatif au domaine d&apos;activit&#xE9;) R&#xE8;gles de bonnes pratiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit des march&#xE9;s publics </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit hospitalier </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion administrative </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions fonctionnelles, encadrement de sant&#xE9; et m&#xE9;decins, instances de l&apos;&#xE9;tablissement et usagers du syst&#xE8;me de sant&#xE9; pour leur apporter les expertises et conseils dont ils ont besoin<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+5 dans le domaine (droit de la sant&#xE9;, droit administratif...)<br>Ou<br>Dipl&#xF4;me de niveau (Master) dans le domaine du droit de la sant&#xE9; ou du droit public<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Contractuel de cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Cadre soignant de pôle', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et coordonner les soins / les activit&#xE9;s du p&#xF4;le<br>Contribuer &#xE0; la gestion m&#xE9;dico-&#xE9;conomique au sein du p&#xF4;le<br>D&#xE9;cliner le projet de soin institutionnel au sein du p&#xF4;le en d&#xE9;veloppant la d&#xE9;marche qualit&#xE9; en relation avec le chef de p&#xF4;le<br>Collaborer au pilotage strat&#xE9;gique du p&#xF4;le<br>Manager l&apos;&#xE9;quipe d&apos;encadrement de proximit&#xE9;.<br>Mettre en place une politique de communication dans un objectif de coh&#xE9;rence des organisations et de coh&#xE9;sion des &#xE9;quipes.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Cadre de sant&#xE9; de secteur de soins<br>Cadre sup&#xE9;rieur de sant&#xE9;<br>Responsable param&#xE9;dical de p&#xF4;le<br>Cadre responsable param&#xE9;dical de p&#xF4;le<br>Cadre param&#xE9;dical de p&#xF4;le<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de cadre de sant&#xE9; et un exercice de 4 ans en qualit&#xE9; de cadre de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Coordination et suivi de la prise en charge de prestations </li> <li>&#xC9;laboration et mise en place de l&apos;organisation des services, dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et r&#xE9;daction de rapports d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Montage, mise en oeuvre, suivi et gestion de projets sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Planification de la r&#xE9;alisation du projet et/ou &#xE9;tude et/ou produit et des ressources </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Corps m&#xE9;dical pour l&apos;&#xE9;laboration du projet et de la gestion du p&#xF4;le<br>equipe de direction pour la gestion et le suivi des projets<br>services logistiques pour les achats de mat&#xE9;riels<br>fournisseurs pour la veille sur les nouveaux mat&#xE9;riels<br>r&#xE9;seaux pour la gestion des flux d&apos;activit&#xE9;s ou de patients<br>centres de formation pour la planification des stages, ou l&apos;organisation de formations<br>service de formation continue pour le plan de formation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me professionnel param&#xE9;dical permettant l&apos;exercice dans l&apos;une des professions param&#xE9;dicales,<br>Dipl&#xF4;me de cadre de sant&#xE9;<br>Dipl&#xF4;me d&apos;&#xE9;tat de sage-femme pour les p&#xF4;les concern&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des cadres de sant&#xE9; param&#xE9;dicale (cadre sup&#xE9;rieur)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Auxiliaire de puériculture', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Dispenser, en collaboration avec l&apos;infirmier, des soins d&apos;hygi&#xE8;ne, de confort et de pr&#xE9;vention pour pr&#xE9;server et restaurer la continuit&#xE9; de la vie, le bien-&#xEA;tre et l&apos;autonomie de l&apos;enfant.<br>R&#xE9;aliser des activit&#xE9;s d&apos;&#xE9;veil et d&apos;&#xE9;ducation.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement du patient et/ou du r&#xE9;sident et/ou de son entourage (s&#xE9;jour, retour au domicile, placement, etc) </li> <li>Accueil des personnes (patients, usagers, familles, etc.) </li> <li>Accueil, information et formation des nouveaux personnels, des stagiaires et des professionnels de sant&#xE9; </li> <li>Aide l&apos;infirmier &#xE0; la r&#xE9;alisation de soins </li> <li>Assistance technique pour la r&#xE9;alisation des soins, sp&#xE9;cifique au domaine d&apos;activit&#xE9; </li> <li>Entretien de l&apos;environnement imm&#xE9;diat de l&apos;enfant et des mat&#xE9;riels de soins et ludiques </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Identification, recensement des besoins et des attentes des patients, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Observation de l&apos;&#xE9;tat de sant&#xE9; et du d&#xE9;veloppement de l&apos;enfant </li> <li>Observation du bon fonctionnement des appareillages et dispositifs m&#xE9;dicaux </li> <li>Observation et mesure des param&#xE8;tres vitaux </li> <li>Prendre soin de l&apos;enfant dans ses activit&#xE9;s de la vie quotidienne de la naissance &#xE0; l&apos;adolescence </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner l&apos;enfant dans les activit&#xE9;s d&apos;&#xE9;veil et les parents dans leur r&#xF4;le &#xE9;ducatif </li> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Analyser / &#xE9;valuer la situation clinique d&apos;une personne, d&apos;un groupe de personnes, relative &#xE0; son domaine de comp&#xE9;tence </li> <li>Etablir une communication adapt&#xE9;e &#xE0; la personne et &#xE0; son entourage </li> <li>Organiser son travail au sein d&apos;une &#xE9;quipe pluriprofessionnelle </li> <li>R&#xE9;aliser des soins adapt&#xE9;s &#xE0; l&apos;&#xE9;tat clinique de l&apos;enfant </li> <li>Rechercher, traiter et transmettre les informations pour assurer la continuit&#xE9; des soins </li> <li>Utiliser les techniques d&apos;entretien des locaux et du mat&#xE9;riel sp&#xE9;cifiques aux &#xE9;tablissements sanitaires, sociaux et m&#xE9;dico-sociaux </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">D&#xE9;veloppement psychomoteur de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Droit des enfants et de la protection de la petite enfance </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Nutrition infantile </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Pu&#xE9;riculture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;ponse aux besoins de l&apos;enfant, activit&#xE9;s d&apos;&#xE9;veil, accompagnement des parents </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Infirmi&#xE8;re pu&#xE9;ricultrice, p&#xE9;di&#xE2;tre, &#xE9;ducateur de jeunes enfants, pour le suivi de l&apos;enfant, rnSage femme pour le travail en collaboration &#xE0; la maternit&#xE9;,<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;auxiliaire de pu&#xE9;riculture (41 semaines, modulaire)<br>Dipl&#xF4;me accessible par la validation des acquis et de l&apos;exp&#xE9;rience<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des aide-soignant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Aide-soignant<br>Aide m&#xE9;dico-psychologique<br>infirmier en soins g&#xE9;n&#xE9;raux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chargé(e) des relations sociales', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Assurer le d&#xE9;ploiement de la politique sociale en coh&#xE9;rence avec les axes strat&#xE9;giques de la DRH dans le respect de la l&#xE9;gislation sociale. Il met en &#x153;uvre et d&#xE9;veloppe les relations sociales avec les partenaires sociaux et est en charge des instances repr&#xE9;sentatives du personnel et du dialogue social.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Charg&#xE9;(e) du dialogue social,<br>Responsable des relations sociales,<br>Responsable de la gestion sociale,<br>Charg&#xE9;(e) d&apos;affaires sociales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) en organisation', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Analyser, piloter, r&#xE9;aliser des missions d&apos;organisation.<br>Concevoir, pr&#xE9;parer, structurer et accompagner les processus de changement, en anticipant leurs cons&#xE9;quences sur les aspects humains, technologiques, financiers, informatiques, de s&#xE9;curit&#xE9;.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Consultant(e) en organisation<br>Assistant(e) en organisation<br>Organisateur(trice)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>D&#xE9;finition et mise en place de mesures correctives dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et mise en place de l&apos;organisation des services, dans son domaine d&apos;activit&#xE9; </li> <li>Elaboration et mise en &#x153;uvre d&apos;outils et/ou de m&#xE9;thodes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Etude et optimisation des organisations et des processus </li> <li>Identification, analyse et validation des impacts (technologiques, organisationnels, fonctionnels et financiers) li&#xE9;s &#xE0; des nouveaux projets </li> <li>Montage, mise en &#x153;uvre, suivi, communication et gestion de projets sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Organisation et suivi op&#xE9;rationnel des activit&#xE9;s/projets, coordination avec les interlocuteurs internes et externes </li> <li>Planification des activit&#xE9;s / interventions internes/externes, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Analyser et optimiser un / des processus </li> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Concevoir et mettre en oeuvre des changements organisationnels </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Conseiller et orienter les choix d&apos;une personne ou d&apos;un groupe </li> <li>&#xC9;laborer et argumenter des sc&#xE9;narii/ des solutions acceptables &#xE0; des probl&#xE8;mes relevant de son domaine de comp&#xE9;tence </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Organiser une unit&#xE9;, un service, en fonction de diff&#xE9;rents crit&#xE8;res (missions, strat&#xE9;gie, moyens, etc.) </li> <li>Rechercher, s&#xE9;lectionner, exploiter et capitaliser les informations li&#xE9;es &#xE0; la veille dans son domaine d&apos;activit&#xE9; </li> <li>Traduire la strat&#xE9;gie en orientations, en projets, en plans d&apos;actions et en moyens de r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Audit interne </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;valuation de la charge de travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Finances publiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation des processus </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction(s) de l&apos;&#xE9;tablissement pour le conseil, le pilotage de projet.<br>Equipes de terrain pour les audits, la conduite de projet.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau 1 et sp&#xE9;cialisation en organisation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Contractuel de Cat&#xE9;gorie A<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable logistique<br>Coordinateur qualit&#xE9;/gestion des risques<br>Responsable des travaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chef(fe) de projet(s) de recherche clinique', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>G&#xE9;rer un portefeuille de projets en recherche clinique, dont l&apos;&#xE9;tablissement est promoteur ou gestionnaire, sur les aspects r&#xE9;glementaires, financiers, logistiques, administratifs, organisationnels et humains.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable d&apos;etudes cliniques<br>Gestionnaire de projets de recherche clinique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le du respect des d&#xE9;lais, des dates limites (produits, dossiers, interventions.) </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement de cahier des charges, relatif &#xE0; la probl&#xE9;matique client </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Recherche et gestion de moyens financiers, humains, logistiques pour la mise en oeuvre des projets </li> <li>Traitement et analyse des r&#xE9;ponses aux appels d&apos;offres </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Concevoir et r&#xE9;diger une documentation technique, sp&#xE9;cifique &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir, allouer et optimiser les ressources au regard des priorit&#xE9;s, des contraintes et variations externes / internes </li> <li>&#xC9;valuer une charge de travail </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation relative &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>D&#xE9;l&#xE9;gu&#xE9;s des unit&#xE9;s de recherche clinique, centre d&apos;investigation clinique, coordinateurs d&apos;etudes cliniques, promoteurs, p&#xF4;les hospitaliers (cliniciens, pharmaciens,...), m&#xE9;decins investigateurs pour le pilotage du projet<br>Instances administratives et r&#xE9;glementaires (comit&#xE9; de protection des personnes, agence fran&#xE7;aise de s&#xE9;curit&#xE9; sanitaire des produits de sant&#xE9;,...) pour la validation, le suivi et le contr&#xF4;le des &#xE9;tudes cliniques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau I (BAC+5 &#xE0; BAC+8) cursus scientifique, m&#xE9;dical ou d&apos;ing&#xE9;nierie<br>BAC + 5 master en recherche clinique, cursus d&apos;ing&#xE9;nierie<br>BAC +8 doctorat en recherche clinique, cursus scientifique, m&#xE9;dical, pharmacie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br>Cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(e) des investissements et de la maintenance\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir et piloter la mise en &#x153;uvre de la politique de travaux et de maintenance dans le cadre de la strat&#xE9;gie de l&apos;&#xE9;tablissement.&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Directeur(e) du patrimoine<br>Directeur des services techniques<br>Directeur(e) des travaux<br>Directeur(e) des investissements et des travaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Directeur(e) biom&#xE9;dical<br>Directeur(e) de la s&#xE9;curit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des activit&#xE9;s, dans son domaine </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Gestion de biens, de patrimoine immobilier </li> <li>Montage, mise en oeuvre, suivi et gestion de projets sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir un plan / programme d&apos;investissement </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">B&#xE2;timents et installations </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Poly-techniques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur, pour les prises de d&#xE9;cision<br>M&#xE9;decins et cadres de soins pour la connaissance des besoins<br>Responsable des services biom&#xE9;dicaux, infomatiques pour l&apos;int&#xE9;gation des &#xE9;quipements dans les projets d&apos;am&#xE9;nagement ou de construction<br>Architecte, bureau d&apos;&#xE9;tudes, entreprises, programmiste pour l&apos;&#xE9;laboration des projets<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC +5<br>BAC +6<br>Dipl&#xF4;me d&apos;ing&#xE9;nieur des &#xE9;coles g&#xE9;n&#xE9;ralistes<br>Sp&#xE9;cialit&#xE9; dans les domaines techniques<br>Formation compl&#xE9;mentaire (continu) en management<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant (e) archives', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre en oeuvre la politique d&apos;archivage, en animant une &#xE9;quipe de professionnels et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissance des techniques archivistiques et de l&apos;informatique sp&#xE9;cialis&#xE9;e<br>Connaissances des techniques de management<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Formation professionnelle de personnes (agents, stagiaires, etc.) </li> <li>Organisation de la collecte, de la conservation et de la communication des fonds d&apos;archives </li> <li>Organisation du circuit de l&apos;archivage, des conditions de conservation et de communication des archives </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Identifier les fonds d&apos;archives &#xE0; collecter </li> <li>Organiser et classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Classement et archivistique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion des archives </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation des archives </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeurs ou responsables des affaires g&#xE9;n&#xE9;rales, de l&apos;accueil des usagers, de la qualit&#xE9;, de la logistique, m&#xE9;decins DIM, pr&#xE9;sident de CCM.<br>Directions et responsables des services de l&apos;&#xE9;tablissement.<br>Prestataires externes (externalisation, &#xE9;limination, d&#xE9;m&#xE9;nagement.)<br>Administration des archives publiques (services d&#xE9;partementaux d&apos;archives)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Licence professionnelle archives,<br>Bac+2/3 fili&#xE8;re archives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitalier<br>Aide M&#xE9;dico-Administratif<br>Technicien Sup&#xE9;rieur Hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable archives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) hôtelier(e)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Assister, former et conseiller sous l&apos;angle h&#xF4;telier les personnels des services logistiques et &#xE9;valuer la satisfaction des clients.<br>Contr&#xF4;ler et assurer le suivi de prestations externalis&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Gouvernant(e)<br>Intendant(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>CAP BEP<br>Facilit&#xE9;s relationnelles<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil p&#xE9;dagogique aupr&#xE8;s des personnels / utilisateurs / usagers dans son domaine </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; des services / prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le des connaissances et des comp&#xE9;tences des personnes form&#xE9;es </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de prestations sous-trait&#xE9;es (linge, nettoyage, restauration...) </li> <li>Coordination et suivi de la prise en charge de prestations </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>&#xC9;valuer le niveau de comp&#xE9;tence n&#xE9;cessaire &#xE0; des interventions techniques, relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Promouvoir, mettre en valeur un produit ou un service </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser des mat&#xE9;riels, des outils de diagnostic, de travail ou/et de contr&#xF4;le sp&#xE9;cifiques &#xE0; son m&#xE9;tier </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">H&#xF4;tellerie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;h&#xF4;tellerie/ restauration </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrement services de soins pour un suivi et une &#xE9;valuation r&#xE9;guli&#xE8;re des prestations propos&#xE9;es<br>agents h&#xF4;teliers, as pour des actions de formation relatives aux r&#xE8;gles d&apos;hygi&#xE8;ne alimentaire, bionettoyage et tenue des stocks<br>responsable h&#xF4;tellerie restauration pour information sur la satisfaction des clients concernant les prestations, au plan qualitatif et quantitatif, propositions d&apos;axes d&apos;am&#xE9;lioration<br>di&#xE9;t&#xE9;ticiens pour l&apos;adaptation des menus en fonction de l&apos;attente des patients<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formation m&#xE9;tiers de l&apos;h&#xF4;tellerie<br>bep serveur<br>cafas<br>bac pro h&#xF4;telier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corsp des Aide-soignant et des agents des services hospitaliers qualifi&#xE9;s<br>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Ma&#xEE;tre / Ma&#xEE;tresse de maison<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Dessinateur (trice) projeteur (trice) tous corps d''état (TCE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, r&#xE9;aliser les pi&#xE8;ces graphiques d&apos;un projet architectural et technique au regard des exigences et des contraintes du programme.<br>G&#xE9;rer les &#xE9;l&#xE9;ments graphiques du patrimoine de l&apos;&#xE9;tablissement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Dessinateur(trice) tous corps d&apos;&#xE9;tat (TCE)<br>Projeteur(euse) tous corps d&apos;&#xE9;tat (TCE)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>D&#xE9;coration<br>Architecture<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la conformit&#xE9; et / ou de validit&#xE9; des documents, relatifs &#xE0; son domaine </li> <li>&#xC9;laboration et mise &#xE0; jour de sch&#xE9;mas et/ou plans techniques et/ou plans de b&#xE2;timent, relevant de son domaine d&apos;activit&#xE9; </li> <li>Montage et instruction de dossiers relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation de sch&#xE9;mas et plans techniques, relevant de son domaine d&apos;activit&#xE9; </li> <li>Retouches d&apos;images </li> <li>Tirage, pliage de documents, de plans </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir et r&#xE9;aliser une charte graphique </li> <li>Imaginer une composition visuelle </li> <li>Lire et interpr&#xE9;ter des plans techniques, des croquis relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Mesurer et retranscrire les informations sur un plan b&#xE2;timent </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">B&#xE2;timents et installations </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Ergonomie </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion documentaire </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Logiciels DAO/CAO </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\"></td> </tr> </tbody></table></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable des services techniques<br>responsable de maintenance<br>conducteur d&apos;op&#xE9;rations<br>encadrement de maintenance<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC +2 dans le domaine<br>BAC professionnel<br>Brevet professionnel<br>Dessinateur de b&#xE2;timent, BTS, DUT<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des dessinateurs<br>Corps des techniciens sup&#xE9;rieurs hospitaliers et des techniciens hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Conducteur de travaux tous corps d&apos;&#xE9;tat (TCE)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant (e) logistique', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, piloter, coordonner et contr&#xF4;ler au quotidien la gestion des flux et stocks physiques de mati&#xE8;res, marchandises et produits et &#xE9;ventuellement transport non sanitaire des personnes en encadrant une ou plusieurs &#xE9;quipes d&apos;exploitation logistique, dans le respect des proc&#xE9;dures QHSE<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Produits de plateaux techniques<br>Encadrant (e) gestion des d&#xE9;chets<br>Encadrant (e) magasin (alimentaire, pharmacie, informatique, technique...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Bac, bac + 2 ou promotion interne<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Organisation et description des processus sp&#xE9;cifiques &#xE0; son domaine, traitement des dysfonctionnements </li> <li>Planification de l&apos;activit&#xE9; et de l&apos;ensemble des moyens (humains, financiers, techniques) &#xE0; mettre en &#x153;uvre pour la continuit&#xE9; de service </li> <li>Planification, r&#xE9;organisation des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits et fournisseurs </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Services utilisateurs pour adapter les livraisons (volumes, fr&#xE9;quences.)<br>Fournisseurs pour d&#xE9;finir les r&#xE8;gles de livraison<br>Acheteurs et gestionnaire administratif des achats pour la mise &#xE0; jour des produits g&#xE9;r&#xE9;s et la gestion des litiges<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Exp&#xE9;rience en gestion des stocks et achats<br>etude en logistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien Hospitalier<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Acheteur<br>Responsable logistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chargé(e) de la formation continue', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Apr&#xE8;s rep&#xE9;rage et analyse des besoins individuels et collectifs en formation, mettre en oeuvre et &#xE9;valuer les r&#xE9;sultats, l&apos;efficacit&#xE9; de la politique de formation &#xE9;labor&#xE9;e en coh&#xE9;rence avec les orientations du projet d&apos;&#xE9;tablissement et la politique des ressources humaines.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable du service de formation<br>Responsable de la formation du personnel<br>Responsable de la formation professionnelle<br>Responsable de la formation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En ing&#xE9;nierie p&#xE9;dagogique<br>En animation de stages de formation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Achat de prestations: actions de formation, programmes de DPC </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Elaboration et mise en oeuvre d&apos;outils (tableaux de bord de la formation, grilles d&apos;analyse, d&apos;&#xE9;valuation...)sp&#xE9;cifiques &#xE0; la formation </li> <li>&#xC9;laboration et mise en place des programmes de formation et de DPC, en relation avec l&apos;&#xE9;quipe p&#xE9;dagogique </li> <li>Elaboration, gestion administrative et suivi des budgets de la formation </li> <li>Elaboration, mise en oeuvre et suivi des plans de formation et de DPC </li> <li>Information, conseil et orientation aupr&#xE8;s des agents en mati&#xE8;re de formation, de parcours professionnel et de d&#xE9;veloppement professionnel continu </li> <li>Montage, mise en oeuvre, suivi et gestion de projets en mati&#xE8;re de formation </li> <li>Organisation et animation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es, groupes de travail et groupes projet </li> <li>Veille relative &#xE0; la formation et &#xE0; l&apos;&#xE9;valuation des pratiques </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Concevoir et organiser des dispositifs et actions de formation, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, conduire, adapter le programme de d&#xE9;veloppement professionnel d&apos;une cat&#xE9;gorie professionnelle ou d&apos;une personne </li> <li>Concevoir, piloter et &#xE9;valuer un plan de formation et de d&#xE9;veloppement professionnel continu </li> <li>Elaborer un cahier des clauses techniques particuli&#xE8;res (CCTP) </li> <li>&#xC9;valuer une prestation, un projet, une solution, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>G&#xE9;rer un budget ou une norme quantitative et optimiser la gestion financi&#xE8;re propre &#xE0; son domaine d&apos;activit&#xE9; et rapporter &#xE0; sa hi&#xE9;rarchie tout risque d&apos;&#xE9;cart. </li> <li>N&#xE9;gocier des prestations, des contrats, des accords avec des interlocuteurs internes/externes </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des march&#xE9;s publics </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ing&#xE9;nierie de la formation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion de la formation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">March&#xE9; de la formation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;f&#xE9;rentiels qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions fonctionnelles et d&apos;&#xE9;tablissement, responsables de service et de p&#xF4;le, encadrement, personnel pour recueil de besoins<br>Organismes de formation, formateurs externes et internes, pour la n&#xE9;gociation des contenus et des co&#xFB;ts, organisation et &#xE9;valuation des actions, gestion financi&#xE8;re<br>Organisme Paritaire Collecteur Agr&#xE9;&#xE9; -OPCA- (ANFH...), Instituts de formation, universit&#xE9;s et &#xE9;coles, pour des &#xE9;changes d&apos;informations<br>Instances de l&apos;&#xE9;tablissement CTE, Commission de formation, CME<br>Organisations syndicales pour des &#xE9;changes et n&#xE9;gociation du plan de formation<br>Organisme Gestionnaire du D&#xE9;veloppement Professionnel Continu -OGDPC- pour le suivi du d&#xE9;veloppement professionnel continu<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de cadre de sant&#xE9; ou dipl&#xF4;me de niveau II (license professionnelle) g&#xE9;n&#xE9;raliste ou technique, compl&#xE9;t&#xE9; par un dipl&#xF4;me de niveau I de sp&#xE9;cialisation en sciences humaines (Master en psychologie du travail, en gestion des ressources humaines, administration, sciences de l&apos;&#xE9;ducation...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re,<br>Adjoint des cadres hospitaliers,<br>Cadre de sant&#xE9;,<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Charg&#xE9; du d&#xE9;veloppement des ressources humaines<br>Responsable des ressources humaines<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) conjugal(e)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Recevoir des personnes ou des couples pr&#xE9;sentant une demande d&apos;aide psychologique, li&#xE9;e &#xE0; une difficult&#xE9; personnelle, conjugale ou familiale, afin d&apos;assurer l&apos;&#xE9;coute de chacun, de favoriser l&apos;&#xE9;change en offrant un cadre spatio-temporel structurant, d&apos;analyser leur demande, d&apos; aider &#xE0; une &#xE9;laboration psychique et de les orienter.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>M&#xE9;diateur(trice) familial(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc.), dans son domaine </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>M&#xE9;diation des relations avec la famille, le repr&#xE9;sentant l&#xE9;gal de la personne et les diff&#xE9;rents partenaires ext&#xE9;rieurs </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Tenue d&apos;entretiens avec des personnes (patients, familles,...) </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Conduire un entretien d&apos;aide </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit civil </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Intervention sociale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>L&apos;activit&#xE9; s&apos;effectue principalement sous la forme d&apos;une relation bilat&#xE9;rale mais s&apos;inscrit de plus en plus dans un travail d&apos;&#xE9;quipe &#xE0; l&apos;int&#xE9;rieur comme &#xE0; l&apos;ext&#xE9;rieur de la structure (travail partenarial ou interinstitutionnel). .<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Accessible g&#xE9;n&#xE9;ralement aux titulaires d&apos;un dipl&#xF4;me d&apos;etat de travailleur social (de d&apos;assistant de service social, de d&apos;&#xE9;ducateur, de de conseiller en &#xE9;conomie sociale et familiale...).<br>N&#xE9;cessit&#xE9; d&apos;une attestation d&apos;aptitude au conseil conjugal et familial d&#xE9;livr&#xE9;e &#xE0; l&apos;issue d&apos;une formation de 400 heures. dispens&#xE9;e par des organismes agr&#xE9;&#xE9;s. La formation s&apos;&#xE9;chelonne sur environ deux ans. Elle est ouverte aux candidats poss&#xE9;dant un dipl&#xF4;me d&apos;etat m&#xE9;dical, param&#xE9;dical, social, &#xE9;ducatif, psychologique, juridique ou d&apos;animation ainsi qu&apos;aux titulaires de l&apos;unit&#xE9; de formation pour une &#xE9;ducation &#xE0; la vie (160 heures) justifiant de 200 heures d&apos;activit&#xE9; d&apos;accueil ou d&apos;information dans le domaine conjugal.<br>Exercer le m&#xE9;tier de conseiller conjugal et familial, le postulant doit obligatoirement poss&#xE9;der l&apos;attestation de qualification au conseil conjugal et familial.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Assistant socio-&#xE9;ducatif<br>Conseiller en &#xE9;conomie sociale et familiale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Psychologue<br>Assitant social<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chargé(e) des relations avec les usagers', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;velopper et mettre en oeuvre la politique relative aux droits des usagers et des associations de patients, en coordination avec les diff&#xE9;rents acteurs internes / externes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Charg&#xE9;(e) des relations client&#xE8;le<br>Coordinateur(trice) des relations avec les usagers / associations<br>Technicien(ne) qualit&#xE9; charg&#xE9; des relations usagers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Charg&#xE9;(e) des relations avec les usagers de la psychiatrie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Exp&#xE9;rience approfondie dans un &#xE9;tablissement de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil des personnes (patients, usagers, familles, etc.) </li> <li>&#xC9;laboration et mise en service d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Identification, recensement des besoins et des attentes des patients, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Information et conseil &#xE0; des tiers (agents, patients, familles, etc.) dans son domaine d&apos;activit&#xE9; </li> <li>Mise en place d&apos;action de pr&#xE9;vention des conflits et de traitement des r&#xE9;clamations </li> <li>Organisation et animation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es, groupes de travail et groupes projet </li> <li>Organisation et mise en place de partenariats ext&#xE9;rieurs </li> <li>Promotion des r&#xE9;alisations / des projets sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation et exploitation d&apos;enqu&#xEA;tes, de donn&#xE9;es et d&apos;entretiens, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction de documents techniques, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et orienter des personnes, des groupes, des publics </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Concevoir et exploiter des enqu&#xEA;tes </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er des supports de communication et p&#xE9;dagogiques adapt&#xE9;s aux publics </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>&#xC9;laborer et argumenter des sc&#xE9;narii/ des solutions acceptables &#xE0; des probl&#xE8;mes relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la satisfaction des patients / des clients / des personnels </li> <li>Former et conseiller guider les choix d&apos;une personne ou d&apos;une &#xE9;quipe. </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Traiter et r&#xE9;soudre des situations agressives ou conflictuelles </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication de crise </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la sant&#xE9; dans son domaine d&apos;activit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques / assurance qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes des sciences sociales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodologie d&apos;analyse de situation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;seaux sanitaires, sociaux et m&#xE9;dico-sociaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Service social de l&apos;&#xE9;tablissement pour d&#xE9;finir l&apos;aide la plus efficace &#xE0; apporter au patient<br>Responsable qualit&#xE9; gestionnaire des risques dans le cadre de l&apos;&#xE9;coute client<br>Responsable juridique en vue de pr&#xE9;venir les contentieux<br>Associations partenaires, Commission des relations avec les usagers et de la qualit&#xE9; de la prise en charge, m&#xE9;diateurs membres de cette commission, pour traiter les r&#xE9;clamations, trouver des solutions et tirer des enseignements pour les prises en charge<br>Sous-commission qualit&#xE9; s&#xE9;curit&#xE9; des soins de la cme<br>Services de soin<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;Administration Hospitali&#xE8;re<br>Cadre de sant&#xE9;<br>Adjoint des cadres hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Animateur qualit&#xE9; / gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Cuisinier(ère)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser les pr&#xE9;parations alimentaires, le conditionnement, le stockage, la distribution en respectant la r&#xE8;glementation en hygi&#xE8;ne alimentaire, les modes op&#xE9;ratoires et les quantit&#xE9;s command&#xE9;es.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Agent(e) de production culinaire / alimentaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Agent(e) biberonnerie<br>Agent(e) de di&#xE9;t&#xE9;tique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>D&#xE9;tention du dipl&#xF4;me et exp&#xE9;rience en restauration collective ou en cuisine traditionnelle<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Comptage de la production </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; des produits relatifs &#xE0; son domaine </li> <li>Contr&#xF4;le de la qualit&#xE9; des produits </li> <li>Contr&#xF4;le du respect des d&#xE9;lais, des dates limites (produits, dossiers, interventions.) </li> <li>Contr&#xF4;le et suivi de la propret&#xE9; des locaux, dans son domaine d&apos;activit&#xE9; </li> <li>Dressage des pr&#xE9;parations culinaires </li> <li>&#xC9;laboration des entr&#xE9;es p&#xE2;tissi&#xE8;res, p&#xE2;tisseries et pi&#xE8;ces de buffet </li> <li>&#xC9;tablissement des menus </li> <li>Pr&#xE9;paration et r&#xE9;alisation des prestations de restauration et d&apos;h&#xF4;tellerie </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements, outillages, dans son domaine de comp&#xE9;tence </li> <li>Choisir et utiliser des mat&#xE9;riels, des outils de travail ou / et de contr&#xF4;le, aff&#xE9;rents &#xE0; son m&#xE9;tier </li> <li>Cr&#xE9;er des recettes, des pr&#xE9;parations culinaires </li> <li>&#xC9;laborer des plats </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">&#xC9;quipements et technologie des mat&#xE9;riels de cuisson </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;h&#xF4;tellerie/ restauration </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de commandes des repas et de gestion des stocks </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">P&#xE2;tisserie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Techniques culinaires </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable de production culinaire pour les consignes de fabrication et propositions d&apos;am&#xE9;lioration<br>Clients du restaurant du personnel pour le service du plat chaud et le recueil de la satisfaction<br>Agents des autres secteurs du service restauration pour la coordination des activit&#xE9;s de restauration<br>Responsable qualit&#xE9; domaine restauration pour l&apos;&#xE9;volution du syst&#xE8;me HACCP<br>Service de maintenance pour le signalement de pannes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>M&#xE9;tiers de bouche et formations compl&#xE9;mentaires<br>CAP/BEP cuisine<br>BAC pro restauration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant(e) de production culinaire / alimentaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) maintenance électrique / automatismes', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter l&apos;activit&#xE9; des ateliers de courants forts et faibles, &#xE9;lectrotechnique et automatisme, en animant une &#xE9;quipe de professionnels et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies, soit en animant une &#xE9;quipe de professionnels, soit par le biais de prestataires ext&#xE9;rieurs<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable d&apos;atelier maintenance &#xE9;lectrique / automatismes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Administrateur(trice) GTC GTB<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Habilitation &#xE9;lectrique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Achat de produits / de prestations, dans son domaine </li> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; des r&#xE9;sultats au regard du cahier des charges, des sp&#xE9;cifications, co&#xFB;ts, performances, d&#xE9;lais... </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des activit&#xE9;s, dans son domaine </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Entretien, maintenance, pr&#xE9;vention et d&#xE9;pannage des mat&#xE9;riels, outillages, &#xE9;quipements, syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement et r&#xE9;daction de protocoles de maintenance et de contr&#xF4;le, dans son domaine </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Suivi de la r&#xE9;alisation des travaux, de la r&#xE9;solution des probl&#xE8;mes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Choisir une m&#xE9;thode, un moyen correspondant &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Lire et interpr&#xE9;ter des plans techniques, des croquis relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier et coordonner des travaux et / ou des interventions (maintenance, entretien et /ou installation de mat&#xE9;riels, outils, &#xE9;quipements, syst&#xE8;mes...) </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Automatisme </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectricit&#xE9; g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;quipements de t&#xE9;l&#xE9;communication - t&#xE9;l&#xE9;phonie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Maintenance industrielle / &#xE9;quipements </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des b&#xE2;timents </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Gestionnaire de maintenance pour les prises de d&#xE9;sision<br>Responsables de maintenance des autres domaines pour l&apos;uniformisation des procesus et proc&#xE9;dures<br>Cadres de soins pour l&apos;analyse des besoins<br>Prestataires ext&#xE9;rieurs (entreprises, bureaux d&apos;&#xE9;tudes, organismes de contr&#xF4;le technique) en lien avec l&apos;activit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAP, BEP<br>Baccalaur&#xE9;at ou BAC +2 de maintenance courants forts et faibles electrotechnique automatismes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Corps des techniciens et techniciens sup&#xE9;rieurs hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Conducteur de travaux tous corps d&apos;&#xE9;tat (TCE)<br>Responsable de la maintenance tous corps d&apos;&#xE9;tat (TCE)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) en prévention des risques professionnels', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Evaluer les risques professionnels &#xE0; partir de l&apos;analyse des situations de travail actuelles ou futures.<br>D&#xE9;finir et proposer des strat&#xE9;gies de pr&#xE9;vention des risques professionnels et d&apos;am&#xE9;lioration des conditions de travail, contr&#xF4;ler leur mise en &#x153;uvre et en &#xE9;valuer les r&#xE9;sultats.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Pr&#xE9;venteur<br>Conseiller(&#xE8;re) en s&#xE9;curit&#xE9; et conditions de travail<br>Charg&#xE9; de la s&#xE9;curit&#xE9; au travail<br>Charg&#xE9;(e) des conditions de travail<br>Intervenant(e) en pr&#xE9;vention des risques professionnels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En radioprotection,<br>En toxicologie,<br>En psychologie de la sant&#xE9; au travail,<br>En ergonomie.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Analyse d&apos;un incident, d&apos;un accident, ou d&apos;une maladie professionnelle </li> <li>Appui aux &#xE9;quipes techniques et logistiques pour l&apos;&#xE9;laboration, la mise en &#x153;uvre et le suivi du plan de pr&#xE9;vention avec les entreprises ext&#xE9;rieures et du protocole de s&#xE9;curit&#xE9; Chargement &#x2013; D&#xE9;chargement </li> <li>Conception et animation d&apos;interventions et des actions de formation &#xE0; la pr&#xE9;vention des risques professionnels </li> <li>Conseil aux d&#xE9;cideurs et aux professionnels sur les risques professionnels </li> <li>Coordination de la gestion de l&apos;&#xE9;valuation individuelle des risques professionnels </li> <li>Coordination de la gestion des risques professionnels et de la tra&#xE7;abilit&#xE9; de l&apos;&#xE9;valuation </li> <li>&#xC9;laboration des programmes annuels de pr&#xE9;vention des risques professionnels d&apos;am&#xE9;lioration des conditions de travail, des contrats locaux d&apos;am&#xE9;lioration des conditions de travail </li> <li>&#xC9;laboration et actualisation des proc&#xE9;dures, protocoles, consignes et autres supports de communication </li> <li>Mise en oeuvre de la d&#xE9;marche d&apos;&#xE9;valuation des risques professionnels et de leur pr&#xE9;vention </li> <li>Suivi des actions de pr&#xE9;vention des risques professionnels et r&#xE9;alisation des bilans institutionnels et documents pr&#xE9;paratoires aux instances comp&#xE9;tentes dans le domaine de la s&#xE9;curit&#xE9; et sant&#xE9; au travail et tra&#xE7;abilit&#xE9; des donn&#xE9;es &#xE0; caract&#xE8;re r&#xE9;glementaire </li> <li>Traitement des contr&#xF4;les des organismes agr&#xE9;&#xE9;s, habilit&#xE9;s ou accr&#xE9;dit&#xE9;s et des corps d&apos;inspections portant sur la s&#xE9;curit&#xE9; et la sant&#xE9; au travail </li> <li>Traitement des risques &#xE9;lev&#xE9;s dans le document unique d&apos;&#xE9;valuation des risques professionnels, des causes d&apos;accident du travail et de maladies professionnelles, des dangers graves et imminents et autres interventions d&apos;urgence </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et &#xE9;valuer une situation, un syst&#xE8;me, une organisation </li> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Concevoir et mettre en oeuvre des changements organisationnels </li> <li>Concevoir et organiser des dispositifs et actions de formation, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Construire / adapter des outils / m&#xE9;thodes de travail </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Base de donn&#xE9;es, techniques d&apos;enqu&#xEA;te </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Conception,adaptation et mise en oeuvre d&apos;un syst&#xE8;me de gestion documentaire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ergonomie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Fondamentaux du droit </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion de crise </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne - S&#xE9;curit&#xE9; - Environnement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions fonctionnelles en charge des ressources humaines, soins, investissements, finances travaux, &#xE9;quipements, qualit&#xE9; et gestion des risques, direction g&#xE9;n&#xE9;rale, CHSCT pour d&#xE9;finir, soutenir et adapter la politique de pr&#xE9;vention des risques professionnels et d&apos;am&#xE9;lioration des conditions de travail.<br>Les interlocuteurs en appui sur les risques professionnelles : m&#xE9;decin du travail, service d&apos;hygi&#xE8;ne hospitali&#xE8;re, personne comp&#xE9;tente en radioprotection, s&#xE9;curit&#xE9;-incendie, services de s&#xE9;curit&#xE9; des personnes et des biens, ergonome, etc. pour leur expertise dans le cadre du diagnostic partag&#xE9; concernant la s&#xE9;curit&#xE9; des postes de travail, les conditions de travail.<br>Les interlocteurs externes : organismes de pr&#xE9;vention, inspection du travail, autorit&#xE9; de s&#xFB;ret&#xE9; nucl&#xE9;aire, etc. notamment pour leurs avis techniques et observations de conformit&#xE9;,<br>organismes nationaux de recherche et de veille sanitaire dans le cadre des op&#xE9;rations de veille, pour obtenir des avis techniques, pour alerter.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de niveau III et exp&#xE9;rience approfondie dans un &#xE9;tablissement de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Cadre de sant&#xE9;<br>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Conseiller environnement et d&#xE9;veloppement durable<br>Animateur qualit&#xE9; / gestion des risques<br>Coordinateur qualit&#xE9; / gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) environnement et développement durable', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finir et piloter la mise en oeuvre de la politique et des actions li&#xE9;s &#xE0; la protection de l&apos;environnement et &#xE0; l&apos;am&#xE9;lioration du cadre de vie (&#xE9;limination et recyclage des d&#xE9;chets, qualit&#xE9; de l&apos;air et de l&apos;eau, contr&#xF4;le des nuisances et du bruit, esth&#xE9;tique, risques...) pour une meilleure ma&#xEE;trise des impacts environnementaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable environnement<br>Responsable HSE<br>Charg&#xE9;(e) de mission environnement<br>Sp&#xE9;cialiste environnement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Sp&#xE9;cialiste du traitement eau<br>sp&#xE9;cialiste du traitement air<br>sp&#xE9;cialiste du traitement amiante<br>sp&#xE9;cialiste de la gestion des &#xE9;nergies / d&#xE9;veloppement durable / &#xE9;nergies renouvelables<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Mise en conformit&#xE9; des installations, en application des normes et r&#xE8;glementations en vigueur </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s / interventions internes/externes, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recensement, identification, analyse et traitement des risques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Suivi des contr&#xF4;les et des organismes agr&#xE9;&#xE9;s </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Concevoir et mettre en oeuvre des changements organisationnels </li> <li>Concevoir et organiser des dispositifs et actions de formation, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir, allouer et optimiser les ressources au regard des priorit&#xE9;s, des contraintes et variations externes / internes </li> <li>D&#xE9;finir, conduire et &#xE9;valuer la politique relative &#xE0; son domaine de comp&#xE9;tence </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Identifier et analyser les &#xE9;volutions et leurs impacts sur les activit&#xE9;s, les syst&#xE8;mes relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Pr&#xE9;voir et simuler les cons&#xE9;quences et actions de pr&#xE9;vention suite &#xE0; des incidents critiques, des situations de crise </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion de crise </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne - S&#xE9;curit&#xE9; - Environnement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; au management de la qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation environnementale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions fonctionnelles, services techniques, services de soins pour qu&apos;ils adaptent leurs politiques, leurs pratiques &#xE0; ces nouvelles dimensions (achats, consommations, maintenance, d&#xE9;chets...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+2 &#xE0; bac+5 dans le domaine<br>dut hygi&#xE8;ne s&#xE9;curit&#xE9;<br>master, ou &#xE9;cole d&apos;ing&#xE9;nieur dans le domaine (d&#xE9;veloppement durable, qualit&#xE9;, environnement...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier<br>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Conseiller en pr&#xE9;vention des risques professionnels<br>Coordinateur qualit&#xE9; - gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Coordinateur (trice) de prélèvement ou de transplantation d''organe', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;velopper l&apos;activit&#xE9; de transplantation en optimisant qualitativement et quantitativement la collecte de greffons dans son &#xE9;tablissement de sant&#xE9; et dans ceux du r&#xE9;seau de sant&#xE9; auquel il est rattach&#xE9;.<br>Accueillir et accompagner les proches des donneurs potentiels et garantir aupr&#xE8;s d&apos;eux le respect de la volont&#xE9; du d&#xE9;funt.<br>D&#xE9;velopper la motivation des services partenaires et concevoir et animer des actions de formation des personnels et d&apos;information du public.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Infirmier(&#xE8;re)-coordinateur(trice) des dons d&apos;organes et de tissus<br>Coordinateur(trice) Hospitalier(&#xE8;re) de Pr&#xE9;l&#xE8;vement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, accompagnement et suivi des proches des donneurs potentiels d&apos;organes et de tissus. </li> <li>Contr&#xF4;le de conformit&#xE9; des processus et des documents </li> <li>D&#xE9;veloppement des relations professionnelles avec les correspondants du r&#xE9;seau de pr&#xE9;l&#xE8;vement </li> <li>Elaboration et mise en place d&apos;un plan de communication relatif au domaine d&apos;activit&#xE9; </li> <li>Elaboration et mise en place de formations pour les personnels et les &#xE9;tudiants hospitaliers </li> <li>Elaboration et mise en place de s&#xE9;ances d&apos;informations du public et/ou d&apos;&#xE9;v&#xE8;nements de promotion du don d&apos;organes et de tissus </li> <li>Identification et recensement des donneurs potentiels d&apos;organes et/ou de tissus. Suivi des patients c&#xE9;r&#xE9;bro-l&#xE9;s&#xE9;s en &#xE9;tat de coma grave. </li> <li>Mise en oeuvre d&apos;une d&#xE9;marche de gestion des risques </li> <li>Organisation et gestion des archives (dossiers donneurs) dans les conditions r&#xE9;glementaires </li> <li>Planification des interventions des &#xE9;quipes chirurgicales de pr&#xE9;l&#xE8;vement en liaison avec l&apos;organisation locale du bloc op&#xE9;ratoire. Organisation du transport des &#xE9;quipes des greffons Assistance op&#xE9;ratoire du m&#xE9;decin pr&#xE9;leveur (pr&#xE9;l&#xE8;vement de tissus) </li> <li>Production et exploitation de statistiques relevant de son domaine d&apos;activit&#xE9; </li> <li>Recherche, en collaboration avec le m&#xE9;decin du donneur, d&apos;une &#xE9;ventuelle opposition au pr&#xE9;l&#xE8;vement, accueil et accompagnement des proches </li> <li>Recueil et transmission des donn&#xE9;es n&#xE9;cessaires &#xE0; la qualification du donneur potentiel puis &#xE0; la qualification des greffons en temps r&#xE9;el et en diff&#xE9;r&#xE9; (dossier informatis&#xE9; partag&#xE9;) </li> <li>Traitement et suivi des &#xE9;v&#xE8;nements ind&#xE9;sirables </li> <li>Veille documentaire et r&#xE9;glementaire </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et accompagner les proches d&apos;un donneur potentiel dans un contexte de crise et/ou dans leur processus de deuil. </li> <li>Analyser, synth&#xE9;tiser et transmettre des informations permettant la prise en charge d&apos;un donneur potentiel </li> <li>Choisir et &#xE9;valuer des outils ou m&#xE9;thodes p&#xE9;dagogiques pertinentes </li> <li>Conduire un entretien d&apos;information et de recueil de donn&#xE9;es avec un groupe de personnes dans un contexte de charge &#xE9;motionnelle </li> <li>Evaluer, am&#xE9;liorer ses pratiques professionnelles. Analyser et adapter sa pratique professionnelle en regard de l&apos;&#xE9;volution des recommandations </li> <li>Organiser et coordonner les activit&#xE9;s de soins li&#xE9;es au processus op&#xE9;ratoire. R&#xE9;ajuster l&apos;organisation de son travail en fonction d&apos;&#xE9;v&#xE9;nements ind&#xE9;sirables ou impr&#xE9;vus </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>S&apos;exprimer en face &#xE0; face avec un ou plusieurs personnes. S&apos;exprimer en public </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les outils bureautiques et les logiciels m&#xE9;tier (CRISTAL) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bureautique/Technologies information et communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit de la sant&#xE9; (relatif au domaine d&apos;activit&#xE9;) R&#xE8;gles de bonnes pratiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des risques et vigilances </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pratiques sociales et rituels fun&#xE9;raires </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins infirmiers en anesth&#xE9;sie et r&#xE9;animation, hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques et organisation des soins en bloc op&#xE9;ratoire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur de l&apos;&#xE9;tablissement de sant&#xE9; pour d&#xE9;l&#xE9;gation d&apos;interrogation du registre du refus et autorisation de pr&#xE9;l&#xE8;vement.<br>Le m&#xE9;decin r&#xE9;f&#xE9;rent de la coordination hospitali&#xE8;re de pr&#xE9;l&#xE8;vement (travail en bin&#xF4;me)<br>Le m&#xE9;decin en charge du donneur potentiel d&apos;organes et/ou de tissus, les correspondants du service de r&#xE9;gulation et d&apos;appui de l&apos;Agence de la biom&#xE9;decine en cours de pr&#xE9;l&#xE8;vement.<br>Tous les personnels des services hospitaliers de l&apos;&#xE9;tablissement de sant&#xE9; d&apos;affectation et les correspondants m&#xE9;dicaux et param&#xE9;dicaux des &#xE9;tablissements du r&#xE9;seau r&#xE9;gional de pr&#xE9;l&#xE8;vement pour les actions de formation et information<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;Infirmier (i&#xE8;re)<br>Exp&#xE9;rience d&apos;au minimum 3 ans dans un secteur hospitalier (R&#xE9;animation / SAU /bloc OP)<br>Parcours de formation professionnelle en cours d&apos;emploi valid&#xE9; par l&apos;Agence de biom&#xE9;decine.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Infirmier, pu&#xE9;ricultrice, infirmier anesth&#xE9;siste, infirmier de bloc op&#xE9;ratoire<br>Cadre de sant&#xE9;, cadre sup&#xE9;rieur de sant&#xE9; (si responsabilit&#xE9; d&apos;&#xE9;quipe et/ou animation de r&#xE9;seau)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales,<br>Cadre soignant de p&#xF4;le,<br>Formateur des professionnels de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Diététicien(ne)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un diagnostic di&#xE9;t&#xE9;tique et les modalit&#xE9;s de la mise en oeuvre des soins di&#xE9;t&#xE9;tiques, de l&apos;&#xE9;ducation et de la pr&#xE9;vention di&#xE9;t&#xE9;tiques, int&#xE9;gr&#xE9;s dans un programme de soin global.<br>Adapter l&apos;alimentation et conseiller une personne ou un groupe afin d&apos;am&#xE9;liorer ou pr&#xE9;server la sant&#xE9;.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Di&#xE9;t&#xE9;ticien(ne)-nutritionniste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>BTS di&#xE9;t&#xE9;tique<br>DUT biologie appliqu&#xE9;e, option di&#xE9;t&#xE9;tique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, encadrement et accompagnement p&#xE9;dagogique de personnes (agents, &#xE9;tudiants, stagiaires, etc.) </li> <li>Conseil et &#xE9;ducation th&#xE9;rapeutiques, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration du plan alimentaire en vue de la r&#xE9;alisation du cahier des charges de la restauration </li> <li>&#xC9;tablissement des menus </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Identification, recensement des besoins et des attentes des patients, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Lancement, suivi et contr&#xF4;le des orientations nutritionnelles d&#xE9;finies par le comit&#xE9; de liaison alimentation nutrition </li> <li>Mise en &#x153;uvre du soin di&#xE9;t&#xE9;tique en lien avec la personne, son entourage et les professionnels concern&#xE9;s </li> <li>Recherche appliqu&#xE9;e &#xE0; l&apos;alimentation et la nutrition humaine </li> <li>R&#xE9;diger un plan de soin di&#xE9;t&#xE9;tique en lien avec la personne, son entourage et les professionnels concern&#xE9;s </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Communiquer et conduire une relation dans un contexte d&apos;intervention </li> <li>Concevoir et mettre en &#x153;uvre un plan de soin di&#xE9;t&#xE9;tique </li> <li>Conseiller le patient et son entourage dans le cadre du projet personnalis&#xE9; du patient </li> <li>Coop&#xE9;rer avec d&apos;autres professionnels </li> <li>&#xC9;laborer des menus, composer des repas </li> <li>&#xC9;laborer et conduire une d&#xE9;marche d&apos;intervention en sant&#xE9; publique : pr&#xE9;vention, d&#xE9;pistage et &#xE9;ducation th&#xE9;rapeutique </li> <li>&#xC9;laborer et formaliser un diagnostic sant&#xE9; de la personne, relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la qualit&#xE9; de l&apos;alimentation et de ses apports nutritionnels </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Mettre en &#x153;uvre une d&#xE9;marche qualit&#xE9; dans diff&#xE9;rents contextes professionnels </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Comportement alimentaire </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Di&#xE9;t&#xE9;tique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins pour la d&#xE9;marche nutritionnelle et les prescriptions de r&#xE9;gimes<br>Personnels param&#xE9;dicaux des services de soin pour la d&#xE9;marche nutritionnelle<br>Personnels de restauration pour la commission menu, l&apos;&#xE9;laboration et la mise en application du cahier des charges et l&apos;&#xE9;laboration des menus<br>Laboratoires et fournisseurs de produts alimentaires / di&#xE9;t&#xE9;tiques pour l&apos;information sur les produits di&#xE9;t&#xE9;tiques de nutrition orale et ent&#xE9;rale, de leur utilisation et du mat&#xE9;riel utilis&#xE9;<br>Services &#xE9;conomiques et Pharmacie, pour l&apos;optimisation des co&#xFB;ts/qualit&#xE9;<br>Services &#xE9;conomiques pour les commandes de produits di&#xE9;t&#xE9;tiques, personnel de magasin<br>Cadres de sant&#xE9; pour l&apos;organisation du travail et le suivi des patients, directeurs des services &#xE9;conomiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BTS de di&#xE9;t&#xE9;tique ou DUT biologie appliqu&#xE9;e option di&#xE9;t&#xE9;tique<br>G&#xE9;nie en biologie, option di&#xE9;t&#xE9;tique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Di&#xE9;t&#xE9;ticien<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Coordinateur de parcours en sant&#xE9;<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Dosimétriste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire:<br>&quot;Pr&#xE9;parer et planifier les traitements par radiations ionisantes en lien avec le physicien m&#xE9;dical et le m&#xE9;decin prescripteur.<br>Mettre en oeuvre les outils permettant le calcul des doses de rayonnements ionisants afin d&apos;optimiser les doses re&#xE7;ues par le patient et de prot&#xE9;ger les tissus sains&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Technicien(ne) en physique m&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Actualisation des proc&#xE9;dures et protocoles d&apos;hygi&#xE8;ne, s&#xE9;curit&#xE9;, et planification des traitements </li> <li>D&#xE9;finition des objectifs de dose collective et individuelle, des mesures de protection adapt&#xE9;es, des moyens n&#xE9;cessaires en cas de situation anormale </li> <li>D&#xE9;termination des trajectoires des faisceaux selon la cible &#xE0; atteindre </li> <li>Ex&#xE9;cution des calculs de distribution de doses absorb&#xE9;es par un organisme ou un objet </li> <li>Formation de personnes aux techniques et proc&#xE9;dures de son domaine, et &#xE0; leur application </li> <li>Gestion de la surveillance dosim&#xE9;trique des personnels </li> <li>Information du patient sur les pr&#xE9;cautions d&apos;usage avant, pendant et apr&#xE8;s un traitement et proc&#xE9;der au d&#xE9;clenchement de l&apos;irradiation </li> <li>Planification des &#xE9;talonnages des instruments de d&#xE9;tection </li> <li>R&#xE9;alisation des bilans d&apos;activit&#xE9; de physique m&#xE9;dicale </li> <li>R&#xE9;alisation des contr&#xF4;les techniques de radioprotection (contr&#xF4;les internes) et organisation des contr&#xF4;les externes selon l&apos;arr&#xEA;t&#xE9; du 21 mai 2010 </li> <li>Recueil des donn&#xE9;es du dossier patient n&#xE9;cessaires &#xE0; la planification dosim&#xE9;trique </li> <li>Suivi des sources de rayonnements et de l&apos;&#xE9;tat des stocks de produits, identification des besoins en approvisionnement en vue de leur transmission </li> <li>Surveillance des installations (balisage selon les modalit&#xE9;s de l&apos;arr&#xEA;t&#xE9; zonage), &#xE9;vacuation et destruction des d&#xE9;chets </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Contr&#xF4;ler au plan qualitatif les dispositifs m&#xE9;dicaux utilis&#xE9;s pour la pr&#xE9;paration et la d&#xE9;livrance des traitements et les syst&#xE8;mes de transfert de donn&#xE9;es </li> <li>Contr&#xF4;ler l&apos;application et apporter un appui technique au personnel </li> <li>Contr&#xF4;ler la gestion des d&#xE9;chets radioactifs produits par l&apos;h&#xF4;pital </li> <li>&#xC9;valuer le risque encouru par les travailleurs expos&#xE9;s et recensement des situations ou des modes de travail susceptibles de conduire &#xE0; des expositions exceptionnelles ou accidentelles. </li> <li>Evaluer les techniques employ&#xE9;es et veiller &#xE0; leur &#xE9;volution </li> <li>Ma&#xEE;triser les techniques standards et leurs &#xE9;volutions </li> <li>Surveiller les r&#xE9;actions, la tol&#xE9;rance de la personne lors de la r&#xE9;alisation de l&apos;examen radiologique et intervenir selon n&#xE9;cessit&#xE9; </li> <li>V&#xE9;rifier le fonctionnement des appareils et informer le service maintenance, le responsable en cas de dysfonctionnement </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Dosim&#xE9;trie et rayonnement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Informatique / Syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de traitement des images </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; en radioth&#xE9;rapie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Mat&#xE9;riel et &#xE9;quipement d&apos;imagerie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Physique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Radioprotection </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation en hygi&#xE8;ne, s&#xE9;curit&#xE9; et conditions de travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Licence professionnelle dosim&#xE9;trie et radioprotection m&#xE9;dicale (Bac+3)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Contractuel de cat&#xE9;gorie A ou B<br>Fili&#xE8;re technique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Chef (fe) de projet informatique', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Conduire des projets informatiques en conformit&#xE9; avec le cahier des charges, jusqu&apos;&#xE0; leur mise en production, dans le respect des co&#xFB;ts, d&#xE9;lais et r&#xE8;gles de l&apos;art, en coordonnant des &#xE9;quipes multidisciplinaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) de projet informatique ma&#xEE;trise d&apos;oeuvre<br>Project manager<br>Sp&#xE9;cialiste d&apos;applications<br>Chef(fe) de projet pour Progiciel Int&#xE9;gr&#xE9; (PGI)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Projets int&#xE9;gration<br>Projets conception<br>Projets Progiciels de Gestion Int&#xE9;gr&#xE9; (PGI)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Analyse, veille et prospection du march&#xE9; des fournisseurs informatiques pour mettre en &#x153;uvre la strat&#xE9;gie d&apos;achat SI </li> <li>Coordination des acteurs du projet avec les diff&#xE9;rentes parties prenantes (MOA, MOE, AMOA, AMOE) </li> <li>D&#xE9;finition et conception g&#xE9;n&#xE9;rale : r&#xE9;daction des sp&#xE9;cifications fonctionnelles et techniques contribuant au cahier des charges et/ou &#xE0; l&apos;&#xE9;laboration du CCTP </li> <li>D&#xE9;finition et gestion des environnements de d&#xE9;veloppement/int&#xE9;gration ou pr&#xE9;-production/production </li> <li>D&#xE9;finition, pilotage et communication sur le projet en coh&#xE9;rence avec le plan de conduite du changement (y compris le plan de communication et de formation) </li> <li>Elaboration, gestion et r&#xE9;alisation du plan de tests technique et des sc&#xE9;narios de reprise de donn&#xE9;e </li> <li>Identification, &#xE9;tude et validation des impacts (technologiques, organisationnels, fonctionnels et financiers) li&#xE9;s au projet </li> <li>Mise en exploitation de l&apos;application avec livraison des documents requis (dossier exploitation,..) </li> <li>Planification, suivi, contr&#xF4;le, arbitrage, impl&#xE9;mentation et reporting sur l&apos;avanc&#xE9;e du projet SI </li> <li>R&#xE9;alisation de la d&#xE9;claration CNIL </li> <li>Recensement et analyse des besoins m&#xE9;tiers, sur le ou les domaines concern&#xE9;s </li> <li>R&#xE9;daction, mise &#xE0; disposition et mise &#xE0; jour de la documentation sur le SI (guide de proc&#xE9;dure, documents utilisateurs, ...) </li> <li>Transmission des sp&#xE9;cifications fonctionnelles et techniques vers l&apos;&#xE9;quipe de maintien en condition op&#xE9;rationnelle du SI (transfert de comp&#xE9;tence) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser les besoins d&apos;adaptation du SI </li> <li>Analyser les risques projet pour &#xE9;tablir les plans d&apos;actions associ&#xE9;s </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Encoder et g&#xE9;rer les supports d&apos;authentification </li> <li>Evaluer une offre dans le cadre d&apos;un appel d&apos;offres, mener un dialogue sur les modalit&#xE9;s, les conditions et les prix (dialogue comp&#xE9;titif et proc&#xE9;dures n&#xE9;goci&#xE9;es) </li> <li>Identifier, s&#xE9;lectionner, exploiter et capitaliser les informations dans le cadre de la veille professionnelle </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, g&#xE9;rer et adapter les moyens mis &#xE0; disposition (ressources financi&#xE8;res, humaines, formation, planning,&#x2026;)du projet SI et organiser, r&#xE9;partir le travail de l&apos;&#xE9;quipe </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Architecture SI m&#xE9;tier et fonctionnelle </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit des donn&#xE9;es informatiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Informatique, SI et veille technologique </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">March&#xE9; des solutions informatiques (technologie, produit et offre) </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thode de conduite de projet (planification, reporting, analyse de risques, ...) et de la conduite du changement </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Organisation, m&#xE9;tiers et fonctionnement de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Outils-Applications m&#xE9;tier/logiciels n&#xE9;cessaires au fonctionnement m&#xE9;tier et &#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Recette projet </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; informatique, s&#xE9;curit&#xE9; SI </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsables fonctionnels, chefs de projet utilisateur ou utilisateurs r&#xE9;f&#xE9;rents pour d&#xE9;finir le besoin, architecte fonctionnel, architecte technique, responsable s&#xE9;curit&#xE9; pour information et respect de la coh&#xE9;rence du SI (technique ou fonctionnelle), analystes fonctionnels et analystes d&apos;exploitation pour la r&#xE9;partition de l&apos;activit&#xE9; n&#xE9;cessaire au projet, fournisseurs pour consultation et r&#xE9;ponse aux besoins et aux appels d&apos;offres<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me sup&#xE9;rieur dans le domaine informatique ou syst&#xE8;me d&apos;information (Bac + 5), Ing&#xE9;nieur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable de S&#xE9;curit&#xE9; des Syst&#xE8;mes d&apos;Informations<br>Architecte-urbaniste du SI<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Maître / Maîtresse de maison', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Coordonner les prestations h&#xF4;teli&#xE8;res (accueil, repas, collations, animations) aupr&#xE8;s des patients et des r&#xE9;sidents en veillant &#xE0; la qualit&#xE9; du cadre de vie et de la chambre, afin de maintenir le bien-&#xEA;tre physique et psychologique des usagers dans les actes de la vie quotidienne.<br>R&#xE9;aliser des t&#xE2;ches m&#xE9;nag&#xE8;res (entretien des locaux, cuisine, lingerie, etc.) en lien avec les &#xE9;quipes soignantes et &#xE9;ducatives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Agent(e) h&#xF4;telier<br>Aide-gouvernante<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Enfants,<br>Personnes &#xE2;g&#xE9;es<br>Adolescents<br>Personnes handicap&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissance du milieu m&#xE9;dico-social,<br>Exp&#xE9;rience en secteur personnes ag&#xE9;es d&#xE9;pendantes et p&#xE9;diatrie<br>Formation compl&#xE9;mentaire en h&#xF4;tellerie/restauration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et installation du patient, am&#xE9;nagement de l&apos;environnement (confort et s&#xE9;curit&#xE9;) </li> <li>Assistance aux personnes, aux patients pour la r&#xE9;alisation des actes de la vie quotidienne (repas, toilette, etc.) </li> <li>Contr&#xF4;le de la qualit&#xE9; des produits </li> <li>Contr&#xF4;le et suivi de la propret&#xE9; des locaux, dans son domaine d&apos;activit&#xE9; </li> <li>Dressage des pr&#xE9;parations culinaires </li> <li>Dressage des tables </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Pr&#xE9;paration et r&#xE9;alisation des prestations de restauration et d&apos;h&#xF4;tellerie </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et orienter des personnes, des groupes, des publics </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Appr&#xE9;cier un niveau de cuisson </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Cr&#xE9;er des recettes, des pr&#xE9;parations culinaires </li> <li>Discerner les saveurs, les ar&#xF4;mes </li> <li>&#xC9;laborer des menus, composer des repas </li> <li>&#xC9;laborer des plats </li> <li>&#xC9;valuer l&apos;&#xE9;tat de propret&#xE9; des surfaces et d&#xE9;finir les travaux &#xE0; r&#xE9;aliser </li> <li>Organiser et optimiser le stockage physique des produits, des mat&#xE9;riels </li> <li>S&apos;exprimer en face -&#xE0;- face aupr&#xE8;s d&apos;une ou plusieurs personnes </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">H&#xF4;tellerie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Nettoyage-d&#xE9;sinfection </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE2;tisserie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie des &#xE2;ges de la vie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Techniques culinaires </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Personnel des services de soins pour la continuit&#xE9; des soins<br>Service restauration pour l&apos;information sur les commandes, les conditions de livraison<br>Service di&#xE9;t&#xE9;tique pour l&apos;adaptation des menus<br>Animatrices pour l&apos;accompagnement aux actions d&apos;animation effectu&#xE9;es au profit des r&#xE9;sidents ou jeunes patients<br>Service de s&#xE9;curit&#xE9; pour le signalement de tous d&#xE9;fauts mettant en jeu la s&#xE9;curit&#xE9; des patients<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Etudes sanitaires et sociales<br>CAP petite enfance<br>BEP sanitaire et social<br>CAP employ&#xE9; technique de collectivit&#xE9;s ou bioservices<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corsp des Aide-soignant et des agents des services hospitaliers qualifi&#xE9;s<br>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Aide-soignant<br>Animateur<br>Aide m&#xE9;dico-psychologique<br>Conseiller h&#xF4;telier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) de maintenance des équipements et matériels mécaniques', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter la gestion du parc de v&#xE9;hicules et mat&#xE9;riels de transports, de manutention, et/ou de parc et jardin, en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies, soit en animant une &#xE9;quipe de professionnels, soit par le biais de prestataires ext&#xE9;rieurs.<br>G&#xE9;rer la relation avec les utilisateurs en r&#xE9;ceptionnant leurs demandes.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable garage<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Approvisionnement en produits, en mat&#xE9;riels, dans son domaine </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectricit&#xE9; automobile </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la maintenance automobile </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Maintenance industrielle / &#xE9;quipements </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits et fournisseurs </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;canique g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation des transports </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Technologie des v&#xE9;hicules </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>ambulanciers, SAMU, services de transports de biens (magasins).entreprises prestataires de transports<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC<br>CAP<br>BEP<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Corps des techniciens et techniciens sup&#xE9;rieurs hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable de la maintenance tous corps d&apos;&#xE9;tat (TCE)<br>Encadrant maintenance corps d&apos;&#xE9;tat secondaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conducteur(trice) de travaux tous corps d''état (TCE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Diriger, coordonner et planifier les travaux sur le plan technique, financier et r&#xE9;glementaire en int&#xE9;grant les contraintes du milieu hospitalier.<br>il peut r&#xE9;aliser des &#xE9;tudes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>Coordination et suivi de la prise en charge de prestations </li> <li>Planification des activit&#xE9;s / interventions internes/externes, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Suivi des contr&#xF4;les et des organismes agr&#xE9;&#xE9;s </li> <li>Suivi et contr&#xF4;le budg&#xE9;taire relatif au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer une prestation, un projet, une solution, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Lire et interpr&#xE9;ter des plans techniques, des croquis relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">B&#xE2;timents et installations </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits et fournisseurs </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Ma&#xEE;tre d&apos;oeuvre,pilote, entreprise, conducteur d&apos;op&#xE9;ration, responsable des services techniques g&#xE9;n&#xE9;raux, bureau de contr&#xF4;le, coordonnateur s&#xE9;curit&#xE9; sant&#xE9;,<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+2<br>Bac +5<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Conducteur d&apos;op&#xE9;ration tous corps d&apos;&#xE9;tat (TCE)<br>Responsable de la maintenance tous corps d&apos;&#xE9;tat (TCE)<br>Encadrant maintenance corps d&apos;&#xE9;tat secondaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conducteur(trice) d''opérations tous corps d''état (TCE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Repr&#xE9;senter le ma&#xEE;tre d&apos;ouvrage sur les plans administratifs, financiers et techniques lors des phases de programmation, conception, r&#xE9;alisation et r&#xE9;ception des projets.il peut r&#xE9;aliser des &#xE9;tudes.<br>En qualit&#xE9; de r&#xE9;f&#xE9;rent technique, p&#xE9;renniser et /ou am&#xE9;liorer les installations techniques de l&apos;&#xE9;tablissement au regard de sa sp&#xE9;cialit&#xE9; d&apos;origine<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Charg&#xE9;(e) d&apos;op&#xE9;ration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En thermique<br>En &#xE9;lectricit&#xE9;<br>En plomberie<br>En fluides m&#xE9;dicaux<br>En g&#xE9;nie civil<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Coordination et suivi de la prise en charge de prestations </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>Montage et instruction de dossiers relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter les maquettes aux &#xE9;volutions des techniques d&apos;impression </li> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer une prestation, un projet, une solution, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Lire et interpr&#xE9;ter des plans techniques, des croquis relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Organiser une unit&#xE9;, un service, en fonction de diff&#xE9;rents crit&#xE8;res (missions, strat&#xE9;gie, moyens, etc.) </li> <li>Planifier et coordonner des travaux et / ou des interventions (maintenance, entretien et /ou installation de mat&#xE9;riels, outils, &#xE9;quipements, syst&#xE8;mes...) </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">B&#xE2;timents et installations </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Conception de programme architectural et technique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable des services techniques pour d&#xE9;cisions<br>directeur, m&#xE9;decin, personnel de soins pour d&#xE9;finir et rationnaliser les besoins<br>mairie, pr&#xE9;fecture drire pour int&#xE9;grer les obligations locales et d&#xE9;partementales<br>programmiste, architecte, bureaux d&apos;&#xE9;tudes, entreprises, contr&#xF4;leurs techniques pour &#xE9;laborer et r&#xE9;aliser les projets<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +5<br>ing&#xE9;nieur des &#xE9;coles g&#xE9;n&#xE9;ralistes ou de sp&#xE9;cialit&#xE9; dans les domaines des techniques du b&#xE2;timent.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable de la maintenance tous corps d&apos;&#xE9;tat (TCE)<br>Responsable des travaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant (e) des secrétariats médicaux', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter les activit&#xE9;s de secr&#xE9;tariat m&#xE9;dical en animant une &#xE9;quipe de professionnels et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Coordinateur (trice) de secr&#xE9;taires m&#xE9;dicaux (cales)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissance du m&#xE9;tier de secr&#xE9;taire m&#xE9;dical(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, encadrement et formation des &#xE9;tudiants et des stagiaires </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Formation de personnes aux techniques et proc&#xE9;dures de son domaine, et &#xE0; leur application </li> <li>Organisation / coordination / supervision des contr&#xF4;les relatifs aux activit&#xE9;s du secteur, de l&apos;unit&#xE9; </li> <li>Organisation et description des processus sp&#xE9;cifiques &#xE0; son domaine, traitement des dysfonctionnements </li> <li>Param&#xE9;trage des outils, logiciels, syst&#xE8;mes relevant de son domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser un / des processus </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir et mettre en oeuvre des changements organisationnels </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Identifier et analyser les &#xE9;volutions et leurs impacts sur les activit&#xE9;s, les syst&#xE8;mes relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit de la sant&#xE9; dans son domaine d&apos;activit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;valuation de la charge de travail </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion administrative </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; au traitement de l&apos;information m&#xE9;dicale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins pour l&apos;&#xE9;valuation des besoins et le recueil des attentes<br>Directions fonctionnelles pour la gestion des ressources humaines et mat&#xE9;rielles<br>Bureau des admissions, service informatique, DIM pour le traitement des donn&#xE9;es m&#xE9;dico-administratives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Licence ou master 1 ou 2 :<br>Gestion/management ,<br>GRH ,<br>Gestion et organisation d&apos;&#xE9;tablissements sanitaires ou sociaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Assistant m&#xE9;dico-administratif<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant archives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(e) d''établissement de santé - chef(fe) d''établissement\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir la strat&#xE9;gie de positionnement &#xE0; moyen et long terme de l&apos;&#xE9;tablissement, coordonner et piloter sa mise en &#x153;uvre dans les diff&#xE9;rents domaines&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) d&apos;&#xE9;tablissement<br>Directeur(trice) g&#xE9;n&#xE9;ral(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\"></td> </tr> </tbody></table></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Conseiller(ère) en ingénierie biomédicale', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Fournir &#xE0; la direction g&#xE9;n&#xE9;rale et aux &#xE9;quipes techniques et m&#xE9;dicales une expertise biom&#xE9;dicale dans la programmation, l&apos;achat et/ou la gestion des &#xE9;quipements biom&#xE9;dicaux, ainsi que sur les &#xE9;volutions technologiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Ing&#xE9;nieur(e) biom&#xE9;dical(e)<br>Sp&#xE9;cialiste en ing&#xE9;nierie biom&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En achat produits / services biom&#xE9;dicaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissances ou exp&#xE9;riences du domaine technique et/ou des &#xE9;tablissements de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Achat de produits / de prestations, dans son domaine </li> <li>Audit et expertise des &#xE9;quipements / installations / syst&#xE8;mes (fonctionnement, conformit&#xE9;, efficacit&#xE9; et performances) sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;utilisation et de l&apos;entretien des mat&#xE9;riels et des locaux, dans son domaine </li> <li>&#xC9;tablissement des &#xE9;l&#xE9;ments et des sp&#xE9;cifications techniques du cahier des charges </li> <li>Etude d&apos;impact et de faisabilit&#xE9; </li> <li>Etude de pertinence strat&#xE9;gique </li> <li>Etude des projets d&apos;installation de mat&#xE9;riels biom&#xE9;dicaux </li> <li>Gestion de parc d&apos;&#xE9;quipements, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Identification, analyse et validation des impacts (technologiques, organisationnels, fonctionnels et financiers) li&#xE9;s &#xE0; des nouveaux projets </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Recensement, identification, analyse et traitement des risques, relatifs au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et &#xE9;valuer les performances d&apos;un syst&#xE8;me, d&apos;un appareil, d&apos;un outil sp&#xE9;cifique &#xE0; son domaine technique </li> <li>Analyser un march&#xE9;, fournisseurs/clients, relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Choisir et mettre en oeuvre les techniques et pratiques adapt&#xE9;es au patient, au regard de son m&#xE9;tier / sa sp&#xE9;cialit&#xE9; </li> <li>Concevoir un plan / programme d&apos;investissement </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Biom&#xE9;dical </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit des march&#xE9;s publics </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectronique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de gestion de maintenance biom&#xE9;dicale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes techniques de s&#xE9;curit&#xE9; des installations sanitaires </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable des services biom&#xE9;dicaux, direction g&#xE9;n&#xE9;rale, directions fonctionnelles (finances, informatique, travaux, logistique, soins...), commission d&apos;equipements, responsables de p&#xF4;les, fournisseurs, maitrise d&apos;oeuvre, bureau d&apos;&#xE9;tudes, corps d&apos;&#xE9;tat techniques, organismes agr&#xE9;&#xE9;s, afssaps, pcr ou pspm<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC+5, BAC+ 6<br>Ecole d&apos;ing&#xE9;nieur ou master ii incluant la sp&#xE9;cialisation g&#xE9;nie biom&#xE9;dical<br>Mast&#xE8;re d&apos;ing&#xE9;nieur biom&#xE9;dical hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable des services biom&#xE9;dicaux,<br>Responsable des travaux<br>Encadrant maintenance biom&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Documentaliste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Traiter, analyser et valider des informations afin de les mettre &#xE0; disposition des diff&#xE9;rents publics.<br>Former les utilisateurs &#xE0; la recherche documentaire.<br>Constituer et maintenir un fonds documentaire tous supports, sur place ou &#xE0; distance, mettre en place des outils de recherche adapt&#xE9;s aux besoins des demandeurs d&apos;information et des utilisateurs potentiels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Charg&#xE9; (e) de veille documentaire<br>Charg&#xE9; (e) d&apos;&#xE9;tudes documentaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Ma&#xEE;trise du traitement de l&apos;information et de la documentation.<br>Ma&#xEE;trise des nouvelles technologies de l&apos;information, adapt&#xE9;es aux besoins de l&apos;utilisateur.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, conseil et orientation des utilisateurs. </li> <li>Conception et administration des contenus de bases de donn&#xE9;es, de sites web, de portails documentaires, de plate-formes (e-learning) </li> <li>D&#xE9;finition et mise en oeuvre de la politique documentaire de l&apos;&#xE9;tablissement ou institut de formation param&#xE9;dicale. </li> <li>&#xC9;laboration d&apos;un budget, d&apos;une politique d&apos;acquisition en conformit&#xE9; avec la politique budg&#xE9;taire de l&apos;&#xE9;tablissement (dans le respect du Code des march&#xE9;s publics) et d&#xE9;finition des moyens </li> <li>Formation des utilisateurs (soignants, m&#xE9;decins, administratifs, ing&#xE9;nieurs, techniciens, &#xE9;tudiants param&#xE9;dicaux, formateurs et enseignants,...). </li> <li>Gestion des documents (mise &#xE0; disposition, &#xE9;changes, classement...). </li> <li>Organisation d&apos;une veille adapt&#xE9;e au contexte de l&apos;&#xE9;tablissement et aux professionnels concern&#xE9;s, afin de leur apporter : une actualisation des connaissances, une anticipation sur les &#xE9;volutions et une aide &#xE0; la d&#xE9;cision. <br><em>Le processus de veille int&#xE8;gre les &#xE9;tapes suivantes : ciblage, r&#xE9;colte, analyse des donn&#xE9;es recueillies et diffusion des r&#xE9;sultats.</em> </li> <li>R&#xE9;alisation de produits et services documentaires (revues de presse, dossiers, bibliographies, sitographies, etc.). </li> <li>Recensement et traitement de la demande documentaire. Organisation, enrichissement du fonds documentaire. </li> <li>Recherche de sources documentaires externes. </li> <li>Suivi des acquisitions (ouvrages, abonnements et autres supports documentaires) sous format papier, &#xE9;lectronique, visuel, etc. </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser, traduire et formuler les besoins documentaires des utilisateurs. </li> <li>Concevoir, organiser et animer des formations utilisateurs </li> <li>&#xC9;valuer la pertinence et la v&#xE9;racit&#xE9; des donn&#xE9;es et/ou informations </li> <li>Maintenir et v&#xE9;rifier le bon fonctionnement des outils informatiques mis &#xE0; disposition. </li> <li>N&#xE9;gocier les contrats avec les prestataires/fournisseurs </li> <li>Optimiser les ressources budg&#xE9;taires. </li> <li>Organiser la recherche d&apos;information, son recueil, son analyse, sa diffusion, son classement et sa mise &#xE0; jour (veille). </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les diff&#xE9;rentes &#xE9;tapes de la cha&#xEE;ne documentaire (collecte, bulletinage, catalogage, indexation, saisie, classement, diffusion, &#xE9;laboration d&apos;un th&#xE9;saurus, liste de mots-cl&#xE9;s...) </li> <li>Utiliser un syst&#xE8;me de gestion documentaire (mise &#xE0; disposition, &#xE9;changes, classement...). </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Gestion comptable et administrative <br><em>Droit de la propri&#xE9;t&#xE9; intellectuelle.El&#xE9;ments de base en gestion comptable et administrative.</em> </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques documentaires <br><em>M&#xE9;thodes de classement et d&apos;archivage. Techniques d&apos;inventaire. Techniques de conservation de documents. Techniques de num&#xE9;risation. Biblioth&#xE9;conomie.</em> </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">TIC et fonction documentaire <br><em>Acc&#xE8;s, stockage, num&#xE9;risation, diffusion. Web 2.0.Internet, Intranet.Utilisation de logiciels de gestion documentaire. Programmation et langage informatique. Outils de diffusion et de cr&#xE9;ation d&apos;images, diaporama ou supports anim&#xE9;s. Techniques de communication.</em> </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>- Associations et r&#xE9;seaux professionnels (ADBS ; Association des Professionnels de l&apos;Information et de la Documentation ; RNDH : R&#xE9;seau National des Documentalistes Hospitaliers...) pour des &#xE9;changes d&apos;information sur la documentation, des &#xE9;changes professionnels, de coop&#xE9;ration, de formation ...<br>- Institutions culturelles et d&apos;enseignement pour la recherche d&apos;informations documentaires et la coop&#xE9;ration d&apos;enseignement (Universit&#xE9; M&#xE9;decine, Droit, Sciences Humaines).<br>- Partenaires ext&#xE9;rieurs (&#xE9;diteurs, consortia, groupement d&apos;achats hospitaliers, agences d&apos;abonnements...)<br>- Les utilisateurs internes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>- DEUST, DUT, licence professionnelle, IUP, ma&#xEE;trise en gestion de l&apos;information et du document. une double comp&#xE9;tence peut &#xEA;tre n&#xE9;cessaire en biologie, pharmacie, chimie.)<br>Niveau III:<br>- Bac + 2 : DUT information communication, option documentation d&apos;entreprise<br>- Bac + 2 pro : DEUST m&#xE9;tiers des biblioth&#xE8;que et de la documentation<br>Niveau II:<br>- Bac + 3 : licence ICD - option documentation (l3)<br>- Bac + 3 pro : licence professionnelle, charg&#xE9;(e) de valorisation des ressources documentaires<br>Niveau I de type Master (Bac +5) d&#xE9;livr&#xE9; par les universit&#xE9;s et/ou l&apos;INTD<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Contractuel de cat&#xE9;gorie A<br>Contractuel de cat&#xE9;gorie B<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Adjoint des cadres hospitaliers<br>Technicien hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant documentaliste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(trice) de site/pôle/projets\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;cliner la strat&#xE9;gie de l&apos;&#xE9;tablissement dans son domaine de d&#xE9;l&#xE9;gation (p&#xF4;le, site, projet), notamment en pilotant des projets structurants&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Directeur(e) de site(s)<br>Directeur(e) de p&#xF4;le(s)<br>Directeur(e) de projet(s)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) du bio-nettoyage', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, piloter et contr&#xF4;ler la r&#xE9;alisation des activit&#xE9;s de bio-nettoyage des locaux, en animant une / des &#xE9;quipes<br>Former et informer les professionnels dans le domaine de l&apos;entretien et l&apos;hygi&#xE8;ne des locaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Gestionnaire du bio-nettoyage<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;utilisation et de l&apos;entretien des mat&#xE9;riels et des locaux, dans son domaine </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Identification, analyse et validation des impacts (technologiques, organisationnels, fonctionnels et financiers) li&#xE9;s &#xE0; des nouveaux projets </li> <li>Lancement, suivi et contr&#xF4;le des r&#xE9;alisations en r&#xE9;gie ou sous-trait&#xE9;es, relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Recherche et mise &#xE0; disposition de tous les mat&#xE9;riels ergonomiques adapt&#xE9;s pour faciliter le bio-nettoyage et les troubles musculo-squelettiques. </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter un / des postes de travail aux caract&#xE9;ristiques des personnels (charge physique ou/et mentale) </li> <li>Concevoir, piloter et contr&#xF4;ler une prestation conc&#xE9;d&#xE9;e </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Construire / adapter des outils / m&#xE9;thodes de travail </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Travailler en partenariat avec les responsables de l&apos;encadrement des unit&#xE9;s concern&#xE9;es </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bio-nettoyage et hygi&#xE8;ne des locaux </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes et techniques d&apos;hygi&#xE8;ne et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation du travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pr&#xE9;vention des troubles musculo-squelettiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>&#xC9;quipe d&apos;hygi&#xE8;ne hospitali&#xE8;re<br>&#xC9;quipes de soins,<br>Services travaux<br>Services techniques et logistiques,<br>Services &#xE9;conomiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>A partir d&apos;un dipl&#xF4;me de niveau IV dans le domaine hygi&#xE8;ne, propret&#xE9;, environnement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien hospitalier<br>Agent de ma&#xEE;trise<br>Aide-soignant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant de production blanchisserie<br>Conseiller h&#xF4;telier<br>Encadrant de st&#xE9;rilisation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Contrôleur(euse) de gestion', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir et mettre en &#x153;uvre des m&#xE9;thodes et outils permettant d&apos;analyser et de garantir l&apos;utilisation efficiente des ressources de l&apos;&#xE9;tablissement et l&apos;exercice des fonctions de pilotage et de contr&#xF4;le par le management.<br>Conseiller les d&#xE9;cideurs au regard du d&#xE9;veloppement des activit&#xE9;s.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Contr&#xF4;leur(euse) analytique<br>Analyste de gestion<br>Conseiller(&#xE8;re) gestion<br>Auditeur(trice) interne<br>Auditeur(trice) de gestion<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Contr&#xF4;leur(euse) de gestion RH<br>Analyste financier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Audit relatif au domaine d&apos;activit&#xE9; </li> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et analyse du compte de r&#xE9;sultat (comptabilit&#xE9; analytique d&apos;exploitation) </li> <li>&#xC9;laboration et mise en place de la comptabilit&#xE9; analytique en fonction des r&#xE9;f&#xE9;rentiels (guide de comptabilit&#xE9; analytique hospitali&#xE8;re) </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Argumenter et convaincre dans une relation de confiance des interlocuteurs si besoin n&#xE9;gocier des accords. </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Concevoir, r&#xE9;diger et mettre en forme une communication orale et/ou &#xE9;crite </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Utiliser les outils bureautiques / TIC </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; analytique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Contr&#xF4;le de gestion </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion de donn&#xE9;es, relatives &#xE0; son domaine </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction g&#xE9;n&#xE9;rale, commission m&#xE9;dicale pour l&apos;analyse des tableaux de bord, notes de synth&#xE8;se, rapports d&apos;&#xE9;tudes et d&apos;alerte<br>&#xC9;quipe de direction et toutes directions fonctionnelles pour les rapports mensuels de gestion<br>D&#xE9;partement d&apos;information m&#xE9;dicale pour la r&#xE9;alisation d&apos;&#xE9;tudes m&#xE9;dico-&#xE9;conomiques<br>Responsable structure m&#xE9;dicale pour les rapports d&apos;&#xE9;tudes<br>Organismes de tutelles (DGOS, ARS.) pour le suivi et les rapports annuels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC +5 : dut contr&#xF4;le de gestion / gestion des entreprises<br>Ecoles sup&#xE9;rieures de commence, DESS<br>Master en contr&#xF4;le de gestion et audit<br>Dipl&#xF4;me de niveau III (DUT contr&#xF4;le de gestion) pour contr&#xF4;leur junior, de niveau I (Master en contr&#xF4;le de gestion, Ecole sup&#xE9;rieure de commerce) pour contr&#xF4;leur senior<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Contractuel de cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable budg&#xE9;taire et financier<br>Cadre administratif de p&#xF4;le (CAP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Coordinateur(trice) d''études cliniques (CEC)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Piloter et coordonner les diff&#xE9;rentes &#xE9;tapes de mise en &#x153;uvre et de suivi des protocoles de recherche clinique, en coop&#xE9;ration avec l&apos;ensemble des intervenants<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la conformit&#xE9; et / ou de validit&#xE9; des documents, relatifs &#xE0; son domaine </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>&#xC9;laboration et mise en place du plan d&apos;archivage des documents </li> <li>&#xC9;laboration et proposition des modifications en cours de projet (objectifs, qualit&#xE9;, co&#xFB;ts, d&#xE9;lais...) li&#xE9;es &#xE0; des contraintes d&apos;&#xE9;tude ou de r&#xE9;alisation </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement et suivi de budget(s) relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Organisation et animation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es, groupes de travail et groupes projet </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Suivi des &#xE9;v&#xE9;nements ind&#xE9;sirables </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Conduire et animer des r&#xE9;unions </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation relative &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Techniciens d&apos;etudes cliniques et investigateurs pour la mise en oeuvre et le suivi des projets<br>Gestionnaires de donn&#xE9;es biom&#xE9;dicales et biostatisticiens pour les corrections diff&#xE9;r&#xE9;es et le choix des items du cahiers d&apos;observation, la mise en place des comit&#xE9;s de relecture.<br>Personnels des p&#xF4;les cliniques pour la r&#xE9;alisation pratique des &#xE9;tudes cliniques.<br>Interlocuteurs techniques, ing&#xE9;nieurs, chercheurs pour la mise en oeuvre de projets relationnels<br>Promoteurs et autorit&#xE9;s pour la gestion des modalit&#xE9;s technico-r&#xE9;glementaires des &#xE9;tudes cliniques; Attach&#xE9;s de Recherche Clinique pour le contr&#xF4;le qualit&#xE9;<br>Prestataires de services internes (pharmacie, laboratoires, plate-forme logistique, ...) et externes pour la programmation des interventions.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC+5, cursus scientifique, m&#xE9;dical, gestion ou d&apos;ing&#xE9;ni&#xE9;rie<br>Master en recherche clinique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br>Cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Chef de projets de recherche clinique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(trice) des affaires médicales (et de la recherche) (DAM)\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir et piloter la mise en &#x153;uvre de la politique m&#xE9;dicale et de gestion des ressources humaines m&#xE9;dicales dans le cadre de la strat&#xE9;gie de l&apos;&#xE9;tablissement&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Directeur(e) des ressources m&#xE9;dicales<br>Directeur(e) de la politique m&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Directeur(e) des affaires m&#xE9;dicales et de la recherche<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Coordination avec le Pr&#xE9;sident de la Commission M&#xE9;dicale d&apos;Etablissement (CME) du dispositif de permanence des soins de l&apos;&#xE9;tablissement. </li> <li>Coordination des interlocuteurs internes et externes sur le territoire de sant&#xE9; (&#xE9;tablissements de sant&#xE9;, lib&#xE9;raux, r&#xE9;seaux, associations) </li> <li>Elaboration, mise en oeuvre et &#xE9;valuation d&apos;un projet social pour les personnels m&#xE9;dicaux </li> <li>Elaboration, mise en oeuvre et &#xE9;valuation d&apos;une politique de GPEC, d&apos;attractivit&#xE9; et de fid&#xE9;lisation </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement et suivi de budget(s) relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion administrative relative au domaine d&apos;activit&#xE9; </li> <li>Gestion des contentieux, dans son domaine d&apos;activit&#xE9; </li> <li>Pr&#xE9;paration des s&#xE9;ances de la Commissions M&#xE9;dicale d&apos;Etablissement (CME) avec son pr&#xE9;sident et participation aux s&#xE9;ances. </li> <li>Production et exploitation de statistiques relevant de son domaine d&apos;activit&#xE9; </li> <li>Suivi de la mise en oeuvre op&#xE9;rationnelle du projet m&#xE9;dical de l&apos;&#xE9;tablissement </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner la mise en oeuvre du projet m&#xE9;dical d&apos;&#xE9;tablissement </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Concevoir, r&#xE9;diger et mettre en forme une communication orale et/ou &#xE9;crite </li> <li>Conduire et animer des r&#xE9;unions </li> <li>D&#xE9;finir, conduire et &#xE9;valuer la politique relative &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>G&#xE9;rer ses &#xE9;motions, son stress </li> <li>Mettre en place et exploiter de fa&#xE7;on optimale un syst&#xE8;me d&apos;information relatif &#xE0; son domaine de comp&#xE9;tence ? </li> <li>Observer et analyser la situation, le discours des diff&#xE9;rents acteurs </li> <li>S&apos;exprimer en public </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication &#xE9;crite </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication orale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit hospitalier </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Statut des personnels m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les membres de l&apos;&#xE9;quipe de direction<br>Le Pr&#xE9;sident de la CME, les m&#xE9;decins et leurs repr&#xE9;sentants<br>Les membres du Conseil d&apos;Administration<br>Les cadres<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Ecole des Hautes Etudes en Sant&#xE9; Publique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Formateur(trice) des professionnels de santé', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Former des professionnels param&#xE9;dicaux. Concevoir et organiser les conditions de leurs apprentissages en formation initiale, en veillant &#xE0; l&apos;efficacit&#xE9; et la qualit&#xE9; des prestations.<br>Organiser et r&#xE9;aliser des actions de formation continue dans des domaines li&#xE9;s aux soins, &#xE0; la sant&#xE9;, &#xE0; la p&#xE9;dagogie et au management.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Cadre p&#xE9;dagogique<br>Cadre de sant&#xE9; formateur(trice)<br>Cadre enseignant(e)<br>Formateur(trice) Institut de Formation param&#xE9;dicale (IFSI, IFMK, IFCS ...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Coordinateur(trice) de promotion, d&apos;&#xE9;quipe<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me professionnel du m&#xE9;tier enseign&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement de la personne dans son projet de formation </li> <li>Animation de la d&#xE9;marche qualit&#xE9; de la formation </li> <li>Conception, organisation, gestion et coordination des dispositifs de formation professionnelle initiale et continue, en soins et en sant&#xE9; </li> <li>Gestion de l&apos;information, des moyens et des ressources pour un dispositif de formation </li> <li>Mise en place et animation de projets </li> <li>R&#xE9;alisation de prestations d&apos;enseignement et de formation aupr&#xE8;s des &#xE9;tudiants et des professionnels en soins et en sant&#xE9; </li> <li>Veille professionnelle, &#xE9;tudes et travaux de recherches et d&apos;innovation </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner les personnes dans leur parcours de formation dans le domaine de la sant&#xE9; et des soins </li> <li>Concevoir et animer des formations initiales et continues relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir et organiser des dispositifs et actions de formation, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, r&#xE9;diger et mettre en forme une communication orale et/ou &#xE9;crite </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Conduire un projet, des travaux d&apos;&#xE9;tude et de recherche </li> <li>&#xC9;laborer un projet d&apos;accueil et de formation pour des personnes / publics divers </li> <li>&#xC9;valuer la qualit&#xE9; des prestations et s&apos;inscrire dans une d&#xE9;marche qualit&#xE9; </li> <li>Evaluer les connaissances et les compt&#xE9;nces des personnes en formation dans le domaine de la sant&#xE9; et des soins </li> <li>Organiser et coordonner les parcours de formation en alternance dans le domaine de la sant&#xE9; et des soins </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ing&#xE9;nierie de la formation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur de l&apos;institut de formation pour l&apos;&#xE9;laboration du projet p&#xE9;dagogique<br>Directeurs des soins et cadres d&apos;unit&#xE9;s pour l&apos;appr&#xE9;hension de l&apos;&#xE9;volution des soins, pour la d&#xE9;finition de l&apos;accueil et l&apos;organisation des stages<br>Intervenants ext&#xE9;rieurs, experts dans les contenus enseign&#xE9;s, pour la n&#xE9;gociation des objectifs et la planification des interventions<br>Formateurs d&apos;autres instituts de formation pour les &#xE9;changes et la coordination<br>Professionnels param&#xE9;dicaux pour le suivi des &#xE9;tudiants<br>Associations professionnelles pour des &#xE9;changes sur les &#xE9;colutions des m&#xE9;tiers et les comp&#xE9;tences de soins et de l&apos;encadrement.<br>Services de formation continue pour les propositions d&apos;actions de formation<br>Universit&#xE9; pour la construction des enseignements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formation et dipl&#xF4;me de cadres de sant&#xE9; (obligatoire)<br>Formation en sciences humaines, dont formation souhait&#xE9;e aux sciences de l&apos;&#xE9;ducation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Cadre de sant&#xE9;<br>Cadre sup&#xE9;rieur de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Cadre soignant de p&#xF4;le<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) de la sécurité des personnes et des biens', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finir, planifier et piloter l&apos;ensemble des activit&#xE9;s de pr&#xE9;vention et de s&#xE9;curit&#xE9; des personnes et des biens en animant la/les &#xE9;quipes s&#xE9;curit&#xE9; anti-malveillance (s&#xFB;ret&#xE9;) et/ou de prestataires ext&#xE9;rieurs<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) de s&#xE9;curit&#xE9; anti-malveillance<br>Chef(fe) de service pr&#xE9;vention<br>Chef(fe) de service de s&#xE9;curit&#xE9; g&#xE9;n&#xE9;rale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Habilitation SSIAP 2 ou SSIAP 3 (arr&#xEA;t&#xE9; du 2 mai 2005 modifi&#xE9;)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et mise en place du plan de pr&#xE9;vention et de traitement des risques, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Gestion des situations de violence et d&apos;atteintes &#xE0; la s&#xE9;curit&#xE9; des personnes et des biens </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Recensement / enregistrement des donn&#xE9;es / des informations li&#xE9;es &#xE0; la nature des activit&#xE9;s </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Animer et encadrer une ou plusieurs &#xE9;quipes, d&#xE9;velopper les comp&#xE9;tences de ses collaborateurs et les &#xE9;valuer </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Surveillance du fonctionnement et de la disponibilit&#xE9; des &#xE9;quipements ou syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> <li>Traiter et r&#xE9;soudre des situations agressives ou conflictuelles </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des b&#xE2;timents </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction pour la validation des activit&#xE9;s et la mise en oeuvre de la politique de pr&#xE9;vention<br>CHSCT<br>Gestionnaire des risques pour la gestion des risques et des crises<br>Responsable de service ou de secteur, pour l&apos;&#xE9;laboration des consignes de pr&#xE9;vention<br>Responsable de s&#xE9;curit&#xE9; incendie et responsable des services techniques, organisme de contr&#xF4;le et soci&#xE9;t&#xE9;s pour l&apos;entretien et la maintenance des &#xE9;quipements de surveillance<br>Police, gendarmerie, pr&#xE9;fecture pour information et coordination des interventions<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me au moins de niveau IV attestant d&apos;une qualification professionnelle dans le domaine de la s&#xE9;curit&#xE9;<br>Aptitude et exp&#xE9;riences professionnelles dans le m&#xE9;tier de la s&#xE9;curit&#xE9; des biens et des personnes (s&#xFB;ret&#xE9;)<br>Habilitation SSIAP 2 ou SSIAP 3<br>CAP, BEP, Bac Pro, BTS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable de la s&#xE9;curit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Gestionnaire comptable et achat', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>G&#xE9;rer le processus comptable de l&apos;&#xE9;mission des commandes ou / et des achats<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gestionnaire comptable<br>Gestionnaire achats<br>Gestionnaire fournisseurs<br>Gestionnaire des services &#xE9;conomiques<br>Gestionnaire des investissements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Exp&#xE9;rience en comptabilit&#xE9; publique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; des services / prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le des donn&#xE9;es / documents relatifs &#xE0; son domaine </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Information et conseil &#xE0; des tiers (agents, patients, familles, etc.) dans son domaine d&apos;activit&#xE9; </li> <li>Organisation et description des processus sp&#xE9;cifiques &#xE0; son domaine, traitement des dysfonctionnements </li> <li>Suivi et contr&#xF4;le budg&#xE9;taire relatif au domaine d&apos;activit&#xE9; </li> <li>Traitement des litiges et contentieux </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Optimiser la tr&#xE9;sorerie </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion budg&#xE9;taire </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion comptable </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Bureaux des entr&#xE9;es pour le suivi de la facturation<br>Clients ext&#xE9;rieurs de l&apos;&#xE9;tablissement pour le suivi des litiges et des cr&#xE9;ances irr&#xE9;couvrables<br>Fournisseurs pour le suivi des litiges et des d&#xE9;lais de r&#xE8;glement non respect&#xE9;s<br>Gestionnaire de comptes pour la fixation des objectifs et des directives<br>Tr&#xE9;sor public pour les &#xE9;changes d&apos;informations, la coh&#xE9;rence des comptes et le conseil<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me comptable ou formation sp&#xE9;cialis&#xE9;e dans le domaine (dipl&#xF4;me d&apos;&#xE9;tudes comptables sup&#xE9;rieures notamment)<br>licence ou master 1 ou 2<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint administratif hospitalier<br>Adjoint des cadres hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Gestionnaire finances<br>Gestionnaire admissions frais de s&#xE9;jour traitement externe<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Hygièniste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, coordonner et mettre en &#x153;uvre les actions relatives &#xE0; l&apos;hygi&#xE8;ne hospitali&#xE8;re, la pr&#xE9;vention du risque infectieux et la lutte contre les infections associ&#xE9;es aux soins.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Infirmier(&#xE8;re) hygi&#xE9;niste<br>Cadre hygi&#xE9;niste<br>Cadre conseil en hygi&#xE8;ne hospitali&#xE8;re<br>Sp&#xE9;cialiste hygi&#xE9;niste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Exercice professionnel approfondi dans les services de soins.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Appui au contr&#xF4;le du respect des r&#xE8;gles, proc&#xE9;dures, protocoles, normes et standards. </li> <li>Audit et enqu&#xEA;tes relatifs &#xE0; la pr&#xE9;vention des infections associ&#xE9;es aux soins, r&#xE9;daction des rapports, suivi des plans d&apos;actions. </li> <li>Conception, formation (initiale et continue), accompagnement et sensibilisation des professionnels et des usagers &#xE0; la pr&#xE9;vention des infections associ&#xE9;es aux soins (techniques, r&#xE8;gles et proc&#xE9;dures). </li> <li>Conseil pour l&apos;&#xE9;laboration et l&apos;actualisation des proc&#xE9;dures, protocoles ou des consignes et r&#xE8;gles relatives &#xE0; la pr&#xE9;vention des infections associ&#xE9;es aux soins. </li> <li>Elaboration de recommandations sur l&apos;organisation des circuits et la protection des personnes lors de la r&#xE9;alisation de travaux, sur la conception de b&#xE2;timents, l&apos;implantation d&apos;&#xE9;quipements, l&apos;achat de mat&#xE9;riels et de produits d&apos;hygi&#xE8;ne. </li> <li>&#xC9;laboration et r&#xE9;daction de rapports d&apos;activit&#xE9; </li> <li>Etude financi&#xE8;re dans le cadre de projets en rapport avec la pr&#xE9;vention des infections associ&#xE9;es aux soins. </li> <li>Mise en oeuvre d&apos;am&#xE9;liorations des pratiques de soins au regard de la r&#xE9;glementation et des recommandations des soci&#xE9;t&#xE9;s savantes. </li> <li>Mise en place et suivi de plans et d&apos;actions de pr&#xE9;vention. </li> <li>Renseignement des donn&#xE9;es et suivi des indicateurs nationaux et institutionnels. </li> <li>Signalement, traitement et suivi des infections associ&#xE9;es aux soins et des alertes </li> <li>Veille r&#xE9;glementaire et professionnelle et diffusion aux professionnels. </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter et convaincre dans une relation de confiance des interlocuteurs si besoin n&#xE9;gocier des accords. </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>Entreprendre toutes les actions adapt&#xE9;es en situation de crise ou d&apos;&#xE9;pid&#xE9;mie </li> <li>&#xC9;valuer la conformit&#xE9; de la mise en oeuvre d&apos;une proc&#xE9;dure/norme/r&#xE8;gle, consigne relevant de son domaine de comp&#xE9;tence </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Former et conseiller guider les choix d&apos;une personne ou d&apos;une &#xE9;quipe. </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bureautique (Excel et Word avanc&#xE9;s et bases de donn&#xE9;es) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;pid&#xE9;miologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Mat&#xE9;riels de soins et consommables. </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Microbiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions des soins et activit&#xE9;s param&#xE9;dicales et direction des travaux et des services techniques pour l&apos;int&#xE9;gration de l&apos;hygi&#xE8;ne dans leur politique (soins, investissements).<br>Sous-commission de la CME charg&#xE9;e de la lutte contre les infections nosocomiales, des m&#xE9;dicaments pour r&#xE9;pondre aux demandes.<br>Ensemble des professionnels m&#xE9;dicaux, para- m&#xE9;dicaux et en lien avec l&apos;hygi&#xE8;ne, pour am&#xE9;liorer les pratiques, adapter les proc&#xE9;dures, s&apos;assurer qu&apos;elles sont mises en &#x153;uvre, les &#xE9;valuer<br>Direction de la qualit&#xE9; et de la gestion des risques pour la coordination des orientations et actions<br>Laboratoire de microbiologie pour le suivi de l&apos;&#xE9;cologie microbienne de l&apos;&#xE9;tablissement (&#xE9;volution des r&#xE9;sistances, flore microbienne en pr&#xE9;sence)<br>Les CLIN et r&#xE9;seaux r&#xE9;gionaux pour le suivi r&#xE9;gional des alertes et pour les enqu&#xEA;tes nationales et r&#xE9;gionales<br>Services &#xE9;conomiques pour les achats de produits d&apos;entretien, de mat&#xE9;riel<br>Conseiller en pr&#xE9;vention des risques professionnels et service de sant&#xE9; au travail dans le cadre de la pr&#xE9;vention des risques professionnels (risque biologique, vaccinations)<br>Instituts de formation pour sensibiliser les professionnels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me para-m&#xE9;dicale (niv III) et compl&#xE9;ment en en hygi&#xE8;ne hospitali&#xE8;re (DU par exemple)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des infirmiers et infirmiers sp&#xE9;cialis&#xE9;s<br>Corps des cadres param&#xE9;dicaux de sant&#xE9; et cadres de sant&#xE9;<br>Technicien de laboratoire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Animateur qualit&#xE9; / gestion des risques<br>Coordinateur qualit&#xE9; / gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant (e) production culinaire / alimentaire', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier les productions alimentaires, le conditionnement, le stockage et les allotissements. Organiser et contr&#xF4;ler les r&#xE9;ceptions, la fabrication et la distribution des produits, dans le respect de la r&#xE9;glementation en vigueur.<br>Garantir l&apos;efficacit&#xE9; et la qualit&#xE9; des prestations fournies<br>Il peut g&#xE9;rer des ressources humaines, &#xE9;conomiques et techniques mises &#xE0; disposition.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) de cuisine<br>Responsable de production<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Qualiticien(ne)<br>Hazard Analysis, Critical Control<br>Point (HACCP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>D&#xE9;tention du dipl&#xF4;me correspondant,<br>exp&#xE9;rience souhaitable en encadrement dans la fili&#xE8;re restauration collective, si possible en secteur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des produits </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des activit&#xE9;s, dans son domaine </li> <li>Coordination et suivi de la prise en charge de prestations </li> <li>D&#xE9;finition et mise en place de l&apos;organisation, des structures </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recensement, identification, analyse et traitement des risques, relatifs au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;laborer et argumenter des sc&#xE9;narii/ des solutions acceptables &#xE0; des probl&#xE8;mes relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Di&#xE9;t&#xE9;tique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;quipements et technologie des mat&#xE9;riels de cuisson </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;h&#xF4;tellerie/ restauration </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques culinaires </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur restauration pour le suivi de la gestion alimentaire et les axes de progr&#xE8;s en terme d&apos;organisation<br>Encadrement di&#xE9;t&#xE9;tique pour l&apos;&#xE9;laboration et le changement de menus<br>Encadrement des services de soins pour le suivi, l&apos;&#xE9;valuation des prestations culinaires produites pour les patients<br>Service de maintenance interne ou externe pour l&apos;entretien, les d&#xE9;pannages, la planification de la maintenance pr&#xE9;ventive<br>Direction des services &#xE9;conomiques et logistiques pour la d&#xE9;finition des besoins en produits alimentaires et les remont&#xE9;es d&apos;information sur les non-conformit&#xE9;s constat&#xE9;es<br>Fournisseurs pour la pr&#xE9;sentation de nouveaux produits ou mat&#xE9;riels, traitement des non-conformit&#xE9;s, nouveaux proc&#xE9;d&#xE9;s technologiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Fili&#xE8;re d&apos;&#xE9;tudes en restauration/h&#xF4;tellerie jusqu&apos;&#xE0; BAC+3<br>BAC Pro, BTS, DUT, licence professionnelle,<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien Hospitalier<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable Restauration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Ergothérapeute', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un diagnostic et &#xE9;valuer les int&#xE9;grit&#xE9;s, les l&#xE9;sions, les capacit&#xE9;s de la personne ainsi que ses performances motrices, sensorielles, cognitives, psychiques.<br>Analyser les besoins, les habitudes de vie, les facteurs environnementaux, les situations de handicap.<br>Concevoir des environnements de mani&#xE8;re s&#xE9;curis&#xE9;e, accessible, adapt&#xE9;e, &#xE9;volutive et durable.<br>Concevoir et pr&#xE9;coniser des aides techniques et entrainer les personnes &#xE0; leur utilisation.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En ergonomie environnementale<br>En ergonomie cognitive<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;etat d&apos;ergoth&#xE9;rapeute<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Application et r&#xE9;alisation de traitements orth&#xE9;tiques et pr&#xE9;conisation d&apos;aides techniques ou animali&#xE8;res et d&apos;assistances technologiques </li> <li>Conseil, &#xE9;ducation, pr&#xE9;vention et expertise vis &#xE0; vis d&apos;une ou de plusieurs personnes, de l&apos;entourage et des institutions </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>Organisation, coordination des activit&#xE9;s en sant&#xE9; et traitement de l&apos;information </li> <li>R&#xE9;alisation de soins et d&apos;activit&#xE9;s &#xE0; vis&#xE9;e de r&#xE9;&#xE9;ducation, r&#xE9;adaptation, r&#xE9;insertion et r&#xE9;habilitation sociale </li> <li>R&#xE9;alisation et suivi de projets d&apos;am&#xE9;nagement de l&apos;environnement </li> <li>Recueil d&apos;informations, entretiens et &#xE9;valuations visant au diagnostic ergoth&#xE9;rapique </li> <li>Veille professionnelle, formation tout au long de la vie, &#xE9;tudes et recherche </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Concevoir et conduire un projet d&apos;intervention en ergoth&#xE9;rapie et d&apos;am&#xE9;nagement de l&apos;environnement </li> <li>Concevoir, r&#xE9;aliser, adapter les orth&#xE8;ses provisoires, extemporan&#xE9;es, &#xE0; vis&#xE9;e fonctionnelle ou &#xE0; vis&#xE9;e d&apos;aide technique, adapter et pr&#xE9;coniser les orth&#xE8;ses de s&#xE9;rie, les aides techniques ou animali&#xE8;res et les assistances technologiques </li> <li>Conduire une relation dans un contexte d&apos;intervention en ergoth&#xE9;rapie </li> <li>&#xC9;laborer et conduire une d&#xE9;marche d&apos;&#xE9;ducation et de conseil en ergoth&#xE9;rapie et en sant&#xE9; publique </li> <li>&#xC9;valuer et faire &#xE9;voluer la pratique professionnelle </li> <li>&#xC9;valuer une situation et &#xE9;laborer un diagnostic ergoth&#xE9;rapique </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>Mettre en oeuvre et conduire des activit&#xE9;s de soins, de r&#xE9;&#xE9;ducation, de r&#xE9;adaptation, de r&#xE9;insertion et de r&#xE9;habilitation psychosociale en ergoth&#xE9;rapie </li> <li>Organiser les activit&#xE9;s et coop&#xE9;rer avec les diff&#xE9;rents acteurs </li> <li>Rechercher, traiter et analyser des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ergonomie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ergoth&#xE9;rapie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Equipe inter-professionnelle pour la coordination des activit&#xE9;s th&#xE9;rapeutiques<br>Partenaires externes des services de maintien &#xE0; domicile pour le suivi du patient/client<br>Architectes, employeurs, pour la r&#xE9;insertion dans les milieux de vie de la personne<br>Fournisseurs de mat&#xE9;riel m&#xE9;dical pour le choix des mat&#xE9;riels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;etat d&apos;ergoth&#xE9;rapeute<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ergoth&#xE9;rapeute<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Coordinateur de parcours en sant&#xE9;<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Menuisier - agenceur', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des travaux de maintenance, d&apos;entretien et de r&#xE9;novation des ouvrages en bois, d&#xE9;riv&#xE9;s et mat&#xE9;riaux de synth&#xE8;se, de fabrication et d&apos;am&#xE9;nagement des ouvrages menuis&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Poseur de cloison<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Assemblage de pi&#xE8;ces menuis&#xE9;es ( montage, assemblage, finition) </li> <li>Conception de l&apos;ouvrage en bois, d&#xE9;riv&#xE9;s et mat&#xE9;riaux de synth&#xE8;se &#xE0; r&#xE9;aliser (plan...) </li> <li>Installation d&apos;ouvrages menuis&#xE9;s et d&apos;&#xE9;quipements li&#xE9;s &#xE0; l&apos;agencement de pi&#xE8;ces </li> <li>Mise en place de protections de chantier et / ou individuelles </li> <li>Nettoyage et entretien des &#xE9;quipements, machines, outillages, v&#xE9;hicules sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Pose de cloisons, de rev&#xEA;tements, de protections murales </li> <li>R&#xE9;alisation de travaux de menuiserie (usinage de pi&#xE8;ces sur machine &#xE0; bois...) </li> <li>R&#xE9;alisation des maquettes, dans son domaine de comp&#xE9;tence </li> <li>Renseignement de documents, de fichiers (fiches d&apos;activit&#xE9;, de tra&#xE7;abilit&#xE9;, etc.) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements, outillages, dans son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer l&apos;&#xE9;tat d&apos;un mat&#xE9;riau, d&apos;un produit </li> <li>&#xC9;valuer l&apos;&#xE9;tat d&apos;un ouvrage, relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la r&#xE9;sistance m&#xE9;canique appropri&#xE9;e &#xE0; l&apos;ouvrage r&#xE9;alis&#xE9; </li> <li>&#xC9;valuer, choisir, utiliser les produits et/ou mat&#xE9;riaux n&#xE9;cessaire &#xE0; la pratique de son m&#xE9;tier </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Utiliser des mat&#xE9;riels, des outils de d&#xE9;pannage, de r&#xE9;paration ou de maintenance, relatifs &#xE0; son m&#xE9;tier </li> <li>Utiliser des mat&#xE9;riels, des outils de diagnostic, de travail ou/et de contr&#xF4;le sp&#xE9;cifiques &#xE0; son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bois et d&#xE9;riv&#xE9;s et mat&#xE9;riaux de synth&#xE8;se </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Calcul, g&#xE9;om&#xE9;trie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Dessin en b&#xE2;timent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Machines et outillages de menuiserie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Menuiserie manuelle / machine </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable maintenance TCE pour la programmation du travail et la prise de d&#xE9;cision en mati&#xE8;re de r&#xE9;paration<br>Magasinier pour l&apos;approvisionnement en produits<br>Encadrement des services pour la programmation et le d&#xE9;roulement des travaux<br>Fournisseurs ext&#xE9;rieurs pour le choix et la r&#xE9;ception des mat&#xE9;riaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Certificat d&apos;aptitude professionnelle (CAP) ou brevet d&apos;&#xE9;tudes professionnelles (BEP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de maintenance g&#xE9;n&#xE9;rale des b&#xE2;timents<br>Encadrant maintenance corps d&apos;&#xE9;tat secondaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant (e) Socio-éducatif', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser, piloter et contr&#xF4;ler les r&#xE9;alisations d&apos;une &#xE9;quipe m&#xE9;dico-socio-&#xE9;ducative ou m&#xE9;dico-sociale pluridisciplinaire dans le cadre du projet de service<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Educateur(trice) chef<br>cadre socio-&#xE9;ducatif(ve)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Certificat d&apos;aptitude aux fonctions d&apos;encadrement et de responsable d&apos;<br>unit&#xE9; d&apos;intervention sociale (CAFERUIS)<br>Dipl&#xF4;me socio-&#xE9;ducatif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil des personnes (patients, usagers, familles, etc.) </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Coordination et suivi de la prise en charge de prestations </li> <li>&#xC9;laboration et r&#xE9;daction de projets individuels </li> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Intervention sociale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions, pour l&apos;&#xE9;laboration et le suivi de projets<br>R&#xE9;seau pour le suivi des projets individuels<br>Familles pour l&apos;accueil des personnes prises en charge<br>Responsables fonctionnels (ressources humaines, finances, logistiques, etc.) pour le fonctionnement de l&apos;unit&#xE9; au quotidien<br>Autres services de soins, m&#xE9;dico-technique, logistiques, administratif pour l&apos;organisation des activit&#xE9;s et des soins et pour la gestion des mat&#xE9;riels<br>Partenaires ext&#xE9;rieurs pour la continuit&#xE9; des prestations<br>Fournisseurs externes de mat&#xE9;riel pour la veille technique et le choix des moyens<br>Services de formation continue pour organiser des formations adapt&#xE9;es aux besoins des personnels<br>Instituts de formation pour l&apos;accueil et l&apos;encadrement des stagiaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Caferuis<br>Dipl&#xF4;me de niveau III<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Cadre socio-&#xE9;ducatif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable socio-&#xE9;ducatif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN ATTENTE DU PROJET DE LOI EN SANTE: \"Infirmier(ère) clinicien(ne) / Paramédical en pratique avancée\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>FICHE METIER EN ATTENTE DU PROJET DE LOI EN SANTE<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>FICHE METIER EN ATTENTE DU PROJET DE LOI EN SANTE<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>FICHE METIER EN ATTENTE DU PROJET DE LOI EN SANTE<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>FICHE METIER EN ATTENTE DU PROJET DE LOI EN SANTE<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(trice) de la communication et des relations avec les usagers\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire:<br>&quot;Elaborer la strat&#xE9;gie de communication interne et externe.<br>D&#xE9;finir et organiser un syst&#xE8;me de remont&#xE9;e d&apos;informations permettant de conna&#xEE;tre le degr&#xE9; de satisfaction du public.&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(e) des systèmes d''information (DSI)\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir la strat&#xE9;gie du syst&#xE8;me d&apos;information (SI), piloter les activit&#xE9;s de conception, de mise en oeuvre et de maintenance du syst&#xE8;me d&apos;information (qualit&#xE9; / s&#xE9;curit&#xE9;, co&#xFB;ts et performances), et l&apos;ensemble des moyens correspondants.<br>Evaluer la performance du SI , en animant une &#xE9;quipe de collaborateurs et en assurant la mise en coh&#xE9;rence continue du SI avec la strat&#xE9;gie de l&apos;&#xE9;tablissement.&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable de service informatique (RSI)<br>Directeur(e) informatique<br>Directeur(e) des Syst&#xE8;mes d&apos;Information et d&apos;organisation (DSIO)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Certification des SI (ex : norme ISO) de l&apos;&#xE9;tablissement </li> <li>Contr&#xF4;le de la coh&#xE9;rence globale des projets par rapport au sch&#xE9;ma directeur des syst&#xE8;mes d&apos;information (SDSI) avec les interlocuteurs internes et externes </li> <li>Coordination des acteurs du projet avec les diff&#xE9;rentes parties prenantes </li> <li>D&#xE9;finition des orientations strat&#xE9;giques et de la politique SI de l&apos;&#xE9;tablissement, et des indicateurs associ&#xE9;s, Animation des instances de gouvernance SI </li> <li>Elaboration et mise en oeuvre du sch&#xE9;ma directeur des syst&#xE8;mes d&apos;information (SD-SI) &#xE0; partir de la strat&#xE9;gie </li> <li>Information sur le SI et ses &#xE9;volutions, en coh&#xE9;rence avec le plan de conduite du changement d&#xE9;fini </li> <li>Mise en &#x153;uvre, suivi et contr&#xF4;le des moyens et des activit&#xE9;s du SDSI (humains, financiers, techniques...) de l&apos;&#xE9;tablissement </li> <li>Pr&#xE9;paration et suivi budg&#xE9;taire des ex&#xE9;cutions et proc&#xE9;dures march&#xE9; </li> <li>Suivi des &#xE9;volutions r&#xE9;glementaires et technologiques du SI </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et planifier une organisation, des structures de gouvernance du projet, en fonction de diff&#xE9;rents crit&#xE8;res (missions, strat&#xE9;gie, moyens, etc.) </li> <li>Construire et utiliser des outils de pilotage (crit&#xE8;res, indicateurs / tableau de bord) </li> <li>D&#xE9;cliner la strat&#xE9;gie SI en orientations, en plans d&apos;actions et en moyens de r&#xE9;alisation (&#xE9;volution des m&#xE9;tiers, formation, communication, ...) </li> <li>&#xC9;laborer une strat&#xE9;gie de recherche et de veille d&apos;informations en contexte professionnel </li> <li>Evaluer et analyser les risques SI en se basant sur les r&#xE9;sultats des outils de pilotage </li> <li>Evaluer, d&#xE9;velopper, motiver et favoriser le d&#xE9;veloppement des comp&#xE9;tences professionnelles de ses collaborateurs </li> <li>Identifier, analyser l&apos;environnement SI en vue d&apos;optimiser le Sch&#xE9;ma Directeur des Syst&#xE8;mes d&apos;Information (SDSI) </li> <li>N&#xE9;gocier des prestations, des contrats, des accords avec des interlocuteurs internes/externes </li> <li>Planifier, g&#xE9;rer, coordonner et adapter les moyens mis &#xE0; disposition (ressources financi&#xE8;res, humaines, techniques, documentations...) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Applications m&#xE9;tier/logiciels (&#xE9;tablissement) </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Architecture SI m&#xE9;tier et fonctionnelle </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Informatique et SI (y compris march&#xE9; des solutions informatiques) </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement, conduite du changement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Outils et concepts de la qualit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Proc&#xE9;dures d&apos;habilitation en vigueur, Standards et normes applicables et protection des donn&#xE9;es </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; informatique, s&#xE9;curit&#xE9; SI </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie SI </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Tous les m&#xE9;tiers DSI + tous les directeurs + CME<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me en informatique BAC + 5 (ing&#xE9;nieur, Master Pro), corps de direction de l&apos;EHESP<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur / Directeur d&apos;h&#xF4;pital<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) installations et maintenance équipements sanitaires, thermiques', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter l&apos;activit&#xE9; des mat&#xE9;riels et &#xE9;quipements sanitaires et thermiques, en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies, soit en animant une &#xE9;quipe de professionnels, soit par le biais de prestataires ext&#xE9;rieurs.<br>Il peut avoir aussi &#xE0; g&#xE9;rer et maintenir la distribution, &#xE0; g&#xE9;rer les conditions de stockage des fluides m&#xE9;dicaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable d&apos;atelier de plomberie<br>Responsable d&apos;atelier thermique<br>Responsable d&apos;atelier sanitaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Administrateur(trice) GTC GTB<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Audit et expertise des &#xE9;quipements / installations / syst&#xE8;mes (fonctionnement, conformit&#xE9;, efficacit&#xE9; et performances) sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; des r&#xE9;sultats au regard du cahier des charges, des sp&#xE9;cifications, co&#xFB;ts, performances, d&#xE9;lais... </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Entretien, maintenance, pr&#xE9;vention et d&#xE9;pannage des mat&#xE9;riels, outillages, &#xE9;quipements, syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement de cahier des charges, relatif &#xE0; la probl&#xE9;matique client </li> <li>&#xC9;tablissement et r&#xE9;daction de protocoles de maintenance et de contr&#xF4;le, dans son domaine </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Lancement, suivi et contr&#xF4;le des r&#xE9;alisations en r&#xE9;gie ou sous-trait&#xE9;es, relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Mise en place du carnet sanitaire </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Suivi de la r&#xE9;alisation des travaux, de la r&#xE9;solution des probl&#xE8;mes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Lire et interpr&#xE9;ter des plans techniques, des croquis relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier et coordonner des travaux et / ou des interventions (maintenance, entretien et /ou installation de mat&#xE9;riels, outils, &#xE9;quipements, syst&#xE8;mes...) </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Automatisme </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Climatique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Fluides m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Maintenance industrielle / &#xE9;quipements </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes techniques de s&#xE9;curit&#xE9; des installations sanitaires </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des b&#xE2;timents </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Thermique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Gestionnaire de maintenance pour les prises de d&#xE9;sision<br>Responsables de maintenance des autres domaines pour l&apos;uniformisation des processus et des proc&#xE9;dures<br>Cadres de soins pour l&apos;analyse des besoins<br>Prestataires ext&#xE9;rieurs (entreprises, bureaux d&apos;&#xE9;tudes, organismes de contr&#xF4;le technique en lien avec l&apos;activit&#xE9;)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAP, BEP<br>BAC ou BAC +2 thermique climatique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Corps des techniciens et techniciens sup&#xE9;rieurs hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Conducteur de travaux tous corps d&apos;&#xE9;tat (TCE)<br>Responsable de la maintenance tous corps d&apos;&#xE9;tat (TCE)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(trice) de la logistique et des achats\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir et piloter la mise en &#x153;uvre de la politique logistique / achats dans le cadre de la strat&#xE9;gie de l&apos;&#xE9;tablissement.&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Directeur(e) des services &#xE9;conomiques<br>Directeur(e) des affaires &#xE9;conomiques (et logistiques)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir un plan / programme d&apos;investissement </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logistique g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des b&#xE2;timents </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction pour la d&#xE9;cision sur les programmes de travaux et d&apos;&#xE9;quipements et leur financement.<br>Responsable des achats pour la formalisation et l&apos;ex&#xE9;cutrion des commandes<br>Personnels d&apos;encadrement pour la d&#xE9;finition des besoins et le suivi du fonctionnement<br>Services d&#xE9;partementaux et communaux pour les autorisations administratives<br>Concessionnaires publics et priv&#xE9;s pour la gestion des raccordements aux diff&#xE9;rents r&#xE9;seaux<br>Intervenants externes pour r&#xE9;aliser des prestations<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formations de niveaux III, II ou I (BTS, DUT, ma&#xEE;trise des sciences et techniques, dipl&#xF4;me d&apos;ing&#xE9;nieur, troisi&#xE8;me cycle de l&apos;enseignement sup&#xE9;rieur), compl&#xE9;t&#xE9;es par une formation sp&#xE9;cifique achats ou gestion.<br>L&apos;acc&#xE8;s est &#xE9;galement possible avec un dipl&#xF4;me d&apos;une &#xE9;cole sup&#xE9;rieure de commerce, &#xE9;cole de gestion, de l&apos;ecole sup&#xE9;rieure d&apos;approvisionnement, de l&apos;institut d&apos;administration des entreprises, ou encore avec un BTS ou un DUT en gestion, compl&#xE9;t&#xE9;s par une formation technique aux produits achet&#xE9;s et fabriqu&#xE9;s.<br>Le DUT gestion logistique et transport et le BTS transport peuvent suffire pour occuper un poste d&apos;assistant. Quelques ann&#xE9;es d&apos;exp&#xE9;rience professionnelle sont alors n&#xE9;cessaires avant de pouvoir acc&#xE9;der &#xE0; un poste de responsable dans une entreprise de transport.<br>Pour les plus jeunes qui s&apos;int&#xE9;ressent au transport tr&#xE8;s t&#xF4;t il existe des bacs professionnels logistique et exploitation des transports.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien superieur hospitalier<br>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Expert (e) technique (réseau, système, BDD,...)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Conduire et r&#xE9;aliser les &#xE9;tudes d&apos;architecture technique du syst&#xE8;me d&apos;information en garantissant la coh&#xE9;rence de l&apos;ensemble des moyens informatiques (mat&#xE9;riels, applicatifs, bases de donn&#xE9;es, r&#xE9;seaux, middleware, syst&#xE8;me d&apos;exploitation) et de leur &#xE9;volution.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Syst&#xE8;me exploitation / R&#xE9;seau/T&#xE9;l&#xE9;com / Architecture de service (messagerie, mobilit&#xE9;, .) / Base de donn&#xE9;es / Visio<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Analyse, veille et prospection du march&#xE9; des fournisseurs informatiques </li> <li>D&#xE9;finition des r&#xE8;gles de gestion de la documentation technique et mise en &#x153;uvre </li> <li>D&#xE9;finition et conception technique : r&#xE9;daction des sp&#xE9;cifications techniques (standard) et des interfaces contribuant au cahier des charges et/ou &#xE0; l&apos;&#xE9;laboration du CCTP </li> <li>Identification, &#xE9;tude et validation des impacts (technologiques, organisationnels, fonctionnels et financiers) li&#xE9;s au projet </li> <li>Initialisation, adaptation, r&#xE9;alisation du param&#xE9;trage, reprise de donn&#xE9;es et int&#xE9;gration du SI </li> <li>Pilotage de l&apos;exploitation des syst&#xE8;mes, r&#xE9;seaux et applications </li> <li>Planification, pr&#xE9;paration, installation et configuration des mat&#xE9;riels (informatique, t&#xE9;l&#xE9;phonie, visio), syst&#xE8;mes, r&#xE9;seaux et applications </li> <li>R&#xE9;daction, mise &#xE0; disposition et mise &#xE0; jour de la documentation </li> <li>Suivi des &#xE9;volutions technologiques du SI </li> <li>Tests et analyses des performances des syst&#xE8;mes, r&#xE9;seaux et applications </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et &#xE9;valuer les prestations SI (r&#xE9;seaux, applications, BDD,...) de service internes et externes et enclencher les actions cons&#xE9;cutives n&#xE9;cessaires </li> <li>Analyser les besoins d&apos;adaptation du SI, formuler et &#xE9;valuer la configuration cible de la solution et son dimensionnement </li> <li>Construire et utiliser des outils de pilotage (crit&#xE8;res, indicateurs / tableau de bord) </li> <li>D&#xE9;finir et communiquer aux fournisseurs les conditions d&apos;intervention (plan de pr&#xE9;vention de l&apos;&#xE9;tablissement et sp&#xE9;cificit&#xE9;s de la DSI) </li> <li>Elaborer des sc&#xE9;narios de reprise de donn&#xE9;e, et proc&#xE9;dures de secours en cas de d&#xE9;faillance de la solution et assurer la sauvegarde des BDD </li> <li>Exploiter les donn&#xE9;es des outils informatiques ou applications m&#xE9;tier/logiciels </li> <li>Identifier, s&#xE9;lectionner, exploiter et capitaliser les informations dans le cadre de la veille professionnelle </li> <li>Installer, configurer, param&#xE9;trer et tester un syst&#xE8;me et/ou un logiciel et/ou un composant et/ou un poste de travail </li> <li>Mettre en &#x153;uvre les r&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es </li> <li>R&#xE9;diger et garantir la diffusion de la documentation technique </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Architecture SI m&#xE9;tier et fonctionnelle </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Documentation technique fournie par l&apos;&#xE9;diteur ou l&apos;int&#xE9;grateur et archivage </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des donn&#xE9;es informatiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Informatique, SI et veille technologique </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">March&#xE9; des solutions informatiques (technologie, produit et offre) </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation, m&#xE9;tiers et fonctionnement de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Outils-Applications m&#xE9;tier/logiciels n&#xE9;cessaires au fonctionnement m&#xE9;tier et &#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;seaux et BDD </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; informatique, s&#xE9;curit&#xE9; SI </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Architecte fonctionnel pour prendre en compte la qualit&#xE9; de service attendue sur les applications informatiques / chef(fe) de projet pour information r&#xE9;ciproque et l&apos;application de proc&#xE9;dures informatiques / Architecte s&#xE9;curit&#xE9; informatique pour information et d&#xE9;finition de la politique s&#xE9;curit&#xE9; / Direction du syst&#xE8;me d&apos;information (DSI) pour les directives &#xE0; appliquer et &#xE9;change sur les pr&#xE9;conisations du syst&#xE8;me d&apos;information / Direction pour l&apos;information et le conseil sur l&apos;&#xE9;volution technique du S.I. / Fournisseurs pour consultation et r&#xE9;ponse aux besoins d&apos;&#xE9;quipements informatiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me sup&#xE9;rieur dans le domaine informatique ou syst&#xE8;me d&apos;information (Bac + 5), Ing&#xE9;nieur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Chef de projet informatique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(trice) de la qualité (et de la gestion des risques)\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir et piloter la mise en &#x153;uvre de la politique qualit&#xE9;-gestion des risques dans le cadre de la strat&#xE9;gie de l&apos;&#xE9;tablissement&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Directeur(e) des relations avec les usagers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) de stérilisation', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, coordonner et mettre en oeuvre les actions relatives &#xE0; la st&#xE9;rilisation des mat&#xE9;riels et installations, en encadrant une &#xE9;quipe pluri-professionnelle<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la conformit&#xE9; des services / prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi du bon d&#xE9;roulement du (des) process sp&#xE9;cifique(s) au domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Formation de personnes aux techniques et proc&#xE9;dures de son domaine, et &#xE0; leur application </li> <li>Gestion des fluides (eau, gaz, air) &#xE9;nergies utilis&#xE9;es (&#xE9;lectricit&#xE9;) et produits lessiviels (analyse, contr&#xF4;le, suivi) </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Gestion et suivi de la maintenance du mat&#xE9;riel bio-m&#xE9;dical, autoclave </li> <li>Identification, analyse et validation des impacts (technologiques, organisationnels, fonctionnels et financiers) li&#xE9;s &#xE0; des nouveaux projets </li> <li>Planification, r&#xE9;organisation des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter et optimiser le fonctionnement des &#xE9;quipements, des installations, des syst&#xE8;mes, des m&#xE9;thodes de travail dans son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la conformit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riel, d&apos;une prestation au regard des normes internes/externes </li> <li>&#xC9;valuer la satisfaction des patients / des clients / des personnels </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Identifier et analyser les &#xE9;volutions et leurs impacts sur les activit&#xE9;s, les syst&#xE8;mes relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bonnes pratiques de st&#xE9;rilisation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la pharmacie/ st&#xE9;rilisation </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques / assurance qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Pharmacie car l&apos;activit&#xE9; de st&#xE9;rilisation est sous la responsabilit&#xE9; du pharmacien de la Pharmacie &#xE0; usage int&#xE9;rieur (PUI)<br>Services clients (soins, bloc op&#xE9;ratoire, m&#xE9;dico-techniques) pour conna&#xEE;tre leurs besoins, adapter les fonctionnements du service (relation client-fournisseur)<br>services techniques et biom&#xE9;dicaux pour la maintenance pr&#xE9;ventive et curative des installations<br>services &#xE9;conomiques, logistiques pour l&apos;am&#xE9;lioration des flux de mat&#xE9;riels<br>service d&apos;hygi&#xE8;ne hospitali&#xE8;re, pharmacie pour leur expertise technique sur le domaine<br>CHS-CT<br>CLIN<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>A partir d&apos;un dipl&#xF4;me de niveau IV et exp&#xE9;rience professionnelle dans le domaine de l&apos;hygi&#xE8;ne et de la st&#xE9;rilisation<br>Dipl&#xF4;me de niveau III<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des infirmiers et infirmiers sp&#xE9;cialis&#xE9;s, grade IBODE<br>Cadres param&#xE9;dicaux en sant&#xE9; et cadre de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant bio-nettoyage<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Infirmier(ère) perfusionniste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, r&#xE9;aliser, surveiller la circulation extracorporelle (h&#xE9;matose, l&apos;irrigation tissulaire, surveillance biologique) et la &quot;situation&quot; h&#xE9;modynamique du patient ainsi que le fonctionnement de l&apos;appareil au cours de la circulation extracorporelle.<br>Assurer une assistance cardio-circulatoire et/ou respiratoire de courte et longue dur&#xE9;e<br>Peut-&#xEA;tre amen&#xE9; &#xE0; exercer au sein de son &#xE9;tablissement ou d&apos;autres structures.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Infirmier(&#xE8;re) en assistance circulatoire,<br>Cardio-perfusionniste,<br>Technicien(ne) de circulation extra-corporelle<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Transport m&#xE9;dicalis&#xE9; d&apos;un patient sous assistance circulatoire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat Infirmier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le, tra&#xE7;abilit&#xE9;, pr&#xE9;paration de l&apos;environnement du patient dans son domaine d&apos;activit&#xE9; </li> <li>Coordination de l&apos;entretien des machines, gestion du mat&#xE9;riel et d&#xE9;veloppement des techniques utilis&#xE9;es, en lien avec l&apos;&#xE9;quipe m&#xE9;dicale et bio-m&#xE9;dicale </li> <li>Evaluation des pratiques et veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Information et formation des professionnels de sant&#xE9; des services en lien avec le domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation de mesures de qualit&#xE9; et de s&#xE9;curit&#xE9; (contr&#xF4;le, tra&#xE7;abilit&#xE9;, vigilance) dans son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation, organisation des soins aupr&#xE8;s des patients dans son domaine d&apos;activit&#xE9; </li> <li>Recueil d&apos;information, pr&#xE9;paration des param&#xE8;tres et &#xE9;quipements en vue de la prise en charge du patient dans son domaine d&apos;activit&#xE9; </li> <li>Surveillance des fonctions vitales du patient dans son domaine d&apos;activit&#xE9; </li> <li>Transmission des informations et tra&#xE7;abilit&#xE9; de soins et activit&#xE9;s </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser les conditions de qualit&#xE9; et s&#xE9;curit&#xE9; et d&#xE9;terminer si n&#xE9;cessaire la mise en place de mesure appropri&#xE9;e pour une prise en charge optimale du patient </li> <li>Analyser, adapter son comportement et sa pratique professionnelle notamment face &#xE0; des situations d&apos;urgence dans son domaine de comp&#xE9;tence. </li> <li>Concevoir et mettre en oeuvre des modes de prise en charge des personnes, adapt&#xE9;es aux techniques choisies et aux situations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Concevoir et mettre en &#x153;uvre des formations dans son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;aliser les diff&#xE9;rentes &#xE9;tapes de la CEC </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie, biocompatibilit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion du stress </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re et Pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation du travail </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">St&#xE9;rilisation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques et organisation des soins en CEC et assistance cardio-circulatoire et/ou respiratoire </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Chirurgiens, anesth&#xE9;sistes-r&#xE9;animateur, infirmiers-anesth&#xE9;sistes, infirmiers de bloc op&#xE9;ratoire, infirmiers de r&#xE9;animation et autres professionnels param&#xE9;dicaux pour la collaboration et la continuit&#xE9; des soins et h&#xE9;movigilance<br>Services bio-m&#xE9;dicaux pour l&apos;entretien et le renouvellement du mat&#xE9;riel m&#xE9;dical,<br>Pharmaciens et personnels de st&#xE9;rilisation pour le traitement et la gestion des dispositifs m&#xE9;dicaux st&#xE9;riles,<br>Hygi&#xE9;nistes pour l&apos;adaptation des proc&#xE9;dures d&apos;hygi&#xE8;ne dans le domaine d&apos;activit&#xE9;,<br>Laboratoires industriels / fournisseurs pour la formation et l&apos;&#xE9;volution des techniques.<br>Laboratoires de biologie m&#xE9;dicale pour analyse des produits biologiques<br>Fournisseurs de produits sanguins labiles pour transfusion<br>Personnels de recherche clinique pour les &#xE9;tudes et recherche dans son domaine d&apos;activit&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;mes universitaires dans le domaine de la circulation extra-corporelle et l&apos;assistance cardio-circulatoire et/ou respiratoire (DU assistance circulatoire, DU circulation extracorporelle, accr&#xE9;ditation, European Board of cardiovascular perfusion,&#xFFFD;&#x20AC;&#xFFFD;)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des infirmiers en soins g&#xE9;n&#xE9;raux et sp&#xE9;cialis&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Infirmier de bloc op&#xE9;ratoire en chirurgie cardiaque<br>Infirmier en anesth&#xE9;sie- r&#xE9;animation en chirurgie cardiaque<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br>Formateur des professionnels de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(trice) des soins (DS) en établissement\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir et piloter la mise en &#x153;uvre de la politique des soins dans le cadre de la strat&#xE9;gie de l&apos;&#xE9;tablissement&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Coordonnateur(trice) g&#xE9;n&#xE9;ral(e) des soins<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Les dipl&#xF4;mes pr&#xE9;cit&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Conseil p&#xE9;dagogique aupr&#xE8;s des personnels / utilisateurs / usagers dans son domaine </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et r&#xE9;daction de rapports d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Conduire et animer des r&#xE9;unions </li> <li>D&#xE9;finir, conduire et &#xE9;valuer la politique relative &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>S&apos;exprimer en public </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit hospitalier </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion budg&#xE9;taire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Sociologie des organisations </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur et membres de l&apos;&#xE9;quipe de direction dans le cadre des orientations politiques et strat&#xE9;giques,<br>Pr&#xE9;sident de la CME et membres du corps m&#xE9;dical dans le cadre des projets, des organisations et des activit&#xE9;s dans la prise en charge des patients,<br>Les responsables de p&#xF4;les, le conseil des p&#xF4;les dans le cadre de l&apos;&#xE9;laboration, la mise en oeuvre et &#xE9;valuation de la contractualisation<br>Directeurs des activit&#xE9;s de formation aux m&#xE9;tiers de soin dans le cadre de l&apos;organisation des stages et de l&apos;&#xE9;volution des pratiques professionnelles<br>Partenaires sociaux dans le cadre du dialogue social, des conditions de travail des personnels, des organisations et des &#xE9;volutions professionnelles,<br>Service de m&#xE9;decine et sant&#xE9; au travail dans le cadre des conditions de travail et de l&apos;am&#xE9;nagement des postes pour les personnels<br>Ensemble des acteurs du territoire de sant&#xE9; dans le cadre de la coordination de l&apos;offre de soins<br>Conseiller technique et p&#xE9;dagogique r&#xE9;gional et national en soins<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me professionnel,<br>Dipl&#xF4;me d&apos;&#xE9;tat,<br>Brevet professionnel permettant l&apos;exercice dans l&apos;une des professions de sant&#xE9;<br>Dipl&#xF4;me cadre de sant&#xE9;, certificat de validation du cycle de formation de l&apos;E.H.E.S.P.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Directeur de soins<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant (e) de département d''information médicale', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter les activit&#xE9;s de recueil et de traitement des donn&#xE9;es de soins, en animant une &#xE9;quipe de professionnels et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Cadre administratif de p&#xF4;le<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) maintenance corps d''état secondaires', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter les activit&#xE9;s de maintenance de corps d&apos;&#xE9;tat secondaire (ma&#xE7;onnerie, serrurerie, menuiserie, rev&#xEA;tements de mur et de sol ,...) et voirie r&#xE9;seaux divers (VRD) soit en animant une &#xE9;quipe de professionnels, soit par le biais de prestataires ext&#xE9;rieurs<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable d&apos;ateliers<br>Encadrant(e) de maintenance des b&#xE2;timents<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Achat de produits / de prestations, dans son domaine </li> <li>Contr&#xF4;le de la conformit&#xE9; des r&#xE9;sultats au regard du cahier des charges, des sp&#xE9;cifications, co&#xFB;ts, performances, d&#xE9;lais... </li> <li>Contr&#xF4;le et suivi de l&apos;&#xE9;tat et du fonctionnement des &#xE9;quipements / des installations, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Organisation du syst&#xE8;me de management de la qualit&#xE9; </li> <li>Planification des activit&#xE9;s / interventions internes/externes, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Utilisation de la Gestion de maintenance Assist&#xE9;e par Ordinateur (GMAO) dont il est garant de la qualit&#xE9; des donn&#xE9;es qui y sont saisies </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter et optimiser le fonctionnement des &#xE9;quipements, des installations, des syst&#xE8;mes, des m&#xE9;thodes de travail dans son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer le niveau de comp&#xE9;tence n&#xE9;cessaire &#xE0; des interventions techniques, relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer une charge de travail </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Identifier, &#xE9;valuer, choisir des mat&#xE9;riels, des outils adapt&#xE9;s &#xE0; la r&#xE9;alisation des activit&#xE9;s de son domaine </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier et coordonner des travaux et / ou des interventions (maintenance, entretien et /ou installation de mat&#xE9;riels, outils, &#xE9;quipements, syst&#xE8;mes...) </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de maintenance technique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Maintenance industrielle / &#xE9;quipements </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des b&#xE2;timents </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">TCE / VRD </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Gestionnaire de maintenance pour les prises de d&#xE9;cision<br>Responsables de maintenance des autres domaines pour l&apos;unifrmisation des processus et des proc&#xE9;dures<br>Cadres de soins pour l&apos;analyse des besoins<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAP, BEP<br>BAC<br>Gestion de la maintenance des b&#xE2;timents<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Technicien hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Conducteur de travaux tous corps d&apos;&#xE9;tat (TCE)<br>Responsable de la maintenance tous corps d&apos;&#xE9;tat (TCE)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Intervenant(e) / Support Informatique utilisateurs', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Fournir le support et l&apos;assistance de proximit&#xE9; aux utilisateurs des syst&#xE8;mes d&apos;information pour tout ce qui concerne les postes de travail et les applications informatiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Soutien &#xE0; distance (Hot-liner)<br>Technicien(ne) poste de travail<br>Support technique<br>Technicien(ne) service desk<br>Assistant(e) informatique utilisateurs<br>Technicien(ne) d&apos;intervention<br>Assistant(e) centre de service<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Support technique<br>Support applicatif<br>Support bureautique<br>T&#xE9;l&#xE9;phonie<br>Visio,...<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Capitalisation de l&apos;ensemble de la documentation sur le SI utilisateur </li> <li>Elaboration et gestion du catalogue de services </li> <li>Enregistrement et suivi des demandes d&apos;assistance des utilisateurs relatives aux mat&#xE9;riels et applications </li> <li>Identification, diagnostic et qualification des demandes d&apos;assistance des utilisateurs relatives aux mat&#xE9;riels (informatique, t&#xE9;l&#xE9;phonie, visio) et applications </li> <li>Information des utilisateurs aux probl&#xE8;mes de s&#xE9;curit&#xE9; (sauvegarde, virus, confidentialit&#xE9; des donn&#xE9;es...) </li> <li>Intervention cons&#xE9;cutive aux demandes d&apos;assistance (sur le terrain ou &#xE0; distance) </li> <li>Organisation de l&apos;assistance utilisateur </li> <li>Organisation du transfert de comp&#xE9;tence entre l&apos;&#xE9;quipe projet et l&apos;&#xE9;quipe de maintien en condition op&#xE9;rationnelle (MCO) </li> <li>Planification, pr&#xE9;paration, installation et configuration des mat&#xE9;riels (informatique, t&#xE9;l&#xE9;phonie, visio), syst&#xE8;mes, r&#xE9;seaux et applications </li> <li>R&#xE9;daction, mise &#xE0; disposition et mise &#xE0; jour de la documentation utilisateur </li> <li>Test des mises &#xE0; jour ou correction (y compris de non r&#xE9;gression) avant la mise en production </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter et mettre &#xE0; jour la documentation utilisateur (proc&#xE9;dure, manuel utilisateurs, ...) </li> <li>Analyser, exploiter les donn&#xE9;es des outils informatiques basiques ou applications m&#xE9;tier/logiciels et justifier des r&#xE9;sultats </li> <li>Appliquer les r&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es </li> <li>Diagnostiquer les dysfonctionnements, les pannes, les incidents r&#xE9;seau, le d&#xE9;faut des mat&#xE9;riaux, des &#xE9;quipements et des applications </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Installer et utiliser les outils d&apos;assistance pour un tra&#xE7;age syst&#xE9;matique des sources d&apos;erreur ou de d&#xE9;faillance technique </li> <li>Installer, configurer, param&#xE9;trer et tester un syst&#xE8;me et/ou un logiciel et/ou un composant et/ou un poste de travail </li> <li>Interagir avec les utilisateurs afin de d&#xE9;terminer les dysfonctionnements et les orienter vers le bon niveau de prise en charge </li> <li>Maintenir et entretenir le parc des syst&#xE8;mes d&apos;information (mat&#xE9;riel, logiciel, composant syst&#xE8;mes,...) </li> <li>Planifier et coordonner des travaux et / ou des interventions (maintenance, entretien et /ou installation de mat&#xE9;riels, outils, &#xE9;quipements, syst&#xE8;mes...) </li> <li>Traduire et formuler les demandes ou besoins utilisateurs en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes et en &#xE9;ch&#xE9;ancier d&apos;intervention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Architecture SI m&#xE9;tier et fonctionnelle </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion de parc informatique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Informatique / Syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation, m&#xE9;tiers et fonctionnement de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Outils de diagnostic et d&apos;investiguation des incidents </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Proc&#xE9;dures internes de remont&#xE9;e des demandes d&apos;assistance des utilisateurs, d&apos;information et d&apos;incidents critiques ou non </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Produits et march&#xE9; informatiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Standards et normes applicables </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et Politique S&#xE9;curit&#xE9; de l&apos;&#xE9;tablissement (PSSI) </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BTS, DUT<br>Dipl&#xF4;me informatique minimum bac pro<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des techniciens et techniciens sup&#xE9;rieurs hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Exploitant technique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Maçon', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser, entretenir et maintenir des ouvrages de ma&#xE7;onnerie ou de b&#xE9;ton et, accessoirement, des travaux de pl&#xE2;trerie ou de carrelage.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Ma&#xE7;on-carreleur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Construction de cloisons &#xE0; base de pl&#xE2;tre, ou en cloisons s&#xE8;ches </li> <li>Mise en place de protections de chantier et / ou individuelles </li> <li>Pose de cloisons, de rev&#xEA;tements, de protections murales </li> <li>R&#xE9;alisation d&apos;ouvrages neufs (pierre, briques...) de gros oeuvre </li> <li>R&#xE9;alisation de divers travaux de ma&#xE7;onnerie ((enduit, carrelage, fa&#xEF;ence, scellement...) </li> <li>R&#xE9;alisation de divers travaux de terrassement, de fondation ou de d&#xE9;molition </li> <li>R&#xE9;alisation de divers travaux de voiries, r&#xE9;seaux et divers </li> <li>R&#xE9;alisation de scellements et de fixations </li> <li>R&#xE9;alisation de travaux de b&#xE9;ton (coffrage, ferraillage, coulage...) </li> <li>R&#xE9;alisation de travaux de ma&#xE7;onnerie annexes &#xE0; la couverture ( solins, joints de chemin&#xE9;e...) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements, outillages, dans son domaine de comp&#xE9;tence </li> <li>Calepiner, suivre un plan pr&#xE9;cis de pose </li> <li>Dessiner un plan ou un croquis technique relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer l&apos;&#xE9;tat d&apos;un ouvrage, relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>Evaluer la quantit&#xE9; de produit n&#xE9;cessaire &#xE0; la r&#xE9;alisation d&apos;un ouvrage de ma&#xE7;onnerie </li> <li>&#xC9;valuer la r&#xE9;sistance m&#xE9;canique appropri&#xE9;e &#xE0; l&apos;ouvrage r&#xE9;alis&#xE9; </li> <li>Identifier, &#xE9;valuer, choisir des mat&#xE9;riels, des outils adapt&#xE9;s &#xE0; la r&#xE9;alisation des activit&#xE9;s de son domaine </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Reconna&#xEE;tre les structures porteuses d&apos;un b&#xE2;timent </li> <li>Utiliser des mat&#xE9;riels, des outils de d&#xE9;pannage, de r&#xE9;paration ou de maintenance, relatifs &#xE0; son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Calcul, g&#xE9;om&#xE9;trie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Dessin en b&#xE2;timent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Ma&#xE7;onnerie, pl&#xE2;trerie, c&#xE9;ramique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrant maintenance en b&#xE2;timent VRD pour la programmation du travail et la prise de d&#xE9;cision en mati&#xE8;re de r&#xE9;paration<br>Encadrement des services m&#xE9;dicaux et non m&#xE9;dicaux pour la programmation quotidienne et le d&#xE9;roulement des travaux<br>Magasinier pour l&apos;approvisionnement en mat&#xE9;riaux et outillage<br>Fournisseurs pour le choix et la r&#xE9;ception des produits et mat&#xE9;riels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Certificat d&apos;aptitude professionnelle (CAP)<br>Ou brevet d&apos;&#xE9;tudes professionnelles (BEP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de maintenance g&#xE9;n&#xE9;rale des b&#xE2;timents<br>Encadrant maintenance corps d&apos;&#xE9;tat secondaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) espaces verts', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter la gestion des espaces verts et les ext&#xE9;rieurs en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies, soit en animant une &#xE9;quipe de professionnels, soit par le biais de prestataires ext&#xE9;rieurs.<br>Il peut avoir &#xE0; animer des ateliers p&#xE9;dagogiques et th&#xE9;rapeutiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable des jardins<br>Responsable espaces verts<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Horticulture<br>Floriculture, p&#xE9;pini&#xE8;re et espaces verts<br>Terrasses et murs v&#xE9;g&#xE9;talis&#xE9;s<br>Am&#xE9;nagement paysagiste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Certiphyto obligatoire &#xE0; partir d&apos;octobre 2014<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la conformit&#xE9; des services / prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la qualit&#xE9; des produits </li> <li>Contr&#xF4;le de la r&#xE9;alisation de divers travaux de voiries, r&#xE9;seaux et divers </li> <li>&#xC9;laboration et mise en place du plan d&apos;entretien des pelouses et massifs floraux </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Gestion des d&#xE9;chets verts </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Installation de mat&#xE9;riels, d&apos;&#xE9;quipements, de syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer une prestation, un projet, une solution, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Planifier et coordonner des travaux et / ou des interventions (maintenance, entretien et /ou installation de mat&#xE9;riels, outils, &#xE9;quipements, syst&#xE8;mes...) </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Contr&#xF4;le de gestion </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Espaces verts </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Floriculture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Horticulture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">P&#xE9;pini&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Produits &#xE0; risques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation environnementale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur d&apos;&#xE9;tablissement pour le suivi des demandes en am&#xE9;nagements floraux ou d&apos;espaces verts.<br>Responsables des services techniques pour l&apos;entretien des &#xE9;quipements et de l&apos;outillage et pour la collaboration technique &#xE0; la r&#xE9;alisation d&apos;am&#xE9;nagements.<br>Direction des services &#xE9;conomiques pour la prise de d&#xE9;cision li&#xE9;e &#xE0; la r&#xE9;alisation d&apos;am&#xE9;nagements d&apos;espaces verts et autres activit&#xE9;s (exploitation agricole).<br>Fournisseurs pour l&apos;achat de v&#xE9;g&#xE9;taux, de produits phytosanitaires et d&apos;amendements.<br>Soci&#xE9;t&#xE9;s charg&#xE9;es de l&apos;&#xE9;vacuation des d&#xE9;chets verts et produits phytosanitaires pour l&apos;organisation du tri et la programmation des collectes/soci&#xE9;t&#xE9;s d&apos;&#xE9;tayage.<br>Soci&#xE9;t&#xE9;s de jardinage pour r&#xE9;alisation de prestations structurales.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BTS, BTSA espaces verts et/ou paysagiste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant maintenance corps d&apos;&#xE9;tat secondaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(trice) des finances (et contrôle de gestion)\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir et piloter la mise en &#x153;uvre de la politique financi&#xE8;re dans le cadre de la strat&#xE9;gie de l&apos;&#xE9;tablissement&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Directeur(e) des affaires financi&#xE8;res<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) transport sanitaire', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, coordonner, superviser les activit&#xE9;s de transports sanitaires en animant une &#xE9;quipe de professionnels (brancardiers, ambulanciers et coursiers (pour &#xE9;chantillons, pr&#xE9;l&#xE8;vements sanguins &#xE9;ventuellement produits sanguins labiles - Etablissement fran&#xE7;ais du sang)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Coordonnateur(trice) des transports sanitaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de tra&#xE7;abilit&#xE9; des prestations et du mat&#xE9;riel </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations et du mat&#xE9;riel(transports, nettoyage et bio-nettoyage du mat&#xE9;riel, &#xE9;tat des v&#xE9;hicules, etc), dans son domaine d&apos;activit&#xE9; </li> <li>Encadrement d&apos;&#xE9;quipe(s) et conseil aupr&#xE8;s de son &#xE9;quipe de transporteurs sanitaires </li> <li>&#xC9;tablissement des sp&#xE9;cifications techniques du cahier des charges </li> <li>Gestion des moyens et des ressources : techniques, financi&#xE8;res, humaines, informationnelles </li> <li>Gestion des stocks et des r&#xE9;approvisionnements, des commandes (v&#xE9;hicules, mat&#xE9;riels de transport sanitaire, linge, obus d&apos;oxyg&#xE8;ne, tenues des agents, etc.) </li> <li>Organisation et suivi op&#xE9;rationnel des activit&#xE9;s / projets, en relation avec les interlocuteurs internes et externes </li> <li>Planification et gestion des flux des transports sanitaires et autres activit&#xE9;s </li> <li>Recensement et analyse des besoins des utilisateurs des transports </li> <li>Recueil et analyse des demandes de transports des unit&#xE9;s </li> <li>Saisie des donn&#xE9;es, &#xE9;tablissement et exploitation des tableaux de bord </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et encadrer une ou plusieurs &#xE9;quipes, d&#xE9;velopper les comp&#xE9;tences de ses collaborateurs et les &#xE9;valuer </li> <li>Construire et utiliser des outils de pilotage (crit&#xE8;res, indicateurs / tableau de bord) </li> <li>&#xC9;valuer la qualit&#xE9; des prestations et s&apos;inscrire dans une d&#xE9;marche qualit&#xE9; </li> <li>G&#xE9;rer les contrats de maintenance des v&#xE9;hicules </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>N&#xE9;gocier des prestations, des contrats, des accords avec des interlocuteurs internes/externes </li> <li>Organiser et optimiser le stockage physique des produits, des mat&#xE9;riels </li> <li>Planifier, optimiser, prioriser les transports en fonction des besoins utilisateurs </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation d&apos;&#xE9;quipe </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel m&#xE9;tier </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logistique transports </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation des transports sanitaires </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable atelier de maintenance pour l&apos;entretien des v&#xE9;hicules<br>Gestionnaire de parc (interne ou externe) pour le renouvellement des v&#xE9;hicules<br>Clients internes pour la contractualisation et l&apos;&#xE9;valuation de la prestation transport<br>Equipe de direction pour la mise en &#x153;uvre de la strat&#xE9;gie, l&apos;allocation de moyens et le reporting<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Infirmier(ère) en soins généraux (IDE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Evaluer l&apos;&#xE9;tat de sant&#xE9; d&apos;une personne et analyser les situations de soins. Concevoir, d&#xE9;finir et planifier des projets de soins personnalis&#xE9;s.<br>Dispenser des soins de nature pr&#xE9;ventive, curative ou palliative visant &#xE0; promouvoir, maintenir et restaurer la sant&#xE9;.<br>R&#xE9;aliser l&apos;&#xE9;ducation th&#xE9;rapeutique et l&apos;accompagnement des patients.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Infirmier(&#xE8;re)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En stomath&#xE9;rapie<br>En gypsoth&#xE9;rapie<br>En douleur<br>En sant&#xE9; au travail<br>D&apos;accueil et d&apos;orientation<br>Technicien(ne) du sommeil<br>En milieu scolaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;infirmier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le et gestion des mat&#xE9;riels, dispositifs m&#xE9;dicaux et produits </li> <li>Coordination et organisation des activit&#xE9;s et des soins </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Information et &#xE9;ducation de la personne, de son entourage et d&apos;un groupe de personnes </li> <li>Observation de l&apos;&#xE9;tat de sant&#xE9; et le comportement relationnel et social d&apos;une personne </li> <li>R&#xE9;alisation de soins de confort et de bien-&#xEA;tre </li> <li>R&#xE9;alisation de soins et d&apos;activit&#xE9;s &#xE0; vis&#xE9;e pr&#xE9;ventive, diagnostique, th&#xE9;rapeutique </li> <li>Recueil de donn&#xE9;es cliniques </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Surveillance de l&apos;&#xE9;volution de l&apos;&#xE9;tat de sant&#xE9; de la personne </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses soins quotidiens </li> <li>Analyser / &#xE9;valuer la situation clinique d&apos;une personne, d&apos;un groupe de personnes, relative &#xE0; son domaine de comp&#xE9;tence </li> <li>Analyser, synth&#xE9;tiser des informations permettant la prise en charge de la personne soign&#xE9;e et la continuit&#xE9; des soins </li> <li>Concevoir et conduire un projet de soins, dans son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Conduire un entretien d&apos;aide </li> <li>Conseiller le patient et son entourage dans le cadre du projet personnalis&#xE9; du patient </li> <li>&#xC9;laborer et formaliser un diagnostic sant&#xE9; de la personne, relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Informer et former des professionnels et des personnes en formation </li> <li>Initier et mettre en &#x153;uvre des soins &#xE9;ducatifs et pr&#xE9;ventifs </li> <li>Mettre en &#x153;uvre des actions &#xE0; vis&#xE9;e diagnostique et th&#xE9;rapeutique </li> <li>Organiser et coordonner des interventions soignantes </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ergonomie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion du stress </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re et Pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodologie d&apos;analyse de situation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pharmacologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sciences sociales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decin et autres professionnels param&#xE9;dicaux pour le suivi des personnes soign&#xE9;es<br>services prestataires dans l&apos;institution pour assurer le fonctionnement des unit&#xE9;s de soins<br>infirmiers d&apos;autres services et secteurs de soins pour assurer la continuit&#xE9; des soins lors des transferts des personnes soign&#xE9;es<br>travailleurs sociaux pour am&#xE9;liorer la prise en charge de personnes soign&#xE9;es<br>instituts de formation pour l&apos;encadrement des stagiaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>3 ann&#xE9;es d&apos;&#xE9;tudes post baccalaur&#xE9;at, apr&#xE8;s &#xE9;preuves de s&#xE9;lection dans un institut de formation en soins infirmiers agr&#xE9;&#xE9;<br>Possibilit&#xE9; d&apos;envisager ces &#xE9;tudes en promotion professionnelle apr&#xE8;s s&#xE9;lection.<br>Dipl&#xF4;me d&apos;&#xE9;tat d&apos;infirmier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des infirmiers en soins g&#xE9;n&#xE9;raux et sp&#xE9;cialis&#xE9;s (Grade 1 et 2)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Hygi&#xE9;niste<br>Coordinateur de pr&#xE9;l&#xE8;vement ou de transplantation d&apos;organe<br>&quot;Coordinateur de parcours en sant&#xE9;&quot;<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant (e) Documentaliste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter la mise en oeuvre de la politique de documentation,<br>en animant une &#xE9;quipe de professionnels et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Ma&#xEE;trise du traitement de l&apos;information et de la documentation.<br>Ma&#xEE;trise des nouvelles technologies de l&apos;information, adapt&#xE9;es aux besoins de l&apos;utilisateur.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>D&#xE9;finition et mise en oeuvre de la politique documentaire de l&apos;&#xE9;tablissement ou institut de formation param&#xE9;dicale. </li> <li>&#xC9;laboration d&apos;un budget, d&apos;une politique d&apos;acquisition en conformit&#xE9; avec la politique budg&#xE9;taire de l&apos;&#xE9;tablissement (dans le respect du Code des march&#xE9;s publics) et d&#xE9;finition des moyens </li> <li>Elaboration d&apos;un plan de communication pour faire connaitre les ressources et services en partenariat avec le service communication de l&apos;&#xE9;tablissement </li> <li>Encadrement de l&apos;&#xE9;quipe des documentalistes et personnels du service de documentation. </li> <li>Evaluation de l&apos;activit&#xE9; sur la base d&apos;indicateurs et reporting aupr&#xE8;s de la direction de l&apos;&#xE9;tablissement </li> <li>Formation des utilisateurs (soignants, m&#xE9;decins, administratifs, ing&#xE9;nieurs, techniciens, &#xE9;tudiants param&#xE9;dicaux, formateurs et enseignants,...). </li> <li>Gestion des documents (mise &#xE0; disposition, &#xE9;changes, classement...). </li> <li>Organisation d&apos;une veille adapt&#xE9;e au contexte de l&apos;&#xE9;tablissement et aux professionnels concern&#xE9;s, afin de leur apporter : une actualisation des connaissances, une anticipation sur les &#xE9;volutions et une aide &#xE0; la d&#xE9;cision. <br><em>Le processus de veille int&#xE8;gre les &#xE9;tapes suivantes : ciblage, r&#xE9;colte, analyse des donn&#xE9;es recueillies et diffusion des r&#xE9;sultats.</em> </li> <li>Organisation de partenariats, benchmarking documentaire, animation de r&#xE9;seaux, de groupes de travail </li> <li>R&#xE9;alisation de produits et services documentaires (revues de presse, dossiers, bibliographies, sitographies, etc.). </li> <li>Recherche de sources documentaires externes. </li> <li>Suivi des acquisitions (ouvrages, abonnements et autres supports documentaires) sous format papier, &#xE9;lectronique, visuel, etc. </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser, traduire et formuler les besoins documentaires des utilisateurs. </li> <li>Animer et encadrer une ou plusieurs &#xE9;quipes, d&#xE9;velopper les comp&#xE9;tences de ses collaborateurs et les &#xE9;valuer </li> <li>Concevoir, organiser et animer des formations utilisateurs </li> <li>&#xC9;valuer la pertinence et la v&#xE9;racit&#xE9; des donn&#xE9;es et/ou informations </li> <li>N&#xE9;gocier les contrats avec les prestataires/fournisseurs </li> <li>Optimiser les ressources budg&#xE9;taires. </li> <li>Organiser la recherche d&apos;information, son recueil, son analyse, sa diffusion, son classement et sa mise &#xE0; jour (veille). </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les diff&#xE9;rentes &#xE9;tapes de la cha&#xEE;ne documentaire (collecte, bulletinage, catalogage, indexation, saisie, classement, diffusion, &#xE9;laboration d&apos;un th&#xE9;saurus, liste de mots-cl&#xE9;s...) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Gestion comptable et administrative <br><em>Droit de la propri&#xE9;t&#xE9; intellectuelle.El&#xE9;ments de base en gestion comptable et administrative.</em> </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques documentaires <br><em>M&#xE9;thodes de classement et d&apos;archivage. Techniques d&apos;inventaire. Techniques de conservation de documents. Techniques de num&#xE9;risation. Biblioth&#xE9;conomie.</em> </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">TIC et fonction documentaire <br><em>Acc&#xE8;s, stockage, num&#xE9;risation, diffusion. Web 2.0.Internet, Intranet.Utilisation de logiciels de gestion documentaire. Programmation et langage informatique. Outils de diffusion et de cr&#xE9;ation d&apos;images, diaporama ou supports anim&#xE9;s. Techniques de communication.</em> </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>- Associations et r&#xE9;seaux professionnels (ADBS ; Association des Professionnels de l&apos;Information et de la Documentation, RNDH : R&#xE9;seau National des Documentalistes Hospitaliers, ...) pour des &#xE9;changes d&apos;information sur la documentation, des &#xE9;changes professionnels, de coop&#xE9;ration, de formation ...<br>- Institutions culturelles et d&apos;enseignement pour la recherche d&apos;informations documentaires et la coop&#xE9;ration d&apos;enseignement (Universit&#xE9; M&#xE9;decine, Droit, Sciences Humaines).<br>- Partenaires ext&#xE9;rieurs (&#xE9;diteurs, consortia, groupement d&apos;achats hospitaliers, agences d&apos;abonnements...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>- DEUST, DUT, licence professionnelle, IUP, ma&#xEE;trise en gestion de l&apos;information et du document (une double comp&#xE9;tence peut &#xEA;tre n&#xE9;cessaire en biologie, pharmacie, chimie).<br>- Bac + 2 : DUT information communication, option documentation d&apos;entreprise<br>- Bac + 2 pro : DEUST m&#xE9;tiers des biblioth&#xE8;que et de la documentation<br>- Bac + 3 : licence ICD -option documentation (I3)<br>- Bac + 3 pro: licence professionnelle, charg&#xE9;(e) de valorisation des ressources documentaires Niveau I de type Master (Bac +5) d&#xE9;livr&#xE9; par les universit&#xE9;s et/ou l&apos;INTD<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Contractuel de cat&#xE9;gorie A<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Gestionnaire admissions frais de séjour traitement externe', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Accueillir les patients, recueillir et traiter les donn&#xE9;es n&#xE9;cessaires &#xE0; son identification et &#xE0; la facturation de l&apos;activit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Gestionnaire des entr&#xE9;es<br>Gestionnaire du bureau des entr&#xE9;es<br>Admissioniste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint administratif hospitalier<br>Adjoint des cadres hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Gestionnaire comptable et achat<br>Gestionnaire finances<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Infirmier(ère) de bloc opératoire (IBODE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, r&#xE9;aliser des soins et des activit&#xE9;s en lien avec le geste op&#xE9;ratoire, en pr&#xE9;, per et post interventionnel aupr&#xE8;s des personnes b&#xE9;n&#xE9;ficiant d&apos;interventions chirurgicales, endoscopiques et autres actes techniques invasifs &#xE0; vis&#xE9;e diagnostique et/ou th&#xE9;rapeutique.<br>Mettre en oeuvre des mesures d&apos;hygi&#xE8;ne et de s&#xE9;curit&#xE9; en tenant compte des risques inh&#xE9;rents &#xE0; la nature des interventions et &#xE0; la sp&#xE9;cificit&#xE9; des patients, au travail en zone prot&#xE9;g&#xE9;e et &#xE0; l&apos;utilisation de dispositifs m&#xE9;dicaux sp&#xE9;cifiques.<br>Mettre en oeuvre les actes et activit&#xE9;s relevant de sa comp&#xE9;tence exclusive.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Panseur(euse) -Circulant(e)<br>Instrumentiste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;infirmier(&#xE8;re)<br>Exercice professionnel pr&#xE9;alable de 2 ans en qualit&#xE9; d&apos;IDE<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le, pr&#xE9;paration, pr&#xE9;sentation et utilisation des dispositifs m&#xE9;dicaux st&#xE9;riles et non st&#xE9;riles </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion d&apos;&#xE9;quipements, de dispositifs m&#xE9;dicaux et de produits au bloc op&#xE9;ratoire et dans les secteurs associ&#xE9;s </li> <li>R&#xE9;alisation de mesures de qualit&#xE9; et de s&#xE9;curit&#xE9; au bloc op&#xE9;ratoire et dans les secteurs associ&#xE9;s </li> <li>R&#xE9;alisation de soins aupr&#xE8;s d&apos;une personne b&#xE9;n&#xE9;ficiaire d&apos;une intervention </li> <li>R&#xE9;alisation de soins et d&apos;activit&#xE9;s li&#xE9;es &#xE0; l&apos;intervention et au geste op&#xE9;ratoire </li> <li>R&#xE9;alisation et contr&#xF4;le de mesures d&apos;hygi&#xE8;ne en bloc op&#xE9;ratoire et dans les secteurs associ&#xE9;s </li> <li>Transmission d&apos;information, organisation, planification en bloc op&#xE9;ratoire </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser la qualit&#xE9; et la s&#xE9;curit&#xE9; en bloc op&#xE9;ratoire et en salle d&apos;intervention, d&#xE9;terminer les mesures appropri&#xE9;es </li> <li>Concevoir et mettre en oeuvre des modes de prise en charge des personnes adapt&#xE9;s aux situations rencontr&#xE9;es en bloc op&#xE9;ratoire </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Mettre en oeuvre des techniques et des pratiques adapt&#xE9;es au processus op&#xE9;ratoire </li> <li>Organiser et coordonner les activit&#xE9;s de soins li&#xE9;es au processus op&#xE9;ratoire </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> <li>Utiliser, mettre en oeuvre des techniques et des pratiques adapt&#xE9;es &#xE0; la chirurgie abdomino-pelvienne et vasculaire </li> <li>Utiliser, mettre en oeuvre des techniques et des pratiques adapt&#xE9;es &#xE0; la chirurgie ost&#xE9;o-articulaire </li> <li>Utiliser, mettre en oeuvre des techniques et des pratiques adapt&#xE9;es &#xE0; la chirurgie vid&#xE9;o-assist&#xE9;e et &#xE0; la microchirurgie </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion du stress </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re et Pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation du travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation en hygi&#xE8;ne, s&#xE9;curit&#xE9; et conditions de travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">St&#xE9;rilisation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Techniques et organisation des soins en bloc op&#xE9;ratoire </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Chirurgiens, personnels des services d&apos;hospitalisation et de consultation pour la continuit&#xE9; des soins.<br>Pharmaciens et personnel de st&#xE9;rilisation pour le traitement et la gestion des dispositifs m&#xE9;dicaux st&#xE9;riles.<br>Manipulateurs en &#xE9;lectro-radiologie pour les radiologies per interventionnelles<br>Ing&#xE9;nieurs et techniciens biom&#xE9;dicaux pour la maintenance pr&#xE9;ventive et curative des appareils m&#xE9;dicaux.<br>Techniciens de laboratoires pour les diff&#xE9;rents pr&#xE9;l&#xE8;vements.<br>Cadres formateurs pour l&apos;encadrement des &#xE9;tudiants<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;&#xE9;tat d&apos;infimier(&#xE8;re) de bloc op&#xE9;ratoire.<br>Avant le 31 d&#xE9;cembre 2020, les professionnels IBO titulaires du DE en exercice &#xE0; la date du 27 janvier 2015 ou les IDE en cours de formation pr&#xE9;parant au DE d&apos;IBO &#xE0; cette m&#xEA;me date, doivent suivre une formation compl&#xE9;mentaire pour accomplir les actes et activit&#xE9;s d&#xE9;finis &#xE0; l&apos;article R.4311-11-1 du Code de la sant&#xE9; publique (d&#xE9;cret n&#xB0;2015-74 du 27 janvier 2015 et arr&#xEA;t&#xE9; du 27 janvier 2015).<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des infirmiers en soins g&#xE9;n&#xE9;raux et sp&#xE9;cialis&#xE9;s (Grade 2 et 3)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Hygi&#xE9;niste<br>Coordinateur de pr&#xE9;l&#xE8;vement ou de transplantation d&apos;organe<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Ingénieur(e) de recherche hospitalier (IR)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir et d&#xE9;velopper des ruptures technologiques et/ou organisationnelles dans tous les domaines li&#xE9;s &#xE0; la sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>D&#xE9;finition de la conception technique et des sp&#xE9;cifications d&#xE9;taill&#xE9;es d&apos;un projet </li> <li>Elaboration de la d&#xE9;finition et la faisabilit&#xE9; des projets avec les demandeurs </li> <li>&#xC9;laboration et proposition des modifications en cours de projet (objectifs, qualit&#xE9;, co&#xFB;ts, d&#xE9;lais...) li&#xE9;es &#xE0; des contraintes d&apos;&#xE9;tude ou de r&#xE9;alisation </li> <li>&#xC9;laboration et r&#xE9;daction de rapports d&apos;activit&#xE9; </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes et de travaux de recherche dans son domaine </li> <li>R&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation ou supervision et contr&#xF4;le du d&#xE9;roulement du projet </li> <li>Recherche de moyens financiers, humains, logistiques pour la mise en oeuvre des projets </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Concevoir et r&#xE9;diger une documentation technique, sp&#xE9;cifique &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>S&apos;exprimer en public </li> <li>S&apos;exprimer, comprendre et r&#xE9;diger dans une langue &#xE9;trang&#xE8;re </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Biologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Structures en charge du pilotage et de la gestion de la recherche (unit&#xE9;s de recherche, DRCI, promoteur...)<br>M&#xE9;decins cliniciens, pharmaciens et biologistes pour l&apos;&#xE9;laboration et le suivi des projets de recherche<br>Ing&#xE9;nieurs Biologistes Hospitaliers pour<br>l&apos;adaptation des nouvelles technologies.<br>Cadres de sant&#xE9; pour l&apos;organisation des activit&#xE9;s et l&apos;&#xE9;valuation des besoins logistiques et financiers<br>Equipes m&#xE9;dico-techniques du p&#xF4;le pour l&apos;encadrement technique et la formation<br>M&#xE9;decins cliniciens pour l&apos;information et le retour d&apos;information de l&apos;analyse des donn&#xE9;es issues de leurs unit&#xE9;s<br>Chercheurs en amont de la recherche clinique pour transf&#xE9;rer et mettre en oeuvre les nouvelles m&#xE9;thodes<br>Structures de valorisation de la recherche et de transfert de technologie (CHU, INSERM, Universit&#xE9;,..) pour valoriser les m&#xE9;thodes de recherche.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+8 dans le domaine scientifique, sciences humaines et sociales, droit, &#xE9;conomie, science politique ou sant&#xE9; publique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(trice) des affaires générales\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;Elaborer et piloter les montages et les supports juridiques et administratifs li&#xE9;s au fonctionnement et aux projets de l&apos;&#xE9;tablissement.<br>Monter et piloter des partenariats externes&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Secr&#xE9;taire g&#xE9;n&#xE9;ral(e)<br>Directeur(e) juridique<br>Directeur(e) de cabinet<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;daction de documents techniques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Coordination des interlocuteurs internes et externes sur le territoire de sant&#xE9; (&#xE9;tablissements de sant&#xE9;, lib&#xE9;raux, r&#xE9;seaux, associations) </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Organisation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es </li> <li>Suivi des contr&#xF4;les et des organismes agr&#xE9;&#xE9;s </li> <li>Suivi des indicateurs institutionnels et nationaux. </li> <li>Veille r&#xE9;glementaire et professionnelle et diffusion aux professionnels. </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Argumenter et convaincre dans une relation de confiance des interlocuteurs si besoin n&#xE9;gocier des accords. </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Conduire et animer des r&#xE9;unions </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Organiser / animer des activit&#xE9;s sp&#xE9;cifiques &#xE0; son domaine de comp&#xE9;tence pour des patients, des personnels/des groupes </li> <li>Organiser une unit&#xE9;, un service, en fonction de diff&#xE9;rents crit&#xE8;res (missions, strat&#xE9;gie, moyens, etc.) </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Droit administratif </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;conomie de la sant&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie du secteur sanitaire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement des structures sociales et m&#xE9;dico-sociales </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">R&#xE9;seaux sanitaires, sociaux et m&#xE9;dico-sociaux </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur d&apos;&#xE9;tablissement (politique institutionnelle)<br>Directeurs-adjoints (d&#xE9;l&#xE9;gations de gestion)<br>Pr&#xE9;sident de la CME (projet d&apos;&#xE9;tablissement)<br>Chefs de p&#xF4;le (d&#xE9;l&#xE9;gations de gestion et projet d&apos;&#xE9;tablissement)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau I et II<br>Bac+5<br>Ecole des Hautes Etudes en Sant&#xE9; Publique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;Administration Hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Gestionnaire des ressources humaines', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser les activit&#xE9;s de gestion RH, dans le respect des r&#xE8;gles et proc&#xE9;dures d&#xE9;finies.<br>Recevoir, informer, conseiller les professionnels de l&apos;&#xE9;tablissement.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Gestionnaire administratif(ve) du personnel<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gestionnaire paie<br>Gestionnaire carri&#xE8;res/ retraite<br>Gestionnaire temps de travail<br>Gestionnaire recrutement<br>Gestionnaire protection sociale<br>Assistant(e) de formation<br>Gestionnaire des affaires m&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, orientation, conseil et information aux agents de l&apos;&#xE9;tablissement dans le domaine des ressources humaines (carri&#xE8;re, statut, r&#xE9;mun&#xE9;ration, formation, recrutement, mobilit&#xE9;...) </li> <li>Contr&#xF4;le de l&apos;application de la r&#xE9;glementation et des r&#xE8;gles RH propres &#xE0; l&apos;&#xE9;tablissement (carri&#xE8;re, formation, GPMC, paie, gestion du temps, absence, recrutement..) </li> <li>Gestion administrative collective et individuelle des agents </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>R&#xE9;daction de courriers relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Renseignement et suivi au fil de l&apos;eau de bases de donn&#xE9;es RH, tableaux de bord et autres tableaux de suivi pouvant inclure des donn&#xE9;es financi&#xE8;res. </li> <li>Suivi et contr&#xF4;le budg&#xE9;taire relatif au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>Conseiller et orienter les choix des personnes </li> <li>D&#xE9;finir et utiliser les techniques et pratiques adapt&#xE9;es &#xE0; son m&#xE9;tier </li> <li>G&#xE9;rer un budget ou une norme quantitative et optimiser la gestion financi&#xE8;re propre &#xE0; son domaine d&apos;activit&#xE9; et rapporter &#xE0; sa hi&#xE9;rarchie tout risque d&apos;&#xE9;cart. </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Utiliser les outils bureautiques et les logiciels m&#xE9;tiers </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Droit du travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion budg&#xE9;taire et administrative </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion RH </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Statut des personnels m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Statut des personnels non m&#xE9;dicaux de la fonction publique hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>- Agents pour traitement des situations individuelles<br>- Direction et encadrants RH<br>- Autres directions de l&apos;&#xE9;tablissement (finances, affaires m&#xE9;dicales, soins...)<br>- Partenaires externes en lien avec l&apos;activit&#xE9; (tr&#xE9;sorerie, p&#xF4;le emploi, ARS, CNRACL, URSSAF, DGOS, OPCA...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de niveau III (BTS, DUT), de niveau II (Licence professionnelle) en gestion des ressources humaines, gestion des entreprises et/ou des administrations.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint administratif hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant des ressources humaines<br>Vers les autres m&#xE9;tiers de gestionnaire des autres sous-familles.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) sécurité incendie', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;terminer, piloter et coordonner l&apos;ensemble des activit&#xE9;s de s&#xE9;curit&#xE9; incendie et de pr&#xE9;vention.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) de service de s&#xE9;curit&#xE9; incendie<br>Charg&#xE9;(e) de s&#xE9;curit&#xE9; incendie<br>Chef(fe) de service de s&#xE9;curit&#xE9;<br>Charg&#xE9;(e) de s&#xE9;curit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Habilitation SSIAP 2 ou SSIAP 3 (arr&#xEA;t&#xE9; du 2 mai 2005 modifi&#xE9;)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le des donn&#xE9;es / documents relatifs &#xE0; son domaine </li> <li>&#xC9;laboration et mise en place du plan concernant la s&#xE9;curit&#xE9; des personnes </li> <li>&#xC9;laboration et mise en place du plan de pr&#xE9;vention et de traitement des risques, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Planification et r&#xE9;alisation d&apos;exercice d&apos;incendie </li> <li>Planification, contr&#xF4;le et reporting des activit&#xE9;s et des moyens </li> <li>Recensement / enregistrement des donn&#xE9;es / des informations li&#xE9;es &#xE0; la nature des activit&#xE9;s </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir, conduire et &#xE9;valuer la politique relative &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer une prestation, un projet, une solution, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication orale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la s&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des b&#xE2;timents </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Techniques et mat&#xE9;riels de lutte contre l&apos;incendie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Le responsable de s&#xE9;curit&#xE9;<br>Le CHSCT<br>L&apos;ensemble des personnels pour l&apos;information sur les risques incendie.<br>Service d&#xE9;partemental incendie et de secours, pr&#xE9;fecture du d&#xE9;partement et autres administrations pour la prise en compte et application des proc&#xE9;dures incendie.<br>Soci&#xE9;t&#xE9;s de contr&#xF4;le et de maintenance des installations li&#xE9;es &#xE0; la s&#xE9;curit&#xE9; incendie pour la v&#xE9;rification de la conformit&#xE9; et l&apos;entretien des installations incendie.<br>Services techniques pour la collaboration dans les actions et travaux.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me au moins de niveau IV attestant d&apos;une qualification professionnelle dans le domaine de la s&#xE9;curit&#xE9;<br>Aptitude et exp&#xE9;rience professionnelles dans le m&#xE9;tier de la s&#xE9;curit&#xE9; incendie<br>Habilitation SSIAP 2 ou SSIAP 3<br>Bac +2<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Oculariste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, fabriquer et adapter des appareillages du globe oculaire non fonctionnel ou d&apos;une cavit&#xE9; orbitaire, par proth&#xE8;se oculaire externe sur mesure.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Adaptation et livraison des proth&#xE8;ses oculaires et suivi de la personne </li> <li>Apport de conseils et d&apos;expertise au sein d&apos;une &#xE9;quipe pluridisciplinaire et aupr&#xE8;s de diff&#xE9;rents professionnels de sant&#xE9; </li> <li>Conseil et information &#xE0; la personne et &#xE0; son entourage </li> <li>Constitution des dossiers techniques et r&#xE9;alisation des proth&#xE8;ses oculaires sur mesure </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Mise en place de d&#xE9;marches qualit&#xE9; / environnement et r&#xE9;daction de documents requis pour l&apos;activit&#xE9; de l&apos;entreprise </li> <li>R&#xE9;alisation de l&apos;&#xE9;ducation proth&#xE9;tique </li> <li>Recueil des informations visant &#xE0; &#xE9;tablir un bilan </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter l&apos;appareillage aux besoins et aux caract&#xE9;ristiques du patient </li> <li>Communiquer et conduire une relation dans un contexte d&apos;intervention </li> <li>Concevoir et conduire une d&#xE9;marche d&apos;information, de conseil et d&apos;&#xE9;ducation </li> <li>Concevoir une proth&#xE8;se oculaire au regard d&apos;un projet d&apos;intervention personnalis&#xE9; </li> <li>Coop&#xE9;rer avec d&apos;autres professionnels </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Evaluer une situation et &#xE9;laborer un diagnostic dans le domaine d&apos;activit&#xE9; de l&apos;oculariste </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Mettre en &#x153;uvre les techniques de fabrication de la proth&#xE8;se oculaire </li> <li>R&#xE9;aliser la prise d&apos;empreinte et/ou le moulage </li> <li>R&#xE9;aliser le suivi de l&apos;appareillage </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Confection de proth&#xE8;se oculaire </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>N&#xE9;ant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(e) d''établissement social et médico-social - Chef(fe) d''établissement\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;D&#xE9;finir une politique strat&#xE9;gique de l&apos;&#xE9;tablissement social et m&#xE9;dico-social en lien avec les politiques sociales, m&#xE9;dico-sociales et sanitaires de son territoire&quot;<br>Coordonner et piloter sa mise en &#x153;uvre dans les diff&#xE9;rents domaines<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) d&apos;&#xE9;tablissement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Ingénieur(e) en biologie médicale', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre en &#x153;uvre, optimiser l&apos;organisation et la gestion de plateaux techniques en biologie m&#xE9;dicale et d&#xE9;velopper et adapter de nouvelles m&#xE9;thodes ou technologies en biologie m&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Ing&#xE9;nieur(e) en biologie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>&#xC9;tablissement / actualisation et mise en &#x153;uvre de proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation de personnes aux techniques et proc&#xE9;dures de son domaine, et &#xE0; leur application </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Mise en &#x153;uvre de travaux de d&#xE9;veloppement dans son domaine </li> <li>Publication d&apos;informations en interne / externe </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> <li>R&#xE9;daction de documents techniques, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Traitement de l&apos;information biologique: extraction, regroupement, repr&#xE9;sentation </li> <li>Utilisation des outils, logiciels, syst&#xE8;mes relevant de son domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>V&#xE9;rification, mise en route, pilotage et surveillance des &#xE9;quipements, mise &#xE0; l&apos;arr&#xEA;t </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Analyser et &#xE9;valuer les performances d&apos;un syst&#xE8;me, d&apos;un appareil, d&apos;un outil sp&#xE9;cifique &#xE0; son domaine technique </li> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la conformit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riel, d&apos;une prestation au regard des normes internes/externes </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>S&apos;exprimer, comprendre et r&#xE9;diger dans une langue &#xE9;trang&#xE8;re </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> <li>Travailler en &#xE9;quipe / en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Biologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion de donn&#xE9;es, relatives &#xE0; son domaine </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la biologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>- Biologistes pour la mise en place des techniques et outils ainsi que pour l&apos;&#xE9;laboration et le suivi des projets de recherche<br>- Equipes m&#xE9;dico-techniques du p&#xF4;le pour l&apos;encadrement technique et la formation<br>- Fournisseurs pour les nouvelles techniques et le SAV (r&#xE9;actifs et &#xE9;quipements)<br>- Responsable assurance qualit&#xE9; pour la mise en &#x153;uvre de la politique qualit&#xE9;<br>- Services techniques de gestion des &#xE9;quipements pour la gestion des &#xE9;quipements<br>- Cadres des laboratoires pour l&apos;organisation des activit&#xE9;s de laboratoire et l&apos;&#xE9;valuation des besoins logistiques et financiers<br>- Centrales d&apos;achat pour l&apos;&#xE9;laboration des cahiers des charges relatifs aux &#xE9;quipements, r&#xE9;actifs et consommables et pour la construction des grilles d&apos;&#xE9;valuation et les essais en laboratoire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau I (BAC+5): Master scientifique ou dipl&#xF4;me d&apos;ing&#xE9;nieur en biologie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des ing&#xE9;nieurs<br>Contractuel de cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur d&apos;&#xE9;tudes hospitalier<br>Ing&#xE9;nieur de recherche hospitalier<br>Bio informaticien<br>Encadrant maintenance biom&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Epithésiste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, fabriquer et adapter des appareillages de la face et/ou des oreilles, par proth&#xE8;se faciale externe sur mesure.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>DU proth&#xE8;se faciale appliqu&#xE9;e<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Adaptation et livraison des proth&#xE8;ses faciales externes et suivi de la personne </li> <li>Apport de conseils et d&apos;expertise au sein d&apos;une &#xE9;quipe pluridisciplinaire et aupr&#xE8;s de diff&#xE9;rents professionnels de sant&#xE9; </li> <li>Conseil et information &#xE0; la personne et &#xE0; son entourage </li> <li>Constitution des dossiers techniques et r&#xE9;alisation des proth&#xE8;ses faciales externes sur mesure </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Mise en place de d&#xE9;marches qualit&#xE9; / environnement et r&#xE9;daction de documents requis pour l&apos;activit&#xE9; de l&apos;entreprise </li> <li>R&#xE9;alisation de l&apos;&#xE9;ducation proth&#xE9;tique </li> <li>Recueil des informations visant &#xE0; &#xE9;tablir un bilan </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter l&apos;appareillage aux besoins et aux caract&#xE9;ristiques du patient </li> <li>Communiquer et conduire une relation dans un contexte d&apos;intervention </li> <li>Concevoir et conduire une d&#xE9;marche d&apos;information, de conseil et d&apos;&#xE9;ducation </li> <li>Concevoir une proth&#xE8;se faciale externe au regard d&apos;un projet d&apos;intervention personnalis&#xE9; </li> <li>Coop&#xE9;rer avec d&apos;autres professionnels </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Evaluer une situation et &#xE9;laborer un diagnostic dans le domaine d&apos;activit&#xE9; de l&apos;&#xE9;pith&#xE9;siste </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Mettre en oeuvre les techniques de fabrication de la proth&#xE8;se faciale externe </li> <li>R&#xE9;aliser la prise d&apos;empreinte et/ou le moulage </li> <li>R&#xE9;aliser le suivi de l&apos;appareillage </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Confection d&apos;&#xE9;pith&#xE8;ses </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pharmacologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>DU proth&#xE8;se faciale appliqu&#xE9;e<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>N&#xE9;ant<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Manipulateur(trice) en électro-radiologie médicale (MERM)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des actes relevant de l&apos;imagerie m&#xE9;dicale, de la m&#xE9;decine nucl&#xE9;aire, des explorations fonctionnelles et de la radioth&#xE9;rapie qui concourent &#xE0; la pr&#xE9;vention, au d&#xE9;pistage, au diagnostic, au traitement et &#xE0; la recherche.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Manipulateur en radiologie<br>Manipulateur en radiologie m&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En radioprotection<br>Technicien(ne) du sommeil<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>DEMER ( dipl&#xF4;me d&apos;etat manipulateur d&apos;&#xE9;lectroradiologie m&#xE9;dicale)<br>DTSIMRT( Dipl&#xF4;me de technicien sup&#xE9;rieur en imagerie m&#xE9;dicale et radiologie th&#xE9;rapeutique)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil de la personne soign&#xE9;e et recueil des donn&#xE9;es </li> <li>Contr&#xF4;le et gestion des mat&#xE9;riels, dispositifs m&#xE9;dicaux et produits </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Exploitation, gestion et transfert des donn&#xE9;es et images </li> <li>Formation et information des professionnels et &#xE9;tudiants </li> <li>Information de la personne soign&#xE9;e et mise en oeuvre des soins dans le cadre de la continuit&#xE9; des soins </li> <li>Mise en oeuvre des mesures de radioprotection </li> <li>Mise en oeuvre des mesures li&#xE9;es &#xE0; la qualit&#xE9; et &#xE0; la pr&#xE9;vention des risques </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>R&#xE9;alisation de soins &#xE0; vis&#xE9;e diagnostique et th&#xE9;rapeutique dans le champ de l&apos;imagerie, la m&#xE9;decine nucl&#xE9;aire, la radioth&#xE9;rapie et les explorations fonctionnelles </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser la situation clinique de la personne et d&#xE9;terminer les modalit&#xE9;s des soins &#xE0; r&#xE9;aliser </li> <li>Conduire une relation avec la personne soign&#xE9;e </li> <li>Evaluer, am&#xE9;liorer ses pratiques professionnelles. Analyser et adapter sa pratique professionnelle en regard de l&apos;&#xE9;volution des recommandations </li> <li>G&#xE9;rer les informations li&#xE9;es &#xE0; la r&#xE9;alisation des soins &#xE0; vis&#xE9;e diagnostique et th&#xE9;rapeutique </li> <li>Informer et former des professionnels et des personnes en formation </li> <li>Mettre en oeuvre les normes et principes de qualit&#xE9;, d&apos;hygi&#xE8;ne et de s&#xE9;curit&#xE9; pour assurer la qualit&#xE9; des soins </li> <li>Mettre en oeuvre les r&#xE8;gles et les pratiques de radioprotection des personnes soign&#xE9;es, des personnels et du public </li> <li>Mettre en oeuvre les soins &#xE0; vis&#xE9;e diagnostique et th&#xE9;rapeutique en imagerie m&#xE9;dicale, m&#xE9;decine nucl&#xE9;aire, radioth&#xE9;rapie et explorations fonctionnelles, en assurant la continuit&#xE9; des soins </li> <li>Organiser les activit&#xE9;s et coop&#xE9;rer avec les diff&#xE9;rents acteurs </li> <li>Rechercher, traiter et analyser des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re et Pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; en &#xE9;lectroradiologie m&#xE9;dicale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; en radioth&#xE9;rapie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Mat&#xE9;riel et &#xE9;quipement d&apos;imagerie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Pharmacologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Physique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Radioprotection </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sciences humaines </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decin pour le choix du protocole, l&apos;ajustement et l&apos;interpr&#xE9;tation de l&apos;examen,<br>Secr&#xE9;tariat pour g&#xE9;rer le circuit patient<br>Responsable des r&#xE9;seaux d&apos;imagerie m&#xE9;dicale pour les transferts d&apos;images<br>Brancardiers et ambulanciers pour le transport et l&apos;installation des patients<br>Unit&#xE9; de soins pour la prestation d&apos;actes,<br>Service de maintenance pour le suivi technique des appareils<br>Ing&#xE9;nieur biom&#xE9;dical et radiophysicien m&#xE9;dical pour la mise en oeuvre des traitements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +3<br>IFMEM ( minist&#xE8;re de la sant&#xE9;)<br>Lyc&#xE9;e ( minist&#xE8;re de l&apos;education nationale)<br>DEMER ( dipl&#xF4;me d&apos;etat manipulateur d&apos;&#xE9;lectroradiologie m&#xE9;dicale)<br>DTSIMRT( Dipl&#xF4;me de technicien sup&#xE9;rieur en imagerie m&#xE9;dicale et radiologie th&#xE9;rapeutique)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Manipulateur en &#xE9;lectroradiologie m&#xE9;dicale.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Coordinateur de parcours en sant&#xE9; Dosim&#xE9;triste<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Gestionnaire de données biomédicales', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Assurer le regroupement et le traitement des donn&#xE9;es de recherche clinique recueillies. Elaborer des bases de donn&#xE9;es. Contr&#xF4;ler la coh&#xE9;rence et la qualit&#xE9; des donn&#xE9;es.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;daction de documents techniques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la coh&#xE9;rence des donn&#xE9;es / informations sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; des r&#xE9;sultats au regard du cahier des charges, des sp&#xE9;cifications, co&#xFB;ts, performances, d&#xE9;lais... </li> <li>Contr&#xF4;le des donn&#xE9;es / documents relatifs &#xE0; son domaine </li> <li>&#xC9;laboration et mise en place de l&apos;architecture technique des serveurs et des bases de donn&#xE9;es du syst&#xE8;me d&apos;informations, </li> <li>Organisation / coordination / supervision des contr&#xF4;les relatifs aux activit&#xE9;s du secteur, de l&apos;unit&#xE9; </li> <li>Organisation et description des processus sp&#xE9;cifiques &#xE0; son domaine, traitement des dysfonctionnements </li> <li>Param&#xE9;trage des outils, logiciels, syst&#xE8;mes relevant de son domaine d&apos;activit&#xE9; </li> <li>Programmation des outils d&apos;automatisation </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Concevoir, actualiser, optimiser une base de donn&#xE9;es, relatives &#xE0; la nature de ses activit&#xE9;s </li> <li>D&#xE9;velopper, programmer param&#xE9;trer et tester une application </li> <li>&#xC9;laborer et argumenter des sc&#xE9;narii/ des solutions acceptables &#xE0; des probl&#xE8;mes relevant de son domaine de comp&#xE9;tence </li> <li>Elaborer, r&#xE9;diger et exploiter des requ&#xEA;tes relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Identifier et diagnostiquer un dysfonctionnement, une panne, le d&#xE9;faut d&apos;un mat&#xE9;riel, d&apos;un &#xE9;quipement, une anomalie d&apos;un syst&#xE8;me, sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Programmer dans diff&#xE9;rents environnements informatiques </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Droit des donn&#xE9;es informatiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion de donn&#xE9;es, relatives &#xE0; son domaine </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Langage de programmation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; informatique, s&#xE9;curit&#xE9; SI </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Statistiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Coordinateur d&apos;etudes cliniques pour la mise en place des outils de recueil et de gestion de la recherche<br>Biostatisticiens pour la gestion de la base de donn&#xE9;es (cr&#xE9;ations du plan de gestion des requ&#xEA;tes et des demandes de corrections diff&#xE9;r&#xE9;es)<br>Assistants de recherche clinique, techniciens d&apos;etudes cliniques pour la r&#xE9;ponse aux requ&#xEA;tes.<br>Service d&apos;informatique hospitali&#xE8;re pour la r&#xE9;cup&#xE9;ration de donn&#xE9;es localis&#xE9;es dans d&apos;autres syst&#xE8;mes d&apos;information.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau II (BAC+3)<br>Formation en informatique appliqu&#xE9;e &#xE0; la gestion de donn&#xE9;es biom&#xE9;dicales.<br>La formation requise ne doit pas tant &#xEA;tre orient&#xE9;e en informatique qu&apos;en termes de gestion de base de donn&#xE9;es : les data-managers ne sont pas uniquement des informaticiens.<br>A c&#xF4;t&#xE9; de cela, la formation doit comprendre un bagage minimal en recherche clinique, afin de comprendre le contexte g&#xE9;n&#xE9;ral dans lequel travaille le gestionnaire de donn&#xE9;es biom&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier Cat&#xE9;gorie B<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Biostatisticien<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Exploitant (e) technique (réseau, système, BDD, téléphonie, visio)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Piloter les ressources d&apos;une unit&#xE9; d&apos;exploitation informatique, assurer leur gestion et les &#xE9;volutions en garantissant le niveau et les engagements de service, ainsi que la qualit&#xE9; des traitements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Technicien(ne) d&apos;exploitation<br>Analyste d&apos;exploitation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>r&#xE9;seau, syst&#xE8;me, BDD, t&#xE9;l&#xE9;phonie, visio<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Archivage des donn&#xE9;es informatiques et liaison avec les &#xE9;ventuels prestataires externes d&apos;h&#xE9;bergement des donn&#xE9;es </li> <li>Diffusion de la documentation technique </li> <li>Gestion de l&apos;ex&#xE9;cution du march&#xE9; de prestation : lancement, suivi et contr&#xF4;le des prestations SI </li> <li>Maintenance correctrice, &#xE9;volutive et/ou pr&#xE9;ventive de la solution, du mat&#xE9;riel et des &#xE9;quipements et param&#xE9;trage du SI </li> <li>Mise en exploitation de l&apos;application avec livraison des documents requis (dossier exploitation,..) </li> <li>Mise en oeuvre des plans de secours et de sauvegarde </li> <li>Pilotage de l&apos;exploitation des syst&#xE8;mes, r&#xE9;seaux et applications et gestion des incidents (diagnostic, intervention, alerte) </li> <li>Planification de l&apos;activit&#xE9; et de l&apos;ensemble des moyens (humains, financiers, techniques) &#xE0; mettre en &#x153;uvre pour la continuit&#xE9; de service </li> <li>R&#xE9;alisation des tests d&apos;installation, d&apos;exploitation et d&apos;int&#xE9;gration technique, et traitement des anomalies et analyses des performances des syst&#xE8;mes, r&#xE9;seaux et applications </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et &#xE9;valuer les prestations SI (r&#xE9;seaux, applications, BDD,...) de service internes et externes et enclencher les actions cons&#xE9;cutives n&#xE9;cessaires </li> <li>Construire et utiliser des outils de pilotage (crit&#xE8;res, indicateurs / tableau de bord) </li> <li>D&#xE9;finir et communiquer aux fournisseurs les conditions d&apos;intervention (plan de pr&#xE9;vention de l&apos;&#xE9;tablissement et sp&#xE9;cificit&#xE9;s de la DSI) </li> <li>Evaluer la coh&#xE9;rence entre les capacit&#xE9;s et efficacit&#xE9; des syst&#xE8;mes int&#xE9;gr&#xE9;s et les sp&#xE9;cifications </li> <li>Evaluer la performance du syst&#xE8;me avant, pendant et apr&#xE8;s son int&#xE9;gration </li> <li>Evaluer les actions de maintenance et communiquer en cons&#xE9;quence </li> <li>Installer, configurer, param&#xE9;trer et tester un syst&#xE8;me et/ou un logiciel et/ou un composant et/ou un poste de travail </li> <li>R&#xE9;diger et garantir la diffusion de la documentation technique </li> <li>R&#xE9;diger les accords contractuels sur les niveaux de service </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Architecture SI m&#xE9;tier et fonctionnelle </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Domaine d&apos;application et disponibilit&#xE9; des outils de diagnostic </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Informatique, SI et veille technologique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / Proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; la gestion du syst&#xE8;me d&apos;informations </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de contr&#xF4;le de la prestation de services </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation, m&#xE9;tiers et fonctionnement de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Proc&#xE9;dures internes de remont&#xE9;e d&apos;information et d&apos;incidents critiques ou non </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; informatique, s&#xE9;curit&#xE9; SI </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Standards et normes applicables </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me en informatique bac + 2 &#xE0; bac + 5<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier<br>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Expert technique (r&#xE9;seau, syst&#xE8;me, BDD, ...)<br>Charg&#xE9; des applications informatiques<br>Chef de projet informatique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable de la maintenance tous corps d''état (TCE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter les activit&#xE9;s et les moyens d&apos;une unit&#xE9; ou d&apos;un service concernant le patrimoine immobilier, les &#xE9;quipements et la maintenance, en animant une &#xE9;quipe de professionnels, et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations, dans le respect des r&#xE8;gles QHSE, des co&#xFB;ts et des d&#xE9;lais<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Coordonnateur(trice) de la gestion de maintenance et de patrimoine<br>Responsable maintenance<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Administrateur(trice) de GMAO<br>Gestion des risques techniques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des activit&#xE9;s, dans son domaine </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Lancement, suivi et contr&#xF4;le des r&#xE9;alisations en r&#xE9;gie ou sous-trait&#xE9;es, relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Mise en conformit&#xE9; des installations, en application des normes et r&#xE8;glementations en vigueur </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Suivi et contr&#xF4;le budg&#xE9;taire relatif au domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Choisir une m&#xE9;thode, un moyen correspondant &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Evaluer et choisir une offre de produits / de services relatifs &#xE0; son domaine </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire la strat&#xE9;gie en orientations, en projets, en plans d&apos;actions et en moyens de r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">B&#xE2;timents et installations </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectricit&#xE9; g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Fluides </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation environnementale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">TCE / VRD </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable de service technique pour prises de d&#xE9;cision<br>Conducteur d&apos;op&#xE9;ration pour l&apos;int&#xE9;gration, dans les projets de travaux, des solutions de r&#xE9;duction et de facilitement de la maintenance<br>Entreprise de maintenance pour la r&#xE9;alisation des op&#xE9;rations de maintenance<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC +2/3 en maintenance<br>BAC +5 Ing&#xE9;nieur maintenance<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Conducteur d&apos;op&#xE9;ration tous corps d&apos;&#xE9;tat (TCE)<br>Responsable des travaux<br>Responsable des services biom&#xE9;dicaux<br>Coordinateur qualit&#xE9; / gestion des risques<br>Acheteur(euse)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Orthoprothésiste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un bilan fonctionnel et situationnel aupr&#xE8;s de personnes pr&#xE9;sentant un handicap, des troubles des fonctions, une d&#xE9;ficience.<br>Concevoir, r&#xE9;aliser et d&#xE9;livrer des orth&#xE8;ses et des proth&#xE8;ses externes sur mesure et sur moulage.<br>Conseiller, adapter et d&#xE9;livrer des appareillages orthop&#xE9;diques de s&#xE9;rie, des proth&#xE8;ses externes non orthop&#xE9;diques, des aides techniques et des assistances technologiques.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Apport de conseils et d&apos;expertise au sein d&apos;une &#xE9;quipe pluridisciplinaire et aupr&#xE8;s de diff&#xE9;rents professionnels de sant&#xE9; </li> <li>Conseil et information &#xE0; la personne et &#xE0; son entourage </li> <li>D&#xE9;finition, r&#xE9;alisation et adaptation de l&apos;appareillage orth&#xE9;tique et proth&#xE9;tique </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Mise en place de d&#xE9;marches qualit&#xE9; / environnement et r&#xE9;daction de documents requis pour l&apos;activit&#xE9; de l&apos;entreprise </li> <li>R&#xE9;alisation de l&apos;&#xE9;ducation proth&#xE9;tique </li> <li>Recueil d&apos;informations, entretiens et &#xE9;valuations visant au bilan orth&#xE9;tique et proth&#xE9;tique </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Suivi de la personne appareill&#xE9;e </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter l&apos;appareillage aux besoins et aux caract&#xE9;ristiques du patient </li> <li>Choisir des appareillages de s&#xE9;rie, des aides techniques et des assistances technologiques au regard d&apos;un projet d&apos;intervention personnalis&#xE9; </li> <li>Communiquer et conduire une relation dans un contexte d&apos;intervention </li> <li>Concevoir et conduire une d&#xE9;marche d&apos;information, de conseil et d&apos;&#xE9;ducation </li> <li>Concevoir un appareillage orth&#xE9;tique et proth&#xE9;tique au regard d&apos;un projet d&apos;intervention personnalis&#xE9; </li> <li>Coop&#xE9;rer avec d&apos;autres professionnels </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Evaluer une situation et &#xE9;laborer un diagnostic dans le domaine d&apos;activit&#xE9; de l&apos;orthoproth&#xE9;siste </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Mettre en &#x153;uvre les techniques de fabrication de l&apos;appareillage dans le domaine de l&apos;orthop&#xE9;die </li> <li>R&#xE9;aliser la prise d&apos;empreinte et/ou le moulage </li> <li>R&#xE9;aliser le suivi de l&apos;appareillage </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Fabrication d&apos;orth&#xE8;ses et de proth&#xE8;ses </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sciences humaines </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BTS Orthoproth&#xE9;siste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Gestionnaire de données', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Traiter les donn&#xE9;es li&#xE9;es &#xE0; la gestion RH, finance et logistique<br>Contr&#xF4;ler la coh&#xE9;rence et la qualit&#xE9; des donn&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Contr&#xF4;leur(euse) de gestion adjoint<br>Assistant(e) contr&#xF4;leur de gestion<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En ressources humaines<br>En finance<br>En logistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Acquisition, traitement et archivage des donn&#xE9;es dans son domaine d&apos;activit&#xE9; </li> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la coh&#xE9;rence des donn&#xE9;es / informations sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le des donn&#xE9;es / documents relatifs &#xE0; son domaine </li> <li>Programmation des outils d&apos;automatisation </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Auditer l&apos;&#xE9;tat g&#xE9;n&#xE9;ral d&apos;une situation, d&apos;un syst&#xE8;me, d&apos;une organisation dans son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er des supports de communication et p&#xE9;dagogiques adapt&#xE9;s aux publics </li> <li>Cr&#xE9;er et/ou utiliser un r&#xE9;f&#xE9;rentiel de donn&#xE9;es dans son domaine </li> <li>Traiter et analyser des bases de donn&#xE9;es et statistiques </li> <li>Utiliser les logiciels m&#xE9;tier </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Analyse financi&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Base de donn&#xE9;es, techniques d&apos;enqu&#xEA;te </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Bureautique (Excel et Word avanc&#xE9;s et bases de donn&#xE9;es) </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Contr&#xF4;le de gestion </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">&#xC9;conomie de la sant&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;analyse financi&#xE8;re </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion RH </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Statistiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Contr&#xF4;leur de gestion<br>Directeur-adjoint en charge du contr&#xF4;le de gestion<br>Chef de p&#xF4;le<br>Cadre administratif de p&#xF4;le<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitaliers<br>Adjoint administratif hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Infirmier(ère) puériculteur(trice) (IPDE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Dispenser des soins aupr&#xE8;s des enfants pour maintenir, restaurer et promouvoir la sant&#xE9;, le d&#xE9;veloppement, l&apos;&#xE9;veil, l&apos;autonomie et la socialisation.<br>Evaluer l&apos;&#xE9;tat de sant&#xE9; d&apos;un enfant, d&#xE9;finir des projets de soins personnalis&#xE9;s, planifier et prodiguer des soins, mettre en &#x153;uvre des traitements.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Pu&#xE9;riculteur(trice)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En protection maternelle et infantile<br>En cr&#xE8;che<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;infirmier et dipl&#xF4;me d&apos;Etat de pu&#xE9;riculteur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Coordination de soins et conduite de projets, concernant le d&#xE9;veloppement, les activit&#xE9;s d&apos;&#xE9;veil, la pathologie </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Mise en oeuvre d&apos;activit&#xE9;s &#xE0; vis&#xE9;e &#xE9;ducative et d&apos;accompagnement &#xE0; la parentalit&#xE9; </li> <li>Mise en oeuvre d&apos;activit&#xE9;s de promotion de la sant&#xE9; dans une approche de sant&#xE9; publique et de protection de l&apos;enfance </li> <li>R&#xE9;alisation de soins et d&apos;activit&#xE9;s &#xE0; vis&#xE9;e diagnostique, th&#xE9;rapeutique et pr&#xE9;ventive aupr&#xE8;s de l&apos;enfant et de son entourage </li> <li>Recueil d&apos;informations concernant le d&#xE9;veloppement et la sant&#xE9; de l&apos;enfant de la naissance &#xE0; l&apos;adolescence </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner et soutenir les familles dans le processus de parentalit&#xE9; </li> <li>Concevoir et conduire un projet de soins et d&apos;&#xE9;ducation adapt&#xE9; &#xE0; l&apos;enfant </li> <li>Concevoir et mettre en &#x153;uvre des activit&#xE9;s de promotion de la sant&#xE9; de l&apos;enfant et de protection de l&apos;enfance </li> <li>Evaluer l&apos;&#xE9;tat de sant&#xE9; et le d&#xE9;veloppement des enfants et des adolescents </li> <li>G&#xE9;rer les ressources d&apos;un service ou d&apos;un &#xE9;tablissement d&apos;accueil d&apos;enfants </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Mettre en &#x153;uvre des soins adapt&#xE9;s aux enfants pr&#xE9;sentant des alt&#xE9;rations de sant&#xE9; </li> <li>Organiser et coordonner les soins et les activit&#xE9;s de d&#xE9;veloppement et d&apos;&#xE9;veil pour des enfants et des adolescents </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">D&#xE9;veloppement psychomoteur de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re et Pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodologie d&apos;analyse de situation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Nutrition infantile </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement des structures sociales et m&#xE9;dico-sociales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE9;diatrie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Psychologie de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pu&#xE9;riculture </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>P&#xE9;diatre, p&#xE9;do-psychiatre, m&#xE9;decin, pu&#xE9;riculteur de la protection maternelle et infantile et autres professionnels param&#xE9;dicaux pour le suivi de l&apos;enfant<br>Sage femme pour le travail en collaboration &#xE0; la maternit&#xE9;<br>Professionnels de l&apos;&#xE9;ducation pour le suivi du d&#xE9;veloppement de l&apos;enfant<br>Travailleurs sociaux pour le suivi social de la famille<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;&#xE9;tat de pu&#xE9;riculteur<br>Un an d&apos;&#xE9;tude apr&#xE8;s le concours d&apos;entr&#xE9;e dans une &#xE9;cole sp&#xE9;cialis&#xE9;e<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des infirmiers en soins g&#xE9;n&#xE9;raux et sp&#xE9;cialis&#xE9;s (Grade 2 et 3)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Hygi&#xE9;niste<br>Coordinateur de parcours en sant&#xE9;<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS Directeur des Ressources Humaines', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire:<br>D&#xE9;finir et piloter la mise en oeuvre de la politique de gestion et de d&#xE9;veloppement des ressources humaines, dans le cadre de la strat&#xE9;gie de l&apos;&#xE9;tablissement.<br>Piloter la masse salariale.<br>Animer les relations sociales avec les diff&#xE9;rents acteurs de l&apos;&#xE9;tablissement&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Directeur(e) des ressources humaines et des relations sociales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs et expertise aux p&#xF4;les, directions fonctionnelles dans le cadre de la d&#xE9;l&#xE9;gation de gestion RH </li> <li>Conseil sur la d&#xE9;finition de(s) projet(s) professionnel(s) / individuel(s) et bilan des r&#xE9;alisations </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Elaboration, mise en oeuvre et &#xE9;valuation d&apos;une politique de GPEC, d&apos;attractivit&#xE9; et de fid&#xE9;lisation </li> <li>Elaboration, mise en oeuvre et &#xE9;valuation de la strat&#xE9;gie RH en mati&#xE8;re de recrutement, fid&#xE9;lisation, attractivit&#xE9; GPEC, mobilit&#xE9;, formation, conditions de travail, communication et politique manag&#xE9;riale </li> <li>Gestion des contentieux, dans son domaine d&apos;activit&#xE9; </li> <li>Pilotage et animation du dialogue social et des instances repr&#xE9;sentatives CTE, CHSCT et CAP </li> <li>Production et exploitation de statistiques relevant de son domaine d&apos;activit&#xE9; </li> <li>Suivi et contr&#xF4;le budg&#xE9;taire relatif au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, conduire, adapter le programme de d&#xE9;veloppement professionnel d&apos;une cat&#xE9;gorie professionnelle ou d&apos;une personne </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir, piloter et mettre en &#x153;uvre la politique d&apos;action sociale de l&apos;&#xE9;tablissement ? </li> <li>D&#xE9;finir, piloter et mettre en &#x153;uvre la politique de recrutement et de mobilit&#xE9; interne ? </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>G&#xE9;rer ses &#xE9;motions, son stress </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>Mettre en place et exploiter de fa&#xE7;on optimale un syst&#xE8;me d&apos;information relatif &#xE0; son domaine de comp&#xE9;tence ? </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> <li>Travailler en partenariat avec les responsables de l&apos;encadrement des unit&#xE9;s concern&#xE9;es </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Audit social </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication &#xE9;crite </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication orale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit du travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion des risques professionnels </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation en hygi&#xE8;ne, s&#xE9;curit&#xE9; et conditions de travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les membres de l&apos;&#xE9;quipe de direction<br>Les cadres<br>Les repr&#xE9;sentants du personnel<br>L&apos;ensemble des personnels m&#xE9;dicaux et non m&#xE9;dicaux<br>Les membres du Conseil d&apos;Administration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Ecole des Hautes Etudes en Sant&#xE9; Publique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Métallier - serrurier', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des travaux de maintenance, d&apos;entretien et de r&#xE9;novation des &#xE9;quipements et des ouvrages m&#xE9;talliques et d&#xE9;riv&#xE9;s ainsi que des dispositifs de cl&#xF4;ture et de fermeture.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Serrurier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gestionnaire de l&apos;organigramme des cl&#xE9;s<br>Storiste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Ajustement, pose et fixation d&apos;&#xE9;quipements sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Assemblage de t&#xF4;les et de profil&#xE9;s </li> <li>Conception d&apos;ouvrages m&#xE9;talliques et d&#xE9;riv&#xE9;s &#xE0; r&#xE9;aliser (plans, choix des mat&#xE9;riaux, calepinage...) </li> <li>Entretien, maintenance, pr&#xE9;vention et d&#xE9;pannage des mat&#xE9;riels, outillages, &#xE9;quipements, syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>Fa&#xE7;onnage de t&#xF4;les et de profil&#xE9;s (blindage, ferronnerie...) </li> <li>Mise en place de protections de chantier et / ou individuelles </li> <li>Renseignement de documents, de fichiers (fiches d&apos;activit&#xE9;, de tra&#xE7;abilit&#xE9;, etc.) </li> <li>Reproduction de cl&#xE9;s </li> <li>Tenue &#xE0; jour des donn&#xE9;es / des fichiers relatifs au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et &#xE9;valuer les performances d&apos;un syst&#xE8;me, d&apos;un appareil, d&apos;un outil sp&#xE9;cifique &#xE0; son domaine technique </li> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements, outillages, dans son domaine de comp&#xE9;tence </li> <li>Dessiner un plan ou un croquis technique relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la r&#xE9;sistance m&#xE9;canique appropri&#xE9;e &#xE0; l&apos;ouvrage r&#xE9;alis&#xE9; </li> <li>&#xC9;valuer, choisir, utiliser les produits et/ou mat&#xE9;riaux n&#xE9;cessaire &#xE0; la pratique de son m&#xE9;tier </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Souder des &#xE9;l&#xE9;ments m&#xE9;talliques de divers nature </li> <li>Utiliser des mat&#xE9;riels, des outils de d&#xE9;pannage, de r&#xE9;paration ou de maintenance, relatifs &#xE0; son m&#xE9;tier </li> <li>Utiliser des mat&#xE9;riels, des outils de diagnostic, de travail ou/et de contr&#xF4;le sp&#xE9;cifiques &#xE0; son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrant maintenance en b&#xE2;timent et /ou responsable maintenance TCE (et VRD) pour la programmation du travail et la prise de d&#xE9;cision en mati&#xE8;re de r&#xE9;paration et de r&#xE9;alisation<br>Magasinier pour l&apos;approvisionnement en mat&#xE9;riaux et pi&#xE8;ces d&#xE9;tach&#xE9;es<br>Accueil- secr&#xE9;tariat des services techniques pour les demandes d&apos;intervention urgentes<br>Encadrement des services pour la programmation et le d&#xE9;roulement des travaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Certificat d&apos;aptitude professionnelle (CAP)<br>Ou brevet d&apos;&#xE9;tudes professionnelles (BEP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de maintenance g&#xE9;n&#xE9;rale des b&#xE2;timents<br>Encadrant maintenance corps d&apos;&#xE9;tat secondaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Gestionnaire finances', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Traiter les op&#xE9;rations budg&#xE9;taires, de recette ou de r&#xE9;gie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gestionnaire recettes<br>Gestionnaire r&#xE9;gie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint administratif hospitalier<br>Adjoint des cadres hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Gestionnaire comptable et achat<br>Gestionnaire admissions frais de s&#xE9;jour traitement externe<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Infirmier(ère) en anesthésie- réanimation (IADE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des soins infirmiers d&apos;anesth&#xE9;sie et/ou de r&#xE9;animation concourant au diagnostic, au traitement et &#xE0; la recherche.<br>Accompagner le patient, dans les domaines de l&apos;intervention chirurgicale, du traitement de la douleur, de l&apos;urgence ou de la r&#xE9;animation.<br>Mettre en &#x153;uvre des mesures qui garantissent la s&#xE9;curit&#xE9; des patients en anesth&#xE9;sie-r&#xE9;animation dans la p&#xE9;riode p&#xE9;ri-interventionnelle.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Infirmier(&#xE8;re) anesth&#xE9;siste<br>Infirmier(&#xE8;re) anesth&#xE9;siste dipl&#xF4;m&#xE9; d&apos;&#xE9;tat<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Tranports sanitaires<br>Formateur(trice) &quot;Attestation de Formation aux Gestes de Soins d&apos;Urgence (AFGSU)&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;infirmier compl&#xE9;t&#xE9; par une exp&#xE9;rience professionnelle d&apos;infirmier pendant au moins 2 ans, dipl&#xF4;me d&apos;Etat d&apos;infirmier anesth&#xE9;siste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des activit&#xE9;s, dans son domaine </li> <li>Etudes, travaux de recherche et formation continue en anesth&#xE9;sie-r&#xE9;animation, douleur et urgences </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Information, communication et accompagnement du patient tout au long de sa prise en charge </li> <li>Mise en &#x153;uvre et contr&#xF4;le de la tra&#xE7;abilit&#xE9; en anesth&#xE9;sie-r&#xE9;animation </li> <li>Mise en &#x153;uvre et contr&#xF4;le des mesures de pr&#xE9;vention des risques et des op&#xE9;rations de vigilance en anesth&#xE9;sie-r&#xE9;animation </li> <li>Mise en &#x153;uvre et suivi de l&apos;anesth&#xE9;sie et de l&apos;analg&#xE9;sie en fonction du patient, de l&apos;intervention et de la technique anesth&#xE9;sique </li> <li>Pr&#xE9;paration et organisation du site et du mat&#xE9;riel d&apos;anesth&#xE9;sie en fonction du patient, du type d&apos;intervention et du type d&apos;anesth&#xE9;sie </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Veille professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Analyser la qualit&#xE9; et la s&#xE9;curit&#xE9; en anesth&#xE9;sie-r&#xE9;animation </li> <li>Analyser la situation, anticiper les risques associ&#xE9;s en fonction du type d&apos;anesth&#xE9;sie, des caract&#xE9;ristiques du patient et de l&apos;intervention et ajuster la prise en charge anesth&#xE9;sique </li> <li>Analyser le comportement du patient, l&apos;accompagner et fournir une information adapt&#xE9;e &#xE0; la situation d&apos;anesth&#xE9;sie </li> <li>Anticiper et mettre en place une organisation du site d&apos;anesth&#xE9;sie en fonction du patient, du type d&apos;intervention et du type d&apos;anesth&#xE9;sie </li> <li>Coordonner ses actions avec les intervenants dans le cadre de l&apos;anesth&#xE9;sie-r&#xE9;animation, de l&apos;urgence intra et extrahospitali&#xE8;re et de la prise en charge de la douleur </li> <li>&#xC9;valuer et traiter la douleur des patients </li> <li>Informer et former des professionnels et des personnes en formation </li> <li>Mettre en &#x153;uvre et adapter la proc&#xE9;dure d&apos;anesth&#xE9;sie en fonction du patient et du d&#xE9;roulement de l&apos;intervention </li> <li>R&#xE9;aliser l&apos;induction anesth&#xE9;sique en collaboration avec le m&#xE9;decin anesth&#xE9;siste </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Anesth&#xE9;sie et r&#xE9;animation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re et Pr&#xE9;vention des infections nosocomiales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation des syst&#xE8;mes d&apos;urgence </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation du travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pharmacologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decin anesth&#xE9;siste pour le travail en collaboration<br>&#xC9;quipe chirurgicale (chirurgiens, IBODE) et radiologique pour le travail en compl&#xE9;mentarit&#xE9;<br>Infirmiers pour la continuit&#xE9; des soins<br>Autres professionnels param&#xE9;dicaux pour la collaboration et la continuit&#xE9; des soins<br>Techniciens bio-m&#xE9;dicaux pour l&apos;entretien et le renouvellemnt du mat&#xE9;riel m&#xE9;dical<br>Pharmacien pour la connaissance des produits pharmaceutiques et pour l&apos;utilisation des dispositifs m&#xE9;dicaux st&#xE9;riles<br>Hygi&#xE9;niste pour l&apos;adaptation des proc&#xE9;dures d&apos;hygi&#xE8;ne dans le domaine d&apos;activit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formation de 2 ans dans une &#xE9;cole sp&#xE9;cialis&#xE9;e<br>Dipl&#xF4;me d&apos;&#xE9;tat d&apos;infirmier anesth&#xE9;siste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des infirmiers en soins g&#xE9;n&#xE9;raux et sp&#xE9;cialis&#xE9;s (Grade 3 et 4)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Hygi&#xE9;niste<br>Coordinateur de pr&#xE9;l&#xE8;vement ou de transplantation d&apos;organe<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant d''unité de soins et d''activités paramédicales', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser l&apos;activit&#xE9; de soins et des prestations associ&#xE9;es,<br>Manager l&apos;&#xE9;quipe et coordonner les moyens d&apos;un service de soins, m&#xE9;dicotechniques ou de r&#xE9;&#xE9;ducation, en veillant &#xE0; l&apos;efficacit&#xE9; et la qualit&#xE9; des prestations.<br>D&#xE9;velopper la culture du signalement et g&#xE9;rer les risques<br>D&#xE9;velopper les comp&#xE9;tences individuelles et collectives.<br>Participer &#xE0; la gestion m&#xE9;dico-&#xE9;conomique au sein du p&#xF4;le.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Cadre de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de cadre de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des soins et activit&#xE9;s param&#xE9;dicales, dans son domaine </li> <li>Coordination et suivi de la prise en charge de prestations </li> <li>&#xC9;laboration et r&#xE9;daction de rapports d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Montage, mise en oeuvre, suivi et gestion de projets sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Organisation et suivi de l&apos;accueil (agents, stagiaires, nouveaux recrut&#xE9;s.) </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Promotion des r&#xE9;alisations / des projets sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Organiser une unit&#xE9;, un service, en fonction de diff&#xE9;rents crit&#xE8;res (missions, strat&#xE9;gie, moyens, etc.) </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation d&apos;&#xE9;quipe </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;valuation de la charge de travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodologie d&apos;analyse de situation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation du travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction des soins pour l&apos;&#xE9;laboration et le suivi de projets<br>m&#xE9;decins pour la gestion journali&#xE8;re de l&apos;unit&#xE9;<br>autres services de soins, m&#xE9;dico techniques, logistiques, administratifs pour l&apos;organisation des activit&#xE9;s et des soins et pour la gestion des mat&#xE9;riels<br>partenaires de sant&#xE9; extra hospitaliers pour la continuit&#xE9; des prestations<br>fournisseurs externes de mat&#xE9;riels pour la veille technique et le choix des moyens<br>service de formation continue pour organiser des formations adapt&#xE9;es aux besoins des personnels<br>instituts de formation pour l&apos;accueil et l&apos;encadrement des stagiaires<br>responsables du p&#xF4;le et autres cadre du p&#xF4;le pour l&apos;&#xE9;laboration et le suivi des projets du p&#xF4;le<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Une ann&#xE9;e de formation, apr&#xE8;s s&#xE9;lection d&apos;entr&#xE9;e, en institut de formation des cadres de sant&#xE9;<br>Dipl&#xF4;me de cadre de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Cadre de sant&#xE9; param&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Cadre soignant de p&#xF4;le<br>Formateur des professionnels de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Podo-orthésiste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un bilan podo-locomoteur aupr&#xE8;s de personnes pr&#xE9;sentant une d&#xE9;ficience de la posture, de la marche et du chaussage.<br>Concevoir et r&#xE9;aliser un appareillage orthop&#xE9;dique externe du pied ou du couple pied-jambe sur mesure ou sur moulage.<br>Conseiller, d&#xE9;livrer et adapter des appareillages de s&#xE9;rie et des aides techniques.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Apport de conseils et d&apos;expertise au sein d&apos;une &#xE9;quipe pluridisciplinaire et aupr&#xE8;s de diff&#xE9;rents professionnels de sant&#xE9; </li> <li>Conception, fabrication et adaptation d&apos;appareillage orthop&#xE9;dique externe du pied et du couple pied-jambe </li> <li>Conseil et information &#xE0; la personne et &#xE0; son entourage </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Mise en place de d&#xE9;marches qualit&#xE9; / environnement et r&#xE9;daction de documents requis pour l&apos;activit&#xE9; de l&apos;entreprise </li> <li>R&#xE9;alisation de l&apos;&#xE9;ducation proth&#xE9;tique </li> <li>Recueil d&apos;informations, r&#xE9;alisation d&apos;entretien et de bilan podo-locomoteur visant &#xE0; poser un diagnostic dans le domaine de la podo-orth&#xE9;sie </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Suivi de la personne appareill&#xE9;e </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter l&apos;appareillage aux besoins et aux caract&#xE9;ristiques du patient </li> <li>Choisir un appareillage orthop&#xE9;dique externe du pied et du couple pied-jambe de s&#xE9;rie au regard d&apos;un projet d&apos;intervention personnalis&#xE9; </li> <li>Communiquer et conduire une relation dans un contexte d&apos;intervention </li> <li>Concevoir et conduire une d&#xE9;marche d&apos;information, de conseil et d&apos;&#xE9;ducation </li> <li>Concevoir un appareillage orthop&#xE9;dique externe du pied et du couple pied-jambe sur mesure au regard d&apos;un projet d&apos;intervention personnalis&#xE9; </li> <li>Coop&#xE9;rer avec d&apos;autres professionnels </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Evaluer une situation et &#xE9;laborer un diagnostic dans le domaine de la podo-orth&#xE9;sie </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Mettre en &#x153;uvre les techniques de fabrication de l&apos;appareillage dans le domaine de la podo-orth&#xE9;sie </li> <li>R&#xE9;aliser la prise d&apos;empreinte et/ou le moulage </li> <li>R&#xE9;aliser le suivi de l&apos;appareillage </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Confection de podo-orth&#xE8;se </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sciences humaines </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BTS podo-orth&#xE9;siste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN CHANTIER \"Coordinateur(trice) de parcours en santé\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&#xAB; Etablir, organiser et g&#xE9;rer des liens permettant l&apos;optimisation de la prise en charge sanitaire, sociale et m&#xE9;dicosociale d&apos;une personne dans un parcours de soins.<br>Coordonner la r&#xE9;alisation d&apos;une d&#xE9;marche pluridisciplinaire selon le projet de soins &#xE9;tabli. &#xBB;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Appellations provisoires :<br>&quot;Gestionnaire de cas<br>Coordinateur(trice) de parcours patient&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Sp&#xE9;cificit&#xE9; provisoire :<br>&quot;Coordinateur(trice) de r&#xE9;seau de sant&#xE9;&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accompagnement du patient et des aidants tout au long de son parcours de sant&#xE9; </li> <li>Accueil, information et formation des nouveaux personnels, des stagiaires et des professionnels de sant&#xE9; </li> <li>Co-construction, suivi et adaptation du projet personnalis&#xE9; du patient (projet de soins et projet de vie) </li> <li>Conception et r&#xE9;alisation d&apos;outils et/ou de m&#xE9;thodes </li> <li>Coordination des interlocuteurs internes et externes sur le territoire de sant&#xE9; (&#xE9;tablissements de sant&#xE9;, lib&#xE9;raux, r&#xE9;seaux, associations) </li> <li>&#xC9;laboration et r&#xE9;daction de rapports d&apos;activit&#xE9; </li> <li>Information sur l&apos;offre de soins du territoire, conseil et orientation des patients </li> <li>Intervention et traitement d&apos;urgence suite &#xE0; alerte ou / et situation &#xE0; risques </li> <li>Recensement des ressources m&#xE9;dicales, environnementales et bio psycho-sociales </li> <li>R&#xE9;daction des comptes rendus relatifs aux observations, aux entretiens et aux interventions </li> <li>Renseignement et mise &#xE0; jour des donn&#xE9;es administratives, m&#xE9;dicales, cliniques, psychosocioculturelles dans le dossier patient </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser, &#xE9;valuer la situation m&#xE9;dicale, clinique et psycho-sociale d&apos;une personne </li> <li>Analyser, synth&#xE9;tiser des informations permettant la prise en charge de la personne soign&#xE9;e et la continuit&#xE9; des soins </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Concevoir, formaliser des proc&#xE9;dures, des protocoles et des fiches de conseil </li> <li>Conduire un entretien d&apos;aide </li> <li>Conseiller le patient et son entourage dans le cadre du projet personnalis&#xE9; du patient </li> <li>Construire / adapter des outils / m&#xE9;thodes de travail </li> <li>Elaborer et formaliser un diagnostic sant&#xE9; du patient </li> <li>&#xC9;valuer la pertinence et la v&#xE9;racit&#xE9; des donn&#xE9;es et/ou informations </li> <li>Evaluer la prise en charge du parcours patient </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bureautique/Technologies information et communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit de la sant&#xE9; dans son domaine d&apos;activit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;conomie de la sant&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodologie d&apos;analyse de situation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Recherche </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;seaux sanitaires, sociaux et m&#xE9;dico-sociaux </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Pour la faisabililit&#xE9; du projet personnalis&#xE9; du patient : les m&#xE9;decins, l&apos;encadrement, les m&#xE9;tiers de la coordination en sant&#xE9;, les r&#xE9;&#xE9;ducateurs, les psychologues, les assistants sociaux et les &#xE9;ducateurs sp&#xE9;cialis&#xE9;s des &#xE9;tablissements de sant&#xE9; (sanitaires, sociaux, m&#xE9;dico-sociaux) ; les m&#xE9;decins de ville, les professionnels lib&#xE9;raux (infirmiers, r&#xE9;&#xE9;ducateurs, sages-femmes,...) ; les m&#xE9;decins, les infirmiers et les psychologues des r&#xE9;seaux ; les aidants<br>Pour la coordination et l&apos;optimisation de la prise en charge : les m&#xE9;decins, l&apos;encadrement, les assistants sociaux, les professionnels de sant&#xE9; sp&#xE9;cialis&#xE9;s, les psychologues des &#xE9;tablissements de sant&#xE9; (sanitaires, sociaux, m&#xE9;dico-sociaux) et des r&#xE9;seaux ; les m&#xE9;decins de ville, les professionnels lib&#xE9;raux (infirmiers, r&#xE9;&#xE9;ducateurs, sages-femmes,...) ; les professionnels de la pharmacie et de la logistique ; les infirmiers de coordination des &#xE9;tablissements de sant&#xE9; et des services de soins infirmiers &#xE0; domicile, les associations ; les prestataires de service ; les aidants ; les centres communaux d&apos;action sociale ; les maisons d&#xE9;partementales des personnes handicap&#xE9;es ; les caisses nationales de solidarit&#xE9; pour l&apos;autonomie<br>Pour am&#xE9;liorer la connaissance et la pertinence de l&apos;offre de soins : les m&#xE9;decins, les directeurs, l&apos;encadrement, les m&#xE9;tiers de la coordination en sant&#xE9; et les assistants sociaux, les &#xE9;ducateurs sp&#xE9;cialis&#xE9;s et les param&#xE9;dicaux des &#xE9;tablissements de sant&#xE9; (sanitaires, sociaux et m&#xE9;dico-sociaux) ; les m&#xE9;decins de ville, les agences r&#xE9;gionales de sant&#xE9; ; les responsables de r&#xE9;seaux et des unions r&#xE9;gionales et nationales de r&#xE9;eaux ; les associations professionnelles ; l&apos;urbanisme des mairies et les &#xE9;tudiants des &#xE9;tablissements de formation (Instituts de formation en soins infirmiers, Instituts de formation de cadres de sant&#xE9;, Universit&#xE9;s)<br>Pour g&#xE9;rer les urgences et les situations complexes qui n&#xE9;cessitent un recours : les maisons d&#xE9;partementales des^personnes handicap&#xE9;es, les services des urgences, les services d&apos;aide m&#xE9;dicale d&apos;urgence<br>Pour conduire des recherches : les universit&#xE9;s, les instituts de formation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Master 2 et exp&#xE9;rience en sant&#xE9; d&apos;au moins 3 ans<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Infirmier, pu&#xE9;ricultrice, infirmier anesth&#xE9;siste, infirmier de bloc op&#xE9;ratoire, di&#xE9;t&#xE9;ticien, ergoth&#xE9;rapeute, masseur-kin&#xE9;sith&#xE9;rapeute, orthophoniste, psychomotricienne, cadre de sant&#xE9;, cadre sup&#xE9;rieur de sant&#xE9;, assistant socio-&#xE9;ducatif, psychologue<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Directeur(e) des soins en institut de formation\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire:<br>&quot;Diriger une structure de formation aux m&#xE9;tiers de la sant&#xE9;&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Directeur(e) d&apos;&#xE9;cole de formation aux m&#xE9;tiers de la sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Conseiller(&#xE8;re) p&#xE9;dagogique r&#xE9;gional, national<br>Coordonnateur(trice) de plusieurs instituts de formation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Exp&#xE9;rience de formateur<br>Exp&#xE9;rience de cadre de secteur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>D&#xE9;finition des sp&#xE9;cifications techniques et r&#xE9;daction du cahier des charges sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Enseignement et supervision de travaux dirig&#xE9;s de recherche et d&apos;&#xE9;tudes, dans son domaine de comp&#xE9;tence </li> <li>Organisation / coordination / supervision des contr&#xF4;les relatifs aux activit&#xE9;s du secteur, de l&apos;unit&#xE9; </li> <li>Organisation et mise en place de partenariats ext&#xE9;rieurs </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>R&#xE9;daction de notes de synth&#xE8;se, relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir et organiser des dispositifs et actions de formation, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>N&#xE9;gocier des prestations, des contrats, des accords avec des interlocuteurs internes/externes </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Gestion budg&#xE9;taire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ing&#xE9;nierie de la formation </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Soins </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur technique et d&apos;enseignement pour l&apos;organisation et la d&#xE9;finition du contenu des enseignements<br>Equipes de direction et/ou les organismes de tutelle(DRASS - ARH) pour des &#xE9;changes et des n&#xE9;gociations<br>Professionnels de terrain pour l&apos;accueil des &#xE9;tudiants lors des stages<br>R&#xE9;seaux r&#xE9;gionaux pour la connaissance et la prise en compte des politiques r&#xE9;gionales<br>Conseil r&#xE9;gional pour la d&#xE9;termination du budget<br>Universit&#xE9;: doyen de la facult&#xE9; de m&#xE9;decine et instances universitaires pour une supervision p&#xE9;dagogique<br>Directeurs d&apos;&#xE9;coles de formation aux m&#xE9;tiers du soin d&apos;autres disciplines pour partenariat<br>Directeur technique et d&apos;enseignement pour l&apos;organisation et le contenu des enseignements<br>Equipes de direction et/ou les organismes de tutelle(DRASS -ARH) pour des &#xE9;changes et des n&#xE9;gociations<br>____ charg&#xE9;s de missions r&#xE9;gionaux (budget)_________<br>Professionnels de terrain pour l&apos;accueil des &#xE9;tudiants lors des stages<br>R&#xE9;seaux r&#xE9;gionaux pour la connaissance des politiques r&#xE9;gionales<br>Conseil r&#xE9;gional pour la d&#xE9;termination du budget<br>Universit&#xE9;: doyen de la facult&#xE9; de m&#xE9;decine et instances universitaires pour une supervision p&#xE9;dagogique<br>Directeurs d&apos;&#xE9;coles de formation aux m&#xE9;tiers du soin d&apos;autres disciplines pour partenariat<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Pour les directeurs sage-femmes :<br>Etudes de sage-femme, dipl&#xF4;me d&apos;etat de sage-femme<br>Ecole cadre sage-femme, certificat cadre sage-femme<br>Etudes universitaires, autres dipl&#xF4;mes universitaires compl&#xE9;mentaires<br>Pour les directeurs param&#xE9;diaux:<br>Dipl&#xF4;me professionnel param&#xE9;dical de la formation dispens&#xE9;e dans l&apos;institut de formation ou dans l&apos;&#xE9;cole<br>Dipl&#xF4;me cadre de sant&#xE9;, certificat de validation du cycle de formation de l&apos;E.H.E.S.P.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Directeur(trice) d&apos;&#xE9;cole de sages-femmes<br>Directeur(trice) d&apos;IFCS ou d&apos;IFSI<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Orthopédiste - orthésiste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un bilan corporel, fonctionnel et situationnel aupr&#xE8;s de personnes pr&#xE9;sentant une l&#xE9;sion ou modification corporelle entra&#xEE;nant une limitation fonctionnelle.<br>Concevoir, r&#xE9;aliser, conseiller, adapter et d&#xE9;livrer des orth&#xE8;ses sur mesure ou de s&#xE9;rie et des proth&#xE8;ses externes de sein ainsi que des aides techniques.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Adaptation et livraison des orth&#xE8;ses de s&#xE9;rie, des proth&#xE8;ses externes de sein et des aides techniques </li> <li>Apport de conseils et d&apos;expertise au sein d&apos;une &#xE9;quipe pluridisciplinaire et aupr&#xE8;s de diff&#xE9;rents professionnels de sant&#xE9; </li> <li>Conseil et information &#xE0; la personne et &#xE0; son entourage </li> <li>Constitution des dossiers techniques et r&#xE9;alisation d&apos;appareillage orth&#xE9;tique sur mesure </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Mise en place de d&#xE9;marches qualit&#xE9; / environnement et r&#xE9;daction de documents requis pour l&apos;activit&#xE9; de l&apos;entreprise </li> <li>R&#xE9;alisation de l&apos;&#xE9;ducation proth&#xE9;tique </li> <li>Recueil des informations visant &#xE0; &#xE9;tablir un bilan corporel, fonctionnel et situationnel </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter l&apos;appareillage aux besoins et aux caract&#xE9;ristiques du patient </li> <li>Communiquer et conduire une relation dans un contexte d&apos;intervention </li> <li>Concevoir et conduire une d&#xE9;marche d&apos;information, de conseil et d&apos;&#xE9;ducation </li> <li>Concevoir un appareillage dans le domaine de l&apos;orthop&#xE9;die-orth&#xE8;se et choisir des aides techniques au regard d&apos;un projet d&apos;intervention personnalis&#xE9; </li> <li>Coop&#xE9;rer avec d&apos;autres professionnels </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Evaluer une situation et &#xE9;laborer un diagnostic dans le domaine de l&apos;orthop&#xE9;die-orth&#xE8;se </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Mettre en &#x153;uvre les techniques de fabrication de l&apos;appareillage dans le domaine de l&apos;orthop&#xE9;die-orth&#xE8;se </li> <li>R&#xE9;aliser le suivi de l&apos;appareillage </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Fabrication d&apos;orth&#xE8;ses et de proth&#xE8;ses </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;mes d&apos;orthop&#xE9;diste-orth&#xE9;siste inscrits au RNCP<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Lingère - couturière', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Entretenir, marquer et g&#xE9;rer le linge personnel des r&#xE9;sidents en secteur d&apos;h&#xE9;bergement.<br>Fournir au personnel des tenues de travail en fonction des besoins pr&#xE9;&#xE9;tablis et en contr&#xF4;ler l&apos;acheminement dans les circuits de distribution.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Linger&#xE8;re<br>Couturier&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Confection de v&#xEA;tements, de linges sp&#xE9;cifiques </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Habillement des agents et essayages ( retouches) </li> <li>Inventaire de produits, de mat&#xE9;riels, &#xE9;quipements, dossiers </li> <li>Marquage et pliage de linge </li> <li>Mise &#xE0; la r&#xE9;forme et remplacement des v&#xEA;tements usag&#xE9;s </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Codifier des donn&#xE9;es, des produits </li> <li>Coudre &#xE0; la main </li> <li>&#xC9;valuer la qualit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riau </li> <li>Identifier et diagnostiquer un dysfonctionnement, une panne, le d&#xE9;faut d&apos;un mat&#xE9;riel, d&apos;un &#xE9;quipement, une anomalie d&apos;un syst&#xE8;me, sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> <li>Utiliser les techniques gestes et postures/ manutention </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Installations- &#xE9;quipements de blanchisserie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; au marquage et au suivi des articles linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; aux processus de nettoiement du linge </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Protocole d&apos;hygi&#xE8;ne du linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Technologie des mat&#xE9;riels de marquage, couture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Technologie des textiles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrement blanchisserie pour la mise en place des protocoles<br>Technicien de maintenance des mat&#xE9;riels et installations pour la r&#xE9;paration des mat&#xE9;riels de couture<br>Personnel soignant des unit&#xE9;s pour les &#xE9;changes d&apos;informations sur la gestion du linge des personnes h&#xE9;berg&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAP couture et habillement<br>CAP / BEP<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de logistique<br>Agent de blanchisserie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Psychomotricien(ne)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un diagnostic et r&#xE9;aliser en individuel ou en groupe des activit&#xE9;s d&apos;&#xE9;ducation psychomotrice, de pr&#xE9;vention, d&apos;&#xE9;ducation pour la sant&#xE9;, de r&#xE9;&#xE9;ducation, de r&#xE9;adaptation des fonctions psychomotrices et de th&#xE9;rapie psychocorporelle.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>R&#xE9;&#xE9;ducateur(trice) en psychomotricit&#xE9;<br>Psychor&#xE9;&#xE9;ducateur(trice)<br>Th&#xE9;rapeute en psychomotricit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;&#xE9;tat de psychomotricien<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Encadrement, information et formation des professionnels et des futurs professionnels </li> <li>Information, conseil, &#xE9;ducation pour la sant&#xE9;, &#xE9;ducation th&#xE9;rapeutique et expertise vis &#xE0; vis d&apos;une ou de plusieurs personnes, de l&apos;entourage et des institutions </li> <li>Interventions et soins en psychomotricit&#xE9; </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>Organisation et coordination des activit&#xE9;s pour le suivi du sujet, en lien avec les professionnels de sant&#xE9; et les autres partenaires </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes et de recherches </li> <li>Recueil de donn&#xE9;es, r&#xE9;alisation d&apos;entretiens et d&apos;&#xE9;valuations visant au diagnostic psychomoteur </li> <li>Veille professionnelle et d&#xE9;veloppement professionnel continu </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et &#xE9;valuer une situation et &#xE9;laborer un diagnostic psychomoteur </li> <li>Analyser et faire &#xE9;voluer sa pratique professionnelle </li> <li>Concevoir, conduire et &#xE9;valuer un projet d&apos;intervention en psychomotricit&#xE9; </li> <li>Conduire une relation dans un contexte d&apos;intervention en psychomotricit&#xE9; </li> <li>Elaborer et conduire une d&#xE9;marche de conseil, de pr&#xE9;vention, une d&#xE9;marche d&apos;&#xE9;ducation pour la sant&#xE9;, d&apos;&#xE9;ducation th&#xE9;rapeutique dans le domaine de la psychomotricit&#xE9; </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Mettre en &#x153;uvre des interventions d&apos;&#xE9;ducation psychomotrice, d&apos;accompagnement et de soins en psychomotricit&#xE9;, de r&#xE9;adaptation, &#xE0; dimension psychoth&#xE9;rapique, et des activit&#xE9;s contribuant au bien-&#xEA;tre, &#xE0; la r&#xE9;insertion, aux soins palliatifs et au traitement de la douleur </li> <li>Organiser les activit&#xE9;s et coop&#xE9;rer avec les diff&#xE9;rents acteurs </li> <li>Rechercher, traiter et analyser des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">D&#xE9;veloppement psychomoteur de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie des &#xE2;ges de la vie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychomotricit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Corps m&#xE9;dical pour la prescription et le suivi des patients.<br>Psychologue pour le suivi de la personne<br>Personnel du secteur social et scolaire pour le suivi du patient<br>Equipe interprofessionnelle pour la coordination la prise en charge de r&#xE9;&#xE9;ducation.<br>Infirmiers pour la co-animation et les travaux de groupe<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;etat de psychomotricien<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Psychomotricien<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Coordinateur de parcours en sant&#xE9;<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Ingénieur(e) d''études hospitalier (IE)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Adapter et mettre en &#x153;uvre les &#xE9;volutions technologiques et/ou organisationnelles pr&#xE9; existantes dans tous les domaines li&#xE9;s &#xE0; la sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Planification de la r&#xE9;alisation du projet et/ou &#xE9;tude et/ou produit et des ressources </li> <li>Planification et mise en oeuvre du d&#xE9;ploiement de mat&#xE9;riels, moyens, syst&#xE8;mes relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction de documents techniques, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction de notes de synth&#xE8;se, relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction et mise &#xE0; jour de la documentation, relative &#xE0; ses activit&#xE9;s </li> <li>Suivi de la r&#xE9;alisation des travaux, de la r&#xE9;solution des probl&#xE8;mes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Tenue &#xE0; jour des donn&#xE9;es / des fichiers relatifs au domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>S&apos;exprimer en public </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Biologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Bureautique/Technologies information et communication </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Informatique / Syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins cliniciens, pharmaciens, biologistes et Ing&#xE9;nieur de Recherche Hospitalier, pour la mise en place des techniques et outils ainsi que pour le suivi des protocoles de recherche<br>M&#xE9;decins cliniciens pour l&apos;information et le retour d&apos;information de l&apos;analyse des donn&#xE9;es issues de leurs unit&#xE9;s<br>Cadre de sant&#xE9; pour l&apos;organisation des besoins m&#xE9;dico-techniques intervenant dans le domaine d&apos;activit&#xE9; et l&apos;&#xE9;valuation des besoins logistiques.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+5 dans un domaine scientifique, sciences humaines et sociale, droit, &#xE9;conomie, science politique ou sant&#xE9; publique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur de recherche hospitalier<br>Ing&#xE9;nieur en biologie m&#xE9;dicale<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable des travaux', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter la conception et la r&#xE9;alisation des travaux neufs et /ou restructurants en animant une &#xE9;quipe de professionnels ou encore par le biais de prestataires ext&#xE9;rieurs.il est garant du respect du Programme Pluri annuel d&apos;Investissement Travaux.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Ing&#xE9;nieur(e) Travaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;daction de documents techniques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Organisation de la consultation des fournisseurs </li> <li>Planification de la r&#xE9;alisation du projet et/ou &#xE9;tude et/ou produit et des ressources </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir un plan / programme d&apos;investissement </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; publique </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation environnementale </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">TCE / VRD </td> <td headers=\"col1\"></td> </tr> </tbody></table></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur et &#xE9;quipe de direction d&apos;&#xE9;tablissement.<br>Fournisseur pour la veille et litige.<br>Autres responsables, syst&#xE8;me d&apos;informations, service biom&#xE9;dical, ressources financi&#xE8;res et contr&#xF4;le de gestion, ressources humaines pour la coordination des interfaces entre les activit&#xE9;s ou les projets, structures de qualit&#xE9; et de vigilance,r&#xE9;seau professionnel, tutelle et agences (HAS..DDE...) pour la veille.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +5<br>&#xE9;cole d&apos;ing&#xE9;nieur ou master (g&#xE9;n&#xE9;raliste, BTP ou sp&#xE9;cialit&#xE9;s d&#xE9;riv&#xE9;es du BTP).<br>formation compl&#xE9;mentaire en management<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Acheteur(euse)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable de crèche', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser, piloter et contr&#xF4;ler les r&#xE9;alisations d&apos;une &#xE9;quipe pluridisciplinaire, dans le cadre d&apos;un projet &#xE9;ducatif sp&#xE9;cifique &#xE0; la cr&#xE8;che et en lien avec le projet social d&apos;&#xE9;tablissement et diriger une structure d&apos;accueil non permanent pour les enfants de moins de 6 ans.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Directeur(trice) de cr&#xE8;che<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Coordinateur (trice) de cr&#xE8;ches<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, encadrement et accompagnement p&#xE9;dagogique de personnes (agents, &#xE9;tudiants, stagiaires, etc.) </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, et de la conformit&#xE9; des produits, services, prestations dans son domaine d&apos;activit&#xE9; </li> <li>Coordination des programmes / projets / activit&#xE9;s </li> <li>D&#xE9;finition et mise en oeuvre du projet &#xE9;ducatif </li> <li>Elaboration et mise en place du plan de pr&#xE9;vention et de traitement des risques sp&#xE9;cifiques &#xE0; la cr&#xE8;che </li> <li>Elaboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques &#xE0; la cr&#xE8;che </li> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Gestion des moyens et des ressources, planification, contr&#xF4;le et reporting </li> <li>Organisation et animation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es, groupes de travail et groupes projet </li> <li>Veille r&#xE9;glementaire et professionnelle et diffusion aux professionnels. </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives au domaine de la cr&#xE8;che </li> <li>Concevoir, piloter et &#xE9;valuer un projet &#xE9;ducatif </li> <li>Conseiller les parents dans le cadre de projets &#xE9;ducatifs ou d&apos;accueil </li> <li>Evaluer l&apos;&#xE9;volution de l&apos;enfant en rep&#xE9;rant les modifications de l&apos;&#xE9;tat de sant&#xE9; ou les situations &#xE0; risque et informer les parents, l&apos;&#xE9;quipe soignante ou les services sociaux </li> <li>Evaluer les pratiques professionnelles dans la cr&#xE8;che </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques </li> <li>Identifier, &#xE9;valuer, choisir des mat&#xE9;riels, des outils adapt&#xE9;s &#xE0; la r&#xE9;alisation des activit&#xE9;s de la cr&#xE8;che </li> <li>Organiser une unit&#xE9;, un service, en fonction de diff&#xE9;rents crit&#xE8;res (missions, strat&#xE9;gie, moyens, etc.) </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation d&apos;&#xE9;quipe </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des enfants et de la protection de la petite enfance </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;valuation de la charge de travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Pu&#xE9;riculture </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Assistante sociale du personnel, psychologue et psychomotricien de la cr&#xE8;che pour le suivi des enfants, des parents et des personnels + CLIN pour le pr&#xE9;l&#xE8;vement et le contr&#xF4;le bact&#xE9;riologique.<br>Commission cr&#xE8;che pour l&apos;arbitrage des places en cr&#xE8;che.<br>Direction des ressources humaines pour la n&#xE9;gociation, le suivi de la masse salariale, la formation et les recrutements.<br>Familles pour l&apos;information et l&apos;organisation de la prise en charge des enfants.<br>M&#xE9;decin du travail pour la gestion personnalis&#xE9;e des personnels.<br>P&#xE9;diatre de la cr&#xE8;che pour la pr&#xE9;vention aupr&#xE8;s des enfants, des parents et des professionnels.<br>Protection maternelle infantile (PMI) pour agr&#xE9;ments, signalement et mise en place d&apos;un projet ville / cr&#xE8;che.<br>Services logistiques pour l&apos;organisation et la qualit&#xE9; des prestations logistiques.<br>Services techniques pour l&apos;&#xE9;laboration du plan de travaux et le suivi de sa mise en oeuvre.<br>Services des achats pour la gestion des &#xE9;quipements.<br>Structure petite enfance, pour la coordination de l&apos;&#xE9;laboration d&apos;un projet de prise en charge de l&apos;enfant et de la famille.<br>Structures de formation initiale et continue, internes et externes, pour l&apos;accueil et la formation des stagiaires et des personnels de la cr&#xE8;che.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;le d&apos;Etat de pu&#xE9;riculture avec 3 ann&#xE9;es d&apos;exp&#xE9;rience professionnelles, formation au management ou dipl&#xF4;me de cadre de sant&#xE9;.<br>Dipl&#xF4;me d&apos;Etat d&apos;&#xE9;ducateur de jeunes enfants (avec trois ans d&apos;exp&#xE9;rience professionnelle et selon la capacit&#xE9; d&apos;accueil, et pour les &#xE9;ducateurs, une certification au moins de niveau II enregistr&#xE9;e au RNCP dans le domaine de l&apos;encadrement ou de la direction).<br>Dipl&#xF4;me d&apos;Etat d&apos;Infirmier justifiant d&apos;une ann&#xE9;e d&apos;exp&#xE9;rience professionnelle aupr&#xE8;s de jeunes enfants pour les structures d&apos;une capacit&#xE9; inf&#xE9;rieure ou &#xE9;gale &#xE0; 40 places.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Selon la capacit&#xE9; d&apos;accueil, infirmi&#xE8;re pu&#xE9;ricultrice ou &#xE9;ducateur de jeunes enfants ou cadre de sant&#xE9; param&#xE9;dical<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br>Responsable socio-&#xE9;ducatif<br>Formateur des professionnels de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Gestionnaire des marchés publics', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser et g&#xE9;rer les proc&#xE9;dures de march&#xE9;s publics pour l&apos;achat de produits, services et travaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gestionnaire de groupement de commandes<br>Gestionnaire des travaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Elaboration des documents techniques en liaison avec les services utilisateurs </li> <li>Gestion de l&apos;attribution des march&#xE9;s en lien avec les acheteurs et le responsable achat (pr&#xE9;paration des tableaux d&apos;analyse des offres, de la notification, etc...) </li> <li>Gestion de la consultation et de l&apos;interface avec les candidats pour les aspects administratifs, ainsi que l&apos;interface utilisateurs en interne </li> <li>Gestion des am&#xE9;nagements n&#xE9;cessaires aux contrats en lien avec l&apos;acheteur du domaine concern&#xE9; et le responsable achat pendant l&apos;ex&#xE9;cution des march&#xE9;s </li> <li>Planification des proc&#xE9;dures de march&#xE9;s </li> <li>R&#xE9;daction des cahiers des charges des march&#xE9;s en lien avec les acheteurs et les utilisateurs des domaines concern&#xE9;s </li> <li>R&#xE9;daction des documents de consultation et publication des march&#xE9;s </li> <li>Suivi de l&apos;ex&#xE9;cution des march&#xE9;s en lien avec les services utilisateurs et les fournisseurs </li> <li>Veille r&#xE9;glementaire sur les march&#xE9;s publics </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Etablir une communication avec les candidats pour l&apos;aspect administratif et la relation avec les utilisateurs internes </li> <li>G&#xE9;rer le planning </li> <li>G&#xE9;rer simultan&#xE9;ment les proc&#xE9;dures multiples </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Utiliser les proc&#xE9;dures, outils, protocoles sp&#xE9;cifiques aux achats publics hospitaliers </li> <li>Utiliser les outils bureautiques / TIC </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Achat </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des march&#xE9;s publics </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;achat public </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits et fournisseurs </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques quantitatives </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur des achats et responsable achat, tous responsables de directions acheteuses pour valider les objectifs annuels<br>Acheteurs pour le suivi des proc&#xE9;dures en lien avec les actions achats de son domaine<br>Services de soins, administratifs et techniques pour la gestion des proc&#xE9;dures de march&#xE9; et le suivi de l&apos;ex&#xE9;cution des march&#xE9;s<br>Fournisseurs pour la gestion des proc&#xE9;dures de march&#xE9;s et le suivi des march&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +2, formation au droit public.<br>Possibilit&#xE9; de se sp&#xE9;cialiser en march&#xE9;s de travaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint administratif hospitalier<br>Adjoint des cadres hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable des march&#xE9;s publics<br>Acheteur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Mandataire judiciaire à la protection des majeurs', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>G&#xE9;rer des mesures de protection juridique des majeurs (tutelle, curatelle,<br>mandat sp&#xE9;cial, mesure d&apos;accompagnement judiciaire, etc.) confi&#xE9;es par le juge<br>des Tutelles en application de la Loi du 05.03.2007 et des textes d&apos;application.<br>Assister ou repr&#xE9;senter la personne prot&#xE9;g&#xE9;e dans tous ses actes de la vie civile,<br>tant dans la gestion de ses biens que dans la protection de sa personne, en<br>respectant sa volont&#xE9;, ses droits et ses libert&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>G&#xE9;rant(e) de tutelle<br>Tuteur(trice)<br>Curateur(trice)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de niveau III<br>Certificat National de Comp&#xE9;tences (CNC) mandataire judiciare<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Assistance et / ou repr&#xE9;sentation des personnes, relative au domaine d&apos;activit&#xE9; </li> <li>Bilan de la situation socio-&#xE9;conomique d&apos;une personne </li> <li>Contr&#xF4;le de la conformit&#xE9; et / ou de validit&#xE9; des documents, relatifs &#xE0; son domaine </li> <li>&#xC9;laboration et r&#xE9;daction de projets individuels </li> <li>&#xC9;tablissement et suivi de budget(s) relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion de biens, de patrimoine immobilier </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Montage et instruction de dossiers relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction d&apos;actes, de documents ou notes juridiques / r&#xE9;glementaires </li> <li>Suivi des actions mises en oeuvre </li> <li>Tenue d&apos;entretiens avec des personnes (patients, familles,...) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la volont&#xE9; et le consentement de la personne prot&#xE9;g&#xE9;e </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>N&#xE9;gocier des prestations, des contrats, des accords avec des interlocuteurs internes/externes </li> <li>Optimiser les revenus mobiliers et immobiliers des personnes prot&#xE9;g&#xE9;es </li> <li>R&#xE9;diger des actes et / ou documents juridiques et / ou contractuels </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Traiter et r&#xE9;soudre des situations agressives ou conflictuelles </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Droit civil </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit fiscal </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;conomie sociale familiale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Intervention sociale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Personnes prot&#xE9;g&#xE9;es et leur famille pour les &#xE9;changes d&apos;informations et la collaboration dans le travail<br>- Juge des tutelles pour les d&#xE9;cisions de mise sous protection, autorisations et<br>restitutions de l&apos;activit&#xE9; ainsi que les greffiers.<br>- Professionnels m&#xE9;dicaux, para-m&#xE9;dicaux et socio-&#xE9;ducatifs dont les assistants sociaux pour la d&#xE9;termination des d&#xE9;cisions dans le cadre de la prise en charge de la personne<br>- Comptable public de l&apos;&#xE9;tablissement pour la comptabilit&#xE9; des majeurs prot&#xE9;g&#xE9;s<br>- Pour &#xE9;changes d&apos;informations et r&#xE8;glement des situations des majeurs prot&#xE9;g&#xE9;s : * Professions juridiques : notaires, avocats, huissiers, etc.<br>* Administrations et institutionnels : Poste, finances publiques, collectivit&#xE9;s territoriales, rectorat, etc.<br>* Organismes bancaires et financiers<br>* Commer&#xE7;ants, artisans, fournisseurs dont les bailleurs de tous ordres<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>diplome niveau III<br>Certificat National de Comp&#xE9;tences (CNC) mandataire judiciare<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Assistant socio-&#xE9;ducatif<br>Conseiller en &#xE9;conomie sociale et familiale<br>Adjoint des cadres hospitaliers<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Charg&#xE9; des relations avec les usagers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) de production blanchisserie', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Programmer la production en fonction des pr&#xE9;visions, organiser les temps de production, contr&#xF4;ler la fiabilit&#xE9; des mat&#xE9;riels, piloter la maintenance. Il peut avoir une fonction d&apos;encadrement.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>R&#xE9;gulateur(trice) de production blanchisserie<br>Pilote d&apos;installation en blanchisserie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En tri<br>En production<br>En exp&#xE9;dition<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissances du m&#xE9;tier de la fonction linge et de l&apos;organisation de la production industrielle<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Approvisionnement en produits, en mat&#xE9;riels, dans son domaine </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Coordination et suivi de la prise en charge de prestations </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation professionnelle de personnes (agents, stagiaires, etc.) </li> <li>Suivi des contrats / des fournisseurs / prestataires de service </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer une charge de travail </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Installations- &#xE9;quipements de blanchisserie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; au marquage et au suivi des articles linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; aux processus de nettoiement du linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Produits de nettoiement du linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologie des textiles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable fonction linge pour conseil &#xE0; l&apos;organisation des activit&#xE9;s de la fonction linge<br>cadres de sant&#xE9; pour l&apos;ajustement des besoins en linge, et du suivi de la m&#xE9;thoide rabc<br>responsable hygi&#xE8;ne pour les controles de la qualit&#xE9; hygi&#xE9;nique du linge<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur de la fonction linge en milieu hospitalier<br>bac technique / bts / dut<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien Hospitalier<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable blanchisserie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('FICHE METIER EN COURS \"Gestionnaire de lit\"', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>FICHE METIER EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>FICHE METIER EN COURS<br>&quot;Gestionnaire de flux patients<br>Gestionnaire de s&#xE9;jours&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>FICHE METIER EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>FICHE METIER EN COURS<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable blanchisserie', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter l&apos;ensemble des secteurs de production fonction linge / blanchisserie, de la r&#xE9;ception &#xE0; l&apos;exp&#xE9;dition du linge, en animant une &#xE9;quipe de professionnels et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable de production blanchisserie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Qualiticien(ne) Risk Analysis Biocontamination Control (RABC)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Approvisionnement en produits, en mat&#xE9;riels, dans son domaine </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi du bon d&#xE9;roulement du (des) process sp&#xE9;cifique(s) au domaine d&apos;activit&#xE9; </li> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Encadrement, information et formation des professionnels et des futurs professionnels </li> <li>Gestion des moyens et des ressources : techniques, financi&#xE8;res, humaines, informationnelles </li> <li>Planification de l&apos;activit&#xE9; et de l&apos;ensemble des moyens (humains, financiers, techniques) &#xE0; mettre en &#x153;uvre pour garantir la production </li> <li>Pr&#xE9;paration et animation de r&#xE9;unions et de divers groupes de travail ou de projets institutionnels </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser un / des processus </li> <li>Analyser les informations n&#xE9;cessaires &#xE0; l&apos;organisation et &#xE0; la r&#xE9;alisation de son activit&#xE9; </li> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Animer et encadrer une ou plusieurs &#xE9;quipes, d&#xE9;velopper les comp&#xE9;tences de ses collaborateurs et les &#xE9;valuer </li> <li>Concevoir, r&#xE9;diger et mettre en forme une communication orale et/ou &#xE9;crite </li> <li>Conduire des entretiens professionnels (recrutement, orientation...) </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Installations- &#xE9;quipements de blanchisserie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; au marquage et au suivi des articles linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; aux processus de nettoiement du linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Produits de nettoiement du linge </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologie des textiles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>- Responsable fonction linge pour conseil &#xE0; l&apos;organisation des activit&#xE9;s de la fonction linge<br>- Cadres de sant&#xE9; pour l&apos;ajustement des besoins en linge, et du suivi de la m&#xE9;thode RABC<br>- Responsable hygi&#xE8;ne pour les contr&#xF4;les de la qualit&#xE9; hygi&#xE9;nique du linge<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Gestionnaire de la fonction linge en &#xE9;tablissement de sant&#xE9;<br>BTS / DUT/ Master<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>TSH / Ing&#xE9;nieur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Physicien(ne) médical(e)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire<br>&quot;Concevoir, pr&#xE9;parer et mettre en oeuvre des &#xE9;tudes dosim&#xE9;triques, des proc&#xE9;dures de suivi technique des appareils utilisant les radiations ionisantes, en imagerie m&#xE9;dicale, radioth&#xE9;rapie, curieth&#xE9;rapie et des proc&#xE9;dures de suivi qualitatif des appareillages.<br>Garantir, en radioth&#xE9;rapie, que la dose de rayonnements re&#xE7;ue par les tissus faisant l&apos;objet de l&apos;exposition correspond &#xE0; celle prescrite par le m&#xE9;decin&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>PSRPM - Personne sp&#xE9;cialis&#xE9;e en radiophysique m&#xE9;dicale<br>Radiophysicien(ne) m&#xE9;dical(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En imagerie<br>En dosim&#xE9;trie<br>En radioprotection<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>DQPRM ( dipl&#xF4;me de qualification en physique radiologique et m&#xE9;dicale)<br>Masters radiophysique et imagerie m&#xE9;dicale<br>doctorat dans la sp&#xE9;cialit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Audit et expertise des &#xE9;quipements / installations / syst&#xE8;mes (fonctionnement, conformit&#xE9;, efficacit&#xE9; et performances) sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Calibration technique, balistique et dosim&#xE9;trique des dispositifs d&apos;irradiation et des &#xE9;quipements d&apos;imagerie et m&#xE9;trologies associ&#xE9;es </li> <li>Conception et r&#xE9;daction de documents techniques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Contribution &#xE0; l&apos;identification et &#xE0; la gestion des risques li&#xE9;s &#xE0; toute proc&#xE9;dure d&apos;exposition aux rayonnements ionisants </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de l&apos;&#xE9;tat et du fonctionnement des &#xE9;quipements / des installations, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des activit&#xE9;s, dans son domaine </li> <li>D&#xE9;veloppement et mise en oeuvre de nouvelles techniques de traitement, de nouvelles techniques de dosim&#xE9;trie, de nouvelles techniques de positionnement. </li> <li>&#xC9;tablissement de cahier des charges, relatif &#xE0; la probl&#xE9;matique client </li> <li>Formation initiale en physique m&#xE9;dicale des internes en physique m&#xE9;dicale, formation initiale et continue des m&#xE9;decins et des manipulateurs exposant le patient aux rayonnements ionisants </li> <li>Mesures des doses d&#xE9;livr&#xE9;es </li> <li>Recensement, identification, analyse et traitement des risques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Recherche appliqu&#xE9;e en radiophysique </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Choisir et utiliser des mat&#xE9;riels, des outils de travail ou / et de contr&#xF4;le, aff&#xE9;rents &#xE0; son m&#xE9;tier </li> <li>Concevoir et animer des formations initiales et continues relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir et r&#xE9;diger une documentation technique, sp&#xE9;cifique &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la conformit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riel, d&apos;une prestation au regard des normes internes/externes </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Identifier et diagnostiquer un dysfonctionnement, une panne, le d&#xE9;faut d&apos;un mat&#xE9;riel, d&apos;un &#xE9;quipement, une anomalie d&apos;un syst&#xE8;me, sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Dosim&#xE9;trie et rayonnement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Informatique / Syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de traitement des images </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; en radioth&#xE9;rapie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Mat&#xE9;riel et &#xE9;quipement d&apos;imagerie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Physique </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Radioprotection </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation en hygi&#xE8;ne, s&#xE9;curit&#xE9; et conditions de travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decin pour la prescription et la mise en oeuvre des traitements<br>Manipulateurs en radiologie ou techniciens de laboratoire sp&#xE9;cialis&#xE9;s en dosim&#xE9;trie pour les &#xE9;tudes dosim&#xE9;triques et la mise en oeuvre des traitements<br>Fournisseurs d&apos;&#xE9;quipement pour l&apos;achat et la maintenance<br>Ing&#xE9;nieur biom&#xE9;dical et technicien de maintenance pour le suivi du mat&#xE9;riel<br>ASN ( autorit&#xE9; de suret&#xE9; nucl&#xE9;aire) pour les dossiers d&apos;autorisation et de d&#xE9;claration des g&#xE9;n&#xE9;rateurs de RX, d&apos;utilisation de radio&#xE9;l&#xE9;ments artificiels en sources non sc&#xE9;ll&#xE9;es, la r&#xE8;glementation en radioprotection et la d&#xE9;claration d&apos;incident en radioprotection<br>IRSN ( institut de radioprotection et de suret&#xE9; nucl&#xE9;aire) pour le suivi dosim&#xE9;trique des travailleurs expos&#xE9;s (centralisation des donn&#xE9;es nationales),le listing des sources radioactives, le relev&#xE9; des niveaux de r&#xE9;f&#xE9;rences diagnostiques ( exposition des patients pour des examens radiologiques et de m&#xE9;decine nucl&#xE9;aire), des &#xE9;tudes ponctuelles de dosim&#xE9;trie ( accidentelle ou pr&#xE9;visionnelle)<br>Soci&#xE9;t&#xE9;s ext&#xE9;rieures agr&#xE9;&#xE9;es (type APAVE, SOCOTEC,...), pour les contr&#xF4;les r&#xE9;glementaires et p&#xE9;riodiques des installations utilisant des rayonnements ionisants.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Master physique puis M2 en physique m&#xE9;dicale<br>Masters radiophysique et imagerie m&#xE9;dicales ,ing&#xE9;ni&#xE9;rie pour la sant&#xE9; et le m&#xE9;dicament ,ing&#xE9;ni&#xE9;rie pour la sant&#xE9; physique m&#xE9;dicale et biologique<br>DQPRM ( dipl&#xF4;me de qualification en physique radiologique et m&#xE9;dicale)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Contractuel de cat&#xE9;gorie A (Ing&#xE9;nieur)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Encadrant(e) gestion des ressources humaines', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, coordonner et contr&#xF4;ler l&apos;activit&#xE9; de son domaine en animant une &#xE9;quipe de gestionnaires et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Encadrant(e) gestion paie<br>Encadrant(e) gestion carri&#xE8;res<br>Encadrant(e) gestion protection sociale<br>Encadrant(e) gestion affaires m&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, orientation, conseil et information aux agents de l&apos;&#xE9;tablissement dans le domaine des ressources humaines (carri&#xE8;re, statut, r&#xE9;mun&#xE9;ration, formation, recrutement, mobilit&#xE9;...) </li> <li>Conception et r&#xE9;alisation d&apos;outils et/ou de m&#xE9;thodes </li> <li>Contr&#xF4;le de l&apos;application de la r&#xE9;glementation et des r&#xE8;gles RH propres &#xE0; l&apos;&#xE9;tablissement (carri&#xE8;re, formation, GPMC, paie, gestion du temps, absence, recrutement..) </li> <li>Elaboration, mise en oeuvre et suivi dans son domaine de comp&#xE9;tence du projet social de l&apos;&#xE9;tablissement </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Ing&#xE9;nierie et gestion de projet (analyse, conception, mise en oeuvre, &#xE9;valuation, ajustement...) </li> <li>Organisation / coordination / supervision des contr&#xF4;les relatifs aux activit&#xE9;s du secteur, de l&apos;unit&#xE9; </li> <li>Pr&#xE9;paration et animation de r&#xE9;unions et de divers groupes de travail ou de projets institutionnels </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction d&apos;actes, de documents ou notes juridiques / r&#xE9;glementaires </li> <li>Suivi budg&#xE9;taire et analytique des d&#xE9;penses de gestion des ressources humaines </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Argumenter et convaincre dans une relation de confiance des interlocuteurs si besoin n&#xE9;gocier des accords. </li> <li>Concevoir, conduire, adapter le programme de d&#xE9;veloppement professionnel d&apos;une cat&#xE9;gorie professionnelle ou d&apos;une personne </li> <li>&#xC9;valuer et choisir un candidat, un collaborateur. </li> <li>G&#xE9;rer un budget ou une norme quantitative et optimiser la gestion financi&#xE8;re propre &#xE0; son domaine d&apos;activit&#xE9; et rapporter &#xE0; sa hi&#xE9;rarchie tout risque d&apos;&#xE9;cart. </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> <li>Travailler en partenariat avec les responsables de l&apos;encadrement des unit&#xE9;s concern&#xE9;es </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit du travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion budg&#xE9;taire et administrative </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion RH </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management et encadrement d&apos;&#xE9;quipes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation en hygi&#xE8;ne, s&#xE9;curit&#xE9; et conditions de travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Statut des personnels m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Statut des personnels non m&#xE9;dicaux de la fonction publique hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>- Agents de son propre service<br>- Autres personnels de l&apos;&#xE9;tablissement pour expertise sur situations individuelles complexes<br>- Direction RH<br>- Autres directions de l&apos;&#xE9;tablissement (finances, contr&#xF4;le de gestion, informatique, affaires m&#xE9;dicales, soins...)<br>- Partenaires externes en lien avec l&apos;activit&#xE9; (tr&#xE9;sorerie, p&#xF4;le emploi, ARS, CNRACL, URSSAF, DGOS, OPCA...)<br>- Instances de l&apos;&#xE9;tablissement CTE, CME, CHSCT, commissions administratives paritaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de niveau I (master) en :<br>- Gestion de ressources humaines,<br>- Gestion des entreprises et/ou des administrations,<br>- Finances/&#xE9;conomie/droit/management<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable des ressources humaines<br>Charg&#xE9; de la formation continue<br>Charg&#xE9; du d&#xE9;veloppement des ressources humaines<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable de la sécurité', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finir, planifier et piloter l&apos;ensemble des activit&#xE9;s de s&#xE9;curit&#xE9; en animant les &#xE9;quipes de s&#xE9;curit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) de service de s&#xE9;curit&#xE9; incendie et assistance aux personnes en cas d&apos;incendie<br>Responsable s&#xE9;curit&#xE9;/s&#xFB;ret&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>S&#xE9;curit&#xE9; des personnes et des biens<br>S&#xE9;curit&#xE9; incendie<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Habilitation SSIAP 3 (arr&#xEA;t&#xE9; du 2 mai 2005 modifi&#xE9;)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et mise en place de l&apos;organisation des services, dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;laboration et mise en place de suivi des plans et actions de pr&#xE9;vention </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>R&#xE9;daction et mise &#xE0; jour des notes, documents et rapports, toute documentation relative &#xE0; ses activit&#xE9;s dans son domaine de comp&#xE9;tences </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>D&#xE9;finir, conduire et &#xE9;valuer la politique relative &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer une prestation, un projet, une solution, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>N&#xE9;gocier des prestations, des contrats, des accords avec des interlocuteurs internes/externes </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication &#xE9;crite </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; incendie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction, directions fonctionnelles, responsables des p&#xF4;les et des services de soins pour d&#xE9;finir et coordonner avec eux la mise en &#x153;uvre de la politique s&#xE9;curit&#xE9; de l&apos;&#xE9;tablissement.<br>CHSCT<br>Service d&#xE9;partemental incendie et de secours, pr&#xE9;fecture du d&#xE9;partement et autres administrations pour la prise en compte et application des proc&#xE9;dures incendie.<br>Soci&#xE9;t&#xE9;s de contr&#xF4;le et de maintenance des installations li&#xE9;es &#xE0; la s&#xE9;curit&#xE9; incendie pour la v&#xE9;rification de la conformit&#xE9; et l&apos;entretien des installations incendie.<br>Les services techniques pour la collaboration dans les actions et travaux. Police, gendarmerie, pr&#xE9;fecture pour information et coordination des interventions<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de niveau I (Ing&#xE9;nieur, master) ou au moins de niveau II pour personnel de cat&#xE9;gorie A dans le domaine de la s&#xE9;curit&#xE9; ayant un dipl&#xF4;me (type DU, master)<br>Habilitation SSIAP 3<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur<br>Personnel de cat&#xE9;gorie A dans la FPH (cadre, cadre sup&#xE9;rieur, attach&#xE9; d&apos;administration hospitali&#xE8;re...)<br>TSH<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Orthoptiste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un diagnostic, d&#xE9;pister, &#xE9;valuer, r&#xE9;&#xE9;duquer, r&#xE9;adapter et explorer les troubles et d&#xE9;ficiences de la vision d&apos;une personne.<br>R&#xE9;aliser des bilans orthoptiques, des actes et des techniques d&apos;exploration, de r&#xE9;&#xE9;ducation et de r&#xE9;adaptation orthoptique.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Education th&#xE9;rapeutique dans le domaine de l&apos;orthoptie aupr&#xE8;s du patient et de son entourage </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>Pr&#xE9;vention et d&#xE9;pistage des troubles visuels </li> <li>R&#xE9;alisation de bilans en vue du diagnostic orthoptique et r&#xE9;alisation d&apos;explorations fonctionnelles </li> <li>R&#xE9;alisation de soins, de traitements orthoptiques individuels et d&apos;actes techniques </li> <li>R&#xE9;alisation de travaux de recherches en orthoptie </li> <li>Veille professionnelle et &#xE9;volution des pratiques professionnelles </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et faire &#xE9;voluer sa pratique professionnelle </li> <li>Communiquer et conduire une d&#xE9;marche th&#xE9;rapeutique dans un contexte d&apos;intervention en orthoptie </li> <li>Concevoir et conduire un projet individualis&#xE9; de traitements orthoptiques </li> <li>Concevoir et Conduire une d&#xE9;marche de pr&#xE9;vention, de d&#xE9;pistage protocolis&#xE9;, d&apos;&#xE9;ducation th&#xE9;rapeutique et de suivi </li> <li>Conduire une d&#xE9;marche de d&#xE9;pistage et d&apos;explorations de la fonction visuelle </li> <li>Conseiller et accompagner l&apos;adaptation aux lentilles de contact, aux aides optiques ou non optiques </li> <li>Evaluer l&apos;&#xE9;tat sensoriel, moteur et fonctionnel au cours du bilan de la fonction visuelle et &#xE9;laborer un diagnostic orthoptique </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Organiser les activit&#xE9;s et coop&#xE9;rer avec les diff&#xE9;rents acteurs </li> <li>Rechercher, traiter et analyser des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Orthoptie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Psychologie des &#xE2;ges de la vie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Corps m&#xE9;dical ville pour la prescription du bilan.<br>Personnels param&#xE9;dicaux pour le suivi des soins<br>Personnels de l&apos;&#xE9;ducation nationale, &#xE9;ducateurs pour la prise en charge du patient<br>Ma&#xEE;tres-chiens guide pour le suivi du patient<br>Orthoptistes lib&#xE9;raux pour les r&#xE9;seaux ville / h&#xF4;pital, l&apos;orientation et le suivi du patient<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Certificat de capacit&#xE9; d&apos;orthoptiste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Orthoptiste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Coordinateur de parcours en sant&#xE9;<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable restauration', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter l&apos;ensemble des activit&#xE9;s de restauration(mono ou multisites) en animant une &#xE9;quipe de professionnels et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Qualiticien(ne) et Hazard Analysis Critical Control Point (HACCP)<br>Suivi et contr&#xF4;le des prestations sous trait&#xE9;es.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Bac +2 dans le domaine de la restauration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conception et r&#xE9;daction de documents techniques, relatifs au domaine d&apos;activit&#xE9; </li> <li>Conseil aux p&#xF4;les et aux projets d&apos;&#xE9;tablissement dans son domaine de comp&#xE9;tences </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;utilisation et de l&apos;entretien des mat&#xE9;riels et des locaux, dans son domaine </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des activit&#xE9;s, dans son domaine </li> <li>Contr&#xF4;le et suivi de prestations sous-trait&#xE9;es (linge, nettoyage, restauration...) </li> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement et suivi de budget(s) relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Planification, r&#xE9;organisation des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Recensement, identification, analyse et traitement des risques, relatifs au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et manager des &#xE9;quipes, en environnement hi&#xE9;rarchique et fonctionnel </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Organiser les activit&#xE9;s et coop&#xE9;rer avec les diff&#xE9;rents acteurs </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication de crise </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne - S&#xE9;curit&#xE9; - Environnement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de commandes des repas et de gestion des stocks </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits et fournisseurs </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;f&#xE9;rentiels qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques culinaires </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>directeur logistique, CLAN, repr&#xE9;sentants des usagers, cadres de sant&#xE9;, cadre di&#xE9;t&#xE9;ticien, EOH, fournisseurs et prestataires de services, Responsable h&#xF4;telier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+2 &#xE0; Bac+5 domaine restauration et/ou industrie agro-alimentaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable h&#xF4;telier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable des marchés publics', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser et piloter les proc&#xE9;dures de march&#xE9;s publics pour l&apos;achat de produits, services et travaux en lien avec le responsable achat. Il peut animer une &#xE9;quipe de gestionnaires des march&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Coordonnateur(trice) de groupement de commandes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la mise en &#x153;uvre des proc&#xE9;dures, outils, protocoles sp&#xE9;cifiques aux achats publics hospitaliers </li> <li>Elaboration des &#xE9;l&#xE9;ments cl&#xE9;s du dossier de consultation en concertation avec le responsable achat et l&apos;acheteur concern&#xE9; notamment pour le choix de la proc&#xE9;dure et la pond&#xE9;ration des crit&#xE8;res de choix </li> <li>Etablissement de la nomenclature des march&#xE9;s et du guide de proc&#xE9;dure interne de l&apos;&#xE9;tablissement en lien avec le responsable achat </li> <li>Mise en place d&apos;actions pour garantir la s&#xE9;curit&#xE9; juridique du march&#xE9; en lien le cas &#xE9;ch&#xE9;ant avec le service juridique </li> <li>Organisation de l&apos;interface avec les services utilisateurs pour l&apos;&#xE9;laboration des documents techniques en lien avec l&apos;acheteur concern&#xE9; </li> <li>Organisation du travail et du fonctionnement de la cellule des march&#xE9;s, management et encadrement des gestionnaires de march&#xE9; et du secr&#xE9;tariat de la cellule des march&#xE9;s </li> <li>Pilotage de la consultation dans ses aspects techniques et administratifs </li> <li>Pilotage de la planification des proc&#xE9;dures de march&#xE9;s par la cellule des march&#xE9;s </li> <li>Pilotage du suivi de l&apos;ex&#xE9;cution des march&#xE9;s en lien avec les acheteurs concern&#xE9;s et les services utilisateurs </li> <li>R&#xE9;daction des actes de notification en collaboration avec le responsable achat </li> <li>Veille r&#xE9;glementaire sur les march&#xE9;s publics </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>D&#xE9;cider de l&apos;attribution des march&#xE9;s avec le responsable achat </li> <li>&#xC9;laborer une strat&#xE9;gie de recherche et de veille d&apos;informations en contexte professionnel </li> <li>Etablir la relation d&apos;interface avec les directions m&#xE9;tiers impliqu&#xE9;es dans les march&#xE9;s </li> <li>Evaluer la conformit&#xE9; de la mise en oeuvre d&apos;une proc&#xE9;dure/norme/r&#xE8;gle, consigne </li> <li>G&#xE9;rer simultan&#xE9;ment des projets multiples </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Achat </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des march&#xE9;s publics </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; l&apos;achat public </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management et encadrement d&apos;&#xE9;quipes </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits et fournisseurs </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques quantitatives </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur des achats et responsable achat, tous responsables de directions acheteuses pour valider les objectifs annuels<br>Acheteurs pour le suivi des proc&#xE9;dures en lien avec les actions achats de son domaine<br>Services de soins, administratifs et techniques pour le pilotage des proc&#xE9;dures de march&#xE9; et le suivi de l&apos;ex&#xE9;cution des march&#xE9;s<br>Fournisseurs pour la gestion des proc&#xE9;dures de march&#xE9;s et le suivi des march&#xE9;s<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +3/5<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitaliers<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Technicien Sup&#xE9;rieur Hospitalier<br>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable des ressources humaines', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser, piloter et contr&#xF4;ler les activit&#xE9;s de la fonction RH, en tenant compte de la r&#xE9;glementation et du projet d&apos;&#xE9;tablissement, en animant une &#xE9;quipe RH.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable du personnel<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Responsable de la gestion du personnel m&#xE9;dical<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs et expertise aux p&#xF4;les, directions fonctionnelles dans le cadre de la d&#xE9;l&#xE9;gation de gestion RH </li> <li>Conseil sur la d&#xE9;finition de(s) projet(s) professionnel(s) / individuel(s) et bilan des r&#xE9;alisations </li> <li>Contr&#xF4;le de l&apos;application de la r&#xE9;glementation et des r&#xE8;gles RH propres &#xE0; l&apos;&#xE9;tablissement (carri&#xE8;re, formation, GPMC, paie, gestion du temps, absence, recrutement..) </li> <li>Elaboration, mise en oeuvre et &#xE9;valuation de la strat&#xE9;gie RH en mati&#xE8;re de recrutement, fid&#xE9;lisation, attractivit&#xE9; GPEC, mobilit&#xE9;, formation, conditions de travail, communication et politique manag&#xE9;riale </li> <li>Elaboration, mise en oeuvre et suivi dans son domaine de comp&#xE9;tence du projet social de l&apos;&#xE9;tablissement </li> <li>Ing&#xE9;nierie et gestion de projet (analyse, conception, mise en oeuvre, &#xE9;valuation, ajustement...) </li> <li>Pr&#xE9;paration et animation de r&#xE9;unions et de divers groupes de travail ou de projets institutionnels </li> <li>Production et exploitation de statistiques relevant de son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction d&apos;actes, de documents ou notes juridiques / r&#xE9;glementaires </li> <li>Suivi budg&#xE9;taire et analytique des d&#xE9;penses de gestion des ressources humaines </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Argumenter et convaincre dans une relation de confiance des interlocuteurs si besoin n&#xE9;gocier des accords. </li> <li>Concevoir, conduire, adapter le programme de d&#xE9;veloppement professionnel d&apos;une cat&#xE9;gorie professionnelle ou d&apos;une personne </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir, mettre en oeuvre, piloter et &#xE9;valuer la politique RH de la structure (recrutement, mobilit&#xE9;, GPMC, formation, politique sociale...) </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> <li>Travailler en partenariat avec les responsables de l&apos;encadrement des unit&#xE9;s concern&#xE9;es </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Audit social </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit du travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion budg&#xE9;taire et administrative </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion des ressources humaines </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion RH </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Management et encadrement d&apos;&#xE9;quipes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation en hygi&#xE8;ne, s&#xE9;curit&#xE9; et conditions de travail </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Statut des personnels m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Statut des personnels non m&#xE9;dicaux de la fonction publique hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les agents et gestionnaires de la DRH<br>Toutes les directions de l&apos;&#xE9;tablissement<br>Les repr&#xE9;sentants du personnel, instances de l&apos;&#xE9;tablissement CTE, CME, CHSCT, commissions administratives paritaires<br>L&apos;ensemble des personnels m&#xE9;dicaux et non m&#xE9;dicaux<br>Les partenaires externes en lien avec les RH (tr&#xE9;sorerie, ARS, DGOS, OPCA...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me de niveau I (master) en :<br>- Gestion des ressources humaines (GRH)<br>- Gestion des entreprises et/ou administration<br>- fincances/&#xE9;conomie/droit/management<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Charg&#xE9; de la formation continue<br>Charg&#xE9; du d&#xE9;veloppement des ressources humaines<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Gestionnaire de stocks', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finir et mettre en &#x153;uvre le plan d&apos;approvisionnement, g&#xE9;rer les commandes, la r&#xE9;ception, le stockage, la pr&#xE9;paration et la distribution des marchandises, mati&#xE8;res et produits n&#xE9;cessaires aux utilisateurs, en optimisant les stocks.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Approvisionneur(euse)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En logistique<br>En alimentaire<br>En h&#xF4;telerie<br>En produits plateaux techniques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Chargement / d&#xE9;chargement de biens, de produits </li> <li>Contr&#xF4;le de la qualit&#xE9; des produits </li> <li>Contr&#xF4;le technique des outils/des mat&#xE9;riels </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Saisie, mise &#xE0; jour et / ou sauvegarde de donn&#xE9;es, d&apos;informations, de tableaux, dans son domaine d&apos;activit&#xE9; </li> <li>Transport et livraison de biens, de produits </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements, outillages, dans son domaine de comp&#xE9;tence </li> <li>Conduire des installations, des &#xE9;quipements relatifs au domaine de comp&#xE9;tence, en optimiser le fonctionnement </li> <li>Conduire un engin motoris&#xE9;, un v&#xE9;hicule de levage, de portage </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Utiliser les logiciels m&#xE9;tier </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestes et postures- manutention </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la s&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de commandes des repas et de gestion des stocks </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Services de l&apos;&#xE9;tablissement et professionnels d&apos;acheminement pour les approvisionnements<br>Fournisseurs et transporteurs pour l&apos;accueil et la r&#xE9;ception des marchandises<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formations de niveaux V (CAP ou BEP) ou IV (Bac professionnel)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien hospitalier<br>Corps de la ma&#xEE;trise ouvri&#xE8;re<br>Corps des personnelles ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant(e) logistique<br>Acheteur(euse)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Orthophoniste', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un diagnostic, pr&#xE9;venir, &#xE9;valuer et prendre en charge des personnes susceptibles de pr&#xE9;senter des troubles de la communication, du langage dans toutes ses dimensions, et des autres activit&#xE9;s cognitives, et des fonctions oro-myo-faciales.<br>Dispenser l&apos;apprentissage d&apos;autres formes de communication non verbale.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Logop&#xE8;de (Belgique)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Certificat de capacit&#xE9; d&apos;orthophoniste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil dans le domaine de l&apos;orthophonie </li> <li>Education th&#xE9;rapeutique des patients et de leur entourage </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>Organisation et coordination des soins </li> <li>Pr&#xE9;vention et d&#xE9;pistage des troubles du langage, de la communication et des fonctions oro-myo-faciales </li> <li>Prise en charge individuelle ou en groupe du patient en orthophonie </li> <li>R&#xE9;alisation du bilan et de l&apos;&#xE9;valuation n&#xE9;cessaires &#xE0; l&apos;&#xE9;tablissement du diagnostic orthophonique et du projet th&#xE9;rapeutique </li> <li>Recherche et &#xE9;tudes en orthophonie </li> <li>Veille professionnelle et actions d&apos;am&#xE9;lioration des pratiques professionnelles </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et faire &#xE9;voluer sa pratique professionnelle </li> <li>Analyser, &#xE9;valuer une situation et &#xE9;laborer un diagnostic orthophonique </li> <li>Concevoir et mettre en oeuvre une prestation d&apos;expertise et de conseil dans le domaine de l&apos;orthophonie </li> <li>Concevoir, conduire et &#xE9;valuer une s&#xE9;ance d&apos;orthophonie </li> <li>&#xC9;laborer et conduire une d&#xE9;marche d&apos;intervention en sant&#xE9; publique : pr&#xE9;vention, d&#xE9;pistage et &#xE9;ducation th&#xE9;rapeutique </li> <li>&#xC9;laborer et mettre en &#x153;uvre un projet th&#xE9;rapeutique en orthophonie adapt&#xE9; &#xE0; la situation du patient </li> <li>&#xC9;tablir et entretenir une relation th&#xE9;rapeutique dans un contexte d&apos;intervention orthophonique </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer et organiser une structure ou un service en optimisant ses ressources </li> <li>Organiser les activit&#xE9;s et coop&#xE9;rer avec les diff&#xE9;rents acteurs </li> <li>Rechercher, traiter et analyser des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">D&#xE9;veloppement psychomoteur de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Orthophonie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie des &#xE2;ges de la vie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE9;seaux sanitaires, sociaux et m&#xE9;dico-sociaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Corps m&#xE9;dical, notamment le m&#xE9;decin prescripteur pour la prescription du bilan et la prise en charge &#xE9;ventuelle.<br>&#xC9;quipe interprofessionnelle pour la coordination des soins et l&apos;&#xE9;laboration des prises en charge.<br>Orthophonistes lib&#xE9;raux pour l&apos;orientation th&#xE9;rapeutique et le suivi du patient, services m&#xE9;dico-sociaux, entourage familial, psychologues, chirurgiens, autres professionnels de sant&#xE9;.<br>Corps enseignant, personnel de l&apos;&#xE9;ducation nationale, &#xE9;ducateurs psychologues scolaires, m&#xE9;decin scolaire, enseignant r&#xE9;f&#xE9;rent pour le suivi d&apos;un enfant pr&#xE9;sentant des troubles de d&#xE9;veloppement ou un handicap. L&apos;orthophoniste fait le lien entre le th&#xE9;rapeutique et le p&#xE9;dagogique.<br>Personnel des &#xE9;tablissements scolaires et les PMI pour l&apos;&#xE9;valuation de la situation, la coordination et l&apos;orientation dans le respect du secret professionnel.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Certificat de capacit&#xE9; d&apos;orthophoniste<br>Ou qualification reconnue selon les termes de la directive europ&#xE9;enne qualifications 2005.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Orthophoniste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Coordinateur de parcours en sant&#xE9;<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Prothésiste dentaire', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, fabriquer et adapter des proth&#xE8;ses dentaires ou des appareils d&apos;orthodontie sur mesure &#xE0; partir des empreintes bucco-dentaires.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><p>Aucune activit&#xE9; de d&#xE9;finie.</p></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><p>Aucun savoir-faire de d&#xE9;fini.</p></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Secrétaire', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser pour un responsable hi&#xE9;rarchique et / ou pour une &#xE9;quipe la r&#xE9;ception, le traitement et la circulation de l&apos;information, des documents et des communications t&#xE9;l&#xE9;phoniques li&#xE9;es au service ou suivi des dossiers.<br>Organiser la gestion logistique (d&#xE9;placements professionnels, organisation de r&#xE9;unions,.).<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Assistant(e) de direction<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Coordination secr&#xE9;tariats<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissance des techniques de secr&#xE9;tariat et de la bureautique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil des personnes (patients, usagers, familles, etc.) </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Organisation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es </li> <li>Prise de notes, frappe et mise en forme des documents </li> <li>Prise de rendez-vous / gestion des agendas </li> <li>R&#xE9;ception et traitement des appels t&#xE9;l&#xE9;phoniques et/ou des messages &#xE9;lectroniques </li> <li>Reproduction et diffusion de documents, de dossiers </li> <li>Traitement des courriers, dossiers, documents dans son domaine (enregistrement, tri, traitement, diffusion, archivage) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et orienter des personnes, des groupes, des publics </li> <li>D&#xE9;finir/adapter et utiliser un plan de classement et d&apos;archivage, un syst&#xE8;me de gestion documentaire </li> <li>&#xC9;laborer, adapter et optimiser le planning de travail, de rendez-vous, de visites </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>Organiser et classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>S&apos;exprimer en face -&#xE0;- face aupr&#xE8;s d&apos;une ou plusieurs personnes </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Accueil t&#xE9;l&#xE9;phonique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Classement et archivistique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Droit hospitalier </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Secr&#xE9;tariat </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Personnel de l&apos;&#xE9;tablissement pour l&apos;accueil et la transmission d&apos;informations<br>Autorit&#xE9;s d&#xE9;concentr&#xE9;es pour l&apos;accueil, la transmission de donn&#xE9;es et la demande d&apos;informations<br>Usagers et fournisseurs pour transmission d&apos;information<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Secr&#xE9;tariat et gestion administrative<br>Bac professionnel secr&#xE9;tariat (niveau IV) / BTS assistante de direction (niveau III)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint administratif hospitalier<br>Assistant m&#xE9;dico-administratif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Secr&#xE9;taire m&#xE9;dicale<br>Vers les autres m&#xE9;tiers de gestionnaire des autres sous-familles<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Opticien-lunetier', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finition provisoire :<br>&quot;Assembler et d&#xE9;livrer des &#xE9;quipements de correction optique &#xE0; des personnes atteintes de troubles ou d&#xE9;ficiences visuels selon la prescription m&#xE9;dicale.<br>Commercialiser des produits d&apos;optique-lunetterie.&quot;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc.), dans son domaine </li> <li>Approvisionnement en produits, en mat&#xE9;riels, dans son domaine </li> <li>Conseil et information &#xE0; la personne et &#xE0; son entourage </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>R&#xE9;alisation de l&apos;examen de r&#xE9;fraction en vue de l&apos;adaptation de la prescription </li> <li>R&#xE9;alisation, montage, d&apos;&#xE9;quipements en optique-lunetterie </li> <li>Vente des produits destin&#xE9;s &#xE0; corriger les d&#xE9;fauts ou d&#xE9;fisciences de la vue </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Conseiller et orienter les choix des personnes </li> <li>Evaluer et choisir une offre de produits / de services relatifs &#xE0; son domaine </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Informer et former des professionnels et des personnes en formation </li> <li>Organiser et optimiser le stockage physique des produits, des mat&#xE9;riels </li> <li>Organiser les activit&#xE9;s et coop&#xE9;rer avec les diff&#xE9;rents acteurs </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Droit du travail </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion comptable et budg&#xE9;taire </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Math&#xE9;matiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Optique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Physique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sciences humaines </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BTS Opticien-lunetier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable des achats', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Piloter la mise en oeuvre de la politique d&apos;achat de l&apos;&#xE9;tablissement en animant une &#xE9;quipe d&apos;acheteurs et d&apos;agents de gestion administratifs.<br>Evaluer et suivre les performances de l&apos;ensemble des achats<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Coordonnateu (trice) de groupement de commandes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissance des techniques d&apos;achat<br>Connaissance des grands principes de la logistique<br>Exp&#xE9;rience dans le management et la gestion d&apos;&#xE9;quipe<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Animation et mise en place d&apos;&#xE9;change de pratiques avec d&apos;autres &#xE9;tablissements, </li> <li>Contr&#xF4;le de la cartographie de l&apos;ensemble des achats en vue de garantir leur bonne couverture par les acheteurs </li> <li>D&#xE9;finition de la strat&#xE9;gie, la politique d&apos;achat et l&apos;approvisionnement en assurant le soutien de la direction g&#xE9;n&#xE9;rale et des grandes directions </li> <li>D&#xE9;finition et mise en &#x153;uvre des actions d&apos;optimisation sur l&apos;ensemble de la cha&#xEE;ne d&apos;approvisionnement (d&#xE9;mat&#xE9;rialisation, gestion des stocks, processus d&apos;ex&#xE9;cution des commandes...) </li> <li>D&#xE9;veloppement et animation des acheteurs et &#xAB; apparent&#xE9;s &#xBB; (coaching, formation...) </li> <li>D&#xE9;veloppement et soutien de l&apos;&#xE9;mergence d&apos;acheteurs dissoci&#xE9;s des prescripteurs / utilisateurs sur les grandes cat&#xE9;gories de d&#xE9;penses. </li> <li>Elaboration et mise en &#x153;uvre d&apos;un plan d&apos;actions achat annuel quantifi&#xE9; sur toutes les cat&#xE9;gories de d&#xE9;penses </li> <li>Etude des demandes des prescripteurs </li> <li>Mise en place de la strat&#xE9;gie de couverture des segments par des groupements nationaux et r&#xE9;gionaux </li> <li>Suivi des gains d&apos;achats </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer des r&#xE9;unions et des discussions avec des prescripteurs-acheteurs </li> <li>Animer et manager des &#xE9;quipes, en environnement hi&#xE9;rarchique et fonctionnel </li> <li>Conduire le changement </li> <li>Evaluer les r&#xE9;sultats &#xE9;conomiques des achats </li> <li>Evaluer les risques internes et externes et mettre en &#x153;uvre les &#xE9;ventuelles actions de correction </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter l&apos;ex&#xE9;cution de plusieurs projets complexes en parall&#xE8;le </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Achat </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Droit des march&#xE9;s publics </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion de projet </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits et fournisseurs </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Techniques quantitatives </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur des achats pour valider les grandes orientations du PAA et les objectifs de gains<br>Acheteurs m&#xE9;tiers pour la d&#xE9;finition et la mise en &#x153;uvre des actions achat<br>Cellule des march&#xE9;s pour la bonne ex&#xE9;cution des march&#xE9;s publics<br>Services de soins, administratifs et techniques pour analyse du besoin et compr&#xE9;hension des sp&#xE9;cifications techniques<br>Fournisseurs pour la veille produit et la structuration du march&#xE9; fournisseurs<br>Fournisseurs pour analyse des offres et n&#xE9;gociation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +3/5 avec au moins 2 ans de sp&#xE9;cialisation dans le domaine des achats<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Ing&#xE9;nieur hospitalier<br>Contractuel de cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Educateur (trice) spécialisé(e)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, conduire et coordonner des actions &#xE9;ducatives et sociales globales des personnes (pr&#xE9;sentant des d&#xE9;ficiences physiques, psychiques et mentales), des groupes ou des familles en difficult&#xE9; dans le d&#xE9;veloppement de leurs capacit&#xE9;s de socialisation, d&apos;autonomie, d&apos;int&#xE9;gration ou d&apos;insertion.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Educateur(trice) g&#xE9;n&#xE9;raliste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En internat, externat, milieu ouvert, g&#xE9;rontologie, psychiatrie...<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;Etat d&apos;&#xE9;ducateur(trice) sp&#xE9;cialis&#xE9;(e) (DEES)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, encadrement et accompagnement p&#xE9;dagogique de personnes (agents, &#xE9;tudiants, stagiaires, etc.) </li> <li>Conseil et &#xE9;ducation th&#xE9;rapeutiques, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Conseil sur la d&#xE9;finition de(s) projet(s) professionnel(s) / individuel(s) et bilan des r&#xE9;alisations </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>&#xC9;laboration et mise en place du projet individuel concernant la personne et le groupe dans son domaine d&apos;activit&#xE9; </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi), en particulier lors des r&#xE9;unions intra et extrahospitali&#xE8;res (service, synth&#xE8;se, partenaires ext&#xE9;rieurs, etc) </li> <li>M&#xE9;diation des relations avec la famille, le repr&#xE9;sentant l&#xE9;gal de la personne et les diff&#xE9;rents partenaires ext&#xE9;rieurs </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction et mise &#xE0; jour de la documentation, relative &#xE0; ses activit&#xE9;s </li> <li>Suivi de la r&#xE9;alisation des travaux, de la r&#xE9;solution des probl&#xE8;mes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Concevoir, conduire et &#xE9;valuer un projet individuel relevant de son domaine de comp&#xE9;tence </li> <li>Conduire et animer des r&#xE9;unions </li> <li>&#xC9;valuer le degr&#xE9; d&apos;autonomie d&apos;une personne ou d&apos;un groupe de personnes </li> <li>Organiser / animer des activit&#xE9;s sp&#xE9;cifiques &#xE0; son domaine de comp&#xE9;tence pour des patients, des personnels/des groupes </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Stimuler les capacit&#xE9;s affectives, intellectuelles, psychomotrices et sociales d&apos;une personne </li> <li>Traiter et r&#xE9;soudre des situations agressives ou conflictuelles </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Bureautique/Technologies information et communication </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Intervention sociale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement des structures sociales et m&#xE9;dico-sociales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dagogie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie de l&apos;enfant et de l&apos;adolescent </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Familles, intervenants ext&#xE9;rieurs et professionnels des &#xE9;tablissements pour les synth&#xE8;ses, transmissions et recueil des donn&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;&#xE9;tat d&apos;&#xE9;ducateur(trice) sp&#xE9;cialis&#xE9;(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps assistant socio-&#xE9;ducatif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable socio-&#xE9;ducatif apr&#xE8;s obtention du Certificat d&apos;Aptitude aux Fonctions d&apos;Encadrement et de Responsable d&apos;Unit&#xE9; d&apos;Intervention Sociale (CAFERUIS)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Surveillant(e) de nuit', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Surveiller de nuit les personnes et les biens afin d&apos;assurer la continuit&#xE9; dans la prise en charge et la s&#xE9;curit&#xE9; des r&#xE9;sidents et des biens, dans les institutions des secteurs sociaux et m&#xE9;dico-sociaux et de l&apos;inadaptation sociale, afin de garantir les conditions du repos et g&#xE9;rer les situations d&apos;urgence et de tension.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Veilleur(se) de nuit<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Brevet de secourisme 1er degr&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc.), dans son domaine </li> <li>Intervention et traitement d&apos;urgence suite &#xE0; alerte ou / et situation &#xE0; risques dans le domaine hygi&#xE8;ne-s&#xE9;curit&#xE9;-sant&#xE9;-environnement (HSE) </li> <li>R&#xE9;ception et traitement des appels t&#xE9;l&#xE9;phoniques et/ou des messages &#xE9;lectroniques </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Surveillance de l&apos;&#xE9;tat de sant&#xE9; des personnes accueillies dans son domaine d&apos;intervention </li> <li>Surveillance du fonctionnement et de la disponibilit&#xE9; des &#xE9;quipements ou syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>V&#xE9;rification de l&apos;accessibilit&#xE9; des voies de circulation sur le site et en p&#xE9;riph&#xE9;rie des b&#xE2;timents </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>Identifier / analyser des situations d&apos;urgence et d&#xE9;finir des actions </li> <li>Identifier, analyser, &#xE9;valuer et pr&#xE9;venir les risques relevant de son domaine, d&#xE9;finir les actions correctives/pr&#xE9;ventives </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Traiter et r&#xE9;soudre des situations agressives ou conflictuelles </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">G&#xE9;ographie et topographie de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion de crise </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne et s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; des biens et des personnes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques et mat&#xE9;riels de lutte contre l&apos;incendie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>&#xC9;quipes &#xE9;ducatives, m&#xE9;dicales pour la transmission d&apos;informations,<br>Services d&apos;intervention et de soins d&apos;urgence, service de police ou de gendarmerie pour des prises en charge particuli&#xE8;res<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Agent de service hospitalier (ASH)<br>Agent d&apos;entretien qualifi&#xE9; (AEQ)<br>Aide-soignant (AS)<br>Aide m&#xE9;dico-psychologique (AMP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Aide m&#xE9;dico-psychologique (AMP)<br>Moniteur-&#xE9;ducateur<br>Agent de pr&#xE9;vention de s&#xE9;curit&#xE9; des personnes et des biens<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Ministre du culte', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Apporter un soutien mat&#xE9;riel et r&#xE9;pondre aux besoins spirituels de personnes, et en particulier des r&#xE9;sidents et patients accueillis au sein de l&apos;&#xE9;tablissement.<br>D&#xE9;finir, organiser et animer des &#xE9;v&#xE9;nements religieux dans l&apos;&#xE9;tablissement.<br>Accompagner individuellement les personnes, &#xE0; partir de leur demande, dans le cadre de son domaine de comp&#xE9;tence.<br>Ce m&#xE9;tier peut &#xEA;tre pratiqu&#xE9; par un clerc ou la&#xEF;c de n&apos;importe quelle religion mandat&#xE9;e.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Aum&#xF4;nier(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>En fonction de la nature des cultes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Agr&#xE9;ment de l&apos;autorit&#xE9; reconnue de la religion concern&#xE9;e<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Formation professionnelle de personnes (agents, stagiaires, b&#xE9;n&#xE9;voles, etc.) </li> <li>Gestion d&apos;&#xE9;quipes de b&#xE9;n&#xE9;voles </li> <li>Identification, recensement des besoins et des attentes spirituelles des patients, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Information des &#xE9;quipes soignantes aux rites et cultes sp&#xE9;cifiques </li> <li>Information et conseil &#xE0; des tiers (agents, patients, familles, etc.) dans son domaine d&apos;activit&#xE9; </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Organisation, animation et suivi d&apos;activit&#xE9;s sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Tenue d&apos;entretiens avec des personnes (patients, familles,...) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Argumenter avec des interlocuteurs </li> <li>Conduire un entretien d&apos;aide </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>G&#xE9;rer ses &#xE9;motions, son stress </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>S&apos;exprimer en face -&#xE0;- face aupr&#xE8;s d&apos;une ou plusieurs personnes </li> <li>S&apos;exprimer en public </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sciences sociales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins fun&#xE9;raires </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Textes fondateurs et traditions religieuses </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Th&#xE9;ologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Patients, familles et personne de confiance en r&#xE9;ponse &#xE0; leurs demandes, en fonction des &#xE9;v&#xE9;nements...<br>Equipes de soins pluridisciplinaires m&#xE9;dicales et non m&#xE9;dicales<br>Assistantes sociales<br>Associations de b&#xE9;n&#xE9;voles pour l&apos;organisation de leur intervention au sein de l&apos;&#xE9;tablissement<br>Rattachement &#xE0; l&apos;autorit&#xE9; religieuse et &#xE0; la direction de l&apos;&#xE9;tablissement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Contractuel<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable de service mortuaire', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Organiser et coordonner l&apos;activit&#xE9; et les moyens d&apos;un service mortuaire en veillant &#xE0; l&apos;efficacit&#xE9; et la qualit&#xE9; des prestations dues au d&#xE9;funt et &#xE0; sa famille<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable de chambre mortuaire<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Formation d&apos;adaptation &#xE0; l&apos;emploi (FAE) dans les conditions d&#xE9;finies par l&apos;arr&#xEA;t&#xE9; du 16 juillet 2009<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, accompagnement et information des familles et des proches du patient d&#xE9;c&#xE9;d&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Organisation, coordination et suivi des activit&#xE9;s </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Pr&#xE9;sentation du patient d&#xE9;c&#xE9;d&#xE9; dans un environnement propice au recueillement </li> <li>Recueil, saisie, mise &#xE0; jour et/ou sauvegarde de donn&#xE9;es administratives et m&#xE9;dicales </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Animer et encadrer une ou plusieurs &#xE9;quipes, d&#xE9;velopper les comp&#xE9;tences de ses collaborateurs et les &#xE9;valuer </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec l&apos;entourage du patient d&#xE9;c&#xE9;d&#xE9; </li> <li>G&#xE9;rer ses &#xE9;motions, son stress </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser une proc&#xE9;dure, un langage, un protocole, une r&#xE9;glementation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit hospitalier et droit fun&#xE9;raire. R&#xE8;gles de bonnes pratiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodologie d&apos;analyse de situation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pratiques sociales, rites et religions </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sociologie et psychologie de la mort et processus du deuil </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins d&apos;hygi&#xE8;ne et de pr&#xE9;sentation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>L&apos;activit&#xE9; s&apos;effectue g&#xE9;n&#xE9;ralement en &#xE9;quipe, souvent en contact avec l&apos;entourage du patient d&#xE9;c&#xE9;d&#xE9;<br>Les services cliniques, les services de soins palliatifs et les maternit&#xE9;s pour la prise en charge des patients d&#xE9;c&#xE9;d&#xE9;s<br>Les &#xE9;quipes de coordination hospitali&#xE8;re pour les pr&#xE9;l&#xE8;vements<br>Les services sociaux pour instruire les dossiers sociaux des indigents<br>Les services d&apos;&#xE9;tat civil de l&apos;h&#xF4;pital et des mairies pour les formalit&#xE9;s administratives<br>Les m&#xE9;decins anatomo-pathologistes et foeto-pathologistes pour les autopsies<br>Les tiers officiant cultuels pour les veill&#xE9;es et toilettes rituelles<br>Des stagiaires pour des actions de formation<br>Les op&#xE9;rateurs fun&#xE9;raires (maitre de c&#xE9;r&#xE9;monie et porteurs) pour l&apos;accueil du cercueil et les actes de thanatopraxie<br>Les fleuristes pour orner les cercueils et la chambre de pr&#xE9;sentation<br>Le service de police et le m&#xE9;decin l&#xE9;giste en cas d&apos;obstacle m&#xE9;dico-l&#xE9;gal et/ou hors communes du lieu de d&#xE9;c&#xE8;s et de cr&#xE9;mation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Cadre de sant&#xE9;<br>Infirmier dipl&#xF4;m&#xE9; d&apos;&#xE9;tat<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Secrétaire médical(e)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Accueillir et renseigner les patients, planifier les activit&#xE9;s (agenda des consultations, admissions, convocations, etc.)<br>G&#xE9;rer, saisir et classer les informations relatives au dossier patient<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissance de la bureautique<br>connaissance de l&apos;environnement m&#xE9;dical<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil physique et t&#xE9;l&#xE9;phonique des patients, des familles </li> <li>Conception et r&#xE9;daction de documents relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion et coordination m&#xE9;dico-administrative (gestion des agendas, organisation de r&#xE9;unions, visites, conf&#xE9;rences et &#xE9;v&#xE9;nements...) </li> <li>Gestion, traitement et coordination des programmations li&#xE9;es &#xE0; la prise en charge m&#xE9;dicale (consultation, hospitalisation, chirurgie, examens...) </li> <li>Identification et recensement des besoins et attentes des patients et des familles </li> <li>Prise de notes, frappe et mise en forme des documents </li> <li>Saisie des donn&#xE9;es li&#xE9;es &#xE0; l&apos;activit&#xE9; m&#xE9;dicale (codage) </li> <li>Tenue &#xE0; jour du dossier patient </li> <li>Traitement des courriers, dossiers, documents dans son domaine (enregistrement, tri, traitement, diffusion, archivage) </li> <li>V&#xE9;rification des informations administratives concernant le patient dans le cadre de l&apos;identitovigilance </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accueillir et orienter des personnes, des groupes, des publics </li> <li>D&#xE9;finir/adapter et utiliser un plan de classement et d&apos;archivage, un syst&#xE8;me de gestion documentaire </li> <li>Elaborer, adapter et optimiser le planning de travail, de rendez-vous, des visites </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>Organiser et classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>S&apos;exprimer avec professionnalisme aupr&#xE8;s d&apos;une ou plusieurs personnes en face &#xE0; face ou par t&#xE9;l&#xE9;phone </li> <li>Traiter et r&#xE9;soudre des situations agressives et conflictuelles </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les outils bureautiques et les logiciels m&#xE9;tiers </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Codage des actes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des usagers du syst&#xE8;me de sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation des archives </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Secr&#xE9;tariat </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Equipe m&#xE9;dicale et soignante pour l&apos;organisation du circuit des patients<br>Bureau des entr&#xE9;es pour la mise en coh&#xE9;rence des donn&#xE9;es administratives relatives aux patients<br>Services m&#xE9;dico-techniques de l&apos;&#xE9;tablissement pour la r&#xE9;alisation d&apos;examens compl&#xE9;mentaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC science m&#xE9;dico-sociale<br>BAC sp&#xE9;cialis&#xE9; compl&#xE9;t&#xE9; par un BTS dans le domaine m&#xE9;dical ou du secr&#xE9;tariat<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Assistant m&#xE9;dico-administratif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Technicien d&apos;information m&#xE9;dicale<br>Encadrant des secr&#xE9;tariats m&#xE9;dicaux<br>Encadrant archives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Préparateur(trice) en pharmacie hospitalière (PPH)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Sous la responsabilit&#xE9; et le contr&#xF4;le effectif d&apos;un pharmacien:<br>pr&#xE9;parer, d&#xE9;livrer, g&#xE9;rer et approvisionner les m&#xE9;dicaments anticanc&#xE9;reux et les dispositifs m&#xE9;dicaux st&#xE9;riles;<br>concourir aux op&#xE9;rations de st&#xE9;rilisation<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Brevet professionnel de pr&#xE9;parateur en pharmacie<br>Dipl&#xF4;me de pr&#xE9;parateur en pharmacie hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (agents, patients, usagers, etc.), dans son domaine </li> <li>Accueil, encadrement et accompagnement p&#xE9;dagogique de personnes (agents, &#xE9;tudiants, stagiaires, etc.) </li> <li>Approvisionnement en produits, en mat&#xE9;riels, dans son domaine </li> <li>Conditionnement de produits et / ou de mat&#xE9;riels sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Constitution et contr&#xF4;les des dotations de services dans le respect des r&#xE8;gles de d&#xE9;tention et de conservation des m&#xE9;dicaments et des dispositifs m&#xE9;dicaux st&#xE9;riles </li> <li>D&#xE9;livrance de m&#xE9;dicaments et de dispositifs m&#xE9;dicaux st&#xE9;riles </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion des stocks de produits, de mat&#xE9;riels, dans son domaine (saisie, suivi, contr&#xF4;le, relance commandes) </li> <li>Pr&#xE9;parations pharmaceutiques </li> <li>R&#xE9;alisation d&apos;op&#xE9;rations de st&#xE9;rilisation </li> <li>R&#xE9;alisation de pr&#xE9;parations gal&#xE9;niques, en zones prot&#xE9;g&#xE9;es ou contr&#xF4;l&#xE9;es </li> <li>R&#xE9;alisation des op&#xE9;rations d&apos;approvisionnement et de gestion </li> <li>Veille professionnelle et d&#xE9;veloppement professionnel continu </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et adapter sa pratique professionnelle au regard de l&apos;&#xE9;volution des recommandations </li> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements et consommables dans son domaine de comp&#xE9;tence </li> <li>Analyser les prescriptions, les demandes de m&#xE9;dicaments ou de produits ou les ordonnances </li> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>Conseiller le patient et son entourage dans le cadre du projet personnalis&#xE9; du patient </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, choisir, utiliser les produits et/ou mat&#xE9;riaux n&#xE9;cessaire &#xE0; la pratique de son m&#xE9;tier </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Fluides m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">L&#xE9;gislation et r&#xE9;glementation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pharmacie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Pharmacologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Radioprotection </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Risques et vigilances </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">St&#xE9;rilisation des dispositifs m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques de pr&#xE9;parations pharmacologiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Traitement des d&#xE9;chets </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Pharmacien pour l&apos;organisation de l&apos;activit&#xE9; quotidienne et le contr&#xF4;le technique<br>Agents administratifs de la pharmacie &#xE0; usage int&#xE9;rieur pour la gestion administrative de l&apos;activit&#xE9;<br>Personnel param&#xE9;dical des unit&#xE9;s de soins et patients pour la d&#xE9;livrance des m&#xE9;dicaments<br>Services logistiques internes pour la gestion des activit&#xE9;s ( approvisionnement, nettoyage ....)<br>Patients ambulatoires pour la d&#xE9;livrance des m&#xE9;dicaments ou conseils concernant un m&#xE9;dicament<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Brevet professionnel de pr&#xE9;parateur en pharmacie<br>Dipl&#xF4;me de pr&#xE9;parateur en pharmacie hospitali&#xE8;re<br>Dipl&#xF4;me accessible par la VAE<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Pr&#xE9;parateur en pharmacie hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Hygi&#xE9;niste<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br>Formateur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Psychologue', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, &#xE9;laborer et mettre en oeuvre des actions pr&#xE9;ventives, curatives et de recherche &#xE0; travers une d&#xE9;marche professionnelle propre prenant en compte les rapports r&#xE9;ciproques entre la vie psychique et les comportements individuels et collectifs, afin de promouvoir l&apos;autonomie de la personnalit&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Psychologue hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Licence de psychologie<br>+ Master 2 ou DESS de psychologie (clinique, psychopathologie, enfance/adolescence,g&#xE9;rontologique...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, encadrement et formation des &#xE9;tudiants et des stagiaires </li> <li>Bilan psychologique (recueil d&apos;information, entretien, interpr&#xE9;tation et synth&#xE8;se, restitution) </li> <li>Collaboration avec les autres membres de l&apos;&#xE9;quipe pluri-professionnelle </li> <li>Conception et &#xE9;laboration d&apos;interventions psychologiques et th&#xE9;rapeutiques &#xE0; vis&#xE9;e pr&#xE9;ventive et curative aupr&#xE8;s des patients et de leurs familles </li> <li>Conseil au projet de p&#xF4;le et au projet d&apos;&#xE9;tablissement </li> <li>Elaboration d&apos;un projet psychologique de p&#xF4;le et d&apos;&#xE9;tablissement </li> <li>Elaboration du diagnostic sur le fonctionnement psychique d&apos;un patient </li> <li>Intervention aupr&#xE8;s d&apos;&#xE9;quipes pluriprofessionnelles interne ou externes (formation, soutien, groupes de parole, analyses des pratiques, analyse institutionnelle). </li> <li>Pratiques d&apos;entretiens individuels ou collectifs </li> <li>Psychoth&#xE9;rapies individuelles et de groupe </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes et recherche en psychologie en lien avec les activit&#xE9;s cliniques, enseignement, formation personnelle et vers autrui, expertises </li> <li>Recueil et analyse des besoins et des demandes d&apos;interventions, </li> <li>Travail d&apos;&#xE9;valuation par le biais de groupes de r&#xE9;flexion et de supervision th&#xE9;orico-clinique entre professionnel ou par toutes autres m&#xE9;thodes sp&#xE9;cifiques </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Choisir une m&#xE9;thode correspondant &#xE0; son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir et mettre en oeuvre les activit&#xE9;s th&#xE9;rapeutiques adapt&#xE9;es au patient </li> <li>Formaliser et transmettre son savoir professionnel </li> <li>Observer et analyser la situation, le discours des diff&#xE9;rents acteurs </li> <li>Organiser un cadre th&#xE9;rapeutique singulier ou institutionnel (ajuster la th&#xE9;rapeutique en fonction du patient et de son environnement) </li> <li>R&#xE9;diger et mettre en forme des notes cliniques, documents et/ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tences </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Approche m&#xE9;dico-l&#xE9;gale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Bilan psychologique </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Ethnopsychologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodologie de recherche en psychologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Psycho-sociologie des organisations </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie clinique et psychopathologie </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Psychologie des &#xE2;ges de la vie </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Psychologie g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Psychosomatique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Psychoth&#xE9;rapies </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>&#xC9;quipes pluriprofessionnelles pour les projets de soins, p&#xE9;dagogiques et &#xE9;ducatifs.<br>Services sociaux, &#xE9;ducatifs, judiciaires, juridiques, maison du handicap, tribunal, &#xE9;cole, universit&#xE9;..., pour le travail de liaison et de coordination.<br>R&#xE9;seaux de villes et associations (CMP, CMPP, PMI, psychiatres, psychologues lib&#xE9;raux) pour le suivi des patients.<br>Groupes de pairs<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Licence de psychologie<br>+ master 2 ou DESS en psychologie clinique, psychopathologie, enfance/adolescence, g&#xE9;rontologique...<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Psychologue<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Pédicure podologue', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un diagnostic, r&#xE9;aliser des actes sur les troubles cutan&#xE9;s, morphostatiques et dynamiques du pied et des affections ungu&#xE9;ales du pied.<br>Prescrire, confectionner ou adapter des dispositifs m&#xE9;dicaux podologiques externes du pied.<br>Prescrire et appliquer des topiques et des pansements.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;etat de p&#xE9;dicure - podologue<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil et &#xE9;ducation th&#xE9;rapeutiques, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Etablissement du diagnostic dans le domaine de la p&#xE9;dicurie-podologie </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Gestion des ressources humaines, administratives et comptables </li> <li>Identification, recensement des besoins et des attentes des patients, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Information et conseil sur la sant&#xE9; et l&apos;hygi&#xE8;ne du pied, sur la pr&#xE9;vention des troubles morphostatiques et des risques podologiques aupr&#xE8;s de l&apos;enfant, l&apos;adulte, la personne &#xE2;g&#xE9;e en individuel ou en groupe </li> <li>Mise en place de d&#xE9;marches qualit&#xE9; / environnement et r&#xE9;daction de documents requis pour l&apos;activit&#xE9; de l&apos;entreprise </li> <li>Mise en &#x153;uvre d&apos;actions de pr&#xE9;vention et de d&#xE9;pistage </li> <li>Organisation du d&#xE9;roulement de la s&#xE9;ance de soins de p&#xE9;dicurie-podologie et planification des activit&#xE9;s et des soins </li> <li>R&#xE9;alisation de soins de p&#xE9;dicurie-podologie des affections du pied, de soins cutan&#xE9;s et ungu&#xE9;aux (podologie instrumentale) et suivi des patients </li> <li>R&#xE9;alisation des orth&#xE8;ses dans le domaine de la p&#xE9;dicurie-podologie et suivi des patients </li> <li>Recueil de donn&#xE9;es cliniques </li> <li>R&#xE9;daction et mise &#xE0; jour du dossier patient </li> <li>Veille et recherche professionnelle </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et &#xE9;valuer une situation et &#xE9;laborer un diagnostic dans le domaine de la p&#xE9;dicurie podologie </li> <li>Communiquer et conduire une relation dans un contexte d&apos;intervention </li> <li>Concevoir et conduire un projet de soins, dans son domaine de comp&#xE9;tence </li> <li>Concevoir et conduire une d&#xE9;marche d&apos;information, de conseil et d&apos;&#xE9;ducation </li> <li>Coop&#xE9;rer avec d&apos;autres professionnels </li> <li>D&#xE9;finir et mettre en oeuvre les soins et activit&#xE9;s th&#xE9;rapeutiques adapt&#xE9;s au patient, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;laborer et conduire une d&#xE9;marche d&apos;intervention en sant&#xE9; publique : pr&#xE9;vention, d&#xE9;pistage et &#xE9;ducation th&#xE9;rapeutique </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>Former et informer des professionnels et des personnes en formation </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Rechercher, traiter et produire des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">P&#xE9;dicurie/ podologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Pharmacologie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychologie des &#xE2;ges de la vie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins pour la mise en oeuvre de la prise en charge du patient.<br>Infirmi&#xE8;res pour l&apos;harmonisation et le suivi de la prise en charge.<br>Kin&#xE9;sith&#xE9;rapeutes et aides-soignantes pour le d&#xE9;pistage des troubles n&#xE9;cessitant une prise en charge en soins de p&#xE9;dicurie-podologie.<br>Services sociaux pour l&apos;orientation des patients<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;etat de p&#xE9;dicure - podologue<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>P&#xE9;dicure-podologue<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable budgétaire et financier', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre en oeuvre, suivre et contr&#xF4;ler la proc&#xE9;dure budg&#xE9;taire et les impacts financiers qui en d&#xE9;coulent, en collaboration avec les acteurs internes et externes dans un contexte pluriannuel.<br>Garantir la qualit&#xE9; du processus comptable.<br>Optimiser les recettes en animant une &#xE9;quipe de professionnels.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Responsable admission frais de s&#xE9;jour traitement externe<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>&#xC9;laboration et analyse de bilan (comptabilit&#xE9; analytique d&apos;exploitation) </li> <li>&#xC9;laboration et mise en place et suivi du plan budg&#xE9;taire pluriannuel </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement et suivi de budget(s) relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Mise en place et suivi des processus assurant la fiabilisation et/ou la certification des comptes financiers </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;daction de notes de synth&#xE8;se, relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser des donn&#xE9;es, des tableaux de bord et justifier des r&#xE9;sultats relatifs aux activit&#xE9;s de son domaine </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Analyse financi&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Comptabilit&#xE9; publique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Contr&#xF4;le de gestion </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la gestion budg&#xE9;taire </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directions fonctionnelles, p&#xF4;les et services pour les &#xE9;changes d&apos;informations sur le suivi budg&#xE9;taire et la mise en place d&apos;actions correctives<br>Autorit&#xE9;s d&#xE9;concentr&#xE9;es pour la mise en coh&#xE9;rence des informations comptables et budg&#xE9;taires<br>Tr&#xE9;sorerie pour l&apos;application des r&#xE8;gles comptables<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me en comptabilit&#xE9; g&#xE9;n&#xE9;rale ou publique / finances / management des organisation de sant&#xE9;<br>Master 2<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Adjoint des cadres hospitaliers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Contr&#xF4;leur de gestion<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable sécurité des systèmes d''information (RSSI)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir et animer la d&#xE9;marche s&#xE9;curit&#xE9; et confidentialit&#xE9; des syst&#xE8;mes d&apos;information (mat&#xE9;riels, donn&#xE9;es et logiciels), en veillant &#xE0; ce que les niveaux de s&#xE9;curit&#xE9; et de confidentialit&#xE9; soient conformes &#xE0; la r&#xE9;glementation externe et aux standards internes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Sp&#xE9;cialiste s&#xE9;curit&#xE9; des syst&#xE8;mes d information<br>Expert(e) s&#xE9;curit&#xE9; informatique<br>Administrateur(trice) s&#xE9;curit&#xE9; informatique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Audit et contr&#xF4;le de la s&#xE9;curit&#xE9; du SI en termes de confidentialit&#xE9;, int&#xE9;grit&#xE9;, disponibilit&#xE9; </li> <li>Contr&#xF4;le de l&apos;application des normes, standards, proc&#xE9;dures et outils li&#xE9;es &#xE0; la s&#xE9;curit&#xE9; du SI </li> <li>D&#xE9;finition et mise en oeuvre de la politique de s&#xE9;curit&#xE9; des SI (PSSI), en coh&#xE9;rence avec la r&#xE9;glementation en vigueur (interne et externe) </li> <li>D&#xE9;finition, planification et mise en place des normes, standards, proc&#xE9;dures et outils li&#xE9;es &#xE0; la s&#xE9;curit&#xE9; du SI (MSSI) </li> <li>Information des utilisateurs aux probl&#xE8;mes de s&#xE9;curit&#xE9; (sauvegarde, virus, confidentialit&#xE9; des donn&#xE9;es...) </li> <li>Mise en oeuvre des plans de secours et de sauvegarde de la s&#xE9;curit&#xE9; du SI </li> <li>Recensement, analyse et traitement des risques li&#xE9;s &#xE0; la s&#xE9;curit&#xE9; du SI et la confidentialit&#xE9; des donn&#xE9;es </li> <li>R&#xE9;daction du volet s&#xE9;curit&#xE9; SI du cahier des clauses techniques particuli&#xE8;res (CCTP) </li> <li>Veille et suivi des &#xE9;volutions r&#xE9;glementaires et technologiques du SI afin de garantir le maintien de la s&#xE9;curit&#xE9; du SI </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser les dysfonctionnements de la s&#xE9;curit&#xE9; du SI et organiser les retours d&apos;exp&#xE9;rience </li> <li>Concevoir et r&#xE9;diger un manuel de s&#xE9;curit&#xE9; du SI (MSSI) et ses documents annexes et assurer leur mise &#xE0; disposition </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Conduire une analyse de risques et &#xE9;tablir les plans d&apos;actions associ&#xE9;s </li> <li>D&#xE9;finir, argumenter et mettre en &#x153;uvre des mesures d&apos;urgence efficaces </li> <li>D&#xE9;finir, argumenter une politique d&apos;authentification des utilisateurs du SI </li> <li>Identifier les besoins de s&#xE9;curit&#xE9; inh&#xE9;rents aux activit&#xE9;s dans le cadre des SI utilis&#xE9;s, formuler une proposition de solution et son dimensionnement </li> <li>Mettre en &#x153;uvre les normes, les bonnes pratiques et les exigences l&#xE9;gales de la s&#xE9;curit&#xE9; de l&apos;information </li> <li>Mettre en &#x153;uvre les r&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es, la charte d&apos;utilisation du SI et la charte d&apos;identito-vigilance </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Traiter les dysfonctionnements de la s&#xE9;curit&#xE9; du SI </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Architecture SI m&#xE9;tier et fonctionnelle </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Informatique, SI et veille technologique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thode d&apos;analyse des risques et normes li&#xE9;s &#xE0; la s&#xE9;curit&#xE9; du SI </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thode d&apos;audit interne des SI </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence (MSSI) </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Produits et march&#xE9; informatique et son &#xE9;volution </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Projet d&apos;&#xE9;tablissement (orientations strat&#xE9;giques) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE8;gles li&#xE9;es &#xE0; la protection des donn&#xE9;es </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; informatique, s&#xE9;curit&#xE9; SI </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et Politique S&#xE9;curit&#xE9; de l&apos;&#xE9;tablissement (PSSI) </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> </tbody></table> <ul> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction pour faire valider la politique en terme de s&#xE9;curit&#xE9; / Service d&apos;information m&#xE9;dicale pour d&#xE9;finir la politique d&apos;acc&#xE8;s aux donn&#xE9;es m&#xE9;dicales / Equipe informatique pour la mise en oeuvre de la politique s&#xE9;curit&#xE9; / Fournisseurs pour les consultations et r&#xE9;ponses aux besoins internes (mat&#xE9;riels, progiciels, etc.)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac + 5<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Chef de projet informatique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable hôtelier(ère)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser et piloter l&apos;ensemble des activit&#xE9;s d&apos;h&#xF4;tellerie en animant une &#xE9;quipe de professionnels et en veillant &#xE0; l&apos;efficacit&#xE9; et &#xE0; la qualit&#xE9; des prestations fournies<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Suivi et contr&#xF4;le de prestations sous-trait&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Bac+2/3 en h&#xF4;tellerie ou exp&#xE9;rience h&#xF4;teli&#xE8;re / dans le suivi de prestations h&#xF4;teli&#xE8;res sous-trait&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conseil aux p&#xF4;les et aux projets d&apos;&#xE9;tablissement dans son domaine de comp&#xE9;tences </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards relatifs &#xE0; l&apos;hygi&#xE8;ne de l&apos;environnement </li> <li>Contr&#xF4;le et suivi de prestations sous-trait&#xE9;es (linge, nettoyage, restauration...) </li> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement et suivi de budget(s) relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Organisation et suivi op&#xE9;rationnel des activit&#xE9;s/projets, coordination avec les interlocuteurs internes et externes </li> <li>Planification, r&#xE9;organisation des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Etablir, &#xE9;valuer et optimiser un budget </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">H&#xF4;tellerie </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Logistique g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrement de proximit&#xE9; logistique, directeurs, cadres de sant&#xE9;, cadres techniques, soignants, patients, fournisseurs et prestataires de services<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +2/3 en h&#xF4;tellerie ou experience<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>TH, TSH<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable restauration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Technicien(ne) d''études cliniques (TEC)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre en &#x153;uvre la logistique du protocole, le recueil et la saisie des donn&#xE9;es cliniques sous la responsabilit&#xE9; des investigateurs et de l&apos;&#xE9;quipe m&#xE9;dicale lors de la r&#xE9;alisation de protocoles de recherche clinique sur les lieux autoris&#xE9;s.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Technicien(ne) de recherche clinique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la faisabilit&#xE9; des circuits logistiques de l&apos;&#xE9;tude </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>Information et conseil &#xE0; des tiers (agents, patients, familles, etc.) dans son domaine d&apos;activit&#xE9; </li> <li>Organisation de la v&#xE9;rification des donn&#xE9;es en vue des monitorages </li> <li>Pr&#xE9;paration des &#xE9;chantillons biologiques, de leur stockage ou de leur acheminement </li> <li>Pr&#xE9;paration des &#xE9;l&#xE9;ments de file active et d&apos;activit&#xE9; sous forme synth&#xE9;tique et graphique </li> <li>Recueil / collecte de donn&#xE9;es ou informations sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Renseignement de documents, de fichiers (fiches d&apos;activit&#xE9;, de tra&#xE7;abilit&#xE9;, etc.) </li> <li>Reprographie, anonymisation des r&#xE9;sultats et envoi des donn&#xE9;es au centre de gestion </li> <li>Suivi des &#xE9;v&#xE9;nements ind&#xE9;sirables </li> <li>Traitement des courriers, dossiers, documents dans son domaine (enregistrement, tri, traitement, diffusion, archivage) </li> <li>Traitement pr&#xE9;-analytique des &#xE9;chantillons </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et utiliser des informations &#xE0; partir du dossier hospitalier du patient et du dossier m&#xE9;dical personnalis&#xE9; </li> <li>Classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>&#xC9;valuer la pertinence et la v&#xE9;racit&#xE9; des donn&#xE9;es et/ou informations </li> <li>Identifier les informations communicables &#xE0; autrui en respectant le secret professionnel </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>S&apos;exprimer en face -&#xE0;- face aupr&#xE8;s d&apos;une ou plusieurs personnes </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">bases de donn&#xE9;es </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion de donn&#xE9;es, relatives &#xE0; son domaine </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche clinique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation relative &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les &#xE9;quipes m&#xE9;dicales et param&#xE9;dicales, les patients, les laboratoires d&apos;analyses biologiques internes et externes, le service des archives, le centre de gestion et de coordination d&apos;essais cliniques (les assistants de recherche clinique, les coordinateurs d&apos;etudes cliniques, les chefs de projets de recherche clinique) pour la mise en oeuvre et la conduite des &#xE9;tudes cliniques<br>Les m&#xE9;decins investigateurs pour la pr&#xE9;paration de l&apos;inclusion des patients et la mise en oeuvre de la logistique des protocoles.<br>Les pharmaciens des pharmacies &#xE0; usage int&#xE9;rieur pour le contr&#xF4;le du circuit m&#xE9;dicament dans le cadre des &#xE9;tudes cliniques<br>Les repr&#xE9;sentants de l&apos;industrie pharmaceutique pour les &#xE9;tudes de faisabilit&#xE9; et le retour d&apos;informations sur les &#xE9;tudes.<br>Service de pharmacovigilance pour remonter les &#xE9;v&#xE8;nements ind&#xE9;sirables<br>Service informatique pour consulter les dossiers patients<br>Service central des Archives pour les modifications de donn&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau II (BAC+3)<br>Etudes param&#xE9;dicales ou dipl&#xF4;me universitaire dans le domaine des &#xE9;tudes cliniques ou d&apos;autres domaines scientifiques (biologie, m&#xE9;decine, pharmacie, informatique, ...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier,<br>Infirmier dipl&#xF4;m&#xE9; d&apos;etat,<br>Technicien de laboratoire, ...<br>Cat&#xE9;gorie B<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Assistant de recherche clinique<br>Coordinateur d&apos;etudes cliniques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Monteur(euse) en installations et maintenance des installations sanitaires et thermiques', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des travaux de maintenance, d&apos;entretien, d&apos;installation et de r&#xE9;novation des r&#xE9;seaux et &#xE9;quipements sanitaires, thermiques et de traitement d&apos;air<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Thermicien(ne)<br>Chauffagiste<br>Frigoriste<br>Plombier(&#xE8;re)<br>Technicien(ne) fluides m&#xE9;dicaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le de la qualit&#xE9; de l&apos;eau </li> <li>D&#xE9;pannage, r&#xE9;paration, remise en &#xE9;tat, remplacement de mat&#xE9;riels, d&apos;&#xE9;quipements relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Entretien, maintenance, pr&#xE9;vention et d&#xE9;pannage des mat&#xE9;riels, outillages, &#xE9;quipements, syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Installation de mat&#xE9;riels, d&apos;&#xE9;quipements, de syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>Mise en conformit&#xE9; des installations, en application des normes et r&#xE8;glementations en vigueur </li> <li>Pr&#xE9;vention et suivi des risques sanitaires : l&#xE9;gionnelles </li> <li>Suivi des contr&#xF4;les et des organismes agr&#xE9;&#xE9;s </li> <li>Tenue &#xE0; jour des donn&#xE9;es / des fichiers relatifs au domaine d&apos;activit&#xE9; </li> <li>V&#xE9;rification, mise en route, pilotage et surveillance des &#xE9;quipements, mise &#xE0; l&apos;arr&#xEA;t </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Conduire des installations, des &#xE9;quipements relatifs au domaine de comp&#xE9;tence, en optimiser le fonctionnement </li> <li>Dessiner un plan ou un croquis technique relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer la qualit&#xE9; de l&apos;eau </li> <li>&#xC9;valuer, choisir, utiliser les produits et/ou mat&#xE9;riaux n&#xE9;cessaire &#xE0; la pratique de son m&#xE9;tier </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Identifier et diagnostiquer un dysfonctionnement, une panne, le d&#xE9;faut d&apos;un mat&#xE9;riel, d&apos;un &#xE9;quipement, une anomalie d&apos;un syst&#xE8;me, sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Lire et interpr&#xE9;ter des plans techniques, des croquis relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Maintenir et d&#xE9;panner un mat&#xE9;riel, un &#xE9;quipement, une installation et/ou un syst&#xE8;me relatif &#xE0; son m&#xE9;tier </li> <li>Utiliser des mat&#xE9;riels, des outils de d&#xE9;pannage, de r&#xE9;paration ou de maintenance, relatifs &#xE0; son m&#xE9;tier </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">A&#xE9;raulique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Dessin en b&#xE2;timent </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectricit&#xE9; g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectronique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hydraulique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;canique g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Plomberie zinguerie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">R&#xE9;glementation des installations de chaufferie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Soudage </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Services pour la r&#xE9;alisation des taches aff&#xE9;rentes &#xE0; son m&#xE9;tier.<br>fournisseurs pour la mise en service et les formations aux nouveaux mat&#xE9;riels<br>prestataires de services pour l&apos;accompagnement, le suivi et le contr&#xF4;le qualit&#xE9; des interventions<br>cadres experts (hygi&#xE9;niste, pcrp, m&#xE9;decine du travail..) pour l&apos;hygi&#xE8;ne et la s&#xE9;curit&#xE9; lors des interventions<br>cellule hygi&#xE8;ne du clin pour les pr&#xE9;l&#xE8;vements et contr&#xF4;les biologiques<br>responsable des services techniques g&#xE9;n&#xE9;raux, de maintenance sanitaire, thermique, climatique, fluidique et responsable maintenance tce pour organisation des interventions<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAP, BEP, habilitation(s)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant installations et maintenance &#xE9;quipements sanitaires, thermiques<br>Coordinateur qualit&#xE9; / gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Peintre en bâtiment', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Appliquer de la peinture ou des produits couvrants sur des mat&#xE9;riaux ou des surfaces.<br>Poser des rev&#xEA;tements muraux, des vitres et des glaces et r&#xE9;aliser des travaux de d&#xE9;coration<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Peintre<br>Peintre-vitrier<br>Peintre - applicateur de rev&#xEA;tements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Poseur de rev&#xEA;tements de sol<br>Agent(e) de signal&#xE9;tique<br>Plaquiste<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation de travaux sp&#xE9;cifiques : fresques murales par application de peinture </li> <li>Marquage de lettres et logos sur diff&#xE9;rents supports (pour la signal&#xE9;tique) </li> <li>Mise en place de protections de chantier et / ou individuelles </li> <li>Pose de cloisons, de rev&#xEA;tements, de protections murales </li> <li>Protection ou d&#xE9;coration des supports &#xE0; l&apos;aide de produits fluides ou &#xE9;pais </li> <li>R&#xE9;alisation de travaux d&apos;appr&#xEA;t (nettoiement, &#xE9;grenage, pon&#xE7;age) </li> <li>Remplacement de vitres simples ou isolantes sur tous les types de menuiserie </li> <li>Renseignement de documents, de fichiers (fiches d&apos;activit&#xE9;, de tra&#xE7;abilit&#xE9;, etc.) </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et optimiser les stocks de produits, mat&#xE9;riaux, &#xE9;quipements, outillages, dans son domaine de comp&#xE9;tence </li> <li>Choisir et tailler un vitrage </li> <li>Composer une teinte de peinture </li> <li>&#xC9;valuer l&apos;&#xE9;tat d&apos;un mat&#xE9;riau, d&apos;un produit </li> <li>&#xC9;valuer l&apos;&#xE9;tat d&apos;un ouvrage, relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Monter un &#xE9;chafaudage </li> <li>Utiliser des mat&#xE9;riels, des outils de d&#xE9;pannage, de r&#xE9;paration ou de maintenance, relatifs &#xE0; son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Calcul, g&#xE9;om&#xE9;trie </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Dessin en b&#xE2;timent </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Machines et outillage de peinture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Mat&#xE9;riaux de peinture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Peinture </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Responsable maintenance tous corps d&apos;&#xE9;tat (TCE) pour la programmation du travail et la prise de d&#xE9;cision en mati&#xE8;re de r&#xE9;paration<br>Encadrement des services pour la programmation quotidienne et le d&#xE9;roulement des travaux<br>Magasinier pour l&apos;approvisionnement en mat&#xE9;riaux et outillage<br>Fournisseurs pour le choix et la r&#xE9;ception des produits et mat&#xE9;riels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Certificat d&apos;aptitude professionnelle (CAP)<br>Ou brevet d&apos;&#xE9;tudes professionnelles (BEP)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Agent de maintenance g&#xE9;n&#xE9;rale des b&#xE2;timents<br>Encadrant maintenance corps d&apos;&#xE9;tat secondaires<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Masseur(euse) kinésithérapeute', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Elaborer un diagnostic kin&#xE9;sith&#xE9;rapique et des objectifs de soins.<br>Mettre en oeuvre des actes et techniques de soins de r&#xE9;&#xE9;ducation de fa&#xE7;on manuelle ou instrumentale dans un but th&#xE9;rapeutique ou non pour favoriser le maintien ou l&apos;am&#xE9;lioration de la sant&#xE9; physique, psychique et sociale et la gestion de l&apos;handicap.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Kin&#xE9;sith&#xE9;rapeute<br>Masseur<br>Physioth&#xE9;rapeute (Qu&#xE9;bec)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;etat de masseur kin&#xE9;sith&#xE9;rapeute<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil, &#xE9;ducation, pr&#xE9;vention et d&#xE9;pistage en kin&#xE9;sith&#xE9;rapie et en sant&#xE9; publique </li> <li>Etudes et recherches dans les domaines en lien avec la kin&#xE9;sith&#xE9;rapie, la r&#xE9;&#xE9;ducation, r&#xE9;adaptation et r&#xE9;habilitation </li> <li>Examen, recueil de donn&#xE9;es, d&apos;informations et diagnostic n&#xE9;cessaire &#xE0; l&apos;intervention du kin&#xE9;sith&#xE9;rapeute </li> <li>Formation et information de nouveaux personnels et de stagiaires </li> <li>Interventions et soins en kin&#xE9;sith&#xE9;rapie </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>Organisation et coordination des soins </li> <li>Veille professionnelle et d&#xE9;veloppement professionnel continu </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser et faire &#xE9;voluer sa pratique professionnelle </li> <li>Concevoir et conduire un projet de soins, dans son domaine de comp&#xE9;tence </li> <li>Concevoir et conduire une d&#xE9;marche de conseil, d&apos;&#xE9;ducation, de pr&#xE9;vention et d&#xE9;pistage en kin&#xE9;sith&#xE9;rapie et en sant&#xE9; publique </li> <li>Conduire une relation dans un contexte d&apos;intervention kin&#xE9;sith&#xE9;rapique </li> <li>Elaborer un diagnostic en kin&#xE9;sith&#xE9;rapie </li> <li>G&#xE9;rer une structure et ses ressources </li> <li>Informer et former des professionnels et des personnes en formation </li> <li>Mettre en &#x153;uvre les activit&#xE9;s de r&#xE9;&#xE9;ducation, de r&#xE9;adaptation et de r&#xE9;insertion dans le domaine de la kin&#xE9;sith&#xE9;rapie </li> <li>Organiser les activit&#xE9;s et coop&#xE9;rer avec les diff&#xE9;rents acteurs </li> <li>Rechercher, traiter et analyser des donn&#xE9;es professionnelles et scientifiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;ducation sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Ergonomie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Kin&#xE9;sith&#xE9;rapie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes de recherche en soins </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Sport et techniques sportives </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins prescripteurs ou consult&#xE9;s pour avis et prescriptions<br>Equipe soignante inter-professionnelle pour la coordination des soins<br>Services sociaux pour l&apos;orientation des patients<br>Psychologues et neuro-psychologues pour avis<br>Fournisseurs d&apos;orthop&#xE9;die m&#xE9;dicale et orthoproth&#xE9;siste pour la fourniture et la pose de mat&#xE9;riels<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;etat de masseur kin&#xE9;sith&#xE9;rapeute<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Masseur kin&#xE9;sith&#xE9;rapeute<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Formateur des professionnels de sant&#xE9;<br>Coordinateur de parcours en sant&#xE9;<br>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Statisticien(ne)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des &#xE9;tudes chiffr&#xE9;es et des mod&#xE8;les math&#xE9;matiques qui permettent de d&#xE9;gager les causes des ph&#xE9;nom&#xE8;nes &#xE9;tudi&#xE9;s, en utilisant les moyens informatiques (mat&#xE9;riels et logiciels) afin d&apos;aider &#xE0; la prise de d&#xE9;cision, d&apos;anticiper ou de pr&#xE9;voir des mesures utiles.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de la conformit&#xE9; et / ou de validit&#xE9; des documents, relatifs &#xE0; son domaine </li> <li>&#xC9;laboration, mise en place et exploitation de tableaux de bord sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Gestion et traitement des donn&#xE9;es / informations (recherche, recueil, analyse, priorisation, diffusion, classement, suivi) </li> <li>R&#xE9;alisation d&apos;&#xE9;tudes, de travaux de synth&#xE8;se, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation et exploitation d&apos;enqu&#xEA;tes, de donn&#xE9;es et d&apos;entretiens, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Tenue &#xE0; jour des donn&#xE9;es / des fichiers relatifs au domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser, traduire et formuler un besoin utilisateur en &#xE9;tudes de faisabilit&#xE9;, en solutions, en programmes </li> <li>Argumenter, influencer et convaincre un ou plusieurs interlocuteurs (interne et/ou externe), dans son domaine de comp&#xE9;tence </li> <li>Choisir et utiliser des outils statistiques </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Elaborer, r&#xE9;diger et exploiter des requ&#xEA;tes relatives &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Identifier, analyser, prioriser et synth&#xE9;tiser les informations relevant de son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;diger et mettre en forme des notes, documents et /ou rapports, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Renseigner des personnes au regard de son m&#xE9;tier </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais scientifique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">D&#xE9;veloppement informatique / analyse et programmation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;pid&#xE9;miologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion de donn&#xE9;es, relatives &#xE0; son domaine </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Informatique / Syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la recherche clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Statistiques </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>DIM des autres &#xE9;tablissements pour l&apos;analyse comparative et statistiques des donn&#xE9;es m&#xE9;dicales<br>Tous services n&#xE9;cessitant une ressource statistique pour le traitement statistique de donn&#xE9;es de diverses natures<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>master 2 au minimum<br>doctorat &#xE0; privil&#xE9;gier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Socio - esthéticien(ne)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des soins socio-esth&#xE9;tiques adapt&#xE9;s, le toucher relationnel, l&apos;hygi&#xE8;ne corporelle appliqu&#xE9;e et les soins port&#xE9;s sur l&apos;apparence de la personne dans le but de revaloriser l&apos;image de soi et de favoriser la r&#xE9;paration identitaire contribuant &#xE0; l&apos;inscription de la personne dans son environnement.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Psycho-socio esth&#xE9;ticien(ne)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>et formation de niveau bts psycho-socio-esth&#xE9;tique (premi&#xE8;re session : fin 2008)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil, encadrement et accompagnement p&#xE9;dagogique de personnes (agents, &#xE9;tudiants, stagiaires, etc.) </li> <li>Bilan clinique d&apos;un patient, sp&#xE9;cifique au domaine (entretien / examen, recueil d&apos;information, travail de synth&#xE8;se, restitution...) </li> <li>Contr&#xF4;le et suivi de la qualit&#xE9; des prestations, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le, suivi et bilan des actions r&#xE9;alis&#xE9;es, dans son domaine </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>&#xC9;laboration du projet de soins et du plan du traitement du patient, de la personne </li> <li>Enseignement et supervision de travaux dirig&#xE9;s de recherche et d&apos;&#xE9;tudes, dans son domaine de comp&#xE9;tence </li> <li>R&#xE9;alisation de soins corporels et esth&#xE9;tiques dans son domaine d&apos;activit&#xE9; </li> <li>R&#xE9;alisation de soins sp&#xE9;cifiques &#xE0; son domaine d&apos;intervention </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Adapter l&apos;esth&#xE9;tique du produit au go&#xFB;t du patient </li> <li>Analyser / &#xE9;valuer la situation clinique d&apos;une personne, d&apos;un groupe de personnes, relative &#xE0; son domaine de comp&#xE9;tence </li> <li>Argumenter et convaincre avec un ou plusieurs interlocuteurs (interne et externe) </li> <li>Choisir et mettre en oeuvre les techniques et pratiques adapt&#xE9;es au patient, au regard de son m&#xE9;tier / sa sp&#xE9;cialit&#xE9; </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Cr&#xE9;er et d&#xE9;velopper une relation de confiance et d&apos;aide avec le patient et/ou la personne accueillie et / ou son entourage </li> <li>D&#xE9;finir et mettre en oeuvre les soins esth&#xE9;tiques adapt&#xE9;s &#xE0; la personne, relatifs &#xE0; dans son domaine de comp&#xE9;tence </li> <li>D&#xE9;finir et utiliser les techniques et pratiques adapt&#xE9;es &#xE0; son m&#xE9;tier </li> <li>&#xC9;valuer la volont&#xE9; et le consentement de la personne prot&#xE9;g&#xE9;e </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie m&#xE9;dicales </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychologie clinique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Psychosomatique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Sant&#xE9; publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Soins esth&#xE9;tiques </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Techniques et protocoles de soins </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Equipe m&#xE9;dicale du service pour l&apos;accord d&apos;intervention sur un patient ou sur un groupe de patients.<br>Equipe soignante et autres prestataires de service (coiffeur, kin&#xE9;sith&#xE9;rapeute) pour l&apos;int&#xE9;gration dles soins esth&#xE9;tiques dans la planification des soins au patient.<br>Cadres de sant&#xE9; pour la transmission des besoins et organisation du temps de travail.<br>Direction des services &#xE9;conomiques pour les commandes de mat&#xE9;riels et produits.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formation de niveau BTS psycho-socio-esth&#xE9;tique<br>Bac pro esth&#xE9;tique ou &#xE9;quivalent dipl&#xF4;me niveau IV en esth&#xE9;tique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des aide-soignant<br>Contractuel de cat&#xE9;gorie B<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Technicien(ne) de laboratoire médical', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre en oeuvre les technologies n&#xE9;cessaires &#xE0; l&apos;obtention des r&#xE9;sultats des examens de biologie m&#xE9;dicale et d&apos;anatomie et cytologie pathologiques &#xE0; vis&#xE9;e de pr&#xE9;vention, de d&#xE9;pistage, de diagnostic et de suivi th&#xE9;rapeutique.<br>R&#xE9;aliser des pr&#xE9;l&#xE8;vements sanguins, veineux et capillaires.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Technicien(ne) de laboratoire<br>Technicien(ne) d&apos;analyse biologique<br>Technicien(ne) d&apos;analyse en biologie m&#xE9;dicale<br>Laborantin(e)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Certificat de capacit&#xE9; &#xE0; effectuer des pr&#xE9;l&#xE8;vements sanguins<br>AFGSU 1er et deuxi&#xE8;me niveau<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le et enregistrement de la conformit&#xE9; des pr&#xE9;l&#xE8;vements </li> <li>Formation et information des professionnels et &#xE9;tudiants </li> <li>Mise en oeuvre des mesures li&#xE9;es &#xE0; la qualit&#xE9; et &#xE0; la pr&#xE9;vention des risques </li> <li>Organisation des activit&#xE9;s et gestion des ressources </li> <li>Pr&#xE9;paration, qualification, distribution et d&#xE9;livrance des produits sanguins labiles, des produits biologiques &#xE0; vis&#xE9;e th&#xE9;rapeutique et d&apos;assistance m&#xE9;dicale &#xE0; la procr&#xE9;ation </li> <li>R&#xE9;alisation de pr&#xE9;l&#xE8;vements en vue d&apos;examen de biologie m&#xE9;dicale et dans le cadre du don de sang </li> <li>R&#xE9;alisation des analyses de biologie m&#xE9;dicale, des techniques d&apos;anatomie et cytologie pathologiques et des techniques d&apos;assistance m&#xE9;dicale &#xE0; la procr&#xE9;ation </li> <li>R&#xE9;ception et traitement des &#xE9;chantillons de biologie m&#xE9;dicale, d&apos;anatomie et cytologie pathologiques, des produits biologiques &#xE0; vis&#xE9;e th&#xE9;rapeutique et des documents associ&#xE9;s </li> <li>Recueil, traitement et transmission des informations </li> <li>Veille professionnelle, &#xE9;tudes et travaux de recherches et d&apos;innovation </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner la personne et communiquer dans un contexte de pr&#xE9;l&#xE8;vement </li> <li>Analyser et traiter les r&#xE9;sultats des examens de biologie m&#xE9;dicale, des techniques d&apos;anatomie et cytologie pathologique et des activit&#xE9;s biologiques &#xE0; vis&#xE9;e th&#xE9;rapeutique </li> <li>Analyser les informations n&#xE9;cessaires &#xE0; l&apos;organisation et &#xE0; la r&#xE9;alisation de son activit&#xE9; </li> <li>Exploiter des donn&#xE9;es scientifiques et professionnelles, analyser et am&#xE9;liorer sa pratique professionnelle </li> <li>G&#xE9;rer les &#xE9;quipements, mat&#xE9;riels, consommables, r&#xE9;actifs et les stocks de produits et d&apos;&#xE9;chantillons biologiques </li> <li>Identifier les &#xE9;l&#xE9;ments, cellules ou micro-organismes normaux ou pathog&#xE8;nes dans les &#xE9;chantillons biologiques </li> <li>Informer et former des professionnels et des personnes en formation </li> <li>Mettre en oeuvre les normes et principes de qualit&#xE9;, d&apos;hygi&#xE8;ne et de s&#xE9;curit&#xE9; pour assurer la qualit&#xE9; des activit&#xE9;s de biologie m&#xE9;dicale, d&apos;anatomie et cytologie pathologique et des activit&#xE9;s biologiques &#xE0; vis&#xE9;e th&#xE9;rapeutique. </li> <li>Mettre en oeuvre les techniques appliqu&#xE9;es aux examens de biologie m&#xE9;dicale, d&apos;anatomie et cytologie pathologiques et aux activit&#xE9;s biologiques &#xE0; vis&#xE9;e th&#xE9;rapeutique </li> <li>Mettre en oeuvre les techniques de pr&#xE9;l&#xE8;vement et de traitement des &#xE9;chantillons biologiques </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Biologie </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion des flux stocks/produits </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hygi&#xE8;ne hospitali&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la biologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales g&#xE9;n&#xE9;rales et/ ou scientifiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Physique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques de laboratoire </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Traitement des d&#xE9;chets </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>M&#xE9;decins et biologistes pour la r&#xE9;alisation des examens<br>Secr&#xE9;taires pour l&apos;enregistrement des demandes et la diffusion des r&#xE9;sultats<br>Personnel m&#xE9;dical et param&#xE9;dical des services pour la demande d&apos;examens et de pr&#xE9;cisions sur les r&#xE9;sultats<br>Services techniques et logistiques pour la maintenance et l&apos;approvisionnement<br>Prestataires externes pour les maintenances et la sous-traitance d&apos;examens<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac +2<br>BTS de biochimie, d&apos;analyses biologiques et de technobiologie<br>DUT de biologie, sp&#xE9;cialit&#xE9; g&#xE9;nie biologique, options analyses biologiques et biochimiques et/ou environnement<br>DETAB Bac+3 ( Minist&#xE8;re de la sant&#xE9;)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien de laboratoire m&#xE9;dical<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant d&apos;unit&#xE9; de soins et d&apos;activit&#xE9;s param&#xE9;dicales<br>Hygi&#xE9;niste<br>Coordinateur qualit&#xE9; / gestion des risques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Webmestre éditorial', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir ou d&#xE9;velopper une interface multim&#xE9;dia de communication &#xE0; partir de la strat&#xE9;gie de communication de l&apos;&#xE9;tablissement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Editeur(trice) en ligne<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>D&#xE9;veloppeur(euse) WEB<br>Animateur(trice) / gestionnaire de communaut&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Animation du site par la collecte des contenus, la r&#xE9;daction et la mise en ligne tout en veillant &#xE0; la coh&#xE9;rence graphique et &#xE9;ditoriale du site </li> <li>D&#xE9;finition des sp&#xE9;cifications techniques et r&#xE9;daction du cahier des charges sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Exploitation des domaines et adressages (r&#xE9;f&#xE9;rencement du site) </li> <li>Gestion du d&#xE9;veloppement du site (int&#xE9;gration de pages dynamiques, acc&#xE8;s &#xE0; des bases de donn&#xE9;es) </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Veille et utilisation des outils du web </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Actualiser les interfaces d&#xE9;velopp&#xE9;es (site internet) </li> <li>Choisir et utiliser des mat&#xE9;riels, des outils de travail ou / et de contr&#xF4;le, aff&#xE9;rents &#xE0; son m&#xE9;tier </li> <li>Concevoir et r&#xE9;diger des contenus pour le web </li> <li>Identifier et analyser les &#xE9;volutions et leurs impacts sur les activit&#xE9;s, les syst&#xE8;mes relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Ma&#xEE;triser les techniques de r&#xE9;daction web </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit des donn&#xE9;es informatiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Environnement web, r&#xE9;seaux sociaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Informatique / Syst&#xE8;me d&apos;information </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Produits et march&#xE9; informatiques </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Charg&#xE9;(e) de communication pour d&#xE9;finir les projets de sites<br>Utilisateurs pour la prise en compte des besoins utilisateurs puis pour leur mise en oeuvre<br>Chef de projet informatique pour suivi<br>Agence multim&#xE9;dia pour la cr&#xE9;ation de site<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Formation en communication ou infographisme multimedia ou journaliste<br>Dipl&#xF4;me de niveau III (DEUST, DUT), niveau II (license professionnelle) &#xE0; niveau I (master) de webmaster ou dans les m&#xE9;tiers de l&apos;internet et/ou multim&#xE9;dia<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Adjoint des cadres hospitalier<br>Assistant m&#xE9;dico-administratif<br>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Gestionnaire de bases de donn&#xE9;es<br>Technicien communication multim&#xE9;dia<br>Charg&#xE9; de communication<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable des services biomédicaux', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, conduire, piloter la mise en oeuvre des investissements, la gestion du parc, la maintenance et la s&#xE9;curit&#xE9; des &#xE9;quipements biom&#xE9;dicaux, en dirigeant l&apos;ensemble des unit&#xE9;s biom&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable du service biom&#xE9;dical<br>Directeur(e) des ressources biom&#xE9;dicales<br>Directeur(e) des &#xE9;quipements biom&#xE9;dicaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gestion des risques biom&#xE9;dicaux<br>Administrateur GMAO<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Exp&#xE9;rience souhait&#xE9;e dans le domaine technique et/ou des &#xE9;tablissements de sant&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Contr&#xF4;le et suivi de la qualit&#xE9; et de la s&#xE9;curit&#xE9; des activit&#xE9;s, dans son domaine </li> <li>Coordination des programmes / des projets / des activit&#xE9;s </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Encadrement de proximit&#xE9; d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Gestion de parc d&apos;&#xE9;quipements, relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir un plan / programme d&apos;investissement </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Biom&#xE9;dical </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">M&#xE9;thodes / outils de la gestion des risques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">N&#xE9;gociation : m&#xE9;thodes et techniques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeur et &#xE9;quipe de direction d&apos;&#xE9;tablissement, commission m&#xE9;dicale et responsables m&#xE9;dicaux pour les d&#xE9;cisions strat&#xE9;giques li&#xE9;es aux techniques m&#xE9;dicales,<br>Fournisseurs pour la veille et les litiges,<br>Autres responsables : services techniques, syst&#xE8;me d&apos;information, affaires &#xE9;conomiques, ressources financi&#xE8;res et contr&#xF4;le de gestion, ressources humaines pour la coordination des interfaces entre les activt&#xE9;s ou les projets,<br>Structures qualit&#xE9; et de vigilance, reseau professionnel, tutelle et agences (HAS, AFSSAPS, ASN,...) pour la veille.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BAC+5,<br>&#xC9;cole d &apos;ing&#xE9;nieur ou master incluant sp&#xE9;cialisatiion g&#xE9;nie biom&#xE9;dical<br>Formation compl&#xE9;mentaire ing&#xE9;nieur biom&#xE9;dical hospitalier<br>Formation compl&#xE9;mentaire (continue) en management<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Responsable des travaux<br>Responsable de la maintenance tous corps d&apos;&#xE9;tat (TCE)<br>Coordinateur qualit&#xE9; / gestion des risques<br>Acheteur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable logistique', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>D&#xE9;finir, organiser et contr&#xF4;ler l&apos;ad&#xE9;quation besoins-ressources entre des pr&#xE9;visions de besoins et les capacit&#xE9;s de production et de distribution des services logistiques en coh&#xE9;rence avec la politique de l&apos;&#xE9;tablissement. Il peut avoir une fonction d&apos;encadrement.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Responsable des services logistiques<br>Planificateur(trice) logistique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Gestionnaire de parc<br>R&#xE9;gulateur(trice) transport<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Formation niveau bac+3/5 en logistique et/ou organisation<br>Exp&#xE9;rience professionnelle<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation d&apos;outils et / ou de m&#xE9;thodes sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>Conseil aux p&#xF4;les et aux projets d&apos;&#xE9;tablissement dans son domaine de comp&#xE9;tences </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>D&#xE9;finition et mise en place de l&apos;organisation, des structures </li> <li>&#xC9;laboration et mise en place de l&apos;organisation des services, dans son domaine d&apos;activit&#xE9; </li> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement et suivi de budget(s) relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Organisation et description des processus sp&#xE9;cifiques &#xE0; son domaine, traitement des dysfonctionnements </li> <li>Organisation et suivi op&#xE9;rationnel des activit&#xE9;s/projets, coordination avec les interlocuteurs internes et externes </li> <li>Planification de l&apos;activit&#xE9; et de l&apos;ensemble des moyens (humains, financiers, techniques) &#xE0; mettre en &#x153;uvre pour la continuit&#xE9; de service </li> <li>Planification, r&#xE9;organisation des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir un plan / programme d&apos;investissement </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>&#xC9;tablir / &#xE9;valuer / optimiser un budget relatif &#xE0; son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Traduire la strat&#xE9;gie en orientations, en projets, en plans d&apos;actions et en moyens de r&#xE9;alisation </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Logistique g&#xE9;n&#xE9;rale </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances d&apos;expert</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances d&apos;expert :<br><em>Connaissances permettant de produire une analyse critique des th&#xE9;ories et des principes, de red&#xE9;finir des pratiques professionnelles dans un champ ou &#xE0; l&apos;interface entre des champs diff&#xE9;rents. Connaissances tr&#xE8;s contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de 3 / 5 et plus ans et plus.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrement de proximit&#xE9; logistique, directeurs, cadres de sant&#xE9;, cadres techniques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+ 5 en organisation ou logistique<br>Bac +3 en organisation ou logistique + exp&#xE9;rience professionnelle<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Ing&#xE9;nieur hospitalier<br>Attach&#xE9; d&apos;administration hospitali&#xE8;re<br>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Mécanicien(ne) de maintenance', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des travaux de maintenance, d&apos;entretien, d&apos;installation et de r&#xE9;novation des mat&#xE9;riels roulants et des organes m&#xE9;caniques / machines et &#xE9;quipements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>R&#xE9;parateur(trice) automobile,<br>Technicien(ne) de maintenance automobile<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>M&#xE9;canique poids lourds,<br>M&#xE9;canique auto<br>Hydraulique,<br>Motoculture<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Permis de conduire B.<br>Fortement recommand&#xE9; permis C, E et D.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>D&#xE9;pannage, r&#xE9;paration, remise en &#xE9;tat, remplacement de mat&#xE9;riels, d&apos;&#xE9;quipements relatifs &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Entretien, nettoyage et rangement des mat&#xE9;riels sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Montage d&apos;accessoire(s) automobile </li> <li>R&#xE9;daction de comptes-rendus relatifs aux observations / aux interventions, dans son domaine d&apos;activit&#xE9; </li> <li>Remorquage de v&#xE9;hicules </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>V&#xE9;rification / contr&#xF4;le du fonctionnement et essais de mat&#xE9;riels, &#xE9;quipements/ des installations sp&#xE9;cifiques &#xE0; son domaine </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Conduire un v&#xE9;hicule automobile </li> <li>&#xC9;valuer la qualit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riau </li> <li>&#xC9;valuer la satisfaction des patients / des clients / des personnels </li> <li>Identifier et diagnostiquer un dysfonctionnement, une panne, le d&#xE9;faut d&apos;un mat&#xE9;riel, d&apos;un &#xE9;quipement, une anomalie d&apos;un syst&#xE8;me, sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Lire et utiliser une notice, dans son domaine de comp&#xE9;tence </li> <li>Maintenir et d&#xE9;panner un mat&#xE9;riel, un &#xE9;quipement, une installation et/ou un syst&#xE8;me relatif &#xE0; son m&#xE9;tier </li> <li>R&#xE9;diger des informations relatives &#xE0; son domaine d&apos;intervention pour assurer un suivi et une tra&#xE7;abilit&#xE9;. </li> <li>Utiliser des mat&#xE9;riels, des outils de d&#xE9;pannage, de r&#xE9;paration ou de maintenance, relatifs &#xE0; son m&#xE9;tier </li> <li>Utiliser des mat&#xE9;riels, des outils de diagnostic, de travail ou/et de contr&#xF4;le sp&#xE9;cifiques &#xE0; son m&#xE9;tier </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">&#xC9;lectricit&#xE9; automobile </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectronique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Hydraulique </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; &#xE0; la maintenance automobile </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">M&#xE9;canique automobile </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Moteurs (essence, diesel) </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; routi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Technologie des v&#xE9;hicules </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Encadrement de maintenance des mat&#xE9;riels de transport pour l&apos;organisation et la planification du travail.<br>Usagers pour le conseil et l&apos;information sur le fonctionnement du mat&#xE9;riel ou du v&#xE9;hicule.<br>Fournisseurs de pi&#xE8;ces d&#xE9;tach&#xE9;es pour les commandes etla r&#xE9;ception de pi&#xE8;ces.<br>Concessionnaire des constructeurs pourl&apos;obtention des informations techniques sp&#xE9;cifiques.<br>Ateliers de carrosserie pour la peinture et la r&#xE9;paration t&#xF4;lerie.<br>Centres de contr&#xF4;les agr&#xE9;&#xE9;s pour la pr&#xE9;sentation des v&#xE9;hicules et des mat&#xE9;riels aux diff&#xE9;rents contr&#xF4;les techniques et r&#xE9;glementaires.<br>Soci&#xE9;t&#xE9;s charg&#xE9;es de l&apos;&#xE9;vacuation des d&#xE9;chets pour la mise &#xE0; disposition des huiles, batteries, m&#xE9;taux, verres, pneumatiques et filtres destin&#xE9;s au recyclage.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Technicien de maintenance automobile<br>BAC professionnel , bac technique<br>CAP, BEP.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des personnels ouvriers<br>Technicien hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant de maintenance des &#xE9;quipements et mat&#xE9;riels m&#xE9;caniques<br>Electrom&#xE9;canicien de maintenance<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Technicien(ne) communication multimédia', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Pr&#xE9;parer et r&#xE9;aliser des documents support de communication multim&#xE9;dia<br>Mettre en forme &#xE9;crite, graphique ou audiovisuelle les messages et publications<br>G&#xE9;rer les parcs de mat&#xE9;riels<br>Intervenir techniquement dans les diff&#xE9;rentes t&#xE2;ches de la r&#xE9;alisation ou de la production<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Infographiste<br>Photographe<br>Cin&#xE9;aste<br>Technicien(ne) audiovisuel<br>Concepteur(trice) signal&#xE9;tique<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et r&#xE9;alisation de montages audiovisuels </li> <li>Conception et r&#xE9;alisation de publications et de documents d&apos;entreprise </li> <li>Conception et r&#xE9;alisation de visuels (pictogrammes, cartes, graphiques, dessins, plans, bd, logo type,...) </li> <li>Contr&#xF4;le technique des outils/des mat&#xE9;riels </li> <li>Cr&#xE9;ation du design des supports multim&#xE9;dia et / ou audiovisuels (site web, borne interactive, c&#xE9;d&#xE9;rom...) </li> <li>D&#xE9;finition de l&apos;architecture technique et de l&apos;organisation du site WEB et des possibilit&#xE9;s techniques </li> <li>&#xC9;tablissement de devis pour des produits / prestations relatifs &#xE0; son domaine d&apos;intervention </li> <li>Exploitation des domaines et adressages (r&#xE9;f&#xE9;rencement du site) </li> <li>Marquage de lettres et logos sur diff&#xE9;rents supports (pour la signal&#xE9;tique) </li> <li>Prises de vue </li> <li>Retouches d&apos;images </li> <li>S&#xE9;lection des images, photos et illustrations n&#xE9;cessaires &#xE0; la conception du support </li> <li>Tournage de s&#xE9;quences vid&#xE9;o et d&apos;enregistrements sonores, montage des images et du son </li> <li>Veille et utilisation des outils du web </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Actualiser les interfaces d&#xE9;velopp&#xE9;es (site internet) </li> <li>Communiquer aupr&#xE8;s d&apos;interlocuteurs vari&#xE9;s </li> <li>D&#xE9;finir et r&#xE9;aliser une charte graphique </li> <li>Imaginer une composition visuelle </li> <li>Programmer dans diff&#xE9;rents environnements informatiques </li> <li>Traiter, retoucher des images et/ou des vid&#xE9;os </li> <li>Utiliser les outils bureautique / Technologie d&apos;Information et de Communication (TIC) </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Anglais informatique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Arts graphiques et plastiques </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Bureautique/Technologies information et communication </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Communication </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conception graphique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">D&#xE9;veloppement informatique / analyse et programmation </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit d&apos;auteur </td> <td headers=\"col1\"></td> </tr> <tr> <td headers=\"col0\">Droit de l&apos;image </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Internet/intranet </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">March&#xE9;s, produits audiovisuels </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">S&#xE9;curit&#xE9; informatique, s&#xE9;curit&#xE9; SI </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Techniques audiovisuelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Technologies de l&apos;information et de la communication (NTIC) </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Charg&#xE9;(e) de communication<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>BTS ou DUT informatique technicien multim&#xE9;dia<br>Licence professionnelle technicien multim&#xE9;dia<br>Master Multim&#xE9;dia<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier<br>Adjoint des cadres hospitaliers<br>Adjoint administratif hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Webmestre &#xE9;ditorial<br>Charg&#xE9; de communication<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable socio-éducatif', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Planifier, organiser, piloter et contr&#xF4;ler les r&#xE9;alisations d&apos;une &#xE9;quipe socio-&#xE9;ducative ou m&#xE9;dico-sociale pluridisciplinaire, en lien avec le projet d&apos;&#xE9;tablissement.<br>(en activit&#xE9;s : coordination de projets, relations multiservices, relations ext&#xE9;rieures)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Chef(fe) de service socio-&#xE9;ducatif<br>Coordinateur(trice) du service social<br>Cadre socio-&#xE9;ducatif(ve)<br>Cadre sup&#xE9;rieur socio-&#xE9;ducatif(ve)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Coordonnateur d&apos;activit&#xE9;s socio-&#xE9;ducatives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Obtention du CAFERUIS ( Certificat d&apos;Aptitude aux Fonctions d&apos;Encadrement et de Responsable d&apos;Unit&#xE9; d&apos;Intervention Sociale)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conseil aux d&#xE9;cideurs concernant les choix, les projets, les activit&#xE9;s du domaine d&apos;activit&#xE9; </li> <li>D&#xE9;finition et mise en oeuvre de la politique sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>&#xC9;tablissement et suivi de budget(s) relatif(s) &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Information et conseil aupr&#xE8;s des personnels / utilisateurs / usagers, relatif au domaine d&apos;activit&#xE9; </li> <li>Organisation et animation de r&#xE9;unions, visites, conf&#xE9;rences, &#xE9;v&#xE9;nements, commissions sp&#xE9;cialis&#xE9;es, groupes de travail et groupes projet </li> <li>Planification des activit&#xE9;s et des moyens, contr&#xF4;le et reporting </li> <li>Recensement et analyse des besoins des utilisateurs, sp&#xE9;cifiques &#xE0; son domaine </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, piloter et &#xE9;valuer un projet / un processus relevant de son domaine de comp&#xE9;tence </li> <li>Conduire et animer des r&#xE9;unions </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Fixer des objectifs, mesurer les r&#xE9;sultats et &#xE9;valuer les performances collectives et/ou individuelles </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire les orientations, plans d&apos;actions et moyens de r&#xE9;alisation en activit&#xE9;s quotidiennes. </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit de la fonction publique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Droit hospitalier </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Encadrement de personnel </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Gestion administrative, &#xE9;conomique et financi&#xE8;re </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Intervention sociale </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Management </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Strat&#xE9;gie et organisation / conduite du changement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Partenaires institutionnels internes (encadrement, m&#xE9;decins, services.) et externes (institutions diverses (Conseil g&#xE9;n&#xE9;ral, Agence r&#xE9;gionale de sant&#xE9;, associations Centre hospitalier sp&#xE9;cialis&#xE9;, Caisse r&#xE9;gionale d&apos;assurance maladie), pour la gestion des activit&#xE9;s sociales et &#xE9;ducatives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>CAFERUIS + concours de cadre socio &#xE9;ducatif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Cadre socio-&#xE9;ducatif<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Chef d&apos;&#xE9;tablissement social et m&#xE9;dicosocial<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Moniteur(trice) éducateur(trice)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Mettre en &#x153;uvre le projet socio&#xE9;ducatif pour des personnes handicap&#xE9;es ou en difficult&#xE9;, afin de d&#xE9;velopper leur capacit&#xE9; de socialisation, d&apos;autonomie, d&apos;int&#xE9;gration et d&apos;insertion<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;&#xE9;tat de moniteur(trice) &#xE9;ducateur(trice)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Accueil et prise en charge des personnes (patients, usagers,etc), dans son domaine </li> <li>Assistance aux personnes, aux patients pour l&apos;insertion ou la r&#xE9;insertion sociale et/ou professionnelle </li> <li>Conseil sur la d&#xE9;finition de(s) projet(s) professionnel(s) / individuel(s) et bilan des r&#xE9;alisations </li> <li>&#xC9;laboration et mise en place du projet individuel concernant la personne et le groupe dans son domaine d&apos;activit&#xE9; </li> <li>M&#xE9;diation des relations avec la famille, le repr&#xE9;sentant l&#xE9;gal de la personne et les diff&#xE9;rents partenaires ext&#xE9;rieurs </li> <li>Organisation des stages, en relation avec les responsables terrain </li> <li>Suivi de la r&#xE9;alisation des travaux, de la r&#xE9;solution des probl&#xE8;mes sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Tenue d&apos;entretiens individuels d&apos;activit&#xE9;, de suivi p&#xE9;dagogique </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Adapter son comportement, sa pratique professionnelle &#xE0; des situations critiques / particuli&#xE8;res, dans son domaine de comp&#xE9;tence </li> <li>Conduire et animer des r&#xE9;unions </li> <li>Conduire un projet individuel </li> <li>&#xC9;valuer l&apos;int&#xE9;gration d&apos;une personne dans un milieu social, de travail </li> <li>&#xC9;valuer une prestation, un projet, une solution, relatifs &#xE0; son domaine de comp&#xE9;tence </li> <li>Organiser / animer des activit&#xE9;s sp&#xE9;cifiques &#xE0; son domaine de comp&#xE9;tence pour des patients, des personnels/des groupes </li> <li>Stimuler les capacit&#xE9;s affectives, intellectuelles, psychomotrices et sociales d&apos;une personne </li> <li>Transf&#xE9;rer un savoir-faire, une pratique professionnelle </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Animation de groupe </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication et relation d&apos;aide </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Conduite de projet </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">&#xC9;thique et d&#xE9;ontologie professionnelles </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Normes, r&#xE8;glements techniques et de s&#xE9;curit&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement des structures sociales et m&#xE9;dico-sociales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Premiers secours </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Psychosociologie du handicap </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Autres professionnels de l&apos;&#xE9;tablissement, intervenants ext&#xE9;rieurs et familles pour synth&#xE8;ses, transmission et recueil de donn&#xE9;es<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Dipl&#xF4;me d&apos;&#xE9;tat de moniteur(trice) &#xE9;ducateur(trice)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Moniteur &#xE9;ducateur<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Educateur sp&#xE9;cialis&#xE9;<br>Educateur technique sp&#xE9;cialis&#xE9;<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Technicien(ne) d''information médicale (TIM)', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Recueillir et contr&#xF4;ler l&apos;exhaustivit&#xE9; et la conformit&#xE9; des informations relatives &#xE0; l&apos;activit&#xE9; m&#xE9;dicale en vue de l&apos;optimisation de la qualit&#xE9; du codage et de la valorisation de l&apos;activit&#xE9; hospitali&#xE8;re<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Archivage des compte-rendu hospitalier (CRH) cod&#xE9;s (tra&#xE7;abilit&#xE9;) </li> <li>Assistance aupr&#xE8;s des services pour l&apos;utilisation des outils de recueil et le codage </li> <li>Codage des situations cliniques (diagnostics...) &#xE0; partir des supports recueillis </li> <li>Contr&#xF4;le de la qualit&#xE9; des donn&#xE9;es du PMSI (Programme de M&#xE9;dicalisation des Syst&#xE8;mes d&apos;Information) </li> <li>Contr&#xF4;le des flux de donn&#xE9;es au sein du syst&#xE8;me d&apos;information </li> <li>Formation des utilisateurs (m&#xE9;decins, secr&#xE9;taires, nouveaux TIM) aux outils de recueil </li> <li>Gestion des dossiers pour les contr&#xF4;les de l&apos;assurance maladie (gestion logistique, conformit&#xE9; des pi&#xE8;ces) </li> <li>Gestion des habilitations des utilisateurs des outils </li> <li>Mise &#xE0; jour des r&#xE9;f&#xE9;rentiels de codage de l&apos;information m&#xE9;dicale </li> <li>Param&#xE9;trage des outils de recueil de l&apos;information m&#xE9;dicale </li> <li>Recueil du codage descriptif du s&#xE9;jour du patient effectu&#xE9; par les professionnels de sant&#xE9; et/ou des supports d&apos;information m&#xE9;dicale (compte-rendu d&apos;hospitalisation, dossier patient, ...) permettant de r&#xE9;aliser/d&apos;affiner le codage </li> <li>Saisie et rectification des donn&#xE9;es n&#xE9;cessaires &#xE0; la constitution des r&#xE9;sum&#xE9;s d&apos;information m&#xE9;dicale </li> <li>Signalement des erreurs relatives &#xE0; des informations administratives concernant les patients et leur s&#xE9;jour </li> <li>Surveillance et suivi du caract&#xE8;re op&#xE9;rationnel des outils et applications informatiques </li> <li>Traitement de donn&#xE9;es relatives au patient et &#xE0; l&apos;activit&#xE9; m&#xE9;dicale (donn&#xE9;es du PMSI) : extraction, regroupement, repr&#xE9;sentation graphique </li> <li>Traitement des atypies et erreurs de groupage </li> <li>Transmission des donn&#xE9;es au sein du syst&#xE8;me d&apos;information et contr&#xF4;le des r&#xE9;sultats des transmissions </li> <li>Veille documentaire </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Analyser les r&#xE9;sultats relatifs &#xE0; la production du codage </li> <li>Communiquer aupr&#xE8;s d&apos;interlocuteurs vari&#xE9;s </li> <li>Extraire et hi&#xE9;rarchiser les informations pertinentes &#xE0; partir du dossier patient </li> <li>Former et conseiller les utilisateurs dans son domaine de comp&#xE9;tence </li> <li>Identifier un dysfonctionnement dans la cha&#xEE;ne de transmissions des donn&#xE9;es </li> <li>Identifier, analyser des erreurs ou anomalies des donn&#xE9;es du PMSI (codage...), en rechercher les causes </li> <li>Planifier, organiser le travail et g&#xE9;rer les priorit&#xE9;s </li> <li>R&#xE9;diger et actualiser des proc&#xE9;dures et modes op&#xE9;ratoires </li> <li>Travailler en &#xE9;quipe pluridisciplinaire&#xA0;/ en r&#xE9;seau </li> <li>Utiliser des outils de gestion de la qualit&#xE9; </li> <li>Utiliser les classifications et nomenclatures de diagnostics et d&apos;actes normalis&#xE9;es selon les r&#xE8;gles m&#xE9;thodologiques </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Archivage </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Bureautique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Classification des actes en sant&#xE9; </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Classification internationale des maladies et des probl&#xE8;mes de sant&#xE9; connexes </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Communication / relations interpersonnelles </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Droit de la sant&#xE9; dans son domaine d&apos;activit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Logiciels d&#xE9;di&#xE9;s &#xE0; la gestion de l&apos;information m&#xE9;dicale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">M&#xE9;dicales </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Organisation et fonctionnement interne de l&apos;&#xE9;tablissement </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Vocabulaire m&#xE9;dical </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Direction du syst&#xE8;me d&apos;information et service des admissions pour le contr&#xF4;le des donn&#xE9;es m&#xE9;dico-administratives<br>Personnel m&#xE9;dical, param&#xE9;dical et administratif pour la transmission, le recueil des donn&#xE9;es et le suivi de leur exhaustivit&#xE9;<br>DIM et TIM des autres &#xE9;tablissements pour l&apos;analyse comparative et statistique des donn&#xE9;es m&#xE9;dico-administratives<br>Direction des affaires financi&#xE8;res pour la pr&#xE9;vision et l&apos;analyse de recettes<br>L&apos;Agence Technique de l&apos;Information sur l&apos;Hospitalisation et l&apos;assurance maladie pour les remont&#xE9;es d&apos;information<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Niveau III<br>DU d&apos;information m&#xE9;dicale<br>Niveau bac + 2<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Assistant M&#xE9;dico-Administratif<br>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Analyste des donn&#xE9;es d&apos;activit&#xE9;s hospitali&#xE8;res<br>Technicien d&apos;&#xE9;tudes cliniques<br>Assistant de recherche clinique<br>Encadrant de d&#xE9;partement d&apos;information m&#xE9;dicale<br>Gestionnaire de donn&#xE9;es biom&#xE9;dicales<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Responsable Archives', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>Concevoir, conduire, piloter la mise en oeuvre de la politique archives.<br>D&#xE9;velopper les m&#xE9;thodes, proc&#xE9;dures et outils de gestion des archives<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>SP&#xC9;CIFICIT&#xC9;</p></div> <div class=\"description\"><p>Responsable archives inter-hospitali&#xE8;res<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Connaissances des techniques archivistiques et de l&apos;informatique sp&#xE9;cialis&#xE9;e<br>Connaissances des techniques de management, RH, administration, gestion de projet et conduite du changement<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Conception et organisation du circuit de l&apos;archivage, des conditions de conservation et de communication des archives </li> <li>Elaboration et mise &#xE0; jour de proc&#xE9;dures qualit&#xE9; </li> <li>Encadrement d&apos;&#xE9;quipe(s), gestion et d&#xE9;veloppement des personnels </li> <li>Expertise des processus informationnels en vue de leur archivage et ce d&#xE8;s leur cr&#xE9;ation <br><em>concerne le dossier patient, mais aussi tous les autres documents administratifs</em> </li> <li>Formation et information des professionnels et &#xE9;tudiants </li> <li>Mise en oeuvre des dispositions l&#xE9;gales, notamment celles relatives aux dur&#xE9;es de conservation et &#xE0; la communication des archives </li> <li>Organisation de la collecte, de la conservation et de la communication des fonds d&apos;archives </li> <li>Organisation et gestion de groupes de travail transversaux </li> <li>Promotion des r&#xE9;alisations / des projets sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Veille sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Animer et d&#xE9;velopper un r&#xE9;seau professionnel </li> <li>Arbitrer et/ou d&#xE9;cider entre diff&#xE9;rentes propositions, dans un environnement donn&#xE9; </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Concevoir, piloter et &#xE9;valuer des projets transversaux et des proc&#xE9;dures, relevant de son domaine de comp&#xE9;tence et en rendre compte </li> <li>&#xC9;valuer les pratiques professionnelles de soins dans son domaine de comp&#xE9;tence </li> <li>&#xC9;valuer, d&#xE9;velopper et valoriser les comp&#xE9;tences de ses collaborateurs </li> <li>Identifier les fonds d&apos;archives &#xE0; collecter, &#xE0; d&#xE9;truire et &#xE0; verser aux services d&apos;archives publics des archives historiques </li> <li>Organiser et classer des donn&#xE9;es, des informations, des documents de diverses natures </li> <li>Piloter et &#xE9;valuer un projet, relevant de son domaine de comp&#xE9;tence </li> <li>Piloter, animer / communiquer, motiver une ou plusieurs &#xE9;quipes </li> <li>Planifier, organiser, r&#xE9;partir la charge de travail et allouer les ressources pour leur r&#xE9;alisation </li> <li>Traduire la strat&#xE9;gie en orientations, en projets, en plans d&apos;actions et en moyens de r&#xE9;alisation </li> <li>Utiliser les logiciels m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><p>Aucune connaissance de d&#xE9;finie.</p></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Directeurs ou responsables des affaires g&#xE9;n&#xE9;rales, de l&apos;accueil des usagers, de la qualit&#xE9;, de la logistique, m&#xE9;decins DIM, pr&#xE9;sident CCM<br>Directions et responsables des services de l&apos;&#xE9;tablissement<br>Prestataires externes (externalisation, &#xE9;limination, d&#xE9;m&#xE9;nagement, commercialisation de solutions informatiques/logiciels m&#xE9;tiers, GED, AES...)<br>Administration des archives publiques (services d&#xE9;partementaux d&apos;archives)<br>Affaires juridiques<br>M&#xE9;decins et CME<br>Professionnels r&#xE9;alisant des contr&#xF4;les sur l&apos;activit&#xE9; de l&apos;&#xE9;tablissement (HAS et experts auditeurs, cour des comptes, s&#xE9;curit&#xE9; sociale,...)<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Master Pro archives et r&#xE9;seaux documentaires<br>Master m&#xE9;tiers d&apos;archives,<br>Master 2 m&#xE9;tiers archivistiques<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Corps des attach&#xE9;s d&apos;administration hospitalier<br>Corps des ing&#xE9;nieurs<br>Contractuel cat&#xE9;gorie A<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
INSERT INTO JobSheets (name, description, activities, knowHow, knowledge, infos) VALUES ('Technicien(ne) biomédical', '<h2>Information g&#xE9;n&#xE9;rale</h2><div class=\"info\"> <div class=\"intitule\"><p>D&#xC9;FINITION</p></div> <div class=\"description\"><p>R&#xE9;aliser des op&#xE9;rations de maintenance, d&apos;entretien, d&apos;installation des dispositifs et &#xE9;quipements biom&#xE9;dicaux. Il peut intervenir sur des r&#xE9;seaux de fluides m&#xE9;dicaux et &#xE9;quipements<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>AUTRES APPELLATIONS</p></div> <div class=\"description\"><p>Technicien(ne) de maintenance des &#xE9;quipements biom&#xE9;dicaux<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PR&#xC9;REQUIS REGLEMENTAIRES POUR EXERCER LE METIER</p></div> <div class=\"description\"><p>Formation de base Bac + 2/3<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>', '<h2>Activit&#xE9;s</h2><div class=\"info\"><ul> <li>Achat de produits / de prestations, dans son domaine </li> <li>Assistance, conseil et formation des &#xE9;quipes, des utilisateurs, sp&#xE9;cifiques au domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le de l&apos;application des r&#xE8;gles, proc&#xE9;dures, normes et standards, dans son domaine d&apos;activit&#xE9; </li> <li>Contr&#xF4;le et suivi de l&apos;&#xE9;tat et du fonctionnement des &#xE9;quipements / des installations, sp&#xE9;cifiques &#xE0; son domaine </li> <li>Contr&#xF4;le et suivi du bon d&#xE9;roulement du (des) process sp&#xE9;cifique(s) au domaine d&apos;activit&#xE9; </li> <li>Entretien, maintenance, pr&#xE9;vention et d&#xE9;pannage des mat&#xE9;riels, outillages, &#xE9;quipements, syst&#xE8;mes, dans son domaine d&apos;activit&#xE9; </li> <li>&#xC9;tablissement / actualisation, organisation et mise en oeuvre de processus, proc&#xE9;dures, protocoles, consignes, sp&#xE9;cifiques &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Intervention technique suite &#xE0; mat&#xE9;riel ou mat&#xE9;riau d&#xE9;fectueux </li> <li>Mise en r&#xE9;seau des &#xE9;quipements biom&#xE9;dicaux </li> <li>Utilisation de la Gestion de maintenance Assist&#xE9;e par Ordinateur (GMAO) dont il est garant de la qualit&#xE9; des donn&#xE9;es qui y sont saisies </li> </ul></div>', '<h2>Savoir-Faire</h2><div class=\"info\"><ul> <li>Accompagner une personne dans la r&#xE9;alisation de ses activit&#xE9;s quotidiennes </li> <li>Choisir et utiliser des mat&#xE9;riels, des outils de travail ou / et de contr&#xF4;le, aff&#xE9;rents &#xE0; son m&#xE9;tier </li> <li>Concevoir, formaliser et adapter des proc&#xE9;dures / protocoles / modes op&#xE9;ratoires / consignes relatives &#xE0; son domaine de comp&#xE9;tence </li> <li>Conduire des installations, des &#xE9;quipements relatifs au domaine de comp&#xE9;tence, en optimiser le fonctionnement </li> <li>&#xC9;valuer la conformit&#xE9; d&apos;un produit, d&apos;un mat&#xE9;riel, d&apos;une prestation au regard des normes internes/externes </li> <li>Identifier et diagnostiquer un dysfonctionnement, une panne, le d&#xE9;faut d&apos;un mat&#xE9;riel, d&apos;un &#xE9;quipement, une anomalie d&apos;un syst&#xE8;me, sp&#xE9;cifique &#xE0; son domaine d&apos;activit&#xE9; </li> <li>Maintenir et d&#xE9;panner un mat&#xE9;riel, un &#xE9;quipement, une installation et/ou un syst&#xE8;me relatif &#xE0; son m&#xE9;tier </li> <li>Utiliser des mat&#xE9;riels, des outils de d&#xE9;pannage, de r&#xE9;paration ou de maintenance, relatifs &#xE0; son m&#xE9;tier </li> </ul></div>', '<h2>Connaissances requises</h2><div class=\"info\"><table width=\"100%\" summary=\"connaissances associ&#xE9;es\"> <tbody><tr class=\"row_first\"> <th scope=\"col\" id=\"col0\">Description</th> <th scope=\"col\" id=\"col1\">Niveau de connaissance</th> </tr> <tr> <td headers=\"col0\">Achat </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">Anatomie, physiologie </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Anglais technique </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Automatisme </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Biom&#xE9;dical </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Economie de la maintenance </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> <tr> <td headers=\"col0\">&#xC9;lectronique </td> <td headers=\"col1\">Connaissances approfondies</td> </tr> <tr> <td headers=\"col0\">Fluides m&#xE9;dicaux </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Logiciel d&#xE9;di&#xE9; de gestion de maintenance biom&#xE9;dicale </td> <td headers=\"col1\">Connaissances op&#xE9;rationnelles</td> </tr> <tr> <td headers=\"col0\">Qualit&#xE9; </td> <td headers=\"col1\">Connaissances g&#xE9;n&#xE9;rales</td> </tr> </tbody></table> <ul> <li> Connaissances g&#xE9;n&#xE9;rales :<br><em>Connaissances g&#xE9;n&#xE9;rales propres &#xE0; un champ. L&apos;&#xE9;tendue des connaissances concern&#xE9;es est limit&#xE9;e &#xE0; des faits et des id&#xE9;es principales. Connaissances des notions de base, des principaux termes. Savoirs le plus souvent fragmentaires et peu contextualis&#xE9;s. Dur&#xE9;e d&apos;acquisition courte de quelques semaines maximum.</em> </li> <li> Connaissances op&#xE9;rationnelles :<br><em>Connaissances d&#xE9;taill&#xE9;es, pratiques et th&#xE9;oriques, d&apos;un champ ou d&apos;un domaine particulier incluant la connaissance des processus, des techniques et proc&#xE9;d&#xE9;s, des mat&#xE9;riaux, des instruments, de l&apos;&#xE9;quipement, de la terminologie et de quelques id&#xE9;es th&#xE9;oriques. Ces connaissances sont contextualis&#xE9;es. Dur&#xE9;e d&apos;acquisition de quelques mois &#xE0; un / deux ans.</em> </li> <li> Connaissances approfondies :<br><em>Connaissances th&#xE9;oriques et pratiques approfondies dans un champ donn&#xE9;. Ma&#xEE;trise des principes fondamentaux du domaine, permettant la mod&#xE9;lisation. Une partie de ces connaissances sont des connaissances avanc&#xE9;es ou de pointe. Dur&#xE9;e d&apos;acquisition de 2 &#xE0; 4/ 5 ans.</em> </li> </ul></div>', '<h2>Informations compl&#xE9;mentaires</h2><div class=\"info\"> <div class=\"intitule\"><p>RELATIONS PROFESSIONNELLES LES PLUS FR&#xC9;QUENTES</p></div> <div class=\"description\"><p>Les services cliniques et m&#xE9;dico-techniques pour la maintenance et la formation<br>les fournisseurs pour la planification et les interventions externes<br>les services techniques pour la maintenance et les mises en service<br>les services logistiques et g&#xE9;n&#xE9;raux pour la gestion et l&apos;organisation<br>les services &#xE9;conomiques et financiers pour la gestion administrative et financi&#xE8;re<br>le correspondant mat&#xE9;rio-vigilance pour l&apos;expertise technique<br>les cadres experts (hygi&#xE9;niste, radio-protecteur, m&#xE9;decin du travail.) pour l&apos;expertise technique<br>Les Conseillers en ing&#xE9;nierie biom&#xE9;dicale pour diagnostic de la panne, priorisation des interventions, arbitrage entre maintenance interne ou recours &#xE0; un prestataire<br>Les m&#xE9;tiers du syst&#xE8;me d&apos;information pour la mise en r&#xE9;seaux des &#xE9;quipements m&#xE9;dicaux,<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>NATURE ET NIVEAU DE FORMATION POUR EXERCER LE M&#xC9;TIER</p></div> <div class=\"description\"><p>Bac+2/3 techniques biom&#xE9;dicales<br>Bac+2/3 Electronique,<br>Bac+2/3 &#xC9;lectrotechnique,<br>Bac+2/3 Informatique industrielle,<br>BTS, TSIBH, licence pro, DUT mesures physiques.<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>CORRESPONDANCES STATUTAIRES &#xC9;VENTUELLES</p></div> <div class=\"description\"><p>Technicien sup&#xE9;rieur hospitalier<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div> <div class=\"info\"> <div class=\"intitule\"><p>PASSERELLES</p></div> <div class=\"description\"><p>Encadrant maintenance biom&#xE9;dicale<br>Conseiller en ing&#xE9;nierie biom&#xE9;dicale<br>Encadrant maintenance &#xE9;lectrique / automatismes<br></p></div> <div class=\"mrpropre\">&#xA0;</div> </div>');
